INSERT INTO `creature_text` VALUES (23671, 0, 0, 'You think I do not know what you are trying to do? You think I haven\'t been watching you?', 14, 0, 100, 1, 0, 0, 0, 22787, 0, 'Halfdan the Ice-Hearted');
INSERT INTO `creature_text` VALUES (23671, 1, 0, 'You will not stop my ascension, tiny $r. Time to die!', 14, 0, 100, 1, 0, 0, 0, 22788, 0, 'Halfdan the Ice-Hearted');
INSERT INTO `creature_text` VALUES (23671, 2, 0, 'No! You will not defeat me!', 14, 0, 100, 0, 0, 0, 0, 22789, 0, 'Halfdan the Ice-Hearted');
INSERT INTO `creature_text` VALUES (23672, 0, 0, 'Halt! Identify yourself immediately!', 12, 9, 100, 0, 0, 0, 0, 23903, 0, 'Garde');
INSERT INTO `creature_text` VALUES (23672, 0, 1, 'This section is off-limits!', 12, 9, 100, 0, 0, 0, 0, 23904, 0, 'Garde');
INSERT INTO `creature_text` VALUES (23672, 0, 2, 'I\'ve never seen one of you around here.', 12, 9, 100, 0, 0, 0, 0, 23905, 0, 'Garde');
INSERT INTO `creature_text` VALUES (23672, 0, 3, 'Hold it! Stop!', 12, 9, 100, 0, 0, 0, 0, 23906, 0, 'Garde');
INSERT INTO `creature_text` VALUES (23672, 0, 4, 'Stop!! Stop!!! Scrap metal moves itself now?', 12, 9, 100, 0, 0, 0, 0, 23907, 0, 'Garde');
INSERT INTO `creature_text` VALUES (23672, 1, 2, 'Right... my bad. Now where was I?', 12, 9, 100, 0, 0, 0, 0, 23910, 0, 'Garde');
INSERT INTO `creature_text` VALUES (23672, 1, 3, 'All hail the great creator!', 12, 9, 100, 0, 0, 0, 0, 23911, 0, 'Garde');
INSERT INTO `creature_text` VALUES (23672, 1, 4, 'You\'re right, of course. Carry on.', 12, 9, 100, 0, 0, 0, 0, 23912, 0, 'Garde');
INSERT INTO `creature_text` VALUES (23672, 1, 5, 'Forgive my insolence!', 12, 9, 100, 0, 0, 0, 0, 23913, 0, 'Garde');
