INSERT INTO `creature_text` VALUES (23678, 0, 0, '%s appears weak!', 16, 0, 100, 0, 0, 0, 0, 23041, 0, 'Chill Nymph');
INSERT INTO `creature_text` VALUES (23678, 1, 0, 'I knew Lurielle would send help! Thank you, friend, and give Lurielle my thanks as well!', 12, 0, 100, 1, 0, 0, 0, 23044, 0, 'Chill Nymph');
INSERT INTO `creature_text` VALUES (23678, 1, 1, 'Where am I? What happened to me? You... you freed me?', 12, 0, 100, 1, 0, 0, 0, 23043, 0, 'Chill Nymph');
INSERT INTO `creature_text` VALUES (23678, 1, 2, 'Thank you. I thought I would die without seeing my sisters again!', 12, 0, 100, 1, 0, 0, 0, 23042, 0, 'Chill Nymph');
INSERT INTO `creature_text` VALUES (23682, 0, 0, 'It is over, your search is done. Let fate choose now, the righteous one.', 14, 0, 100, 0, 0, 11961, 0, 22261, 0, 'headless horseman SAY_ENTRANCE');
INSERT INTO `creature_text` VALUES (23682, 1, 0, 'Here\'s my body, fit and pure!  Now, your blackened souls I\'ll cure!', 14, 0, 100, 0, 0, 12567, 0, 22271, 0, 'headless horseman SAY_REJOINED');
INSERT INTO `creature_text` VALUES (23682, 2, 0, 'Harken, cur! Tis you I spurn! Now feel... the burn!', 12, 0, 100, 0, 0, 0, 0, 22587, 0, 'headless horseman SAY_CONFLAGRATION');
INSERT INTO `creature_text` VALUES (23682, 3, 0, 'Soldiers arise, stand and fight! Bring victory at last to this fallen knight!', 14, 0, 100, 0, 0, 11963, 0, 23861, 0, 'headless horseman SAY_SPROUTING_PUMPKINS');
INSERT INTO `creature_text` VALUES (23682, 4, 0, 'This end have I reached before.  What new adventure lies in store?', 14, 0, 100, 0, 0, 11964, 0, 23455, 0, 'headless horseman SAY_DEATH');
INSERT INTO `creature_text` VALUES (23685, 0, 0, 'YOU WANT DRINK?!  WE GOT DRINK!', 14, 0, 100, 0, 0, 0, 0, 28559, 0, 'Gordok Brew Barker');
INSERT INTO `creature_text` VALUES (23685, 0, 1, 'HEY YOU!  DRINK OGRE BREWS!  MAKE YOU BIG AND STRONG!', 14, 0, 100, 0, 0, 0, 0, 28560, 0, 'Gordok Brew Barker');
INSERT INTO `creature_text` VALUES (23685, 0, 2, 'YOU TRY DA\' BEST, NOW TRY DA\' REST!  OGRE BREW!!', 14, 0, 100, 0, 0, 0, 0, 28561, 0, 'Gordok Brew Barker');
INSERT INTO `creature_text` VALUES (23720, 0, 0, 'Thank you! There\'s no telling what those brutes would\'ve done to me.', 12, 0, 100, 18, 0, 0, 0, 22438, 0, 'Theramore Prisoner');
INSERT INTO `creature_text` VALUES (23728, 0, 0, '%s clears his throat.', 16, 7, 100, 0, 0, 0, 0, 22737, 0, 'Guard Captain Zorek');
INSERT INTO `creature_text` VALUES (23728, 1, 0, 'I look across this base and all I see is fear... Are you gonna be scared when the Lich King\'s armies are sittin\' pretty in Stormwind? Ironforge? NO! You\'re not. And do you know why? Because you\'ll be dead, that\'s why.', 12, 7, 100, 1, 0, 0, 0, 22738, 0, 'Guard Captain Zorek');
INSERT INTO `creature_text` VALUES (23728, 2, 0, 'So when you\'re feeling that tinglin\' sensation at the base of your spine and you\'re \'bout ready to wet your pants, embrace it. Let it enrage you. That fear is the only thing that\'s gonna keep you breathin\'.', 12, 7, 100, 25, 0, 0, 0, 22739, 0, 'Guard Captain Zorek');
INSERT INTO `creature_text` VALUES (23728, 3, 0, 'Am I clear? Do you understand what I\'m sayin\'? I don\'t wanna have to send any more coffins or letters home.', 12, 7, 100, 6, 0, 0, 0, 22740, 0, 'Guard Captain Zorek');
INSERT INTO `creature_text` VALUES (23734, 0, 0, 'That should slow the infection down a little.', 12, 7, 100, 1, 0, 0, 0, 22251, 0, 'Anchorite Yazmina');
INSERT INTO `creature_text` VALUES (23734, 1, 0, 'I can only hope the treatment will work.', 12, 7, 100, 1, 0, 0, 0, 22828, 0, 'Anchorite Yazmina');
INSERT INTO `creature_text` VALUES (23734, 2, 0, 'I just don\'t have the medical supplies I need to properly treat all of you.', 12, 7, 100, 1, 0, 5495, 0, 22829, 0, 'Anchorite Yazmina');
INSERT INTO `creature_text` VALUES (23734, 3, 0, 'Oh my, let me change those bandages!', 12, 7, 100, 5, 0, 0, 0, 22250, 0, 'Anchorite Yazmina');
INSERT INTO `creature_text` VALUES (23734, 4, 0, 'Don\'t worry, soldier. Yazmina is going to take care of you.', 12, 7, 100, 1, 0, 0, 0, 22830, 0, 'Anchorite Yazmina');
INSERT INTO `creature_text` VALUES (23734, 5, 0, '%s shakes her head.', 16, 7, 100, 274, 0, 0, 0, 22831, 0, 'Anchorite Yazmina');
INSERT INTO `creature_text` VALUES (23736, 0, 0, 'Guards!', 14, 6, 100, 0, 0, 0, 0, 4583, 0, 'Pricilla Winterwind');
INSERT INTO `creature_text` VALUES (23768, 1, 0, 'James? James... No, you\'re not James, but I know who you are...', 12, 0, 100, 0, 0, 0, 0, 22244, 0, 'Lynn Hyal');
INSERT INTO `creature_text` VALUES (23768, 2, 0, 'You\'re the one who tracked down the brutes who did this to us.', 12, 0, 100, 0, 0, 0, 0, 22245, 0, 'Lynn Hyal');
INSERT INTO `creature_text` VALUES (23768, 3, 0, 'I tried so hard to tell Jim... to tell anyone... who was behind this, but I couldn\'t find a way...', 12, 0, 100, 0, 0, 0, 0, 22246, 0, 'Lynn Hyal');
INSERT INTO `creature_text` VALUES (23768, 4, 0, 'Thank you for helping us and for helping Jim. If you see him, tell him little Jimmy and I love him and that we\'re waiting for him.', 12, 0, 100, 0, 0, 0, 0, 22247, 0, 'Lynn Hyal');
INSERT INTO `creature_text` VALUES (23768, 5, 0, 'I don\'t know when we\'ll see Daddy again, Jimmy, but I know he loves you and he misses you very much.', 12, 0, 100, 0, 0, 0, 0, 22249, 0, 'Lynn Hyal');
INSERT INTO `creature_text` VALUES (23769, 1, 0, 'Mommy, when will we see Daddy again?', 12, 0, 100, 0, 0, 0, 0, 22248, 0, 'Jimmy Hyal');
INSERT INTO `creature_text` VALUES (23775, 0, 0, 'So eager you are, for my blood to spill. Yet to vanquish me, \'tis my head you must kill!', 14, 0, 100, 0, 0, 11969, 0, 22757, 0, 'headless horseman SAY_LOST_HEAD');
INSERT INTO `creature_text` VALUES (23775, 1, 0, 'Your body lies beaten, battered and broken! Let my curse be your own, fate has spoken!', 14, 0, 100, 0, 0, 11962, 0, 40546, 0, 'headless horseman SAY_PLAYER_DEATH');
INSERT INTO `creature_text` VALUES (23778, 0, 0, 'Keep them split from their main force!', 14, 0, 100, 0, 0, 0, 0, 22382, 0, 'Dark Ranger Lyana');
INSERT INTO `creature_text` VALUES (23778, 0, 1, 'Cover fire, now!', 14, 0, 100, 0, 0, 0, 0, 22381, 0, 'Dark Ranger Lyana');
INSERT INTO `creature_text` VALUES (23778, 0, 2, 'Keep firing!  Cut off their reinforcements!', 14, 0, 100, 0, 0, 0, 0, 22380, 0, 'Dark Ranger Lyana');
INSERT INTO `creature_text` VALUES (23780, 0, 0, 'Archers at the ready!  Hold your fire!', 14, 0, 100, 0, 0, 0, 0, 22662, 0, 'High Executor Anselm');
INSERT INTO `creature_text` VALUES (23780, 1, 0, 'What fool dares to enter her majesty\'s dominion unannounced?', 14, 0, 100, 0, 0, 0, 0, 22663, 0, 'High Executor Anselm');
INSERT INTO `creature_text` VALUES (23780, 2, 0, 'Is that all you\'ve come to say?', 12, 0, 100, 0, 0, 0, 0, 22686, 0, 'High Executor Anselm');
INSERT INTO `creature_text` VALUES (23780, 3, 0, 'Send these scumbags back to hell!  Fire at will!', 14, 0, 100, 0, 0, 0, 0, 22687, 0, 'High Executor Anselm');
INSERT INTO `creature_text` VALUES (23782, 0, 0, 'Reports from the test on the enemy fleet have come in, sir!', 12, 0, 100, 1, 0, 0, 0, 22793, 0, 'Apothecary Ravien');
INSERT INTO `creature_text` VALUES (23782, 1, 0, 'Well... the good news is we forced the reinforcements to turn back.', 12, 0, 100, 6, 0, 0, 0, 22794, 0, 'Apothecary Ravien');
INSERT INTO `creature_text` VALUES (23782, 2, 0, 'The plague was not quite... fatal.  However, reports of harmful effects include nausea, loss of equilibrium and severe stomach pain.', 12, 0, 100, 274, 0, 0, 0, 22795, 0, 'Apothecary Ravien');
INSERT INTO `creature_text` VALUES (23782, 3, 0, 'Yes, sir!', 12, 0, 100, 273, 0, 0, 0, 22796, 0, 'Apothecary Ravien');
INSERT INTO `creature_text` VALUES (23784, 0, 0, 'You couldn\'t have come at a better time!  Let\'s get out of here.', 12, 1, 100, 0, 0, 0, 0, 22485, 0, 'Apothecary Hanes');
INSERT INTO `creature_text` VALUES (23784, 1, 0, 'Yes, let us leave... but not before we leave our Alliance hosts something to remember us by!', 12, 1, 100, 0, 0, 0, 0, 22486, 0, 'Apothecary Hanes');
INSERT INTO `creature_text` VALUES (23784, 2, 0, 'They have limited supplies in this camp.  It would be a real shame if something were to happen to them.', 12, 1, 100, 0, 0, 0, 0, 22487, 0, 'Apothecary Hanes');
INSERT INTO `creature_text` VALUES (23784, 3, 0, 'Ah, yes... watch it burn!', 12, 1, 100, 0, 0, 0, 0, 22489, 0, 'Apothecary Hanes');
INSERT INTO `creature_text` VALUES (23784, 4, 0, 'We\'re almost done!', 12, 1, 100, 0, 0, 0, 0, 22490, 0, 'Apothecary Hanes');
INSERT INTO `creature_text` VALUES (23784, 5, 0, 'That\'ll teach you to mess with an apothecary, you motherless Alliance dogs!', 14, 1, 100, 0, 0, 0, 0, 22491, 0, 'Apothecary Hanes');
INSERT INTO `creature_text` VALUES (23784, 6, 0, 'Let\'s high-tail it out of here.', 12, 1, 100, 0, 0, 0, 0, 22492, 0, 'Apothecary Hanes');
INSERT INTO `creature_text` VALUES (23784, 7, 0, 'Don\'t shoot!  Apothecary coming through!', 14, 1, 100, 0, 0, 0, 0, 22493, 0, 'Apothecary Hanes');
INSERT INTO `creature_text` VALUES (23784, 8, 0, 'We\'re in your debt, friend.  Speak to Lysander at Vengeance Landing; he will arrange a suitable reward for you.', 12, 1, 100, 0, 0, 0, 0, 22494, 0, 'Apothecary Hanes');
INSERT INTO `creature_text` VALUES (23795, 0, 0, 'No way!', 12, 0, 100, 15, 0, 0, 0, 22397, 0, 'Dark Iron Antagonist SAY_ANTAGONIST_1');
INSERT INTO `creature_text` VALUES (23795, 0, 1, 'NO!', 12, 0, 100, 15, 0, 0, 0, 22399, 0, 'Dark Iron Antagonist SAY_ANTAGONIST_1');
INSERT INTO `creature_text` VALUES (23795, 0, 2, 'Not on your life', 12, 0, 100, 15, 0, 0, 0, 22398, 0, 'Dark Iron Antagonist SAY_ANTAGONIST_1');
INSERT INTO `creature_text` VALUES (23795, 1, 0, 'Yeah!', 12, 0, 100, 15, 0, 0, 0, 22393, 0, 'Dark Iron Antagonist SAY_ANTAGONIST_2');
