INSERT INTO `creature_text` VALUES (24023, 2, 0, 'The ritual proceeds too slowly. More sacrifices are needed!', 14, 0, 100, 0, 0, 0, 0, 23802, 0, 'Queen Angerboda');
INSERT INTO `creature_text` VALUES (24023, 2, 1, 'Soon, my love. Soon you will waken and all of Azeroth will know fear!', 14, 0, 100, 0, 0, 0, 0, 23795, 0, 'Queen Angerboda');
INSERT INTO `creature_text` VALUES (24023, 2, 2, 'Awaken, Ymiron, the time of your continued reign is nigh.', 14, 0, 100, 0, 0, 0, 0, 23796, 0, 'Queen Angerboda');
INSERT INTO `creature_text` VALUES (24023, 2, 3, 'Double your efforts! King Ymiron will awaken and then he will ascend!', 14, 0, 100, 0, 0, 0, 0, 23799, 0, 'Queen Angerboda');
INSERT INTO `creature_text` VALUES (24023, 2, 4, 'Sleep no longer, my love. With the might of the Lich King behind you, the world will tremble.', 14, 0, 100, 0, 0, 0, 0, 23797, 0, 'Queen Angerboda');
INSERT INTO `creature_text` VALUES (24023, 2, 5, 'Why is this ritual taking so long? Your master promised that your assistance would speed the process!', 14, 0, 100, 0, 0, 0, 0, 23798, 0, 'Queen Angerboda');
INSERT INTO `creature_text` VALUES (24032, 0, 0, 'Guards!', 14, 6, 100, 0, 0, 0, 0, 4583, 0, 'Celea Frozenmane');
INSERT INTO `creature_text` VALUES (24035, 0, 0, 'Please, let me out of here!', 12, 0, 100, 0, 0, 0, 0, 22637, 0, 'Gjalerbron Prisoner');
INSERT INTO `creature_text` VALUES (24035, 0, 1, 'The key... you have to find the key!', 12, 0, 100, 0, 0, 0, 0, 22638, 0, 'Gjalerbron Prisoner');
INSERT INTO `creature_text` VALUES (24035, 0, 2, 'I think I saw a key on one of those vrykul.', 12, 0, 100, 0, 0, 0, 0, 22639, 0, 'Gjalerbron Prisoner');
INSERT INTO `creature_text` VALUES (24035, 0, 3, 'I don\'t want to be taken down into the catacombs!', 12, 0, 100, 0, 0, 0, 0, 22640, 0, 'Gjalerbron Prisoner');
INSERT INTO `creature_text` VALUES (24035, 0, 4, 'I don\'t want to be sacrificed!', 12, 0, 100, 0, 0, 0, 0, 22641, 0, 'Gjalerbron Prisoner');
INSERT INTO `creature_text` VALUES (24035, 0, 5, 'They\'re performing unholy rites down in the catacombs, and I\'m about to be the sacrifice!', 12, 0, 100, 0, 0, 0, 0, 22642, 0, 'Gjalerbron Prisoner');
INSERT INTO `creature_text` VALUES (24035, 0, 6, 'Is there no hero to rescue me?', 12, 0, 100, 0, 0, 0, 0, 22643, 0, 'Gjalerbron Prisoner');
INSERT INTO `creature_text` VALUES (24035, 0, 7, 'This is the end.', 12, 0, 100, 0, 0, 0, 0, 22644, 0, 'Gjalerbron Prisoner');
INSERT INTO `creature_text` VALUES (24035, 0, 8, 'I think I saw a key on one of those Gjalerbron vrykul up top.', 12, 0, 100, 0, 0, 0, 0, 23649, 0, 'Gjalerbron Prisoner');
INSERT INTO `creature_text` VALUES (24035, 1, 0, 'Oh, thank you, thank you!', 12, 0, 100, 0, 0, 0, 0, 22645, 0, 'Gjalerbron Prisoner');
INSERT INTO `creature_text` VALUES (24035, 1, 1, 'Thank you.  I will not forget this!', 12, 0, 100, 0, 0, 0, 0, 22646, 0, 'Gjalerbron Prisoner');
INSERT INTO `creature_text` VALUES (24035, 1, 2, 'Avenge my friends who have already been sacrificed!', 12, 0, 100, 0, 0, 0, 0, 22647, 0, 'Gjalerbron Prisoner');
INSERT INTO `creature_text` VALUES (24035, 1, 3, 'You have to put a stop to this madness before it\'s too late!', 12, 0, 100, 0, 0, 0, 0, 22648, 0, 'Gjalerbron Prisoner');
INSERT INTO `creature_text` VALUES (24035, 1, 4, 'My thanks to you, hero!', 12, 0, 100, 0, 0, 0, 0, 22649, 0, 'Gjalerbron Prisoner');
INSERT INTO `creature_text` VALUES (24035, 1, 5, 'The vrykul are evil and must be destroyed!', 12, 0, 100, 0, 0, 0, 0, 22650, 0, 'Gjalerbron Prisoner');
INSERT INTO `creature_text` VALUES (24035, 1, 6, 'You saved my life!', 12, 0, 100, 0, 0, 0, 0, 22651, 0, 'Gjalerbron Prisoner');
INSERT INTO `creature_text` VALUES (24035, 1, 7, 'Thank goodness for you!', 12, 0, 100, 0, 0, 0, 0, 22652, 0, 'Gjalerbron Prisoner');
INSERT INTO `creature_text` VALUES (24041, 0, 0, 'Ah, but it is you who intrudes on our master\'s territory.  He could wipe you out in an instant for that transgression alone!  Arthas does not have much love or patience for his escaped slaves.', 12, 0, 100, 0, 0, 0, 0, 22665, 0, 'Prince Keleseth');
INSERT INTO `creature_text` VALUES (24041, 1, 0, 'But... he has learned of your victory over Stormwind\'s North Fleet and thinks you have potential.  Potential to see reason and abandon Sylvanas\' childish rebellion.', 12, 0, 100, 0, 0, 0, 0, 22669, 0, 'Prince Keleseth');
INSERT INTO `creature_text` VALUES (24041, 2, 0, 'Arthas is prepared to offer you power beyond your imagination.  The puny army you lead here would pale in comparison to the phalanxes at your command if you returned to the Scourge\'s embrace.', 12, 0, 100, 0, 0, 0, 0, 22671, 0, 'Prince Keleseth');
INSERT INTO `creature_text` VALUES (24041, 3, 0, 'Behold the Vrykul!   A race that has perfected war and destruction to the point of an art form.  Already they\'ve cast their lot with the Lich King!  Their dwellings surround you and their numbers are easily five times yours.', 12, 0, 100, 0, 0, 0, 0, 22722, 0, 'Prince Keleseth');
INSERT INTO `creature_text` VALUES (24041, 4, 0, 'The choice is yours, Anselm.  Return to the Lich King\'s army and fight alongside them or remain loyal to your so-called queen and suffer their wrath as they drive you from their homelands!', 12, 0, 100, 0, 0, 0, 0, 22723, 0, 'Prince Keleseth');
INSERT INTO `creature_text` VALUES (24041, 5, 0, 'Such a futile gesture.', 12, 0, 100, 0, 0, 0, 0, 22758, 0, 'Prince Keleseth');
INSERT INTO `creature_text` VALUES (24041, 6, 0, 'Listen to your men\'s dying breaths as I drink in their souls!', 12, 0, 100, 0, 0, 0, 0, 22766, 0, 'Prince Keleseth');
INSERT INTO `creature_text` VALUES (24041, 7, 0, 'This will not be the last you hear of me.  I will return to spit on your corpse after Utgarde\'s armies have descended upon you!', 12, 0, 100, 0, 0, 0, 0, 22764, 0, 'Prince Keleseth');
INSERT INTO `creature_text` VALUES (24042, 0, 0, '$N\'s concoction is ruined.  A new one must be created from scratch.', 16, 0, 100, 0, 0, 0, 0, 23540, 0, 'Generic Trigger LAB');
INSERT INTO `creature_text` VALUES (24042, 1, 0, 'The failed concoction spills on the floor and appears to come to life!', 16, 0, 100, 0, 0, 0, 0, 23071, 0, 'Generic Trigger LAB');
INSERT INTO `creature_text` VALUES (24043, 0, 0, 'A creature emerges from the shadows!', 16, 0, 100, 0, 0, 0, 0, 22783, 0, 'Amani Lynx');
INSERT INTO `creature_text` VALUES (24052, 0, 0, 'We don\'t shoe no mules here. \'Sides, it\'s strictly expedition business right now.', 12, 7, 100, 1, 0, 0, 0, 23562, 0, 'Eldrim Mounder');
INSERT INTO `creature_text` VALUES (24052, 1, 0, 'Not my problem! If yer so antsy to get yerself a house built, do it \'ere in the fort and quit yer whinin\'!', 12, 7, 100, 274, 0, 0, 0, 23564, 0, 'Eldrim Mounder');
INSERT INTO `creature_text` VALUES (24052, 2, 0, 'Now, if you\'d be so kind as to get your ass away from my forge...', 12, 7, 100, 25, 0, 0, 0, 23566, 0, 'Eldrim Mounder');
INSERT INTO `creature_text` VALUES (24061, 0, 0, 'Guards!', 14, 6, 100, 0, 0, 0, 0, 4583, 0, 'James Ormsby');
INSERT INTO `creature_text` VALUES (24068, 0, 0, 'Ingvar! Your pathetic failure will serve as a warning to all... you are damned! Arise and carry out the master\'s will!', 14, 0, 100, 0, 0, 13754, 0, 29980, 0, 'Annhylde the Caller to Ingvar the Plunderer');
INSERT INTO `creature_text` VALUES (24078, 0, 0, '%s goes into a frenzy!', 16, 0, 100, 0, 0, 0, 0, 38630, 0, 'Dragonflayer Metalworker');
INSERT INTO `creature_text` VALUES (24080, 0, 0, 'I spit on you!', 12, 0, 100, 0, 0, 0, 0, 30503, 0, 'Dragonflayer Weaponsmith');
INSERT INTO `creature_text` VALUES (24118, 0, 0, 'How unfortunate, Halfdan.', 14, 0, 100, 0, 0, 0, 0, 22791, 0, 'Val\'kyr Observer');
INSERT INTO `creature_text` VALUES (24118, 1, 0, 'We find you unworthy of ascension, Halfdan. Let your soul know oblivion!', 14, 0, 100, 0, 0, 0, 0, 22792, 0, 'Val\'kyr Observer');
INSERT INTO `creature_text` VALUES (24120, 0, 0, 'Someone shoot that bat down!', 14, 0, 100, 0, 0, 0, 0, 22835, 0, 'North Fleet Reservist');
INSERT INTO `creature_text` VALUES (24120, 0, 1, 'That liquid... it reeks!', 14, 0, 100, 0, 0, 0, 0, 22837, 0, 'North Fleet Reservist');
INSERT INTO `creature_text` VALUES (24120, 0, 2, 'I don\'t feel so good...', 14, 0, 100, 0, 0, 0, 0, 22836, 0, 'North Fleet Reservist');
INSERT INTO `creature_text` VALUES (24120, 0, 3, 'It was awful... I dreamt I was fighting against my friends.', 12, 0, 66, 0, 0, 0, 0, 35293, 0, 'North Fleet Reservist');
INSERT INTO `creature_text` VALUES (24120, 0, 4, 'Help!  My eyes!', 14, 0, 33, 0, 0, 0, 0, 22834, 0, 'North Fleet Reservist');
INSERT INTO `creature_text` VALUES (24123, 0, 0, 'These are all excellent. It\'s going to be hard to choose just one.', 12, 0, 100, 1, 0, 0, 0, 22854, 0, 'Nokoma Snowseer');
INSERT INTO `creature_text` VALUES (24123, 1, 0, 'I think I\'ve found the one I\'ll use. Now I have to begin the task of carving it. I don\'t think it\'s going to be easy. I don\'t even know what a female yeti\'s mating call sounds like!', 12, 0, 100, 396, 0, 0, 0, 22855, 0, 'Nokoma Snowseer');
INSERT INTO `creature_text` VALUES (24123, 2, 0, 'I\'ve got most of the basic carving done, but it needs to be tested before I can continue. Are you willing to help me with that, $n?', 12, 0, 100, 396, 0, 0, 0, 22856, 0, 'Nokoma Snowseer');
INSERT INTO `creature_text` VALUES (24126, 0, 0, 'Excellent.  What\'s the lowdown?  Do we have a bodycount?', 12, 0, 100, 11, 0, 0, 0, 22797, 0, 'Apothecary Lysander');
INSERT INTO `creature_text` VALUES (24126, 1, 0, 'Well, spit it out!  What\'s the bad news?', 12, 0, 100, 1, 0, 0, 0, 22798, 0, 'Apothecary Lysander');
INSERT INTO `creature_text` VALUES (24126, 2, 0, 'Severe stomach pain?', 12, 0, 100, 5, 0, 0, 0, 22800, 0, 'Apothecary Lysander');
INSERT INTO `creature_text` VALUES (24126, 3, 0, 'SEVERE STOMACH PAIN?', 14, 0, 100, 15, 0, 0, 0, 22801, 0, 'Apothecary Lysander');
INSERT INTO `creature_text` VALUES (24126, 4, 0, 'We are the Royal Apothecary Society!  Inducing belly aches is not part of our job description!', 12, 0, 100, 0, 0, 0, 0, 22802, 0, 'Apothecary Lysander');
INSERT INTO `creature_text` VALUES (24126, 5, 0, 'Am I surrounded by amateurs?  I want a full analysis of the new strain by tomorrow morning!  Get on it, Ravien!', 12, 0, 100, 25, 0, 0, 0, 22803, 0, 'Apothecary Lysander');
INSERT INTO `creature_text` VALUES (24126, 6, 0, 'We were making progress with our plague in Undercity.  All of a sudden our strains keep failing!', 12, 0, 100, 5, 0, 0, 0, 22406, 0, 'Apothecary Lysander');
INSERT INTO `creature_text` VALUES (24126, 6, 1, 'The last ten strains from Undercity... all of them worthless!', 12, 0, 100, 5, 0, 0, 0, 22403, 0, 'Apothecary Lysander');
INSERT INTO `creature_text` VALUES (24126, 6, 2, 'I cannot work under these conditions!  We need a viable strain!', 12, 0, 100, 5, 0, 0, 0, 22404, 0, 'Apothecary Lysander');
INSERT INTO `creature_text` VALUES (24151, 0, 0, 'What did you do with the people of this village!?', 12, 7, 100, 5, 0, 0, 0, 22882, 0, 'Daegarn');
INSERT INTO `creature_text` VALUES (24151, 0, 1, 'What do you want with me?', 12, 7, 100, 5, 0, 0, 0, 22880, 0, 'Daegarn');
INSERT INTO `creature_text` VALUES (24151, 0, 2, 'Release me, cowards! I\'ll show you what a dwarf is made of!', 12, 7, 100, 5, 0, 0, 0, 22881, 0, 'Daegarn');
INSERT INTO `creature_text` VALUES (24152, 0, 0, 'Wait a minute...', 12, 0, 100, 5, 0, 0, 0, 22958, 0, 'Apothecary Malthus');
INSERT INTO `creature_text` VALUES (24152, 1, 0, 'It\'s just...', 12, 0, 100, 6, 0, 0, 0, 22959, 0, 'Apothecary Malthus');
INSERT INTO `creature_text` VALUES (24152, 2, 0, 'Beer! Mere beer!', 12, 0, 100, 1, 0, 0, 0, 22960, 0, 'Apothecary Malthus');
INSERT INTO `creature_text` VALUES (24152, 3, 0, 'There\'s absolutely nothing out of the ordinary about it.', 12, 0, 100, 1, 0, 0, 0, 22961, 0, 'Apothecary Malthus');
INSERT INTO `creature_text` VALUES (24152, 4, 0, 'You! Not a word to Grick about this! Do you hear me?', 12, 0, 100, 25, 0, 0, 0, 22962, 0, 'Apothecary Malthus');
INSERT INTO `creature_text` VALUES (24155, 0, 0, 'Guards!', 14, 6, 100, 0, 0, 0, 0, 4583, 0, 'Tobias Sarkhoff');
INSERT INTO `creature_text` VALUES (24161, 0, 0, 'Enemies of the Winterskorn, you will be torn limb from limb!', 14, 0, 100, 0, 0, 0, 0, 22867, 0, 'Oric the Baleful');
INSERT INTO `creature_text` VALUES (24162, 0, 0, 'Gunnar, son of Thorvard, has arrived!  Prepare to meet your demise!', 14, 0, 100, 0, 0, 0, 0, 22863, 0, 'Gunnar Thorvardsson');
INSERT INTO `creature_text` VALUES (24168, 0, 0, 'AGH! Not again with the sprayin\'!', 12, 6, 100, 0, 0, 0, 0, 22869, 0, 'Micah Stonebreaker');
INSERT INTO `creature_text` VALUES (24168, 0, 1, 'What\'s that n- AGH!', 12, 6, 100, 0, 0, 0, 0, 22870, 0, 'Micah Stonebreaker');
INSERT INTO `creature_text` VALUES (24168, 0, 2, 'Brann\'s beard, just STOP!', 12, 6, 100, 0, 0, 0, 0, 22871, 0, 'Micah Stonebreaker');
INSERT INTO `creature_text` VALUES (24168, 0, 3, 'The dwarf coughs, sputters, and shivers in the cage.', 16, 0, 100, 0, 0, 0, 0, 22872, 0, 'Micah Stonebreaker');
INSERT INTO `creature_text` VALUES (24168, 0, 4, 'A low, despairing moan escapes the caged dwarf.', 16, 0, 100, 0, 0, 0, 0, 22873, 0, 'Micah Stonebreaker');
INSERT INTO `creature_text` VALUES (24168, 0, 5, '%s shakes the bars of the cage weakly.', 16, 0, 100, 0, 0, 0, 0, 22874, 0, 'Micah Stonebreaker');
INSERT INTO `creature_text` VALUES (24168, 0, 6, '%s slumps against the cage bars dejectedly.', 16, 0, 100, 0, 0, 0, 0, 22875, 0, 'Micah Stonebreaker');
INSERT INTO `creature_text` VALUES (24173, 0, 0, 'Are YOU the one who interrupted my dinner? Well, are you?', 12, 0, 100, 1, 0, 0, 0, 22876, 0, 'Frostgore');
INSERT INTO `creature_text` VALUES (24173, 1, 0, 'You\'re too ugly and scrawny to be a female yeti! And now you\'re making me mad...', 12, 0, 100, 25, 0, 0, 0, 22877, 0, 'Frostgore');
INSERT INTO `creature_text` VALUES (24175, 0, 0, 'Akil\'zon, the invaders approach!', 14, 0, 100, 5, 0, 0, 0, 22971, 0, 'Amani\'shi Lookout');
INSERT INTO `creature_text` VALUES (24177, 0, 1, 'Light... light so bright...', 12, 0, 50, 0, 0, 0, 0, 22900, 0, 'Decomposing Ghoul - On spell hit');
INSERT INTO `creature_text` VALUES (24177, 0, 2, 'ARGHHHH...', 12, 0, 50, 0, 0, 0, 0, 22899, 0, 'Decomposing Ghoul - On spell hit');
INSERT INTO `creature_text` VALUES (24177, 0, 3, 'Away... go...', 12, 0, 50, 0, 0, 0, 0, 22902, 0, 'Decomposing Ghoul - On spell hit');
INSERT INTO `creature_text` VALUES (24177, 0, 4, 'Bad light... hurt...', 12, 0, 50, 0, 0, 0, 0, 22901, 0, 'Decomposing Ghoul - On spell hit');
INSERT INTO `creature_text` VALUES (24177, 0, 5, 'BURNS! IT BURNS!', 12, 0, 50, 0, 0, 0, 0, 22898, 0, 'Decomposing Ghoul - On spell hit');
INSERT INTO `creature_text` VALUES (24177, 1, 1, 'So... Hungry...', 12, 0, 50, 0, 0, 0, 0, 22893, 0, 'Decomposing Ghoul - OOC');
INSERT INTO `creature_text` VALUES (24177, 1, 2, 'Closer... come closer...', 12, 0, 50, 0, 0, 0, 0, 22896, 0, 'Decomposing Ghoul - OOC');
INSERT INTO `creature_text` VALUES (24177, 1, 3, 'FEED ME...', 12, 0, 50, 0, 0, 0, 0, 22894, 0, 'Decomposing Ghoul - OOC');
INSERT INTO `creature_text` VALUES (24177, 1, 4, 'FOOD! EAT YOU!', 12, 0, 50, 0, 0, 0, 0, 22895, 0, 'Decomposing Ghoul - OOC');
INSERT INTO `creature_text` VALUES (24177, 1, 5, 'BRAINNNS!', 12, 0, 50, 0, 0, 0, 0, 22892, 0, 'Decomposing Ghoul - OOC');
INSERT INTO `creature_text` VALUES (24178, 0, 0, '%s wakes up from the smell of fresh meat!', 16, 0, 100, 15, 0, 0, 0, 0, 0, 'Shatterhorn on aggro line and roar at same time');
INSERT INTO `creature_text` VALUES (24199, 0, 0, '%s loses all self control and begins to attack friend and foe alike!', 16, 0, 100, 0, 0, 0, 0, 23081, 0, 'Plagued Dragonflayer Handler');
INSERT INTO `creature_text` VALUES (24200, 0, 0, 'Dalronn! See if you can muster the nerve to join my attack!', 14, 0, 100, 0, 0, 13229, 0, 29531, 0, 'skarvald YELL_SKARVALD_AGGRO');
INSERT INTO `creature_text` VALUES (24200, 1, 0, 'Not... over... yet.', 14, 0, 100, 0, 0, 13230, 0, 29534, 0, 'skarvald YELL_SKARVALD_DAL_DIED');
INSERT INTO `creature_text` VALUES (24200, 2, 0, 'A warrior\'s death.', 14, 0, 100, 0, 0, 13231, 0, 29535, 0, 'skarvald YELL_SKARVALD_SKA_DIEDFIRST');
INSERT INTO `creature_text` VALUES (24200, 3, 0, 'Jarggn olkt!', 14, 0, 100, 0, 0, 13232, 0, 29532, 0, 'skarvald YELL_SKARVALD_KILL');
INSERT INTO `creature_text` VALUES (24200, 4, 0, 'Pfagh! What sort of necromancer lets death stop him? I knew you were worthless!', 14, 0, 100, 0, 0, 13233, 0, 29533, 0, 'skarvald YELL_SKARVALD_DAL_DIEDFIRST');
INSERT INTO `creature_text` VALUES (24201, 0, 0, 'By all means, don\'t assess the situation, you halfwit! Just jump into the fray!', 14, 0, 100, 0, 0, 13199, 0, 29527, 0, 'dalronn YELL_DALRONN_AGGRO');
INSERT INTO `creature_text` VALUES (24201, 1, 0, 'See... you... soon.', 14, 0, 100, 0, 0, 13200, 0, 29529, 0, 'dalronn YELL_DALRONN_SKA_DIED');
INSERT INTO `creature_text` VALUES (24201, 2, 0, 'There\'s no... greater... glory.', 14, 0, 100, 0, 0, 13201, 0, 29530, 0, 'dalronn YELL_DALRONN_DAL_DIEDFIRST');
INSERT INTO `creature_text` VALUES (24201, 3, 0, 'You may serve me yet.', 14, 0, 100, 0, 0, 13202, 0, 29526, 0, 'dalronn YELL_DALRONN_KILL');
INSERT INTO `creature_text` VALUES (24201, 4, 0, 'Skarvald, you incompetent slug! Return and make yourself useful!', 14, 0, 100, 0, 0, 13203, 0, 29528, 0, 'dalronn YELL_DALRONN_SKA_DIEDFIRST');
INSERT INTO `creature_text` VALUES (24211, 0, 0, 'Blessings of the spirits of the land and air upon you.', 12, 1, 100, 1, 0, 0, 0, 22947, 0, 'Freed Winterhoof Longrunner');
INSERT INTO `creature_text` VALUES (24211, 0, 1, 'I must return. Good hunting to you.', 12, 1, 100, 1, 0, 0, 0, 22946, 0, 'Freed Winterhoof Longrunner');
INSERT INTO `creature_text` VALUES (24211, 0, 2, 'I owe you a life debt, stranger.', 12, 1, 100, 1, 0, 0, 0, 22952, 0, 'Freed Winterhoof Longrunner');
INSERT INTO `creature_text` VALUES (24211, 0, 3, 'Strange ghosts walk the land. Be careful!', 12, 1, 100, 1, 0, 0, 0, 22950, 0, 'Freed Winterhoof Longrunner');
INSERT INTO `creature_text` VALUES (24211, 0, 4, 'Thank you, stranger.', 12, 1, 100, 1, 0, 0, 0, 22945, 0, 'Freed Winterhoof Longrunner');
INSERT INTO `creature_text` VALUES (24211, 0, 5, 'They took us one by one. I\'m sure there are other survivors.', 12, 1, 100, 1, 0, 0, 0, 22951, 0, 'Freed Winterhoof Longrunner');
INSERT INTO `creature_text` VALUES (24211, 0, 6, 'We should never have traveled through the Rivenwood. Everyone knows this!', 12, 1, 100, 1, 0, 0, 0, 22949, 0, 'Freed Winterhoof Longrunner');
INSERT INTO `creature_text` VALUES (24211, 0, 7, 'You saved me from certain death. I owe you.', 12, 1, 100, 1, 0, 0, 0, 22948, 0, 'Freed Winterhoof Longrunner');
INSERT INTO `creature_text` VALUES (24216, 0, 0, 'Beg for your life...', 12, 0, 100, 0, 0, 0, 0, 22698, 0, 'Dragonflayer Berserker');
INSERT INTO `creature_text` VALUES (24216, 0, 1, 'Suffer, insect!', 12, 0, 100, 0, 0, 0, 0, 22701, 0, 'Dragonflayer Berserker');
INSERT INTO `creature_text` VALUES (24216, 0, 2, 'Your end draws closer!', 12, 0, 100, 0, 0, 0, 0, 22699, 0, 'Dragonflayer Berserker');
INSERT INTO `creature_text` VALUES (24216, 0, 3, 'My life for Ymiron!', 12, 0, 100, 0, 0, 0, 0, 22702, 0, 'Dragonflayer Berserker');
INSERT INTO `creature_text` VALUES (24216, 0, 4, 'For Ymiron!', 12, 0, 100, 0, 0, 0, 0, 22697, 0, 'Dragonflayer Berserker');
INSERT INTO `creature_text` VALUES (24216, 0, 5, 'YAAARRRGH!', 12, 0, 100, 0, 0, 0, 0, 22700, 0, 'Dragonflayer Berserker');
INSERT INTO `creature_text` VALUES (24216, 1, 0, 'Take me! I am ready to die!', 12, 0, 100, 0, 0, 0, 0, 22953, 0, 'Dragonflayer Berserker');
INSERT INTO `creature_text` VALUES (24216, 1, 1, 'We battle for the glory of Ymiron! We die in the name of the Lich King!', 12, 0, 100, 0, 0, 0, 0, 22957, 0, 'Dragonflayer Berserker');
INSERT INTO `creature_text` VALUES (24216, 1, 2, 'Drink in their misery, brother!', 12, 0, 100, 0, 0, 0, 0, 22954, 0, 'Dragonflayer Berserker');
INSERT INTO `creature_text` VALUES (24216, 1, 3, 'Stand and be judged!', 12, 0, 100, 0, 0, 0, 0, 22955, 0, 'Dragonflayer Berserker');
INSERT INTO `creature_text` VALUES (24216, 1, 4, 'May you die in the Lich King\'s favor!', 12, 0, 100, 0, 0, 0, 0, 22956, 0, 'Dragonflayer Berserker');
INSERT INTO `creature_text` VALUES (24238, 0, 0, '[Crude Vrykul] Bjorns mottar haelgond sygar!', 14, 0, 100, 0, 0, 0, 0, 23008, 0, 'Bjorn Halgurdsson');
INSERT INTO `creature_text` VALUES (24238, 1, 0, 'No one speaks of my mother in that manner!', 14, 0, 100, 0, 0, 0, 0, 23009, 0, 'Bjorn Halgurdsson');
INSERT INTO `creature_text` VALUES (24238, 2, 0, 'Drive the invaders out!  For Queen Angerboda!', 14, 0, 100, 0, 0, 0, 0, 23062, 0, 'Bjorn Halgurdsson');
INSERT INTO `creature_text` VALUES (24238, 2, 1, 'Fight without fear and the Val\'kyr will reward you!', 14, 0, 100, 0, 0, 0, 0, 23063, 0, 'Bjorn Halgurdsson');
