INSERT INTO `creature_text` VALUES (24248, 0, 0, '%s turns to face you.', 16, 0, 100, 0, 0, 0, 0, 23102, 0, 'The Lich King');
INSERT INTO `creature_text` VALUES (24248, 1, 0, '%s grins. ', 16, 0, 100, 11, 0, 14820, 0, 23146, 0, 'The Lich King');
INSERT INTO `creature_text` VALUES (24248, 2, 0, 'Shamanism has brought you here... Its scent permeates the air. *The Lich King laughs* I was once a shaman.', 12, 0, 100, 0, 0, 14742, 0, 23147, 0, 'The Lich King');
INSERT INTO `creature_text` VALUES (24248, 3, 0, 'No, minion. This one is not ready.', 12, 0, 100, 274, 0, 14743, 0, 23148, 0, 'The Lich King');
INSERT INTO `creature_text` VALUES (24248, 4, 0, 'Do you feel it, mortal? Death seeps through me, enveloping all that I touch. With just a snap of my finger your soul will languish in damnation for all eternity.', 12, 0, 100, 1, 0, 14744, 0, 23149, 0, 'The Lich King');
INSERT INTO `creature_text` VALUES (24248, 5, 0, 'But... It is not yet your time to serve the Lich King. Yes, a greater destiny awaits you. Power... You must become more powerful before you are to serve me.', 12, 0, 100, 1, 0, 14745, 0, 23150, 0, 'The Lich King');
INSERT INTO `creature_text` VALUES (24248, 6, 0, 'Now watch, val\'kyr. Observe as I apply pressure. Can you see that it is not yet ripe? Watch as it pops and falls lifeless to the floor.', 12, 0, 100, 1, 0, 14746, 0, 23151, 0, 'The Lich King');
INSERT INTO `creature_text` VALUES (24248, 7, 0, 'Persistence or stupidity? It matters not. Let this be a lesson learned, mortal!', 12, 0, 100, 1, 0, 14747, 0, 27351, 0, 'The Lich King');
