INSERT INTO `creature_text` VALUES (24250, 2, 0, 'I should be in Utgarde Keep, battling at Ingvar\'s side! Instead I am charged to guard little maggots like you.', 12, 0, 100, 0, 0, 0, 0, 23076, 0, 'Dragonflayer Fleshripper');
INSERT INTO `creature_text` VALUES (24250, 2, 1, 'Why we don\'t just kill you is beyond my understanding.', 12, 0, 100, 0, 0, 0, 0, 23077, 0, 'Dragonflayer Fleshripper');
INSERT INTO `creature_text` VALUES (24250, 2, 2, 'Ingvar has plans for you, little one.', 12, 0, 100, 0, 0, 0, 0, 23078, 0, 'Dragonflayer Fleshripper');
INSERT INTO `creature_text` VALUES (24250, 2, 3, 'Your time in the Ring of Judgment comes soon, maggot.', 12, 0, 100, 0, 0, 0, 0, 23079, 0, 'Dragonflayer Fleshripper');
