INSERT INTO `creature_text` VALUES (24271, 0, 0, 'Halt! Identify yourself immediately!', 12, 9, 100, 0, 0, 0, 0, 23903, 0, 'Garde');
INSERT INTO `creature_text` VALUES (24271, 0, 1, 'This section is off-limits!', 12, 9, 100, 0, 0, 0, 0, 23904, 0, 'Garde');
INSERT INTO `creature_text` VALUES (24271, 0, 2, 'I\'ve never seen one of you around here.', 12, 9, 100, 0, 0, 0, 0, 23905, 0, 'Garde');
INSERT INTO `creature_text` VALUES (24271, 0, 3, 'Hold it! Stop!', 12, 9, 100, 0, 0, 0, 0, 23906, 0, 'Garde');
INSERT INTO `creature_text` VALUES (24271, 0, 4, 'Stop!! Stop!!! Scrap metal moves itself now?', 12, 9, 100, 0, 0, 0, 0, 23907, 0, 'Garde');
INSERT INTO `creature_text` VALUES (24271, 1, 2, 'Right... my bad. Now where was I?', 12, 9, 100, 0, 0, 0, 0, 23910, 0, 'Garde');
INSERT INTO `creature_text` VALUES (24271, 1, 3, 'All hail the great creator!', 12, 9, 100, 0, 0, 0, 0, 23911, 0, 'Garde');
INSERT INTO `creature_text` VALUES (24271, 1, 4, 'You\'re right, of course. Carry on.', 12, 9, 100, 0, 0, 0, 0, 23912, 0, 'Garde');
INSERT INTO `creature_text` VALUES (24271, 1, 5, 'Forgive my insolence!', 12, 9, 100, 0, 0, 0, 0, 23913, 0, 'Garde');
INSERT INTO `creature_text` VALUES (24271, 1, 6, 'We will double our efforts!', 12, 9, 100, 0, 0, 0, 0, 23915, 0, 'Garde');
INSERT INTO `creature_text` VALUES (24271, 2, 0, 'Destroy earthen infestation.', 12, 9, 100, 0, 0, 0, 0, 23921, 0, 'Garde');
INSERT INTO `creature_text` VALUES (24271, 2, 1, 'Wrong answer!', 12, 9, 100, 0, 0, 0, 0, 23908, 0, 'Garde');
INSERT INTO `creature_text` VALUES (24271, 3, 0, 'Continue deconstruction.', 12, 9, 100, 0, 0, 0, 0, 23919, 0, 'Garde');
INSERT INTO `creature_text` VALUES (24271, 3, 1, 'Do not loiter.', 12, 9, 100, 0, 0, 0, 0, 23918, 0, 'Garde');
INSERT INTO `creature_text` VALUES (24271, 3, 2, 'Work must continue. Double your efforts.', 12, 9, 100, 0, 0, 0, 0, 23916, 0, 'Garde');
INSERT INTO `creature_text` VALUES (24271, 3, 3, 'Brontes sees all.', 12, 9, 100, 0, 0, 0, 0, 23917, 0, 'Garde');
INSERT INTO `creature_text` VALUES (24271, 3, 4, 'Reinforcements en route. Function must be unabated.', 12, 9, 100, 0, 0, 0, 0, 23920, 0, 'Garde');
INSERT INTO `creature_text` VALUES (24271, 3, 5, 'Do you bring word from the creator?', 12, 9, 100, 0, 0, 0, 0, 23914, 0, 'Garde');
INSERT INTO `creature_text` VALUES (24271, 3, 6, 'All hail the great creator.', 12, 9, 100, 0, 0, 0, 0, 23898, 0, 'Garde');
INSERT INTO `creature_text` VALUES (24277, 0, 0, 'Enough of this charade!', 12, 0, 100, 0, 0, 0, 0, 23064, 0, 'Garwal');
INSERT INTO `creature_text` VALUES (24283, 0, 0, 'Right, that should do it. I think ...hic.', 12, 7, 100, 396, 0, 0, 0, 23086, 0, 'Peppy Wrongnozzle to Player');
INSERT INTO `creature_text` VALUES (24284, 0, 0, 'The vrykul clutches at his throat as he begins to gag and thrash about.', 16, 0, 100, 53, 0, 0, 0, 23087, 0, 'Dragonflayer Vrykul Prisoner to Player');
INSERT INTO `creature_text` VALUES (24288, 0, 0, 'Stone troggs are attacking!', 42, 0, 100, 0, 0, 0, 0, 45178, 0, 'ELM General Purpose Bunny Hide Body to Player');
INSERT INTO `creature_text` VALUES (24288, 1, 0, 'Here come more stone troggs. Defend the catapult!', 42, 0, 100, 0, 0, 0, 0, 45179, 0, 'ELM General Purpose Bunny Hide Body to Player');
INSERT INTO `creature_text` VALUES (24288, 2, 0, ' Looks like a last ditch effort by the stone troggs. Your destination is just around the corner!', 42, 0, 100, 0, 0, 0, 0, 45181, 0, 'ELM General Purpose Bunny Hide Body to Player');
INSERT INTO `creature_text` VALUES (24314, 0, 0, 'So then we too are cursed?', 12, 0, 100, 1, 0, 0, 0, 23116, 0, 'Ancient Male Vrykul');
INSERT INTO `creature_text` VALUES (24314, 1, 0, '%s points to the infant.', 16, 0, 100, 0, 0, 0, 0, 23117, 0, 'Ancient Male Vrykul');
INSERT INTO `creature_text` VALUES (24314, 2, 0, 'The gods have forsaken us! We must dispose of it before Ymiron is notified!', 12, 0, 100, 53, 0, 0, 0, 23119, 0, 'Ancient Male Vrykul');
INSERT INTO `creature_text` VALUES (24314, 3, 0, 'Then what are we to do, wife? The others cannot find out. Should they learn of this aberration, we will all be executed.', 12, 0, 100, 1, 0, 0, 0, 23121, 0, 'Ancient Male Vrykul');
INSERT INTO `creature_text` VALUES (24315, 0, 0, '%s sobs.', 16, 0, 100, 0, 0, 0, 0, 23118, 0, 'Ancient Female Vrykul');
INSERT INTO `creature_text` VALUES (24315, 1, 0, 'NO! You cannot! I beg of you! It is our child!', 12, 0, 100, 0, 0, 0, 0, 23120, 0, 'Ancient Female Vrykul');
INSERT INTO `creature_text` VALUES (24315, 2, 0, 'I... I will hide it. I will hide it until I find it a home, far away from here...', 12, 0, 100, 0, 0, 0, 0, 23122, 0, 'Ancient Female Vrykul');
INSERT INTO `creature_text` VALUES (24321, 0, 0, '%s motions for silence.', 16, 0, 100, 397, 0, 0, 0, 23130, 0, 'King Ymiron emote');
INSERT INTO `creature_text` VALUES (24321, 1, 0, 'Vrykul, your king implores you to listen!', 14, 0, 100, 22, 0, 0, 0, 23134, 0, 'King Ymiron yell');
INSERT INTO `creature_text` VALUES (24321, 2, 0, 'The crowd gasps in horror.', 16, 0, 100, 53, 0, 0, 0, 23136, 0, 'King Ymiron emote');
INSERT INTO `creature_text` VALUES (24321, 3, 0, 'Even now, in our darkest hour, they mock us!', 14, 0, 100, 22, 0, 0, 0, 23137, 0, 'King Ymiron yell');
INSERT INTO `creature_text` VALUES (24321, 4, 0, 'Where are the titans in our time of greatest need? Our women birth abberations - disfigured runts unable to even stand on their own! Weak and ugly... Useless...', 14, 0, 100, 22, 0, 0, 0, 23138, 0, 'King Ymiron yell');
INSERT INTO `creature_text` VALUES (24321, 5, 0, 'Ymiron has toiled. Long have I sat upon my throne and thought hard of our plight. There is only one answer... one reason...', 14, 0, 100, 22, 0, 0, 0, 23139, 0, 'King Ymiron yell');
INSERT INTO `creature_text` VALUES (24321, 6, 0, 'For who but the titans themselves could bestow such a curse? What could have such power?', 14, 0, 100, 1, 0, 0, 0, 23140, 0, 'King Ymiron yell');
INSERT INTO `creature_text` VALUES (24321, 7, 0, 'And the answer is nothing... For it is the titans that have cursed us!', 14, 0, 100, 53, 0, 0, 0, 23141, 0, 'King Ymiron yell');
INSERT INTO `creature_text` VALUES (24321, 8, 0, 'The crowd cheers.', 16, 0, 100, 0, 0, 0, 0, 23144, 0, 'King Ymiron');
INSERT INTO `creature_text` VALUES (24321, 9, 0, 'On this day all Vrykul will shed their old beliefs! We denounce our old gods! All Vrykul will pledge their allegiance to Ymiron! Ymiron will protect our noble race!', 14, 0, 100, 53, 0, 0, 0, 23143, 0, 'King Ymiron yell');
INSERT INTO `creature_text` VALUES (24321, 10, 0, 'And now my first decree upon the Vrykul! All malformed infants born of Vrykul mother and father are to be destroyed upon birth! Our blood must remain pure always! Those found in violation of Ymiron\'s decree will be taken to Gjalerbron for execution!', 14, 0, 100, 1, 0, 0, 0, 23145, 0, 'King Ymiron yell');
INSERT INTO `creature_text` VALUES (24322, 0, 0, 'Silence!', 14, 1, 100, 1, 0, 0, 0, 32240, 0, 'Ancient Citizen of Nifflevar yell');
INSERT INTO `creature_text` VALUES (24322, 1, 0, 'Show them mercy, my king! They are of our flesh and blood!', 14, 0, 100, 53, 0, 0, 0, 23128, 0, 'Ancient Citizen of Nifflevar yell');
INSERT INTO `creature_text` VALUES (24322, 2, 0, 'All hail our glorious king, Ymiron!', 14, 0, 100, 53, 0, 0, 0, 23123, 0, 'Ancient Citizen of Nifflevar yell');
INSERT INTO `creature_text` VALUES (24323, 0, 0, 'Show the abberations no mercy, Ymiron!', 14, 0, 100, 1, 0, 0, 0, 23125, 0, 'Ancient Citizen of Nifflevar yell');
INSERT INTO `creature_text` VALUES (24323, 1, 0, 'They weaken us! Our strength is dilluted by their very existence! Destroy them all!', 14, 0, 100, 113, 0, 0, 0, 23129, 0, 'Ancient Citizen of Nifflevar yell');
INSERT INTO `creature_text` VALUES (24327, 0, 0, 'Shall we prepare it for you, my lord?', 12, 0, 100, 0, 0, 0, 0, 23152, 0, 'Val\'kyr Soulclaimer');
INSERT INTO `creature_text` VALUES (24362, 0, 0, 'They keep their tools in boxes that look like this. Look for them near the pavilions.', 12, 0, 100, 25, 0, 0, 0, 23552, 0, 'Longrunner Pembe');
INSERT INTO `creature_text` VALUES (24362, 1, 0, 'I\'m only going to carve the runes I think will give you command over a giant.', 12, 0, 100, 1, 0, 0, 0, 23246, 0, 'Longrunner Pembe');
INSERT INTO `creature_text` VALUES (24362, 2, 0, 'I\'m omitting the runes for \"north\" and \"west\" because I\'m not sure how they impact the giant. I\'m also including a rune to limit how long you can control it.', 12, 0, 100, 0, 0, 0, 0, 23247, 0, 'Longrunner Pembe');
INSERT INTO `creature_text` VALUES (24362, 3, 0, 'There we go, the Rune of Command... hopefully. Not nearly as vicious as its Iron Dwarf cousin, but functional.', 12, 0, 100, 1, 0, 0, 0, 23248, 0, 'Longrunner Pembe');
INSERT INTO `creature_text` VALUES (24363, 0, 0, 'Da spirits gonna feast today! Begin da ceremonies, sacrifice da prisoners... make room for our new guests!', 14, 0, 100, 0, 0, 12053, 0, 23253, 3, 'Hex Lord Malacrass');
INSERT INTO `creature_text` VALUES (24363, 1, 0, 'Don\'t be shy. Thousands have come before you. Ya not be alone in your service.', 14, 0, 100, 0, 0, 12055, 0, 23317, 3, 'Hex Lord Malacrass');
INSERT INTO `creature_text` VALUES (24363, 2, 0, 'Ya gonna fail, strangers. Many try before you, but dey only make us stronger!', 14, 0, 100, 0, 0, 12056, 0, 23318, 3, 'Hex Lord Malacrass');
INSERT INTO `creature_text` VALUES (24366, 0, 0, 'Guards!', 14, 6, 100, 0, 0, 0, 0, 4583, 0, 'Nizzle');
INSERT INTO `creature_text` VALUES (24371, 0, 0, 'I have fallen! Fear not, brethren! Let nothing keep you from the Stoneforge!', 14, 0, 100, 0, 0, 0, 0, 23530, 0, 'Megalith');
INSERT INTO `creature_text` VALUES (24374, 0, 0, '%s goes into a frenzy!', 16, 0, 100, 0, 0, 0, 0, 1191, 0, 'Amani\'shi Berserker');
INSERT INTO `creature_text` VALUES (24381, 0, 0, 'Arise from the earth, my brothers!', 12, 0, 100, 0, 0, 0, 0, 23311, 0, 'Megalithe');
INSERT INTO `creature_text` VALUES (24381, 1, 0, 'Our iron masters have a mission for us!', 12, 0, 100, 0, 0, 0, 0, 23312, 0, 'Megalithe');
INSERT INTO `creature_text` VALUES (24381, 2, 0, 'Follow me into the mountains to carry out the plan of the masters!', 12, 0, 100, 0, 0, 0, 0, 23315, 0, 'Megalithe');
INSERT INTO `creature_text` VALUES (24381, 3, 0, 'We will not fail!', 12, 0, 100, 0, 0, 0, 0, 23332, 0, 'Megalithe');
INSERT INTO `creature_text` VALUES (24405, 0, 0, 'Thank you!', 12, 0, 100, 17, 0, 0, 0, 37237, 0, 'Adarrah');
INSERT INTO `creature_text` VALUES (24458, 0, 0, 'I\'ve been saving this just for you, big boy!', 12, 0, 100, 0, 0, 0, 0, 23457, 0, 'Lydell');
INSERT INTO `creature_text` VALUES (24525, 0, 0, 'Ku into me carver, ye ugly croaker!  Ku and show me who dares cross me brethren!', 12, 0, 100, 0, 0, 0, 0, 25734, 0, 'Zeh\'gehn');
INSERT INTO `creature_text` VALUES (24525, 1, 0, 'Dis a rankin\' set, mon, no scurvy dog gonna kass-kass me buckos an\' walk aweh! ', 12, 0, 100, 0, 0, 0, 0, 23581, 0, 'Zeh\'gehn');
INSERT INTO `creature_text` VALUES (24528, 0, 0, 'How much\'ll it cost me to get my mules shod?', 12, 7, 100, 6, 0, 0, 0, 23561, 0, 'Brune Grayblade');
INSERT INTO `creature_text` VALUES (24528, 1, 0, 'You don\'t understand! If I\'m not ready to go soon, all the good homesteading land will be taken!', 12, 7, 100, 1, 0, 0, 0, 23563, 0, 'Brune Grayblade');
INSERT INTO `creature_text` VALUES (24528, 2, 0, 'That, sir, is a mule!', 12, 7, 100, 25, 0, 0, 0, 23567, 0, 'Brune Grayblade');
INSERT INTO `creature_text` VALUES (24530, 0, 0, '%s becomes enraged!', 16, 0, 100, 0, 0, 0, 0, 24144, 0, 'Amani Elder Lynx');
INSERT INTO `creature_text` VALUES (24531, 0, 0, 'Yes, darling. You realize you\'ll be eating dire ram mutton for dinner until the crops are planted...', 12, 7, 100, 1, 0, 0, 0, 23608, 0, 'Gavin Ward');
INSERT INTO `creature_text` VALUES (24531, 1, 0, 'I\'d rather listen to his braying than yours, dear.', 12, 7, 100, 0, 0, 0, 0, 23610, 0, 'Gavin Ward');
INSERT INTO `creature_text` VALUES (24531, 2, 0, 'Of course, darling.', 12, 7, 100, 1, 0, 0, 0, 23606, 0, 'Gavin Ward');
INSERT INTO `creature_text` VALUES (24532, 0, 0, 'And I don\'t want our land to have any of those horrible wolves, or giants, or ugly rams!', 12, 7, 100, 1, 0, 0, 0, 23607, 0, 'Sabetta Ward');
INSERT INTO `creature_text` VALUES (24532, 1, 0, 'I\'d sooner butcher that mule of yours! Why did you bring that beast in here, anyway?', 12, 7, 100, 5, 0, 0, 0, 23609, 0, 'Sabetta Ward');
INSERT INTO `creature_text` VALUES (24532, 2, 0, 'Remember, honey, we need to get a plot of land with a nice hot spring.', 12, 7, 100, 1, 0, 0, 0, 23605, 0, 'Sabetta Ward');
INSERT INTO `creature_text` VALUES (24534, 0, 0, 'Welcome to Fort Wildervar, brave homesteaders! There\'s a whole continent out there just waiting to be claimed!', 12, 7, 100, 1, 0, 0, 0, 23570, 0, 'Marrod Silvertongue');
INSERT INTO `creature_text` VALUES (24534, 1, 0, 'True, Northrend is a hard land, but our people are strong, hardy, and equal to the task!', 12, 7, 100, 0, 0, 0, 0, 23571, 0, 'Marrod Silvertongue');
INSERT INTO `creature_text` VALUES (24534, 2, 0, 'We will win this land with the sword, and break it with the plow! You are the men and women who will be remembered for taming the wild continent!', 12, 7, 100, 1, 0, 0, 0, 23572, 0, 'Marrod Silvertongue');
INSERT INTO `creature_text` VALUES (24534, 3, 0, 'But, you will not be alone out there. My men and I have prepared pack mules carrying the supplies you\'ll need most.', 12, 7, 100, 1, 0, 0, 0, 23573, 0, 'Marrod Silvertongue');
INSERT INTO `creature_text` VALUES (24534, 4, 0, 'Axes, picks, seed, nails, food, blankets, water... it\'s all there, waiting for you. I think you\'ll find my prices quite reasonable, too.', 12, 7, 100, 25, 0, 0, 0, 23574, 0, 'Marrod Silvertongue');
INSERT INTO `creature_text` VALUES (24534, 5, 0, 'There are more than enough to go around. Should you need other goods, don\'t hesitate to ask!', 12, 7, 100, 1, 0, 0, 0, 23575, 0, 'Marrod Silvertongue');
INSERT INTO `creature_text` VALUES (24534, 6, 0, 'Now, my loyal custo... err, friends, go forth and conquer this land for our people!', 12, 7, 100, 274, 0, 0, 0, 23576, 0, 'Marrod Silvertongue');
