INSERT INTO `creature_text` VALUES (24664, 2, 0, 'I\'ll turn your world... upside... down.', 14, 0, 100, 0, 0, 12418, 0, 25390, 0, 'Kael\'thas Sunstrider - Gravity Lapse Intro 2');
INSERT INTO `creature_text` VALUES (24664, 3, 0, 'Do not get... too comfortable.', 14, 0, 100, 0, 0, 12420, 0, 25392, 0, 'Kael\'thas Sunstrider to Kael\'thas Sunstrider - Gravity lapse Intro 2');
