INSERT INTO `creature_text` VALUES (24711, 3, 0, 'Too bad Coren\'s dead.  If I could buy that brew from him and resell it -- watered down and rebottled under my own label, of course -- I\'d be rich!', 12, 0, 100, 1, 0, 0, 0, 27438, 0, 'Tapper Swindlekeg');
INSERT INTO `creature_text` VALUES (24711, 4, 0, 'Hey there, and welcome to Brewfest. Come in and have a drink or three!', 12, 0, 100, 3, 0, 0, 0, 23698, 0, 'Tapper Swindlekeg');
