INSERT INTO `creature_text` VALUES (25038, 1, 1, 'The end has come!', 14, 0, 100, 0, 0, 12481, 0, 38040, 0, 'felmyst - YELL_KILL2');
INSERT INTO `creature_text` VALUES (25038, 2, 0, 'Choke on your final breath!', 14, 0, 100, 0, 0, 12478, 0, 25638, 0, 'felmyst - YELL_BREATH');
INSERT INTO `creature_text` VALUES (25038, 3, 0, 'I am stronger than ever before!', 14, 0, 100, 0, 0, 12479, 0, 25639, 0, 'felmyst - YELL_TAKEOFF');
INSERT INTO `creature_text` VALUES (25038, 4, 0, 'No more hesitation! Your fates are written!', 14, 0, 100, 0, 0, 12482, 0, 25642, 0, 'felmyst - YELL_BERSERK');
