INSERT INTO `creature_text` VALUES (25237, 1, 0, 'Shipping lanes... supplies... You bore me to death! We need nothing more than the warrior spirit of the Horde, Saurfang! Now that we are firmly entrenched in this frozen wasteland, nothing shall stop us!', 12, 1, 100, 1, 0, 0, 0, 24492, 0, 'Garrosh Hellscream');
INSERT INTO `creature_text` VALUES (25237, 2, 0, 'Propose? I will show you what I propose!', 12, 1, 100, 6, 0, 0, 0, 24494, 0, 'Garrosh Hellscream');
INSERT INTO `creature_text` VALUES (25237, 3, 0, 'There... Now we now have a shipping lane.', 12, 1, 100, 1, 0, 0, 0, 24495, 0, 'Garrosh Hellscream');
INSERT INTO `creature_text` VALUES (25237, 4, 0, 'And just for good measure...', 12, 1, 100, 5, 0, 0, 0, 24496, 0, 'Garrosh Hellscream');
INSERT INTO `creature_text` VALUES (25237, 5, 0, 'Do not speak to me of consequences, old one. ', 12, 1, 100, 25, 0, 0, 0, 24501, 0, 'Garrosh Hellscream');
INSERT INTO `creature_text` VALUES (25237, 6, 0, 'But surely you cannot think that those children were born into innocence? They would have grown up and taken arms against us!', 12, 1, 100, 5, 0, 0, 0, 24512, 0, 'Garrosh Hellscream');
INSERT INTO `creature_text` VALUES (25237, 7, 0, 'How have you managed to survive for so long, Saurfang? Not fallen victim to your own memories?', 12, 1, 100, 6, 0, 0, 0, 24518, 0, 'Garrosh Hellscream');
