INSERT INTO `creature_text` VALUES (25250, 0, 0, 'What... what happened to me?', 12, 7, 100, 0, 0, 14196, 0, 25841, 0, 'thassarian SAY_ARLOS_1');
INSERT INTO `creature_text` VALUES (25250, 1, 0, 'Ugh!  My head won\'t stop spinning...', 12, 7, 100, 0, 0, 14197, 0, 25847, 0, 'thassarian SAY_ARLOS_2');
INSERT INTO `creature_text` VALUES (25250, 2, 0, 'Your offer will be carefully considered, harbinger.  In the meantime we will make use of your delegation in an advisory role.', 12, 7, 100, 1, 0, 0, 0, 24790, 0, 'General Arlos');
INSERT INTO `creature_text` VALUES (25251, 0, 0, 'Thassarian!  You\'re alive!', 14, 0, 100, 0, 0, 14489, 0, 25842, 0, 'thassarian SAY_LERYSSA_1');
INSERT INTO `creature_text` VALUES (25251, 1, 0, 'I thought... I thought you were... dead.', 12, 0, 100, 0, 0, 14490, 0, 25843, 0, 'thassarian SAY_LERYSSA_2');
INSERT INTO `creature_text` VALUES (25251, 2, 0, 'Don\'t leave me again!  You want to fight for your country, but they don\'t even want you!  They sent you here to die!', 12, 0, 100, 0, 0, 14491, 0, 25848, 0, 'thassarian SAY_LERYSSA_3');
INSERT INTO `creature_text` VALUES (25251, 3, 0, 'I know that look in your eye... I\'m not going to be able to talk you out of this.  If you die on me again...', 12, 0, 100, 0, 0, 14492, 0, 25849, 0, 'thassarian SAY_LERYSSA_4');
INSERT INTO `creature_text` VALUES (25256, 0, 0, 'We are surrounded... Our enemies press in from all sides, young Hellscream.', 12, 1, 100, 1, 0, 0, 0, 24486, 0, 'High Overlord Saurfang');
INSERT INTO `creature_text` VALUES (25256, 1, 0, 'The Scourge descends like locust from the north.', 12, 1, 100, 25, 0, 0, 0, 24487, 0, 'High Overlord Saurfang');
INSERT INTO `creature_text` VALUES (25256, 2, 0, 'The Alliance holds the only secure shipping lane in this region, and even that is at risk of being lost to those dreaded mists.', 12, 1, 100, 25, 0, 0, 0, 24488, 0, 'High Overlord Saurfang');
INSERT INTO `creature_text` VALUES (25256, 3, 0, 'Our only viable port for resupply is held by the Forsaken on the other side of this blasted continent!', 12, 1, 100, 1, 0, 0, 0, 24489, 0, 'High Overlord Saurfang');
INSERT INTO `creature_text` VALUES (25256, 4, 0, 'Anything our zeppelins cannot haul must be brought in by ship and travel the length of Northrend to reach us.', 12, 1, 100, 1, 0, 0, 0, 24490, 0, 'High Overlord Saurfang');
INSERT INTO `creature_text` VALUES (25256, 5, 0, 'Siege engines, ammunition, heavy armor... How do you propose to shatter the walls of Icecrown without those?', 12, 1, 100, 1, 0, 0, 0, 24493, 0, 'High Overlord Saurfang');
INSERT INTO `creature_text` VALUES (25256, 6, 0, 'So the prodigal son has spoken!', 12, 1, 100, 5, 0, 0, 0, 24497, 0, 'High Overlord Saurfang');
INSERT INTO `creature_text` VALUES (25256, 7, 0, 'Your father\'s blood runs strong in you, Hellscream. Impatient as always... Impatient and reckless.', 12, 1, 100, 1, 0, 0, 0, 24498, 0, 'High Overlord Saurfang');
INSERT INTO `creature_text` VALUES (25256, 8, 0, 'You rush headlong into all-out war without a thought of the consequences.', 12, 1, 100, 1, 0, 0, 0, 24500, 0, 'High Overlord Saurfang');
INSERT INTO `creature_text` VALUES (25256, 9, 0, 'I drank of the same blood your father did, Garrosh. Mannoroth\'s cursed venom pumped through my veins as well.', 12, 1, 100, 1, 0, 0, 0, 24502, 0, 'High Overlord Saurfang');
INSERT INTO `creature_text` VALUES (25256, 10, 0, 'I drove my weapons into the bodies and minds of my enemies.', 12, 1, 100, 1, 0, 0, 0, 24503, 0, 'High Overlord Saurfang');
INSERT INTO `creature_text` VALUES (25256, 11, 0, 'And while Grom died a glorious death - freeing us all from the blood curse - he could not wipe away the terrible memory of our past.', 12, 1, 100, 1, 0, 0, 0, 24504, 0, 'High Overlord Saurfang');
INSERT INTO `creature_text` VALUES (25256, 12, 0, 'His act could not erase the horrors we committed. ', 12, 1, 100, 274, 0, 0, 0, 24505, 0, 'High Overlord Saurfang');
INSERT INTO `creature_text` VALUES (25256, 13, 0, '%s pauses.', 16, 1, 100, 0, 0, 0, 0, 24506, 0, 'High Overlord Saurfang');
INSERT INTO `creature_text` VALUES (25256, 14, 0, 'The winter after the curse was lifted, hundreds of veteran orcs like me were lost to despair.', 12, 1, 100, 1, 0, 0, 0, 24507, 0, 'High Overlord Saurfang');
INSERT INTO `creature_text` VALUES (25256, 15, 0, 'Our minds were finally free, yes... Free to relive all of the unthinkable acts that we had performed under the Legions\' influence.', 12, 1, 100, 273, 0, 0, 0, 24508, 0, 'High Overlord Saurfang');
INSERT INTO `creature_text` VALUES (25256, 16, 0, '%s nods.', 16, 1, 100, 273, 0, 0, 0, 24509, 0, 'High Overlord Saurfang');
INSERT INTO `creature_text` VALUES (25256, 17, 0, 'I think it was the sounds of the draenei children that unnerved most of them... You never forget...', 12, 1, 100, 1, 0, 0, 0, 24510, 0, 'High Overlord Saurfang');
INSERT INTO `creature_text` VALUES (25256, 18, 0, 'Have you ever been to Jaggedswine Farm? When the swine are of age for the slaughter... It\'s that sound. The sound of the swine being killed... It resonates the loudest. Those are hard times for us older veterans.', 12, 1, 100, 6, 0, 0, 0, 24511, 0, 'High Overlord Saurfang');
INSERT INTO `creature_text` VALUES (25256, 19, 0, '%s shakes his head.', 16, 1, 100, 274, 0, 0, 0, 24513, 0, 'High Overlord Saurfang');
INSERT INTO `creature_text` VALUES (25256, 20, 0, 'I am not speaking solely of the children of our enemies...', 12, 1, 100, 1, 0, 0, 0, 24515, 0, 'High Overlord Saurfang');
INSERT INTO `creature_text` VALUES (25256, 21, 0, '%s pauses.', 16, 1, 100, 1, 0, 0, 0, 24506, 0, 'High Overlord Saurfang');
INSERT INTO `creature_text` VALUES (25256, 22, 0, 'I won\'t let you take us down that dark path again, young Hellscream. I\'ll kill you myself before that day comes...', 12, 1, 100, 274, 0, 0, 0, 24516, 0, 'High Overlord Saurfang');
INSERT INTO `creature_text` VALUES (25256, 23, 0, 'I don\'t eat pork...', 12, 1, 100, 274, 0, 0, 0, 24519, 0, 'High Overlord Saurfang');
INSERT INTO `creature_text` VALUES (25256, 24, 0, '%s spits.', 16, 1, 100, 0, 0, 0, 0, 24520, 0, 'High Overlord Saurfang');
INSERT INTO `creature_text` VALUES (25258, 0, 0, 'One last drink before we head to the front?', 12, 7, 100, 1, 0, 0, 0, 24475, 0, 'Footman Rob');
INSERT INTO `creature_text` VALUES (25258, 1, 0, 'Barkeep!  Bring out your strongest ale.  We\'re off to the front.', 12, 7, 100, 1, 0, 0, 0, 24478, 0, 'Footman Rob');
INSERT INTO `creature_text` VALUES (25258, 2, 0, 'You\'ll surely make an exception for us, eh?  We might not come back.', 12, 7, 100, 1, 0, 0, 0, 24479, 0, 'Footman Rob');
INSERT INTO `creature_text` VALUES (25258, 3, 0, 'I guess that\'s it then.  Off we go.', 12, 7, 100, 1, 0, 0, 0, 24482, 0, 'Footman Rob');
INSERT INTO `creature_text` VALUES (25259, 0, 0, 'Sure, why not?', 12, 7, 100, 6, 0, 0, 0, 24476, 0, 'Footman George');
INSERT INTO `creature_text` VALUES (25260, 0, 0, 'Hey, guys!  Wait up!', 12, 7, 100, 0, 0, 0, 0, 24761, 0, 'Footman Mitch');
INSERT INTO `creature_text` VALUES (25261, 0, 0, 'That was the worst beer I\'ve ever had.', 12, 7, 100, 0, 0, 0, 0, 24483, 0, 'Footman Chuck');
INSERT INTO `creature_text` VALUES (25274, 0, 0, 'Keep \'em hot you big dummy!', 12, 1, 100, 0, 0, 0, 0, 25133, 0, 'Armorer Orkuruk');
INSERT INTO `creature_text` VALUES (25274, 0, 1, 'Those weapons ain\'t gonna build themselves! Faster!', 12, 1, 100, 0, 0, 0, 0, 25134, 0, 'Armorer Orkuruk');
INSERT INTO `creature_text` VALUES (25274, 0, 2, 'You call that a hammer strike? What kind of amateurs am I workin\' with here?', 12, 1, 100, 0, 0, 0, 0, 25135, 0, 'Armorer Orkuruk');
INSERT INTO `creature_text` VALUES (25274, 0, 3, 'You keep that garbage up and I\'ll ship you back to your momma in a box with a note strapped to your chest that reads: FAILURE.', 12, 1, 100, 0, 0, 0, 0, 25136, 0, 'Armorer Orkuruk');
INSERT INTO `creature_text` VALUES (25274, 0, 4, 'And WHAT are our soldiers supposed to do with that shoddy piece of junk?', 12, 1, 100, 0, 0, 0, 0, 25137, 0, 'Armorer Orkuruk');
INSERT INTO `creature_text` VALUES (25274, 0, 5, 'You\'re an embarassment to this entire operation! Pack up your stuff and get out!', 12, 1, 100, 0, 0, 0, 0, 25138, 0, 'Armorer Orkuruk');
INSERT INTO `creature_text` VALUES (25285, 0, 0, 'Give the word, general.  I will have two regiments at your disposal in a month\'s time.', 12, 7, 100, 0, 0, 0, 0, 24787, 0, 'Harbinger Vurenn');
INSERT INTO `creature_text` VALUES (25285, 1, 0, 'Very well, general.  Should you change your mind, my people will be more than willing to provide military assistance.', 12, 7, 100, 0, 0, 0, 0, 24788, 0, 'Harbinger Vurenn');
INSERT INTO `creature_text` VALUES (25286, 0, 0, 'Yes sir!', 12, 1, 100, 0, 0, 0, 0, 24522, 0, 'Warsong Wind Rider');
INSERT INTO `creature_text` VALUES (25288, 0, 0, 'Guards!', 14, 6, 100, 0, 0, 0, 0, 4583, 0, 'Turida Coldwind');
INSERT INTO `creature_text` VALUES (25289, 0, 0, 'Aye, solja! Lift off time!', 12, 1, 100, 1, 0, 0, 0, 24521, 0, 'Wind Master To\'bor');
INSERT INTO `creature_text` VALUES (25301, 0, 0, 'Our troops, general, consist mostly of villagers and peasants.  Good men, but not quite rid of the prejudices and superstitions of their upbringing.  They\'re not ready to fight alongside our more exotic allies.', 12, 7, 100, 1, 0, 0, 0, 24789, 0, 'Counselor Talbot');
INSERT INTO `creature_text` VALUES (25315, 0, 0, 'Nooooooooooooo!', 14, 0, 100, 0, 0, 12527, 0, 0, 0, 'KJ - SAY_KJ_DEATH');
INSERT INTO `creature_text` VALUES (25315, 1, 0, 'Another step toward destruction!', 14, 0, 100, 0, 0, 12501, 0, 25549, 0, 'KJ - SAY_KJ_SLAY1');
INSERT INTO `creature_text` VALUES (25315, 1, 1, 'Anakh Kyree!', 14, 0, 100, 0, 0, 12502, 0, 25550, 0, 'KJ - SAY_KJ_SLAY2');
INSERT INTO `creature_text` VALUES (25315, 2, 0, 'Who can you trust!', 14, 0, 100, 0, 0, 12503, 0, 25551, 0, 'KJ - SAY_KJ_REFLECTION1');
INSERT INTO `creature_text` VALUES (25315, 2, 1, 'The enemy is among you!', 14, 0, 100, 0, 0, 12504, 0, 25552, 0, 'KJ - SAY_KJ_REFLECTION2');
INSERT INTO `creature_text` VALUES (25315, 3, 0, 'The expendable have perished. So be it! Now I shall succeed where Sargeras could not! I will bleed this wretched world and secure my place as the true master of the Burning Legion! The end has come! Let the unravelling of this world commence!', 14, 0, 100, 0, 0, 12500, 0, 24808, 0, 'KJ - SAY_KJ_EMERGE');
INSERT INTO `creature_text` VALUES (25315, 4, 0, 'CHAOS!', 14, 0, 100, 0, 0, 12505, 0, 29058, 0, 'KJ - SAY_KJ_DARKNESS1');
INSERT INTO `creature_text` VALUES (25315, 4, 1, 'DESTRUCTION!', 14, 0, 100, 0, 0, 12506, 0, 29059, 0, 'KJ - SAY_KJ_DARKNESS2');
INSERT INTO `creature_text` VALUES (25315, 4, 2, 'OBLIVION!', 14, 0, 100, 0, 0, 12507, 0, 34140, 0, 'KJ - SAY_KJ_DARKNESS3');
INSERT INTO `creature_text` VALUES (25315, 5, 0, 'I will not be denied! This world shall fall!', 14, 0, 100, 0, 0, 12508, 0, 24754, 0, 'KJ - SAY_KJ_PHASE3');
INSERT INTO `creature_text` VALUES (25315, 6, 0, 'Do not harbor false hope! You cannot win!', 14, 0, 100, 0, 0, 12509, 0, 24825, 0, 'KJ - SAY_KJ_PHASE4');
INSERT INTO `creature_text` VALUES (25315, 7, 0, 'Aggghh! The powers of the Sunwell... turn... against me! What have you done? What have you done???', 14, 0, 100, 0, 0, 12510, 0, 25199, 0, 'KJ - SAY_KJ_PHASE5');
INSERT INTO `creature_text` VALUES (25315, 8, 0, 'Kil\'jaeden begins to channel dark energy.', 41, 0, 100, 0, 0, 0, 0, 25705, 0, 'KJ - EMOTE_KJ_DARKNESS');
INSERT INTO `creature_text` VALUES (25317, 0, 0, 'What\'s the matter, $c?  Think you\'re too good to stand in line with the rest of us?', 12, 0, 100, 0, 0, 0, 0, 24758, 0, 'Civilian Recruit to Player');
INSERT INTO `creature_text` VALUES (25317, 1, 0, 'This is harder than it looks.', 12, 7, 100, 0, 0, 0, 0, 26488, 0, 'Civilian Recruit');
INSERT INTO `creature_text` VALUES (25317, 1, 1, 'Did that seem okay?', 12, 7, 100, 0, 0, 0, 0, 26487, 0, 'Civilian Recruit');
INSERT INTO `creature_text` VALUES (25317, 1, 2, 'My arm is getting sore.', 12, 7, 100, 0, 0, 0, 0, 26491, 0, 'Civilian Recruit');
INSERT INTO `creature_text` VALUES (25317, 1, 3, 'Hmm....', 12, 7, 100, 0, 0, 0, 0, 26492, 0, 'Civilian Recruit');
INSERT INTO `creature_text` VALUES (25317, 1, 4, 'I think I\'m starting to get the hang of this.', 12, 7, 100, 0, 0, 0, 0, 26490, 0, 'Civilian Recruit');
INSERT INTO `creature_text` VALUES (25317, 1, 5, 'This isn\'t easy.', 12, 7, 100, 0, 0, 0, 0, 26489, 0, 'Civilian Recruit');
INSERT INTO `creature_text` VALUES (25317, 2, 0, 'You aren\'t going to let me sleep are you?', 12, 7, 100, 0, 0, 0, 0, 26497, 0, 'Civilian Recruit');
INSERT INTO `creature_text` VALUES (25317, 2, 1, 'Are you going to be doing this for much longer?', 12, 7, 100, 0, 0, 0, 0, 26502, 0, 'Civilian Recruit');
INSERT INTO `creature_text` VALUES (25317, 2, 2, 'Could you try to be a little quieter?', 12, 7, 100, 0, 0, 0, 0, 26500, 0, 'Civilian Recruit');
INSERT INTO `creature_text` VALUES (25317, 3, 0, 'I just don\'t want to let anyone down.', 12, 7, 100, 0, 0, 0, 0, 26494, 0, 'Civilian Recruit');
INSERT INTO `creature_text` VALUES (25317, 3, 1, 'I need to do something to keep myself busy.', 12, 7, 100, 0, 0, 0, 0, 26495, 0, 'Civilian Recruit');
INSERT INTO `creature_text` VALUES (25317, 3, 2, 'I\'m just nervous.  Sorry.', 12, 7, 100, 0, 0, 0, 0, 26493, 0, 'Civilian Recruit');
INSERT INTO `creature_text` VALUES (25319, 0, 0, 'Strike now heroes, while he is weakened! Vanquish the Deceiver!', 14, 0, 100, 0, 0, 12449, 0, 25822, 0, 'KJ - SAY_KALECGOS_ENCOURAGE');
INSERT INTO `creature_text` VALUES (25319, 1, 0, 'I will channel my powers into the orbs! Be ready!', 14, 0, 100, 0, 0, 12440, 0, 24822, 0, 'KJ - SAY_KALECGOS_READY1');
INSERT INTO `creature_text` VALUES (25319, 2, 0, 'I have empowered another orb! Use it quickly!', 14, 0, 100, 0, 0, 12441, 0, 25542, 0, 'KJ - SAY_KALECGOS_READY2');
INSERT INTO `creature_text` VALUES (25319, 3, 0, 'Another orb is ready! Make haste!', 14, 0, 100, 0, 0, 12442, 0, 25543, 0, 'KJ - SAY_KALECGOS_READY3');
INSERT INTO `creature_text` VALUES (25319, 4, 0, 'I have channeled all I can! The power is in your hands!', 14, 0, 100, 0, 0, 12443, 0, 24824, 0, 'KJ - SAY_KALECGOS_READY4');
INSERT INTO `creature_text` VALUES (25319, 5, 0, 'Anveena, you must awaken! This world needs you!', 14, 0, 100, 0, 0, 12445, 0, 25190, 0, 'KJ - SAY_KALECGOS_AWAKEN');
INSERT INTO `creature_text` VALUES (25319, 6, 0, 'You must let go! You must become what you were always meant to be! The time is now Anveena!', 14, 0, 100, 0, 0, 12446, 0, 25191, 0, 'KJ - SAY_KALECGOS_LETGO');
INSERT INTO `creature_text` VALUES (25319, 7, 0, 'Anveena I love you! Focus on my voice! Come back for me now! Only you can cleanse the Sunwell!', 14, 0, 100, 0, 0, 12447, 0, 25192, 0, 'KJ - SAY_KALECGOS_FOCUS');
INSERT INTO `creature_text` VALUES (25319, 8, 0, 'Yes Anveena! Let fate embrace you now!', 14, 0, 100, 0, 0, 12448, 0, 25193, 0, 'KJ - SAY_KALECGOS_FATE');
INSERT INTO `creature_text` VALUES (25319, 9, 0, 'Goodbye Anveena, my love. Few will remember your name, yet this day you changed the course of destiny. What was once corrupt, is now pure. Heroes, do not let her sacrifice be in vain.', 14, 0, 100, 0, 0, 12450, 0, 25194, 0, 'KJ - SAY_KALECGOS_GOODBYE');
INSERT INTO `creature_text` VALUES (25319, 10, 0, 'You are not alone! The blue dragonflight shall help you vanquish the Deceiver!', 14, 0, 100, 0, 0, 12438, 0, 25541, 0, 'KJ - SAY_KALECGOS_JOIN');
INSERT INTO `creature_text` VALUES (25321, 0, 0, 'Thank you for freeing me! May the tides always favor you.', 12, 0, 100, 1, 0, 0, 0, 24596, 0, 'Kaskala Craftman');
INSERT INTO `creature_text` VALUES (25321, 0, 1, 'Do not allow Kaskala to forget what has happened here.', 12, 0, 100, 1, 0, 0, 0, 24598, 0, 'Kaskala Craftman');
INSERT INTO `creature_text` VALUES (25322, 0, 0, 'May the ancestors always aid you, $c, as you have aided me.', 12, 0, 100, 1, 0, 0, 0, 24601, 0, 'Kaskala Shaman');
INSERT INTO `creature_text` VALUES (25322, 0, 1, 'Thank you, $c. May the winds and seas always deliver you safely.', 12, 0, 100, 1, 0, 0, 0, 24599, 0, 'Kaskala Shaman');
INSERT INTO `creature_text` VALUES (25324, 0, 1, 'What is it you need?', 12, 0, 100, 1, 1500, 0, 0, 25739, 0, 'Earthen ring guide');
INSERT INTO `creature_text` VALUES (25324, 0, 2, 'Have you discovered something?', 12, 0, 100, 1, 1500, 0, 0, 25740, 0, 'Earthen ring guide');
INSERT INTO `creature_text` VALUES (25324, 0, 3, 'The elemental spirits are restless...', 12, 0, 100, 1, 1500, 0, 0, 25741, 0, 'Earthen ring guide');
INSERT INTO `creature_text` VALUES (25334, 0, 0, '%s gains fuel.', 41, 0, 100, 0, 0, 0, 0, 26171, 0, 'Horde Siege Tank');
INSERT INTO `creature_text` VALUES (25335, 0, 0, '$n is going to join us on our assault. Let us bring peace to my ancestors!', 12, 1, 100, 396, 0, 0, 0, 24561, 0, 'Longrunner Proudhoof');
INSERT INTO `creature_text` VALUES (25335, 1, 0, 'WE STRIKE!', 14, 1, 100, 15, 0, 0, 0, 24562, 0, 'Longrunner Proudhoof');
INSERT INTO `creature_text` VALUES (25335, 2, 0, 'An ambush. Return them to their rest!', 12, 1, 100, 5, 0, 0, 0, 24563, 0, 'Longrunner Proudhoof');
INSERT INTO `creature_text` VALUES (25335, 3, 0, 'Regain your strength. The place where Steeljaw was felled is just ahead.', 12, 1, 100, 396, 0, 0, 0, 24564, 0, 'Longrunner Proudhoof');
INSERT INTO `creature_text` VALUES (25335, 4, 0, 'There\'s the dog\'s banner and there\'s his corpse. What\'s that? He\'s upon us!', 12, 1, 100, 25, 0, 0, 0, 24565, 0, 'Longrunner Proudhoof');
INSERT INTO `creature_text` VALUES (25335, 5, 0, 'You fought well. Now go north to your orc outpost and inform Overlord Bor\'gorok of our success!', 12, 1, 100, 113, 0, 0, 0, 24566, 0, 'Longrunner Proudhoof');
INSERT INTO `creature_text` VALUES (25339, 0, 0, 'Oh no... they\'ve shackled his spirit!', 12, 1, 100, 5, 0, 0, 0, 24683, 0, 'Spirit Talker Snarlfang');
INSERT INTO `creature_text` VALUES (25359, 0, 0, 'I\'m freed! Thank you.', 12, 1, 100, 0, 0, 0, 0, 24575, 0, 'Force-Commander Steeljaw');
INSERT INTO `creature_text` VALUES (25379, 0, 0, 'Try to not lose this one, $n. It is important that we at least try and keep up appearances with the Alliance.', 15, 1, 100, 0, 0, 0, 0, 24977, 0, 'Warden Nork Bloodfrenzy');
INSERT INTO `creature_text` VALUES (25379, 1, 0, 'Look at \'em, Ug. You know what those are?', 12, 1, 100, 25, 0, 0, 0, 24631, 0, 'Warden Nork Bloodfrenzy');
INSERT INTO `creature_text` VALUES (25379, 2, 0, 'Those...', 12, 1, 100, 25, 0, 0, 0, 24633, 0, 'Warden Nork Bloodfrenzy');
INSERT INTO `creature_text` VALUES (25379, 3, 0, 'Those are cowards.', 12, 1, 100, 1, 0, 0, 0, 24634, 0, 'Warden Nork Bloodfrenzy');
INSERT INTO `creature_text` VALUES (25379, 4, 0, '%s shakes his head.', 16, 1, 100, 274, 0, 0, 0, 24636, 0, 'Warden Nork Bloodfrenzy');
INSERT INTO `creature_text` VALUES (25379, 5, 0, 'No, blood of my blood, they are soldiers of the Alliance. Our sworn enemies.', 12, 1, 100, 1, 0, 0, 0, 24637, 0, 'Warden Nork Bloodfrenzy');
INSERT INTO `creature_text` VALUES (25379, 6, 0, 'They are here because they do not wish to defend their homes... their families...', 12, 1, 100, 1, 0, 0, 0, 24639, 0, 'Warden Nork Bloodfrenzy');
INSERT INTO `creature_text` VALUES (25379, 7, 0, 'They have given up and surrendered - willingly - to us.', 12, 1, 100, 1, 0, 0, 0, 24640, 0, 'Warden Nork Bloodfrenzy');
INSERT INTO `creature_text` VALUES (25379, 8, 0, 'Ug\'thor, they are afraid to die.', 12, 1, 100, 1, 0, 0, 0, 24641, 0, 'Warden Nork Bloodfrenzy');
INSERT INTO `creature_text` VALUES (25379, 9, 0, '%s laughs.', 16, 1, 100, 11, 0, 0, 0, 24646, 0, 'Warden Nork Bloodfrenzy');
INSERT INTO `creature_text` VALUES (25379, 10, 0, 'That\'s right, Ug. Victory or death!', 12, 1, 100, 273, 0, 0, 0, 24647, 0, 'Warden Nork Bloodfrenzy');
INSERT INTO `creature_text` VALUES (25379, 11, 0, '[In Broken Common] Hear boy, cowards? Boy die! Boy die for freedom!', 12, 0, 100, 273, 0, 0, 0, 24648, 0, 'Warden Nork Bloodfrenzy');
INSERT INTO `creature_text` VALUES (25380, 0, 0, 'And remember, $n, the bloodspore powder can be used to weaken all magnataur in Gammoth.', 15, 0, 100, 0, 0, 0, 0, 24994, 0, 'Primal Mighthorn');
INSERT INTO `creature_text` VALUES (25381, 0, 0, 'How positively awful! You were totally incapacitated? Weak? Hot flashes?', 15, 0, 100, 21, 0, 0, 0, 24992, 0, 'Bloodmage Laurith');
INSERT INTO `creature_text` VALUES (25396, 0, 0, 'You cannot stop us all!', 12, 0, 100, 0, 0, 0, 0, 31650, 0, 'Naxxanar Skeletal Mage');
INSERT INTO `creature_text` VALUES (25396, 0, 1, 'Your death will soon follow....', 12, 0, 100, 0, 0, 0, 0, 13926, 0, 'Naxxanar Skeletal Mage');
INSERT INTO `creature_text` VALUES (25401, 0, 0, '%s armed.', 16, 0, 100, 0, 0, 0, 0, 24590, 0, 'Seaforium Depth Charge');
INSERT INTO `creature_text` VALUES (25401, 1, 0, 'Detonation in 5...', 16, 0, 100, 0, 0, 0, 0, 24591, 0, 'Seaforium Depth Charge');
INSERT INTO `creature_text` VALUES (25401, 2, 0, '4...', 16, 0, 100, 0, 0, 0, 0, 24592, 0, 'Seaforium Depth Charge');
INSERT INTO `creature_text` VALUES (25401, 3, 0, '3...', 16, 0, 100, 0, 0, 0, 0, 24593, 0, 'Seaforium Depth Charge');
INSERT INTO `creature_text` VALUES (25401, 4, 0, '2...', 16, 0, 100, 0, 0, 0, 0, 24594, 0, 'Seaforium Depth Charge');
INSERT INTO `creature_text` VALUES (25401, 5, 0, '1...', 16, 0, 100, 0, 0, 0, 0, 24595, 0, 'Seaforium Depth Charge');
INSERT INTO `creature_text` VALUES (25416, 0, 0, 'Who dares?!', 12, 0, 0, 0, 0, 0, 0, 24675, 0, '');
