INSERT INTO `creature_text` VALUES (26167, 4, 0, 'The smoke in the distance... It is Wartook. He is trying to tell us something...', 12, 1, 100, 1, 0, 0, 0, 25312, 0, 'Taunka\'le Evacuee');
INSERT INTO `creature_text` VALUES (26167, 5, 0, '%s expertly identifies the messages hidden within the smoke signals.', 16, 1, 100, 20, 0, 0, 0, 25315, 0, 'Taunka\'le Evacuee');
INSERT INTO `creature_text` VALUES (26167, 6, 0, 'No... It cannot be true...', 12, 1, 100, 274, 0, 0, 0, 25313, 0, 'Taunka\'le Evacuee');
INSERT INTO `creature_text` VALUES (26167, 7, 0, 'Icemist... Not Icemist... NO!!! Come quickly, hero! Follow me!', 12, 1, 100, 15, 0, 0, 0, 25314, 0, 'Taunka\'le Evacuee');
INSERT INTO `creature_text` VALUES (26170, 0, 0, 'Leryssa!', 14, 0, 100, 0, 0, 14664, 0, 25359, 0, 'thassarian SAY_THASSARIAN_1');
INSERT INTO `creature_text` VALUES (26170, 1, 0, 'What have you done to my sister, you motherless elf scum!?', 14, 0, 100, 0, 0, 14665, 0, 25360, 0, 'thassarian SAY_THASSARIAN_2');
INSERT INTO `creature_text` VALUES (26170, 2, 0, 'I would sooner slit my own throat.  You will pay for what you did to your own men, Arthas... for what you did to me!  I swear it.', 12, 0, 100, 0, 0, 14666, 0, 25366, 0, 'thassarian SAY_THASSARIAN_3');
INSERT INTO `creature_text` VALUES (26170, 3, 0, 'Leryssa... you... you\'re all right!', 12, 0, 100, 0, 0, 14667, 0, 25844, 0, 'thassarian SAY_THASSARIAN_4');
INSERT INTO `creature_text` VALUES (26170, 4, 0, 'I cannot return home with you just yet, Leryssa.  I am not quite done with the Scourge.', 12, 0, 100, 0, 0, 14668, 0, 25845, 0, 'thassarian SAY_THASSARIAN_5');
INSERT INTO `creature_text` VALUES (26170, 5, 0, 'You might be right, sister.  My obligations to my land and King have been fulfilled.  But there is something that I still owe to myself.', 12, 0, 100, 0, 0, 14669, 0, 25846, 0, 'thassarian SAY_THASSARIAN_6');
INSERT INTO `creature_text` VALUES (26170, 6, 0, 'Do not worry, Leryssa.  I will come back to you when I am done.  Nothing in the world will stop me from coming home to the only family that I have left.', 12, 0, 100, 0, 0, 14670, 0, 25850, 0, 'thassarian SAY_THASSARIAN_7');
INSERT INTO `creature_text` VALUES (26170, 7, 0, ' Speak, Lich!  Undeath won\'t save you from what I\'ll put you through!', 12, 0, 100, 0, 4000, 0, 0, 25321, 0, 'Thassarian');
INSERT INTO `creature_text` VALUES (26171, 0, 0, ' Please... all our orders come from Naxxanar... me and my men are kept in the dark!', 12, 0, 100, 0, 0, 0, 0, 25353, 0, 'High Deathpriest Isidorus');
INSERT INTO `creature_text` VALUES (26171, 0, 1, ' Mercy!  You were once with us!  DO you not remember?', 12, 0, 100, 0, 0, 0, 0, 25355, 0, 'High Deathpriest Isidorus');
INSERT INTO `creature_text` VALUES (26171, 0, 2, ' I beg you, death knight!  Keep your minion away from me!  I\'ll do anything!', 12, 0, 100, 0, 0, 0, 0, 25354, 0, 'High Deathpriest Isidorus');
INSERT INTO `creature_text` VALUES (26178, 0, 0, 'You will not stop the Frost Lord from entering this world, mortal. The Tidehunter\'s might will crush that of Ragnaros once and for all, leaving your land a frozen paradise!', 12, 0, 100, 1, 2000, 0, 0, 25372, 0, 'Frostwave Lieutenant intro speech');
INSERT INTO `creature_text` VALUES (26179, 0, 0, 'Victory or death! For the Horde!', 14, 1, 100, 15, 0, 0, 0, 25667, 0, 'Taunka\'le Refugee');
INSERT INTO `creature_text` VALUES (26184, 0, 0, 'Victory or death! For the Horde!', 14, 1, 100, 15, 0, 0, 0, 25667, 0, 'Taunka\'le Refugee');
INSERT INTO `creature_text` VALUES (26185, 0, 0, ' You worship death, little man?  Death is here!', 12, 0, 100, 35, 0, 0, 0, 25347, 0, 'Lurid');
INSERT INTO `creature_text` VALUES (26185, 0, 1, ' Master... you want... I kill puny priest?  Make painful, yes?', 12, 0, 100, 35, 0, 0, 0, 25345, 0, 'Lurid');
INSERT INTO `creature_text` VALUES (26185, 0, 2, ' Yes, weakling... beg Lurid for life!', 12, 0, 100, 35, 0, 0, 0, 25346, 0, 'Lurid');
INSERT INTO `creature_text` VALUES (26185, 1, 0, ' You Arthas lapdog?  You die!', 12, 0, 100, 0, 0, 0, 0, 25835, 0, 'Lurid');
INSERT INTO `creature_text` VALUES (26185, 1, 1, ' Time to kill!', 12, 0, 100, 0, 0, 0, 0, 17565, 0, 'Lurid');
INSERT INTO `creature_text` VALUES (26185, 1, 2, ' Lurid could get used... to working with you.', 12, 0, 100, 0, 0, 0, 0, 25837, 0, 'Lurid');
INSERT INTO `creature_text` VALUES (26185, 1, 3, ' It\'s about time!  Lurid was getting bored.', 12, 0, 100, 0, 0, 0, 0, 25834, 0, 'Lurid');
INSERT INTO `creature_text` VALUES (26185, 1, 4, ' This not... end well for you!', 12, 0, 100, 0, 0, 0, 0, 25836, 0, 'Lurid');
INSERT INTO `creature_text` VALUES (26189, 0, 0, ' The ziggurat\'s lost!  Flee to the temple!', 12, 0, 100, 0, 0, 0, 0, 25350, 0, 'Fleeing Cultist');
INSERT INTO `creature_text` VALUES (26189, 0, 1, ' The death knight... he\'s mad!', 12, 0, 100, 0, 0, 0, 0, 25349, 0, 'Fleeing Cultist');
INSERT INTO `creature_text` VALUES (26189, 0, 2, ' Arthas save us - we\'re doomed!', 12, 0, 100, 0, 0, 0, 0, 25348, 0, 'Fleeing Cultist');
INSERT INTO `creature_text` VALUES (26196, 0, 0, '%s goes into a frenzy!', 16, 0, 100, 0, 0, 0, 0, 38630, 0, 'Loguhn');
INSERT INTO `creature_text` VALUES (26199, 0, 0, '%s goes into a frenzy!', 16, 0, 100, 0, 0, 0, 0, 38630, 0, 'Snowfall Glade Den Mother');
INSERT INTO `creature_text` VALUES (26203, 0, 0, 'Your progress in this region has been impressive, blood prince.  I am pleased.', 12, 0, 100, 0, 0, 14756, 0, 25362, 0, 'thassarian SAY_LICH_1');
INSERT INTO `creature_text` VALUES (26203, 1, 0, 'Now this is a surprise, Thassarian.  I hadn\'t heard from Mograine or the other death knights for months.  You\'ve come to rejoin the Scourge, I take it?', 12, 0, 100, 0, 0, 14757, 0, 25363, 0, 'thassarian SAY_LICH_2');
INSERT INTO `creature_text` VALUES (26203, 2, 0, 'Do not fail me, San\'layn.  Return to Icecrown with this fool\'s head or do not bother to return.', 12, 0, 100, 0, 0, 14758, 0, 25364, 0, 'thassarian SAY_LICH_3');
INSERT INTO `creature_text` VALUES (26204, 0, 0, 'You will not stop the Frost Lord from entering this world, mortal. The Tidehunter\'s might will crush that of Ragnaros once and for all, leaving your land a frozen paradise!', 12, 0, 100, 1, 2000, 0, 0, 25372, 0, 'Chillwind Lieutenant intro speech');
INSERT INTO `creature_text` VALUES (26206, 0, 0, 'Raelorasz, it is good to see you again.', 12, 0, 100, 2, 0, 0, 0, 25463, 0, 'Keristrasza');
INSERT INTO `creature_text` VALUES (26206, 1, 0, 'Fate, it seems, has been more than kind. A blow has been dealt to Malygos. When he learns of it, I am confident he will leave the safety of the Nexus.', 12, 0, 100, 1, 0, 0, 0, 25466, 0, 'Keristrasza');
INSERT INTO `creature_text` VALUES (26206, 2, 0, 'Prepare them well, Raelorasz. Malygos\' power is like nothing they\'ve seen. We will likely only delay his plans with this diversion.', 12, 0, 100, 1, 0, 0, 0, 25469, 0, 'Keristrasza');
INSERT INTO `creature_text` VALUES (26206, 3, 0, 'Farewell, Raelorasz.', 12, 0, 100, 1, 0, 0, 0, 25472, 0, 'Keristrasza');
INSERT INTO `creature_text` VALUES (26214, 0, 0, 'You will not stop the Frost Lord from entering this world, mortal. The Tidehunter\'s might will crush that of Ragnaros once and for all, leaving your land a frozen paradise!', 12, 0, 100, 1, 2000, 0, 0, 25372, 0, 'Frigid Lieutenant intro speech');
INSERT INTO `creature_text` VALUES (26215, 0, 0, 'You will not stop the Frost Lord from entering this world, mortal. The Tidehunter\'s might will crush that of Ragnaros once and for all, leaving your land a frozen paradise!', 12, 0, 100, 1, 2000, 0, 0, 25372, 0, 'Glacial Lieutenant intro speech');
INSERT INTO `creature_text` VALUES (26216, 0, 0, 'You will not stop the Frost Lord from entering this world, mortal. The Tidehunter\'s might will crush that of Ragnaros once and for all, leaving your land a frozen paradise!', 12, 0, 100, 1, 2000, 0, 0, 25372, 0, 'Templar intro speech');
INSERT INTO `creature_text` VALUES (26287, 0, 0, 'Thel\'zan\'s phylactery drops to the ground beneath Icestorm', 41, 0, 100, 0, 0, 0, 0, 27125, 0, 'Icestorm');
INSERT INTO `creature_text` VALUES (26293, 0, 0, 'The Hulking Jormungar falters for a moment, opening its mouth wide.', 41, 0, 100, 0, 0, 0, 0, 25961, 0, 'Hulking Jormungar');
INSERT INTO `creature_text` VALUES (26321, 0, 0, 'The %s gives you its thanks.', 16, 0, 100, 0, 0, 1525, 0, 26039, 0, 'Lothalor Ancient');
INSERT INTO `creature_text` VALUES (26334, 0, 0, 'Constructs, defend the capital at all costs!', 14, 0, 100, 0, 0, 0, 0, 25483, 0, 'Forgemaster Damrath');
INSERT INTO `creature_text` VALUES (26334, 0, 1, 'To battle, my children! Show the enemy no mercy!', 14, 0, 100, 0, 0, 0, 0, 25484, 0, 'Forgemaster Damrath');
INSERT INTO `creature_text` VALUES (26334, 0, 2, 'Crush the children of stone, my iron brethren!', 14, 0, 100, 0, 0, 0, 0, 25487, 0, 'Forgemaster Damrath');
INSERT INTO `creature_text` VALUES (26334, 0, 3, 'For the thane! For Loken!', 14, 0, 100, 0, 0, 0, 0, 25486, 0, 'Forgemaster Damrath');
INSERT INTO `creature_text` VALUES (26349, 0, 0, 'You\'re too late! The accord has been negotiated. Only the details remain. Small details... like you!', 12, 0, 100, 0, 0, 0, 0, 25716, 0, 'Goramosh');
INSERT INTO `creature_text` VALUES (26379, 0, 0, 'We will walk together, $n.', 12, 1, 100, 1, 0, 0, 0, 26131, 0, 'Overlord Agmar');
INSERT INTO `creature_text` VALUES (26379, 1, 0, 'Welcome to the Horde, High Chief. Lok\'tar ogar!', 12, 1, 100, 2, 0, 0, 0, 26141, 0, 'Overlord Agmar');
INSERT INTO `creature_text` VALUES (26379, 2, 0, 'FOR THE HORDE!', 14, 1, 100, 15, 0, 0, 0, 26142, 0, 'Overlord Agmar');
INSERT INTO `creature_text` VALUES (26401, 0, 0, 'Thank you again, $n, for this delectable incense.', 12, 0, 100, 1, 1000, 0, 0, 25699, 0, 'Summer Scorchling');
INSERT INTO `creature_text` VALUES (26401, 1, 0, '%s devours the incense.  It\'s ravenous!', 16, 0, 100, 1, 1000, 0, 0, 25717, 0, 'Summer Scorchling');
INSERT INTO `creature_text` VALUES (26401, 2, 0, 'So good! So packed with energy!', 12, 0, 100, 1, 1000, 0, 0, 25718, 0, 'Summer Scorchling');
INSERT INTO `creature_text` VALUES (26401, 3, 0, 'It has everything a growing scorchling needs!', 12, 0, 100, 1, 1000, 0, 0, 25719, 0, 'Summer Scorchling');
INSERT INTO `creature_text` VALUES (26401, 4, 0, 'I can feel the power SURGING within me!', 12, 0, 100, 1, 1000, 0, 0, 25730, 0, 'Summer Scorchling');
INSERT INTO `creature_text` VALUES (26401, 5, 0, '%s bellows with laughter!', 16, 0, 100, 1, 1000, 0, 0, 25720, 0, 'Summer Scorchling');
INSERT INTO `creature_text` VALUES (26401, 6, 0, 'Now! Finally! Our plans can take effect!', 12, 0, 100, 1, 1000, 0, 0, 25721, 0, 'Summer Scorchling');
INSERT INTO `creature_text` VALUES (26401, 7, 0, 'KNEEL, LITTLE MORTAL! KNEEL BEFORE THE MIGHT OF THE HERALD OF RAGNAROS!', 12, 0, 100, 1, 1000, 0, 0, 25722, 0, 'Summer Scorchling');
INSERT INTO `creature_text` VALUES (26401, 8, 0, 'YOU WILL ALL PERISH IN FLAMES!', 12, 0, 100, 1, 1000, 0, 0, 25723, 0, 'Summer Scorchling');
INSERT INTO `creature_text` VALUES (26401, 9, 0, '%s blinks...', 16, 0, 100, 1, 1000, 0, 0, 25724, 0, 'Summer Scorchling');
INSERT INTO `creature_text` VALUES (26401, 10, 0, 'Ah. I was merely jesting...', 12, 0, 100, 1, 1000, 0, 0, 25725, 0, 'Summer Scorchling');
INSERT INTO `creature_text` VALUES (26417, 0, 0, 'Children of iron, lend me your blades!', 12, 0, 100, 0, 0, 0, 0, 25981, 0, 'Runed Giant');
INSERT INTO `creature_text` VALUES (26417, 0, 1, 'Crush the small one!', 12, 0, 100, 0, 0, 0, 0, 25979, 0, 'Runed Giant');
INSERT INTO `creature_text` VALUES (26417, 0, 2, 'Iron masters, aid me in this battle!', 12, 0, 100, 0, 0, 0, 0, 25967, 0, 'Runed Giant');
INSERT INTO `creature_text` VALUES (26417, 0, 3, 'The enemy grows stronger! Aid me!', 12, 0, 100, 0, 0, 0, 0, 25982, 0, 'Runed Giant');
INSERT INTO `creature_text` VALUES (26417, 0, 4, 'We will destroy you, puny creature!', 12, 0, 100, 0, 0, 0, 0, 25980, 0, 'Runed Giant');
INSERT INTO `creature_text` VALUES (26420, 0, 0, 'Ah, yes. Loken is well known to me.', 12, 0, 100, 0, 0, 0, 0, 26267, 0, 'Gavrock');
INSERT INTO `creature_text` VALUES (26420, 1, 0, 'It is he who commands the sons of iron in their war against us.', 12, 0, 100, 0, 0, 0, 0, 26268, 0, 'Gavrock');
INSERT INTO `creature_text` VALUES (26420, 2, 0, 'From his hiding place, he oversees their preparations for war with the goal of exterminating the stone giants!', 12, 0, 100, 0, 0, 0, 0, 26269, 0, 'Gavrock');
INSERT INTO `creature_text` VALUES (26421, 0, 0, 'The %s is angered by your request and attacks!', 16, 0, 100, 0, 0, 0, 0, 26042, 0, 'emote text');
INSERT INTO `creature_text` VALUES (26421, 1, 0, 'Breaking off a piece of its bark, the %s hands it to you before departing.', 16, 0, 100, 0, 0, 0, 0, 26041, 0, 'emote text');
INSERT INTO `creature_text` VALUES (26437, 0, 0, 'Amidst the tragedy and chaos, this place stands defiantly against the Scourge! It is good to be Horde.', 12, 1, 100, 113, 0, 0, 0, 25727, 0, 'Taunka Soldier');
INSERT INTO `creature_text` VALUES (26445, 0, 0, 'Shall return to the cradle of iron and stone.', 12, 0, 100, 0, 0, 0, 0, 25686, 0, 'Rune Plate');
INSERT INTO `creature_text` VALUES (26445, 1, 0, 'Return to Ulduar, nestled in the embrace of the storm.', 12, 0, 100, 0, 0, 0, 0, 25688, 0, 'Rune Plate');
INSERT INTO `creature_text` VALUES (26445, 2, 0, 'Greatest of the Maker\'s children, arise and claim your birthright.', 12, 0, 100, 0, 0, 0, 0, 25690, 0, 'Rune Plate');
INSERT INTO `creature_text` VALUES (26450, 0, 0, 'The fallen of Pal\'ea will not rest, son.  Find the naga priestess!', 12, 0, 100, 1, 0, 0, 0, 25684, 0, 'Ariut');
INSERT INTO `creature_text` VALUES (26484, 0, 0, 'He\'s out to get me, too! I\'d be careful around here if I was you, traveler. You never know which tree he\'s hiding behind!', 12, 0, 100, 0, 0, 0, 0, 26266, 0, 'Hugh Glass');
INSERT INTO `creature_text` VALUES (26484, 1, 0, 'That\'s not something Limpy Joe would ask! But yeah, I know Loken.', 12, 0, 100, 0, 0, 0, 0, 26265, 0, 'Hugh Glass');
INSERT INTO `creature_text` VALUES (26484, 2, 0, 'By the Light, you killed Limpy Joe and took the meat! How did you know where to find us?', 12, 0, 100, 5, 0, 0, 0, 26639, 0, 'Hugh Glass');
INSERT INTO `creature_text` VALUES (26484, 3, 0, 'Well, if you\'ve come to kill me, too, can you at least wait until I\'ve fed Griselda? Poor girl hasn\'t eaten in days.', 12, 0, 100, 20, 0, 0, 0, 26642, 0, 'Hugh Glass');
INSERT INTO `creature_text` VALUES (26484, 4, 0, 'Here, girl. I\'ve got one last meal for you before Limpy Joe\'s killer does us in!', 12, 0, 100, 22, 0, 0, 0, 26643, 0, 'Hugh Glass');
INSERT INTO `creature_text` VALUES (26484, 5, 0, 'How could you kill poor, defenseless old Joe? And why did you bring the meat back?', 12, 0, 100, 6, 0, 0, 0, 26645, 0, 'Hugh Glass');
INSERT INTO `creature_text` VALUES (26484, 6, 0, 'You\'re an enigma, that\'s what you are... And let me tell you, enigmas and I don\'t get along....', 12, 0, 100, 1, 0, 0, 0, 26646, 0, 'Hugh Glass');
INSERT INTO `creature_text` VALUES (26484, 7, 0, 'I think you\'d best leave before Griselda decides she\'s in the mood for dessert.', 12, 0, 100, 25, 0, 0, 0, 26647, 0, 'Hugh Glass');
INSERT INTO `creature_text` VALUES (26484, 8, 0, 'Wake up, Griselda. Here, girl.', 12, 0, 100, 0, 0, 0, 0, 25700, 0, 'Hugh Glass');
INSERT INTO `creature_text` VALUES (26484, 9, 0, 'They\'ll never find us here, will they? We won\'t let them get us like they got Limpy Joe and Flannel McGee!', 12, 0, 100, 5, 0, 0, 0, 25701, 0, 'Hugh Glass');
INSERT INTO `creature_text` VALUES (26484, 9, 1, 'No, we escaped from those monsters! They\'re nothing like us, Griselda.', 12, 0, 100, 1, 0, 0, 0, 25702, 0, 'Hugh Glass');
INSERT INTO `creature_text` VALUES (26484, 9, 2, 'We\'re hunters, you and I. No wolfmen here, no ma\'am.', 12, 0, 100, 274, 0, 0, 0, 25703, 0, 'Hugh Glass');
INSERT INTO `creature_text` VALUES (26484, 9, 3, 'Keep an eye out for them, girl! Don\'t let them get us!', 12, 0, 100, 25, 0, 0, 0, 25704, 0, 'Hugh Glass');
INSERT INTO `creature_text` VALUES (26497, 0, 0, 'Arthas! You can\'t just--', 12, 0, 100, 396, 0, 12837, 0, 27326, 3, 'culling SAY_PHASE113');
INSERT INTO `creature_text` VALUES (26497, 1, 0, 'I\'m sorry, Arthas. I can\'t watch you do this.', 12, 0, 100, 396, 0, 12838, 0, 27330, 3, 'culling SAY_PHASE117');
INSERT INTO `creature_text` VALUES (26499, 0, 0, 'Glad you could make it, Uther.', 12, 0, 100, 396, 0, 12828, 0, 27314, 3, 'culling SAY_PHASE101');
INSERT INTO `creature_text` VALUES (26499, 1, 0, 'As if I could forget. Listen, Uther, there\'s something about the plague you should know...', 12, 0, 100, 0, 0, 12829, 0, 27316, 3, 'culling SAY_PHASE103');
INSERT INTO `creature_text` VALUES (26499, 2, 0, 'Oh, no. We\'re too late. These people have all been infected! They may look fine now, but it\'s just a matter of time before they turn into the undead!', 12, 0, 100, 5, 0, 12830, 0, 27317, 3, 'culling SAY_PHASE104');
INSERT INTO `creature_text` VALUES (26499, 3, 0, 'This entire city must be purged.', 12, 0, 100, 396, 0, 12831, 0, 27319, 3, 'culling SAY_PHASE106');
INSERT INTO `creature_text` VALUES (26499, 4, 0, 'Damn it, Uther! As your future king, I order you to purge this city!', 12, 0, 100, 5, 0, 12832, 0, 27321, 3, 'culling SAY_PHASE108');
INSERT INTO `creature_text` VALUES (26499, 5, 0, 'Then I must consider this an act of treason.', 12, 0, 100, 396, 0, 12833, 0, 27323, 3, 'culling SAY_PHASE110');
INSERT INTO `creature_text` VALUES (26499, 6, 0, 'Have I? Lord Uther, by my right of succession and the sovereignty of my crown, I hereby relieve you of your command and suspend your paladins from service.', 12, 0, 100, 0, 0, 12834, 0, 27325, 3, 'culling SAY_PHASE112');
INSERT INTO `creature_text` VALUES (26499, 7, 0, 'It\'s done! Those of you who have the will to save this land, follow me! The rest of you... get out of my sight!', 12, 0, 100, 1, 0, 12835, 0, 27327, 3, 'culling SAY_PHASE114');
INSERT INTO `creature_text` VALUES (26499, 8, 0, 'Jaina?', 12, 0, 100, 0, 0, 12836, 0, 27329, 3, 'culling SAY_PHASE116');
INSERT INTO `creature_text` VALUES (26499, 9, 0, 'Take position here, and I will lead a small force inside Stratholme to begin the culling. We must contain and purge the infected for the sake of all of Lordaeron!', 14, 0, 100, 0, 0, 14327, 0, 27466, 3, 'culling SAY_PHASE118');
INSERT INTO `creature_text` VALUES (26499, 10, 0, 'Everyone looks ready. Remember, these people are all infected with the plague and will die soon. We must purge Stratholme to protect the remainder of Lordaeron from the Scourge. Let\'s go.', 12, 0, 100, 0, 0, 14293, 0, 27535, 3, 'culling SAY_PHASE201');
INSERT INTO `creature_text` VALUES (26499, 11, 0, 'I can only help you with a clean death.', 12, 0, 100, 0, 0, 14294, 0, 27548, 3, 'culling SAY_PHASE203');
INSERT INTO `creature_text` VALUES (26499, 12, 0, 'That was just the beginning.', 12, 0, 100, 1, 0, 14295, 0, 27553, 3, 'culling SAY_PHASE205');
INSERT INTO `creature_text` VALUES (26499, 13, 0, 'I won\'t allow it, Mal\'Ganis! Better that these people die by my hand than serve as your slaves in death!', 14, 0, 100, 397, 0, 14296, 0, 27577, 3, 'culling SAY_PHASE208');
INSERT INTO `creature_text` VALUES (26499, 14, 0, 'Mal\'Ganis will send out some of his Scourge minions to interfere with us. Those of you with the strongest steel and magic shall go forth and destroy them. I will lead the rest of my forces in purging Stratholme of the infected.', 14, 0, 100, 1, 0, 14885, 0, 27584, 3, 'culling SAY_PHASE209');
INSERT INTO `creature_text` VALUES (26499, 15, 0, 'Champions, meet me at the Town Hall at once. We must take the fight to Mal\'Ganis.', 14, 0, 100, 0, 0, 14297, 0, 28890, 3, 'culling SAY_PHASE210');
INSERT INTO `creature_text` VALUES (26499, 16, 0, 'Follow me, I know the way through.', 12, 0, 100, 1, 0, 14298, 0, 27800, 3, 'culling SAY_PHASE301');
INSERT INTO `creature_text` VALUES (26499, 17, 0, 'Yes, I\'m glad I could get to you before the plague.', 12, 0, 100, 0, 0, 14299, 0, 27812, 3, 'culling SAY_PHASE303');
INSERT INTO `creature_text` VALUES (26499, 18, 0, 'What is this sorcery?', 12, 0, 100, 0, 0, 14300, 0, 27814, 3, 'culling SAY_PHASE304');
INSERT INTO `creature_text` VALUES (26499, 19, 0, 'Mal\'Ganis appears to have more than Scourge in his arsenal. We should make haste.', 12, 0, 100, 1, 0, 14301, 0, 27840, 3, 'culling SAY_PHASE306. NEEDS VERIFICATION');
INSERT INTO `creature_text` VALUES (26499, 20, 0, 'More vile sorcery! Be ready for anything!', 12, 0, 100, 0, 0, 14302, 0, 27882, 3, 'culling SAY_PHASE307');
INSERT INTO `creature_text` VALUES (26499, 21, 0, 'Let\'s move on.', 12, 0, 100, 396, 0, 14303, 0, 36010, 3, 'culling SAY_PHASE308');
INSERT INTO `creature_text` VALUES (26499, 22, 0, 'Watch your backs: they have us surrounded in this hall.', 12, 0, 100, 1, 0, 14304, 0, 27901, 3, 'culling SAY_PHASE309');
INSERT INTO `creature_text` VALUES (26499, 23, 0, 'One less obstacle to deal with.', 12, 0, 100, 0, 0, 0, 0, 31356, 3, 'culling SAY_PHASE310. NEEDS VERIFICATION');
INSERT INTO `creature_text` VALUES (26499, 24, 0, 'Mal\'Ganis is not making this easy.', 12, 0, 100, 396, 0, 14305, 0, 27902, 3, 'culling SAY_PHASE311');
INSERT INTO `creature_text` VALUES (26499, 25, 0, 'They\'re very persistent.', 12, 0, 100, 396, 0, 14306, 0, 27904, 3, 'culling SAY_PHASE312');
INSERT INTO `creature_text` VALUES (26499, 26, 0, 'What else can he put in my way?', 12, 0, 100, 396, 0, 14307, 0, 27905, 3, 'culling SAY_PHASE313');
INSERT INTO `creature_text` VALUES (26499, 27, 0, 'I do what I must for Lordaeron, and neither your words nor your actions will stop me.', 12, 0, 100, 396, 0, 14309, 0, 27916, 3, 'culling SAY_PHASE315');
INSERT INTO `creature_text` VALUES (26499, 28, 0, 'The quickest path to Mal\'Ganis lies behind that bookshelf ahead.', 12, 0, 100, 0, 0, 14308, 0, 27906, 3, 'culling SAY_PHASE401');
INSERT INTO `creature_text` VALUES (26499, 29, 0, 'This will only take a moment.', 12, 0, 100, 432, 0, 14310, 0, 28040, 3, 'culling SAY_PHASE402');
INSERT INTO `creature_text` VALUES (26499, 30, 0, 'I\'m relieved this secret passage still works.', 12, 0, 100, 0, 0, 14311, 0, 28041, 3, 'culling SAY_PHASE403');
INSERT INTO `creature_text` VALUES (26499, 31, 0, 'Let\'s move through here as quickly as possible. If the undead don\'t kill us, the fires might.', 12, 0, 100, 396, 0, 14312, 0, 28562, 3, 'culling SAY_PHASE404');
INSERT INTO `creature_text` VALUES (26499, 32, 0, 'Rest a moment and clear your lungs, but we must move again soon.', 12, 0, 100, 396, 0, 14313, 0, 28045, 3, 'culling SAY_PHASE405');
INSERT INTO `creature_text` VALUES (26499, 33, 0, 'That\'s enough; we must move again. Mal\'Ganis awaits.', 12, 0, 100, 396, 0, 14314, 0, 28047, 3, 'culling SAY_PHASE406');
INSERT INTO `creature_text` VALUES (26499, 34, 0, 'At last some good luck. Market Row has not caught fire yet. Mal\'Ganis is supposed to be in Crusaders\' Square, which is just ahead. Tell me when you\'re ready to move forward.', 12, 0, 100, 396, 0, 14315, 0, 28048, 3, 'culling SAY_PHASE407');
INSERT INTO `creature_text` VALUES (26499, 35, 0, 'Justice will be done.', 12, 0, 100, 0, 0, 14316, 0, 28563, 3, 'culling SAY_PHASE501');
INSERT INTO `creature_text` VALUES (26499, 36, 0, 'We\'re going to finish this right now, Mal\'Ganis. Just you... and me.', 12, 0, 100, 5, 0, 14317, 0, 28520, 3, 'culling SAY_PHASE502');
INSERT INTO `creature_text` VALUES (26499, 37, 0, 'I\'ll hunt you to the ends of the earth if I have to! Do you hear me? To the ends of the earth!', 12, 0, 100, 0, 0, 14318, 0, 28529, 3, 'culling SAY_PHASE503');
INSERT INTO `creature_text` VALUES (26499, 38, 0, 'You performed well this day. Anything that Mal\'Ganis has left behind is yours. Take it as your reward. I must now begin plans for an expedition to Northrend.', 12, 0, 100, 1, 0, 14319, 0, 28530, 3, 'culling SAY_PHASE504');
INSERT INTO `creature_text` VALUES (26499, 39, 0, 'I can\'t afford to spare you.', 12, 0, 100, 0, 0, 0, 0, 31355, 3, 'Arthas SAY_AGGRO_ZOMBIE');
INSERT INTO `creature_text` VALUES (26499, 40, 0, 'All officers should check in with me when their squads are ready. We\'ll enter Stratholme on my order.', 12, 0, 100, 0, 0, 0, 0, 27517, 3, 'Arthas RP1_LINE_ARTHAS11');
INSERT INTO `creature_text` VALUES (26500, 0, 0, 'I\'ll be waitin\' for ya, mon.', 15, 0, 100, 0, 0, 0, 0, 25877, 0, 'Image of Drakuru');
INSERT INTO `creature_text` VALUES (26520, 0, 0, 'Thank you again, $n, for this delectable incense.', 12, 0, 100, 1, 1000, 0, 0, 25699, 0, 'Festival Scorchling');
INSERT INTO `creature_text` VALUES (26520, 1, 0, '%s devours the incense.  It\'s ravenous!', 16, 0, 100, 1, 1000, 0, 0, 25717, 0, 'Festival Scorchling');
INSERT INTO `creature_text` VALUES (26520, 2, 0, 'So good! So packed with energy!', 12, 0, 100, 1, 1000, 0, 0, 25718, 0, 'Festival Scorchling');
INSERT INTO `creature_text` VALUES (26520, 3, 0, 'It has everything a growing scorchling needs!', 12, 0, 100, 1, 1000, 0, 0, 25719, 0, 'Festival Scorchling');
INSERT INTO `creature_text` VALUES (26520, 4, 0, 'I can feel the power SURGING within me!', 12, 0, 100, 1, 1000, 0, 0, 25730, 0, 'Festival Scorchling');
INSERT INTO `creature_text` VALUES (26520, 5, 0, '%s bellows with laughter!', 16, 0, 100, 1, 1000, 0, 0, 25720, 0, 'Festival Scorchling');
INSERT INTO `creature_text` VALUES (26520, 6, 0, 'Now! Finally! Our plans can take effect!', 12, 0, 100, 1, 1000, 0, 0, 25721, 0, 'Festival Scorchling');
INSERT INTO `creature_text` VALUES (26520, 7, 0, 'KNEEL, LITTLE MORTAL! KNEEL BEFORE THE MIGHT OF THE HERALD OF RAGNAROS!', 12, 0, 100, 1, 1000, 0, 0, 25722, 0, 'Festival Scorchling');
INSERT INTO `creature_text` VALUES (26520, 8, 0, 'YOU WILL ALL PERISH IN FLAMES!', 12, 0, 100, 1, 1000, 0, 0, 25723, 0, 'Festival Scorchling');
INSERT INTO `creature_text` VALUES (26520, 9, 0, '%s blinks...', 16, 0, 100, 1, 1000, 0, 0, 25724, 0, 'Festival Scorchling');
INSERT INTO `creature_text` VALUES (26520, 10, 0, 'Ah. I was merely jesting...', 12, 0, 100, 1, 1000, 0, 0, 25725, 0, 'Festival Scorchling');
INSERT INTO `creature_text` VALUES (26527, 0, 0, 'Oh, no! Adventurers, something awful has happened! A colleague of mine has been captured by the Infinite Dragonflight, and they\'re doing something horrible to him! Keeping Arthas is still your highest priority, but if you act fast you could help save a Guardian of Time!', 15, 0, 100, 0, 0, 0, 0, 32670, 3, 'Chromie');
INSERT INTO `creature_text` VALUES (26527, 1, 0, 'Adventurers, you must hurry! The Guardian of Time cannot last for much longer!', 15, 0, 100, 0, 0, 0, 0, 32678, 3, 'Chromie');
INSERT INTO `creature_text` VALUES (26527, 2, 0, 'I can barely sense the Guardian of Time! His timeline is fading quickly!', 15, 0, 100, 0, 0, 0, 0, 32679, 3, 'Chromie');
INSERT INTO `creature_text` VALUES (26528, 0, 0, 'Watch your tone with me, boy. You may be the prince, but I\'m still your superior as a paladin!', 12, 0, 100, 397, 0, 12839, 0, 27315, 3, 'culling SAY_PHASE102');
INSERT INTO `creature_text` VALUES (26528, 1, 0, 'What?', 12, 0, 100, 5, 0, 12840, 0, 38871, 3, 'culling SAY_PHASE105');
INSERT INTO `creature_text` VALUES (26528, 2, 0, 'How can you even consider that? There\'s got to be some other way.', 12, 0, 100, 6, 0, 12841, 0, 27320, 3, 'culling SAY_PHASE107');
INSERT INTO `creature_text` VALUES (26528, 3, 0, 'You are not my king yet, boy! Nor would I obey that command even if you were!', 12, 0, 100, 396, 0, 12842, 0, 27322, 3, 'culling SAY_PHASE109');
INSERT INTO `creature_text` VALUES (26528, 4, 0, 'Treason? Have you lost your mind, Arthas?', 12, 0, 100, 5, 0, 12843, 0, 27324, 3, 'culling SAY_PHASE111');
INSERT INTO `creature_text` VALUES (26528, 5, 0, 'You\'ve just crossed a terrible threshold, Arthas.', 12, 0, 100, 397, 0, 12844, 0, 27328, 3, 'culling SAY_PHASE115');
INSERT INTO `creature_text` VALUES (26529, 0, 0, 'Play time!', 14, 0, 100, 0, 0, 13428, 0, 27716, 3, 'meathook SAY_AGGRO');
INSERT INTO `creature_text` VALUES (26529, 1, 0, 'Boring...', 14, 0, 100, 0, 0, 13430, 0, 29943, 3, 'meathook SAY_SLAY_1');
INSERT INTO `creature_text` VALUES (26529, 1, 1, 'Why you stop moving?', 14, 0, 100, 0, 0, 13431, 0, 29944, 3, 'meathook SAY_SLAY_2');
INSERT INTO `creature_text` VALUES (26529, 1, 2, 'Get up! Me not done!', 14, 0, 100, 0, 0, 13432, 0, 29945, 3, 'meathook SAY_SLAY_3');
INSERT INTO `creature_text` VALUES (26529, 2, 0, 'New toys!', 14, 0, 100, 0, 0, 13429, 0, 29942, 3, 'meathook SAY_SPAWN');
INSERT INTO `creature_text` VALUES (26529, 3, 0, 'This not fun...', 14, 0, 100, 0, 0, 13433, 0, 29941, 3, 'meathook SAY_DEATH');
INSERT INTO `creature_text` VALUES (26530, 0, 0, 'Ah, the entertainment has arrived!', 14, 0, 100, 0, 0, 13472, 0, 29947, 3, 'salramm SAY_AGGRO');
INSERT INTO `creature_text` VALUES (26530, 1, 0, 'You are too late, champion of Lordaeron. The dead shall have their day.', 14, 0, 100, 0, 0, 13471, 0, 27717, 3, 'salramm SAY_SPAWN');
INSERT INTO `creature_text` VALUES (26530, 2, 0, 'The fun is just beginning!', 14, 0, 100, 0, 0, 13473, 0, 29948, 3, 'salramm SAY_SLAY_1');
INSERT INTO `creature_text` VALUES (26530, 2, 1, 'Ah! Quality materials.', 14, 0, 100, 0, 0, 13474, 0, 29949, 3, 'salramm SAY_SLAY_2');
INSERT INTO `creature_text` VALUES (26530, 2, 2, 'Don\'t worry; I\'ll make good use of you!', 14, 0, 100, 0, 0, 13475, 0, 29950, 3, 'salramm SAY_SLAY_3');
INSERT INTO `creature_text` VALUES (26530, 3, 0, 'You only advance... the master\'s plan...', 14, 0, 100, 0, 0, 13483, 0, 29946, 3, 'salramm SAY_DEATH');
INSERT INTO `creature_text` VALUES (26530, 4, 0, 'BOOM! Hahahahah...', 14, 0, 100, 0, 0, 13478, 0, 29953, 3, 'salramm SAY_EXPLODE_GHOUL_1');
INSERT INTO `creature_text` VALUES (26530, 4, 1, 'Blood... destruction... EXHILARATING!', 14, 0, 100, 0, 0, 13479, 0, 29954, 3, 'salramm SAY_EXPLODE_GHOUL_2');
INSERT INTO `creature_text` VALUES (26530, 5, 0, 'I want a sample...', 14, 0, 100, 0, 0, 13480, 0, 29955, 3, 'salramm SAY_STEAL_FLESH_1');
INSERT INTO `creature_text` VALUES (26530, 5, 1, 'Such strength... it must be mine!', 14, 0, 100, 0, 0, 13481, 0, 29956, 3, 'salramm SAY_STEAL_FLESH_2');
INSERT INTO `creature_text` VALUES (26530, 5, 2, 'Your flesh betrays you.', 14, 0, 100, 0, 0, 13482, 0, 29957, 3, 'salramm SAY_STEAL_FLESH_3');
INSERT INTO `creature_text` VALUES (26530, 6, 0, 'Say hello to some friends of mine.', 14, 0, 100, 0, 0, 13476, 0, 29951, 3, 'salramm SAY_SUMMON_GHOULS_1');
INSERT INTO `creature_text` VALUES (26530, 6, 1, 'Come, citizens of Stratholme! Meet your \"saviors.\"', 14, 0, 100, 0, 0, 13477, 0, 29952, 3, 'salramm SAY_SUMMON_GHOULS_2');
INSERT INTO `creature_text` VALUES (26531, 0, 0, '%s is entranced by the sun\'s reflection on Zeh\'gehn\'s knife.', 16, 0, 100, 0, 0, 0, 0, 25732, 0, 'Mesmerized Frog');
INSERT INTO `creature_text` VALUES (26531, 1, 0, '%s croaks eagerly while directing its gaze to the docked ship.', 16, 0, 100, 0, 0, 0, 0, 25733, 0, 'Mesmerized Frog');
INSERT INTO `creature_text` VALUES (26532, 0, 0, 'Prince Arthas Menethil, on this day, a powerful darkness has taken hold of your soul. The death you are destined to visit upon others will this day be your own.', 14, 0, 100, 0, 0, 13408, 0, 27911, 3, 'epoch SAY_INTRO | culling SAY_PHASE314');
INSERT INTO `creature_text` VALUES (26532, 1, 0, 'We\'ll see about that, young prince.', 14, 0, 100, 0, 0, 13409, 0, 27920, 3, 'epoch SAY_AGGRO');
INSERT INTO `creature_text` VALUES (26532, 2, 0, 'Tick tock, tick tock...', 14, 0, 100, 0, 0, 13410, 0, 30451, 3, 'epoch SAY_TIME_WARP_1');
INSERT INTO `creature_text` VALUES (26532, 2, 1, 'Not quick enough!', 14, 0, 100, 0, 0, 13411, 0, 30452, 3, 'epoch SAY_TIME_WARP_2');
INSERT INTO `creature_text` VALUES (26532, 2, 2, 'Let\'s get this over with.', 14, 0, 100, 0, 0, 13412, 0, 30453, 3, 'epoch SAY_TIME_WARP_3');
INSERT INTO `creature_text` VALUES (26532, 3, 0, 'There is no future for you.', 14, 0, 100, 0, 0, 13413, 0, 30454, 3, 'epoch SAY_SLAY_1');
INSERT INTO `creature_text` VALUES (26532, 3, 1, 'This is the hour of our greatest triumph!', 14, 0, 100, 0, 0, 13414, 0, 30455, 3, 'epoch SAY_SLAY_2');
INSERT INTO `creature_text` VALUES (26532, 3, 2, 'You were destined to fail.', 14, 0, 100, 0, 0, 13415, 0, 30456, 3, 'epoch SAY_SLAY_3');
INSERT INTO `creature_text` VALUES (26533, 0, 0, 'Yes, this is the beginning. I\'ve been waiting for you, young prince. I am Mal\'Ganis.', 14, 0, 100, 1, 0, 14410, 0, 27574, 3, 'malganis SAY_INTRO_1 | culling SAY_PHASE206');
INSERT INTO `creature_text` VALUES (26533, 1, 0, 'As you can see, your people are now mine. I will now turn this city household by household, until the flame of life has been snuffed out... forever.', 14, 0, 100, 0, 0, 14411, 0, 27575, 3, 'malganis SAY_INTRO_2 | culling SAY_PHASE207');
INSERT INTO `creature_text` VALUES (26533, 2, 0, 'This will be a fine test, Prince Arthas.', 14, 0, 100, 0, 0, 14413, 0, 31344, 3, 'malganis SAY_AGGRO');
INSERT INTO `creature_text` VALUES (26533, 3, 0, 'All too easy.', 14, 0, 100, 0, 0, 14416, 0, 31347, 3, 'malganis SAY_KILL_1');
INSERT INTO `creature_text` VALUES (26533, 3, 1, 'The Dark Lord is displeased with your interference.', 14, 0, 100, 0, 0, 14417, 0, 31348, 3, 'malganis SAY_KILL_2');
INSERT INTO `creature_text` VALUES (26533, 3, 2, 'It is Prince Arthas I want, not you!', 14, 0, 100, 0, 0, 14418, 0, 31349, 3, 'malganis SAY_KILL_3');
INSERT INTO `creature_text` VALUES (26533, 4, 0, 'Anakh kyree!', 14, 0, 100, 0, 0, 14422, 0, 30412, 3, 'malganis SAY_SLAY_1');
INSERT INTO `creature_text` VALUES (26533, 4, 1, 'My Onslaught will wash over the Lich King\'s forces!', 14, 0, 100, 0, 0, 14423, 0, 30413, 3, 'malganis SAY_SLAY_2');
INSERT INTO `creature_text` VALUES (26533, 4, 2, 'Your death is in vain, tiny mortal.', 14, 0, 100, 0, 0, 14424, 0, 30414, 3, 'malganis SAY_SLAY_3');
INSERT INTO `creature_text` VALUES (26533, 4, 3, 'Your time has come to an end.', 14, 0, 100, 0, 0, 14425, 0, 30415, 3, 'malganis SAY_SLAY_4');
INSERT INTO `creature_text` VALUES (26533, 5, 0, 'Time out.', 14, 0, 100, 0, 0, 14414, 0, 31345, 3, 'malganis SAY_SLEEP_1');
INSERT INTO `creature_text` VALUES (26533, 5, 1, 'You seem tired.', 14, 0, 100, 0, 0, 14415, 0, 31346, 3, 'malganis SAY_SLEEP_2');
INSERT INTO `creature_text` VALUES (26533, 6, 0, 'Gah! I spent too much time in that weak little shell.', 14, 0, 100, 0, 0, 14426, 0, 30275, 3, 'malganis SAY_30HEALTH');
INSERT INTO `creature_text` VALUES (26533, 7, 0, 'Kirel narak! I am Mal\'Ganis. I AM ETERNAL!', 14, 0, 100, 0, 0, 14427, 0, 30276, 3, 'malganis SAY_15HEALTH');
INSERT INTO `creature_text` VALUES (26533, 8, 0, 'ENOUGH! I waste my time here. I must gather my strength on the homeworld.', 14, 0, 100, 0, 0, 14428, 0, 30277, 3, 'malganis SAY_ESCAPE_SPEECH_1');
INSERT INTO `creature_text` VALUES (26533, 9, 0, 'You\'ll never defeat the Lich King without my forces. I\'ll have my revenge... on him AND you!', 14, 0, 100, 0, 0, 14429, 0, 30278, 3, 'malganis SAY_ESCAPE_SPEECH_2');
INSERT INTO `creature_text` VALUES (26533, 10, 0, 'Your journey has just begun, young prince. Gather your forces and meet me in the arctic land of Northrend. It is there that we shall settle the score between us. It is there that your true destiny will unfold.', 14, 0, 100, 0, 0, 14412, 0, 28528, 3, 'malganis SAY_OUTRO');
INSERT INTO `creature_text` VALUES (26537, 0, 0, 'The zeppelin to Warsong Hold has just arrived! All aboard for Borean Tundra!', 14, 0, 100, 0, 0, 0, 0, 29549, 0, 'Greeb Ramrocket');
INSERT INTO `creature_text` VALUES (26564, 0, 0, 'Faster! Faster! These armaments must be delivered to the Kor\'kron Vanguard TODAY!', 12, 1, 100, 0, 0, 0, 0, 25775, 0, 'Borus Ironbender');
INSERT INTO `creature_text` VALUES (26564, 0, 1, 'I will happily replace you all with goblins! Do not fail me!', 12, 1, 100, 0, 0, 0, 0, 25776, 0, 'Borus Ironbender');
INSERT INTO `creature_text` VALUES (26564, 0, 2, 'Overlord Agmar demands more of us! We must not disappoint!', 12, 1, 100, 0, 0, 0, 0, 25777, 0, 'Borus Ironbender');
INSERT INTO `creature_text` VALUES (26564, 0, 3, 'Don\'t make me jump off of this crate! I will beat your head in, orc.', 12, 1, 100, 0, 0, 0, 0, 25778, 0, 'Borus Ironbender');
INSERT INTO `creature_text` VALUES (26566, 0, 0, 'Guards!', 14, 6, 100, 0, 0, 0, 0, 4583, 0, 'Narzun Skybreaker');
INSERT INTO `creature_text` VALUES (26568, 0, 0, 'Don\'t be shy, elfy. Don\'t you feel dis burnin\' love formin\' between us?', 12, 1, 100, 0, 0, 0, 0, 25790, 0, 'Zebu\'tan');
