INSERT INTO `creature_text` VALUES (26568, 4, 0, 'Dis troll tink you got a crush on he! Come on over!', 12, 1, 100, 0, 0, 0, 0, 25789, 0, 'Zebu\'tan');
INSERT INTO `creature_text` VALUES (26569, 0, 0, 'Get lost, creep!', 12, 1, 100, 0, 0, 0, 0, 25787, 0, 'Alys Vol\'tyr');
INSERT INTO `creature_text` VALUES (26569, 1, 0, 'Ewwww!', 12, 1, 100, 0, 0, 0, 0, 25785, 0, 'Alys Vol\'tyr');
INSERT INTO `creature_text` VALUES (26569, 2, 0, 'Not if you were the last troll on Azeroth!', 12, 1, 100, 0, 0, 0, 0, 25784, 0, 'Alys Vol\'tyr');
INSERT INTO `creature_text` VALUES (26569, 2, 1, 'I\'ve got a man! Now take a hike!', 12, 1, 100, 0, 0, 0, 0, 25788, 0, 'Alys Vol\'tyr');
INSERT INTO `creature_text` VALUES (26569, 3, 0, 'Never!', 12, 1, 100, 0, 0, 0, 0, 25786, 0, 'Alys Vol\'tyr');
