INSERT INTO `creature_text` VALUES (26588, 1, 0, 'He\'s going for Mr. Floppy!', 12, 0, 100, 0, 0, 0, 0, 25804, 0, 'emily SAY_WORGHAGGRO2');
INSERT INTO `creature_text` VALUES (26588, 2, 0, 'Oh, no! Look, it\'s another wolf, and it\'s a biiiiiiig one!', 12, 0, 100, 0, 0, 0, 0, 25806, 0, 'emily SAY_WORGRAGGRO3');
INSERT INTO `creature_text` VALUES (26588, 3, 0, 'He\'s gonna eat Mr. Floppy! You gotta help Mr. Floppy! You just gotta!', 12, 0, 100, 0, 0, 0, 0, 26892, 0, 'emily SAY_WORGRAGGRO4');
INSERT INTO `creature_text` VALUES (26588, 4, 0, 'There\'s a big meanie attacking Mr. Floppy! Help!', 12, 0, 100, 0, 0, 0, 0, 26893, 0, 'emily SAY_RANDOMAGGRO');
INSERT INTO `creature_text` VALUES (26588, 5, 0, 'Let\'s get out of here before more wolves find us!', 12, 0, 100, 0, 0, 0, 0, 25805, 0, 'emily SAY_VICTORY1');
INSERT INTO `creature_text` VALUES (26588, 6, 0, 'Don\'t go toward the light, Mr. Floppy!', 12, 0, 100, 0, 0, 0, 0, 25809, 0, 'emily SAY_VICTORY2');
INSERT INTO `creature_text` VALUES (26588, 7, 0, 'Mr. Floppy, you\'re ok! Thank you so much for saving Mr. Floppy!', 12, 0, 100, 0, 0, 0, 0, 25810, 0, 'emily SAY_VICTORY3');
INSERT INTO `creature_text` VALUES (26588, 8, 0, 'I think I see the camp! We\'re almost home, Mr. Floppy! Let\'s go!', 12, 0, 100, 0, 0, 0, 0, 26894, 0, 'emily SAY_VICTORY4');
INSERT INTO `creature_text` VALUES (26588, 9, 0, 'Mr. Floppy revives', 15, 0, 100, 0, 0, 0, 0, 0, 0, 'emily TEXT_EMOTE_WP1');
INSERT INTO `creature_text` VALUES (26588, 10, 0, 'The Ravenous Worg chomps down on Mr. Floppy', 15, 0, 100, 0, 0, 0, 0, 0, 0, 'emily TEXT_EMOTE_AGGRO');
INSERT INTO `creature_text` VALUES (26588, 11, 0, 'Are you ready, Mr. Floppy? Stay close to me and watch out for those wolves!', 12, 0, 100, 0, 0, 0, 0, 25802, 0, 'emily SAY_QUEST_ACCEPT');
INSERT INTO `creature_text` VALUES (26588, 12, 0, 'Thank you for helping me to get back to the camp. Go tell Walter that I\'m safe now!', 12, 0, 100, 0, 0, 0, 0, 26891, 0, 'emily SAY_QUEST_COMPLETE');
INSERT INTO `creature_text` VALUES (26593, 0, 0, 'Perfect.  As long as you don\'t go hacking and slashing your way through, they\'ll never know the difference.', 12, 0, 100, 0, 0, 0, 0, 26606, 0, 'Serinar');
INSERT INTO `creature_text` VALUES (26602, 0, 0, 'Guards!', 14, 6, 100, 0, 0, 0, 0, 4583, 0, 'Kara Thricestar');
INSERT INTO `creature_text` VALUES (26604, 0, 0, 'Shcourged trooolls roashting....', 12, 0, 100, 396, 0, 0, 0, 27290, 0, 'Mack Fearsen');
INSERT INTO `creature_text` VALUES (26604, 1, 0, '...on an open fire.....', 12, 0, 100, 396, 0, 0, 0, 27291, 0, 'Mack Fearsen');
INSERT INTO `creature_text` VALUES (26604, 2, 0, 'That\'s it! Far as I got... Whatd\'ya think?', 12, 0, 100, 396, 0, 0, 0, 27292, 0, 'Mack Fearsen');
INSERT INTO `creature_text` VALUES (26605, 0, 0, 'You have made a fatal mistake, mortal... There isss no essscape...', 12, 0, 100, 0, 0, 0, 0, 25860, 0, 'Anub\'ar Underlord');
INSERT INTO `creature_text` VALUES (26608, 0, 0, 'Give in, Taunka! Your will cannot hold out against the Scourge!', 14, 0, 100, 15, 0, 0, 0, 25952, 0, 'Under-King Anub\'et\'kan');
INSERT INTO `creature_text` VALUES (26608, 0, 1, 'Leave no survivors!', 14, 0, 100, 15, 0, 0, 0, 25951, 0, 'Under-King Anub\'et\'kan');
INSERT INTO `creature_text` VALUES (26608, 0, 2, 'Tear this pitiful village apart!', 14, 0, 100, 15, 0, 0, 0, 25950, 0, 'Under-King Anub\'et\'kan');
INSERT INTO `creature_text` VALUES (26608, 1, 0, 'Your freedom will be short lived, Icemist!', 14, 0, 100, 15, 0, 0, 0, 25959, 0, 'Under-King Anub\'et\'kan');
INSERT INTO `creature_text` VALUES (26608, 2, 0, 'Insolent mortal! You will be consumed!', 14, 0, 100, 15, 0, 0, 0, 25964, 0, 'Under-King Anub\'et\'kan');
INSERT INTO `creature_text` VALUES (26608, 3, 0, 'ARRGGH!! It cannot be! How...', 14, 0, 100, 15, 0, 0, 0, 25965, 0, 'Under-King Anub\'et\'kan');
INSERT INTO `creature_text` VALUES (26608, 4, 0, 'Anub\'et\'kan is defeated! Take a fragment of his husk as proof of your victory!', 41, 0, 100, 0, 0, 0, 0, 25999, 0, 'Under-King Anub\'et\'kan');
INSERT INTO `creature_text` VALUES (26626, 0, 0, 'Don\'t be so quick to escape! I have a parting gift....', 14, 0, 100, 0, 0, 0, 0, 26474, 0, 'Scourge Reanimator');
INSERT INTO `creature_text` VALUES (26626, 1, 0, 'Rise my warriors and fight for your new liege!', 14, 0, 100, 0, 0, 0, 0, 26464, 0, 'Scourge Reanimator');
INSERT INTO `creature_text` VALUES (26626, 2, 0, 'Please enjoy their company, the Lich King sends his regards!', 14, 0, 100, 0, 0, 0, 0, 26473, 0, 'Scourge Reanimator');
INSERT INTO `creature_text` VALUES (26630, 0, 0, 'Mo\' guts! Mo\' blood! Mo\' food!', 14, 0, 100, 0, 0, 13181, 0, 30041, 0, 'trollgore SAY_AGGRO');
INSERT INTO `creature_text` VALUES (26630, 1, 0, 'Me gonna carve you and eat you!', 14, 0, 100, 0, 0, 13185, 0, 30042, 0, 'trollgore SAY_KILL');
INSERT INTO `creature_text` VALUES (26630, 2, 0, 'So hungry! Must feed!', 14, 0, 100, 0, 0, 13182, 0, 30043, 0, 'trollgore SAY_CONSUME');
INSERT INTO `creature_text` VALUES (26630, 3, 0, 'Corpse go boom!', 14, 0, 100, 0, 0, 13184, 0, 30044, 0, 'trollgore SAY_EXPLODE');
INSERT INTO `creature_text` VALUES (26630, 4, 0, 'Aaaargh...', 14, 0, 100, 0, 0, 13183, 0, 0, 0, 'trollgore SAY_DEATH');
INSERT INTO `creature_text` VALUES (26631, 0, 0, 'The chill you feel is the herald of your doom!', 14, 0, 100, 0, 0, 13173, 0, 26932, 0, 'novos SAY_AGGRO');
INSERT INTO `creature_text` VALUES (26631, 1, 0, 'Such is the fate of all who oppose the Lich King!', 14, 0, 100, 0, 0, 13175, 0, 28035, 0, 'novos SAY_KILL');
INSERT INTO `creature_text` VALUES (26631, 2, 0, 'Your efforts... are in vain.', 14, 0, 100, 0, 0, 13174, 0, 28034, 0, 'novos SAY_DEATH');
INSERT INTO `creature_text` VALUES (26631, 3, 0, 'Bolster my defenses! Hurry, curse you!', 14, 0, 100, 0, 0, 13176, 0, 26933, 0, 'novos SAY_NECRO_ADD');
INSERT INTO `creature_text` VALUES (26631, 4, 0, 'Surely you can see the futility of it all!', 14, 0, 100, 0, 0, 13177, 0, 27777, 0, 'novos SAY_REUBBLE_1');
INSERT INTO `creature_text` VALUES (26631, 4, 1, 'Just give up and die already!', 14, 0, 100, 0, 0, 13178, 0, 30036, 0, 'novos SAY_REUBBLE_2');
INSERT INTO `creature_text` VALUES (26631, 5, 0, '%s calls for assistance!', 41, 0, 100, 0, 0, 0, 0, 29588, 0, 'Novos the Summoner - EMOTE_SUMMONING_ADDS');
INSERT INTO `creature_text` VALUES (26632, 0, 0, 'Tharon\'ja sees all! The work of mortals shall not end the eternal dynasty!', 14, 0, 100, 0, 0, 13862, 0, 29606, 0, 'tharon ja SAY_AGGRO');
INSERT INTO `creature_text` VALUES (26632, 1, 0, 'As Tharon\'ja predicted!', 14, 0, 100, 0, 0, 13863, 0, 29611, 0, 'tharon ja SAY_KILL_1');
INSERT INTO `creature_text` VALUES (26632, 1, 1, 'As it was written!', 14, 0, 100, 0, 0, 13864, 0, 29612, 0, 'tharon ja SAY_KILL_2');
INSERT INTO `creature_text` VALUES (26632, 2, 0, 'Your flesh serves Tharon\'ja now!', 14, 0, 100, 0, 0, 13865, 0, 29613, 0, 'tharon ja SAY_FLESH_1');
INSERT INTO `creature_text` VALUES (26632, 2, 1, 'Tharon\'ja has a use for your mortal shell!', 14, 0, 100, 0, 0, 13866, 0, 29614, 0, 'tharon ja SAY_FLESH_2');
INSERT INTO `creature_text` VALUES (26632, 3, 0, 'No! A taste... all too brief!', 14, 0, 100, 0, 0, 13867, 0, 29615, 0, 'tharon ja SAY_SKELETON_1');
INSERT INTO `creature_text` VALUES (26632, 3, 1, 'Agghh!! Tharon\'ja will have more!', 14, 0, 100, 0, 0, 13868, 0, 29616, 0, 'tharon ja SAY_SKELETON_2');
INSERT INTO `creature_text` VALUES (26632, 4, 0, 'Im... impossible! Tharon\'ja is eternal! Tharon\'ja... is....', 14, 0, 100, 0, 0, 13869, 0, 29617, 0, 'tharon ja SAY_DEATH');
INSERT INTO `creature_text` VALUES (26635, 0, 0, 'Help mon! There\'s too many of dem!', 14, 0, 100, 0, 0, 0, 0, 26538, 0, 'Risen Drakkari Warrior');
INSERT INTO `creature_text` VALUES (26635, 1, 0, 'Backup! We need backup!', 14, 0, 100, 0, 0, 0, 0, 26537, 0, 'Risen Drakkari Warrior');
INSERT INTO `creature_text` VALUES (26648, 0, 0, 'Little $n, why do you call me forth? Are you working with the trolls of this land? Have you come to kill me and take my power as your own?', 14, 0, 100, 33, 0, 11557, 0, 25854, 0, 'Oacha\'noa');
INSERT INTO `creature_text` VALUES (26648, 1, 0, 'I sense uncertainty in you, and I do not trust it whether you are with them, or not. If you wish my augury for the Kalu\'ak, you will have to prove yourself first.', 14, 0, 100, 33, 0, 11557, 0, 25863, 0, 'Oacha\'noa');
INSERT INTO `creature_text` VALUES (26648, 2, 0, 'I will lay a mild compulsion upon you. Jump into the depths before me so that you put yourself into my element and thereby display your submission.', 14, 0, 100, 33, 0, 11557, 0, 25864, 0, 'Oacha\'noa');
INSERT INTO `creature_text` VALUES (26648, 3, 0, 'Though you are compelled, the choice, and the last step before you leap, are yours. You have twenty more seconds to decide.', 14, 0, 100, 33, 0, 11557, 0, 25865, 0, 'Oacha\'noa');
INSERT INTO `creature_text` VALUES (26648, 4, 0, 'I\'m rather curious as to what will happen, should you ignore this and NOT jump into the water.', 14, 0, 100, 33, 0, 11557, 0, 0, 0, 'Oacha\'noa');
INSERT INTO `creature_text` VALUES (26648, 5, 0, 'Well done, $n. Your display of respect is duly noted. Now, I have information for you that you must convey to the Kalu\'ak.', 14, 0, 100, 33, 0, 11557, 0, 25867, 0, 'Oacha\'noa');
INSERT INTO `creature_text` VALUES (26648, 6, 0, 'Simply put, you must tell the tuskarr that they cannot run. If they do so, their spirits will be destroyed by the evil rising within Northrend.', 15, 0, 100, 0, 0, 0, 0, 25868, 0, 'Oacha\'noa');
INSERT INTO `creature_text` VALUES (26648, 7, 0, 'Tell the mystic that his people are to stand and fight alongside the Horde and Alliance against the forces of Malygos and the Lich King.', 15, 0, 100, 0, 0, 0, 0, 25869, 0, 'Oacha\'noa');
INSERT INTO `creature_text` VALUES (26648, 8, 0, 'Now swim back with the knowledge I have granted you. Do what you can for them, $r.', 15, 0, 100, 0, 0, 0, 0, 25870, 0, 'Oacha\'noa');
INSERT INTO `creature_text` VALUES (26648, 9, 0, 'Very well, $n, you have failed to act. The prophecy is not yours to learn. Do not call upon me again until you have found your backbone!', 14, 0, 100, 33, 0, 11557, 0, 25866, 0, 'Oacha\'noa');
INSERT INTO `creature_text` VALUES (26649, 0, 0, 'Pssst... Over here, by the mailbox. Come quickly and make sure you aren\'t followed.', 15, 0, 100, 0, 0, 0, 0, 25823, 0, 'Borus Ironbender');
INSERT INTO `creature_text` VALUES (26653, 0, 0, 'Betrayers! The day of reckoning comes and with it comes your end! See then if your Lich King saves you from the ancient one\'s judgement! Now you die!', 12, 0, 100, 0, 0, 0, 0, 25861, 0, 'Kilix the Unraveler');
INSERT INTO `creature_text` VALUES (26654, 0, 0, 'Release me, fiends!', 12, 0, 100, 15, 0, 0, 0, 25828, 0, 'Roanauk Icemist');
INSERT INTO `creature_text` VALUES (26654, 0, 1, 'Pray to whatever gods you believe in that I am never freed!', 12, 0, 100, 15, 0, 0, 0, 25829, 0, 'Roanauk Icemist');
INSERT INTO `creature_text` VALUES (26654, 1, 0, 'I... am... FREE!', 14, 0, 100, 22, 0, 0, 0, 25954, 0, 'Roanauk Icemist');
INSERT INTO `creature_text` VALUES (26654, 2, 0, 'What have they done to my people...', 12, 0, 100, 6, 0, 0, 0, 25955, 0, 'Roanauk Icemist');
INSERT INTO `creature_text` VALUES (26654, 3, 0, 'You will face the full fury of the ancestors for what you have done, monster!', 14, 0, 100, 22, 0, 0, 0, 25956, 0, 'Roanauk Icemist');
INSERT INTO `creature_text` VALUES (26654, 4, 0, 'Rebirth comes, my brothers... Rise... Rise and fight once more.', 12, 0, 100, 0, 0, 0, 0, 25957, 0, 'Roanauk Icemist');
INSERT INTO `creature_text` VALUES (26654, 5, 0, 'FOR ICEMIST! FOR THE TAUNKA!', 14, 0, 100, 0, 0, 0, 0, 25958, 0, 'Roanauk Icemist');
INSERT INTO `creature_text` VALUES (26654, 6, 0, 'Come down and face me, insect!', 14, 0, 100, 15, 0, 0, 0, 25962, 0, 'Roanauk Icemist');
INSERT INTO `creature_text` VALUES (26654, 7, 0, 'Then I will force you down! Wind grant me flight!', 14, 0, 100, 15, 0, 0, 0, 25963, 0, 'Roanauk Icemist');
INSERT INTO `creature_text` VALUES (26660, 0, 0, 'About time!  Get that lumber and get moving.  The horde won\'t be up there fighting forever, there\'s money to be made!', 12, 0, 100, 25, 1000, 0, 0, 26079, 0, 'Xink');
INSERT INTO `creature_text` VALUES (26666, 0, 0, 'Stay your blade, Chieftain. He may yet have value to us.', 12, 0, 100, 1, 0, 0, 0, 26875, 0, 'Scout Vor\'takh');
INSERT INTO `creature_text` VALUES (26666, 1, 0, 'Remember the dwarven journal that was discovered? Well, this dwarf could be exactly what we need to track down Brann Bronzebeard and capture him.', 12, 0, 100, 1, 0, 0, 0, 26878, 0, 'Scout Vor\'takh');
INSERT INTO `creature_text` VALUES (26666, 2, 0, 'You will if you wish to keep your head attached to your shoulders.', 12, 0, 100, 1, 0, 0, 0, 26880, 0, 'Scout Vor\'takh');
INSERT INTO `creature_text` VALUES (26666, 3, 0, 'Does that satisfy you, Tormak?', 12, 0, 100, 6, 0, 0, 0, 26881, 0, 'Scout Vor\'takh');
INSERT INTO `creature_text` VALUES (26668, 0, 0, 'The sensation is... beyond my imagining. I am yours to command, my king.', 14, 0, 0, 0, 0, 13857, 0, 29686, 0, 'Svala Sorrowgrave SAY_DIALOG_WITH_ARTHAS_2');
INSERT INTO `creature_text` VALUES (26668, 1, 0, 'I will be happy to slaughter them in your name! Come, enemies of the Scourge! I will show you the might of the Lich King!', 14, 0, 0, 0, 0, 13858, 0, 29687, 0, 'Svala Sorrowgrave SAY_DIALOG_WITH_ARTHAS_3');
INSERT INTO `creature_text` VALUES (26668, 2, 0, 'I will vanquish your soul!', 14, 0, 0, 0, 0, 13842, 0, 29672, 0, 'Svala Sorrowgrave SAY_AGGRO');
INSERT INTO `creature_text` VALUES (26668, 3, 0, 'You were a fool to challenge the power of the Lich King!', 14, 0, 0, 0, 0, 13845, 0, 29675, 0, 'Svala Sorrowgrave SAY_SLAY_1');
INSERT INTO `creature_text` VALUES (26668, 3, 1, 'Your will is done, my king!', 14, 0, 0, 0, 0, 13847, 0, 29676, 0, 'Svala Sorrowgrave SAY_SLAY_2');
INSERT INTO `creature_text` VALUES (26668, 3, 2, 'Another soul for my master!', 14, 0, 0, 0, 0, 13848, 0, 29677, 0, 'Svala Sorrowgrave SAY_SLAY_3');
INSERT INTO `creature_text` VALUES (26668, 4, 0, 'Nooo! I did not come this far... to...', 14, 0, 0, 0, 0, 13855, 0, 29683, 0, 'Svala Sorrowgrave SAY_DEATH');
INSERT INTO `creature_text` VALUES (26668, 5, 0, 'Your death approaches....', 14, 0, 0, 0, 0, 13850, 0, 29678, 0, 'Svala Sorrowgrave SAY_SACRIFICE_1');
INSERT INTO `creature_text` VALUES (26668, 5, 1, 'Go now to my master!', 14, 0, 0, 0, 0, 13851, 0, 29679, 0, 'Svala Sorrowgrave SAY_SACRIFICE_2');
INSERT INTO `creature_text` VALUES (26668, 5, 2, 'Your end is inevitable.', 14, 0, 0, 0, 0, 13852, 0, 29680, 0, 'Svala Sorrowgrave SAY_SACRIFICE_3');
INSERT INTO `creature_text` VALUES (26668, 5, 3, 'Yor-guul mak!', 14, 0, 0, 0, 0, 13853, 0, 29681, 0, 'Svala Sorrowgrave SAY_SACRIFICE_4');
INSERT INTO `creature_text` VALUES (26668, 5, 4, 'Any last words?', 14, 0, 0, 0, 0, 13854, 0, 29682, 0, 'Svala Sorrowgrave SAY_SACRIFICE_5');
INSERT INTO `creature_text` VALUES (26687, 0, 0, 'What this place? I will destroy you!', 12, 0, 100, 0, 0, 0, 0, 31447, 0, 'Gortok Palehoof SAY_AGGRO');
INSERT INTO `creature_text` VALUES (26687, 1, 0, 'You die! That what master wants.', 12, 0, 100, 0, 0, 0, 0, 31452, 0, 'Gortok Palehoof SAY_SLAY_1');
INSERT INTO `creature_text` VALUES (26687, 1, 1, 'An easy task!', 12, 0, 100, 0, 0, 0, 0, 31453, 0, 'Gortok Palehoof SAY_SLAY_2');
INSERT INTO `creature_text` VALUES (26693, 0, 0, 'What mongrels dare intrude here? Look alive, my brothers!  A feast for the one that brings me their heads!', 14, 0, 100, 0, 0, 13497, 0, 31454, 0, 'skadi SAY_AGGRO');
INSERT INTO `creature_text` VALUES (26693, 1, 0, 'Not so brash now, are you?', 14, 0, 100, 0, 0, 13504, 0, 31461, 0, 'skadi SAY_KILL_1');
INSERT INTO `creature_text` VALUES (26693, 1, 1, 'I\'ll mount your skull from the highest tower!\n', 14, 0, 100, 0, 0, 13505, 0, 31462, 0, 'skadi SAY_KILL_2');
INSERT INTO `creature_text` VALUES (26693, 2, 0, 'Skadi the Ruthless is within range of the harpoon launchers!', 41, 0, 100, 0, 0, 0, 0, 27809, 0, 'skadi EMOTE_RANGE');
INSERT INTO `creature_text` VALUES (26693, 3, 0, 'ARGH! You call that... an attack? I\'ll...  show... aghhhh...', 14, 0, 100, 0, 0, 13506, 0, 31463, 0, 'skadi SAY_DEATH');
INSERT INTO `creature_text` VALUES (26693, 4, 0, 'Skadi the Ruthless is within range of the harpoon launchers!', 41, 0, 100, 0, 0, 0, 0, 27809, 0, 'skadi EMOTE_RANGE');
INSERT INTO `creature_text` VALUES (26693, 5, 0, 'You motherless knaves! Your corpses will make fine morsels for my new drake!', 14, 0, 100, 0, 0, 13507, 0, 31459, 0, 'skadi SAY_DRAKE_DEATH');
INSERT INTO `creature_text` VALUES (26693, 6, 0, 'Sear them to the bone!', 14, 0, 100, 0, 0, 13498, 0, 0, 0, 'skadi SAY_DRAKE_BREATH_1');
INSERT INTO `creature_text` VALUES (26693, 6, 1, 'Go now! Leave nothing but ash in your wake!', 14, 0, 100, 0, 0, 13499, 0, 0, 0, 'skadi SAY_DRAKE_BREATH_2');
INSERT INTO `creature_text` VALUES (26693, 6, 2, 'Cleanse our sacred halls with flame!', 14, 0, 100, 0, 0, 13500, 0, 0, 0, 'skadi SAY_DRAKE_BREATH_3');
INSERT INTO `creature_text` VALUES (26723, 0, 0, 'Preserve? Why? There\'s no truth in it. No no no... only in the taking! I see that now!', 14, 0, 100, 0, 0, 13450, 0, 26176, 0, 'keristrasza SAY_AGGRO');
INSERT INTO `creature_text` VALUES (26723, 1, 0, 'Now we\'ve come to the truth!', 14, 0, 100, 0, 0, 13453, 0, 29620, 0, 'keristrasza SAY_SLAY');
INSERT INTO `creature_text` VALUES (26723, 2, 0, 'Finish it! Finish it! Kill me, or I swear by the Dragonqueen you\'ll never see daylight again!', 14, 0, 100, 0, 0, 13452, 0, 29619, 0, 'keristrasza SAY_ENRAGE');
INSERT INTO `creature_text` VALUES (26723, 3, 0, 'Dragonqueen... Life-Binder... preserve... me.', 14, 0, 100, 0, 0, 13454, 0, 29621, 0, 'keristrasza SAY_DEATH');
INSERT INTO `creature_text` VALUES (26723, 4, 0, 'Stay. Enjoy your final moments.', 14, 0, 100, 0, 0, 13451, 0, 29618, 0, 'keristrasza SAY_CRYSTAL_NOVA');
INSERT INTO `creature_text` VALUES (26723, 5, 0, '%s goes into a frenzy!', 16, 0, 100, 0, 0, 0, 0, 38630, 0, 'Keristrasza');
INSERT INTO `creature_text` VALUES (26731, 0, 0, 'You know what they say about curiosity....', 14, 0, 100, 0, 0, 13319, 0, 29593, 0, 'grand magus telestra SAY_AGGRO');
INSERT INTO `creature_text` VALUES (26731, 1, 0, 'Death becomes you.', 14, 0, 100, 0, 0, 13324, 0, 29597, 0, 'grand magus telestra SAY_KILL');
INSERT INTO `creature_text` VALUES (26731, 2, 0, 'Damn the... luck.', 14, 0, 100, 0, 0, 13320, 0, 29598, 0, 'grand magus telestra SAY_DEATH');
INSERT INTO `creature_text` VALUES (26731, 3, 0, 'Now to finish the job!', 14, 0, 100, 0, 0, 13323, 0, 29596, 0, 'grand magus telestra SAY_MERGE');
INSERT INTO `creature_text` VALUES (26731, 4, 0, 'There\'s plenty of me to go around.', 14, 0, 100, 0, 0, 13321, 0, 29595, 0, 'grand magus telestra SAY_SPLIT_1');
INSERT INTO `creature_text` VALUES (26731, 4, 1, 'I\'ll give you more than you can handle!', 14, 0, 100, 0, 0, 13322, 0, 29594, 0, 'grand magus telestra SAY_SPLIT_2');
INSERT INTO `creature_text` VALUES (26762, 0, 0, 'Keep them away from the focus!', 12, 0, 100, 0, 0, 0, 0, 25934, 0, 'Captain Emmy Malin');
INSERT INTO `creature_text` VALUES (26763, 0, 0, 'Chaos beckons.', 14, 0, 100, 0, 0, 13186, 0, 29599, 0, 'anomalus SAY_AGGRO');
INSERT INTO `creature_text` VALUES (26763, 1, 0, 'Of course.', 14, 0, 100, 0, 0, 13187, 0, 29604, 0, 'anomalus SAY_DEATH');
INSERT INTO `creature_text` VALUES (26763, 2, 0, 'Reality... unwoven.', 14, 0, 100, 0, 0, 13188, 0, 29601, 0, 'anomalus SAY_RIFT');
INSERT INTO `creature_text` VALUES (26763, 3, 0, 'Indestructible.', 14, 0, 100, 0, 0, 13189, 0, 29602, 0, 'anomalus SAY_SHIELD');
INSERT INTO `creature_text` VALUES (26763, 4, 0, '%s opens a Chaotic Rift!', 41, 0, 100, 0, 0, 0, 0, 27362, 0, 'Anomalus');
INSERT INTO `creature_text` VALUES (26763, 5, 0, '%s shields himself and diverts his power to the rifts!', 41, 0, 100, 0, 0, 0, 0, 27363, 0, 'Anomalus');
INSERT INTO `creature_text` VALUES (26786, 0, 0, 'For the glory of Loken!', 12, 0, 100, 0, 0, 0, 0, 25984, 0, 'Iron Rune Avenger');
INSERT INTO `creature_text` VALUES (26786, 0, 1, 'No one will keep us from our destiny!', 12, 0, 100, 0, 0, 0, 0, 25983, 0, 'Iron Rune Avenger');
INSERT INTO `creature_text` VALUES (26786, 0, 2, 'You are a fool to fight Kurun\'s hopeless battle!', 12, 0, 100, 0, 0, 0, 0, 25986, 0, 'Iron Rune Avenger');
INSERT INTO `creature_text` VALUES (26786, 0, 3, 'You will pay for your interference!', 12, 0, 100, 0, 0, 0, 0, 25985, 0, 'Iron Rune Avenger');
INSERT INTO `creature_text` VALUES (26786, 0, 4, 'Your life is forfeit, ally of stone!', 12, 0, 100, 0, 0, 0, 0, 25987, 0, 'Iron Rune Avenger');
INSERT INTO `creature_text` VALUES (26794, 1, 0, 'Noo!', 14, 0, 0, 0, 0, 13328, 0, 29605, 0, 'ormorok SAY_AGGRO');
INSERT INTO `creature_text` VALUES (26794, 2, 0, 'Aaggh!', 14, 0, 0, 0, 0, 13330, 0, 29608, 0, 'ormorok SAY_DEATH');
INSERT INTO `creature_text` VALUES (26794, 3, 0, 'Back!', 14, 0, 0, 0, 0, 13331, 0, 29609, 0, 'ormorok SAY_REFLECT');
INSERT INTO `creature_text` VALUES (26794, 4, 0, 'Bleed!', 14, 0, 0, 0, 0, 13332, 0, 29610, 0, 'ormorok SAY_CRYSTAL_SPIKES');
INSERT INTO `creature_text` VALUES (26794, 5, 0, 'Kill!', 14, 0, 0, 0, 0, 13329, 0, 29607, 0, 'ormorok SAY_KILL');
INSERT INTO `creature_text` VALUES (26794, 6, 0, '%s goes into a frenzy!', 41, 0, 100, 0, 0, 0, 0, 38630, 0, 'Ormorok the Tree-Shaper');
INSERT INTO `creature_text` VALUES (26796, 0, 0, 'What? Where in the-- don\'t just stand around lads; kill somebody!', 14, 0, 100, 0, 0, 13193, 0, 29627, 0, 'Commander Stoutbeard');
INSERT INTO `creature_text` VALUES (26796, 1, 0, 'Now we\'re gettin\' someplace!', 14, 0, 100, 0, 0, 13195, 0, 29628, 0, 'Commander Stoutbeard');
INSERT INTO `creature_text` VALUES (26796, 2, 0, 'Is that all you\'ve...', 14, 0, 100, 0, 0, 13194, 0, 29629, 0, 'Commander Stoutbeard');
INSERT INTO `creature_text` VALUES (26798, 0, 0, 'What is this? Mok-thorin ka! Kill them!', 14, 0, 100, 0, 0, 13458, 0, 29622, 0, 'Commander Kolurg');
INSERT INTO `creature_text` VALUES (26798, 1, 0, 'Our task is not yet done!', 14, 0, 100, 0, 0, 13459, 0, 29623, 0, 'Commander Kolurg');
INSERT INTO `creature_text` VALUES (26798, 2, 0, 'Gaagh...', 14, 0, 100, 0, 0, 13460, 0, 29624, 0, 'Commander Kolurg');
INSERT INTO `creature_text` VALUES (26810, 0, 0, 'Gather, brothers and sisters.', 12, 1, 100, 1, 0, 0, 0, 26136, 0, 'Roanauk Icemist');
INSERT INTO `creature_text` VALUES (26810, 1, 0, '%s reads from the scroll as the other Taunka gather around him.', 16, 1, 100, 0, 0, 0, 0, 26130, 0, 'Roanauk Icemist');
INSERT INTO `creature_text` VALUES (26810, 2, 0, 'Lok\'tar ogar! Victory or death - it is these words that bind me to the Horde! For they are the most sacred and fundamental of truths to any warrior of the Horde.', 12, 1, 100, 1, 0, 0, 0, 26137, 0, 'Roanauk Icemist');
INSERT INTO `creature_text` VALUES (26810, 3, 0, 'I give my flesh and blood freely to the Warchief. I am the instrument of my Warchief\'s desire. I am a weapon of my Warchief\'s command!', 12, 1, 100, 1, 0, 0, 0, 26138, 0, 'Roanauk Icemist');
INSERT INTO `creature_text` VALUES (26810, 4, 0, 'From this moment until the end of days I live and die - For the Horde!', 12, 1, 100, 1, 0, 0, 0, 26139, 0, 'Roanauk Icemist');
INSERT INTO `creature_text` VALUES (26810, 5, 0, 'For my Taunka brothers and sisters! FOR THE HORDE!', 14, 1, 100, 388, 0, 0, 0, 26140, 0, 'Roanauk Icemist');
INSERT INTO `creature_text` VALUES (26811, 0, 0, 'I could sleep forever, mon....', 12, 0, 100, 0, 0, 0, 0, 26149, 0, 'Ancient Drakkari Warmonger');
