INSERT INTO `creature_text` VALUES (27003, 2, 0, 'Hah!  So be it.  Blow the horn of a magnataur leader at the ring of torches south of the Blue Dragonshrine.  Make peace with your gods... Grom\'thar will come.  ', 14, 0, 100, 0, 0, 0, 0, 26187, 0, 'Dregmar Runebrand yell');
