INSERT INTO `creature_text` VALUES (27202, 1, 1, 'Your protection is strong, child.  Continue your work.', 12, 0, 100, 0, 0, 0, 0, 26390, 0, 'Onslaught Raven Priest');
INSERT INTO `creature_text` VALUES (27202, 1, 2, 'The Grand Admiral himself has come to see to our dedication.  If I see you slacking on your post again, I will gut you myself.', 12, 0, 100, 0, 0, 0, 0, 26655, 0, 'Onslaught Raven Priest');
INSERT INTO `creature_text` VALUES (27202, 1, 3, 'You require the Bishop\'s blessing.  Go to him soon.', 12, 0, 100, 0, 0, 0, 0, 26391, 0, 'Onslaught Raven Priest');
INSERT INTO `creature_text` VALUES (27202, 2, 0, 'HELP! HELP! THIS $g MAN : WOMAN; HAS STOLEN MY IMAGE!', 12, 0, 100, 0, 0, 0, 0, 26543, 0, 'Onslaught Raven Priest');
INSERT INTO `creature_text` VALUES (27203, 0, 0, 'Be purified by steel!', 12, 0, 100, 0, 0, 0, 0, 26404, 0, 'Onslaught Footman');
INSERT INTO `creature_text` VALUES (27203, 0, 1, 'By the Light!', 12, 0, 100, 0, 0, 0, 0, 26403, 0, 'Onslaught Footman');
INSERT INTO `creature_text` VALUES (27203, 0, 2, 'In the name of the Light!', 12, 0, 100, 0, 0, 0, 0, 26402, 0, 'Onslaught Footman');
INSERT INTO `creature_text` VALUES (27203, 0, 3, 'You will be cleansed!', 12, 0, 100, 0, 0, 0, 0, 26401, 0, 'Onslaught Footman');
INSERT INTO `creature_text` VALUES (27209, 0, 0, 'Come to play?', 12, 0, 100, 0, 0, 0, 0, 26472, 0, 'Torturer LeCraft');
INSERT INTO `creature_text` VALUES (27209, 1, 0, 'Ow! I\'ll tell you NOTHING, filthy $r!', 15, 0, 100, 0, 0, 0, 0, 26480, 0, 'Torturer LeCraft');
INSERT INTO `creature_text` VALUES (27209, 2, 0, 'Wait... WAIT! What is it that you want to know?! I know you\'re the $c named $n.', 15, 0, 100, 0, 0, 0, 0, 26481, 0, 'Torturer LeCraft');
INSERT INTO `creature_text` VALUES (27209, 3, 0, 'OW...NO! We know that you\'ve been stealing our armor and weapons and horses!', 15, 0, 100, 0, 0, 0, 0, 26482, 0, 'Torturer LeCraft');
INSERT INTO `creature_text` VALUES (27209, 4, 0, 'We know... that you don\'t... know why we\'re immune... to your so-called blight. Grand Admiral Westwind somehow gave the high abbot that prayer. I beg you... no more... please?!', 15, 0, 100, 0, 0, 0, 0, 26483, 0, 'Torturer LeCraft');
INSERT INTO `creature_text` VALUES (27209, 5, 0, 'AHHHHHHHHH! Please... we know... that you... have a spy... disguised as... one of us! There... that\'s all that I know. Please... mercy... STOP!', 15, 0, 100, 0, 0, 0, 0, 26484, 0, 'Torturer LeCraft');
INSERT INTO `creature_text` VALUES (27209, 6, 0, 'Please... stop. I\'ll do... anything....', 15, 0, 100, 0, 0, 0, 0, 26498, 0, 'Torturer LeCraft');
INSERT INTO `creature_text` VALUES (27209, 7, 0, 'I beg you... no more.', 15, 0, 100, 0, 0, 0, 0, 26504, 0, 'Torturer LeCraft');
INSERT INTO `creature_text` VALUES (27209, 8, 0, 'OWWWWWWWW!', 15, 0, 100, 0, 0, 0, 0, 26507, 0, 'Torturer LeCraft');
INSERT INTO `creature_text` VALUES (27209, 9, 0, 'You won\'t get... anything else... out of me!', 15, 0, 100, 0, 0, 0, 0, 26506, 0, 'Torturer LeCraft');
INSERT INTO `creature_text` VALUES (27209, 10, 0, 'They all... begged... for their... lives!', 15, 0, 100, 0, 0, 0, 0, 26501, 0, 'Torturer LeCraft');
INSERT INTO `creature_text` VALUES (27209, 11, 0, 'I TOLD YOU... I don\'t know... anything... else!', 15, 0, 100, 0, 0, 0, 0, 26496, 0, 'Torturer LeCraft');
INSERT INTO `creature_text` VALUES (27209, 12, 0, 'Just... end it.', 15, 0, 100, 0, 0, 0, 0, 26499, 0, 'Torturer LeCraft');
INSERT INTO `creature_text` VALUES (27210, 0, 0, 'Now I\'ll show you the REAL power of the Onslaught!', 12, 0, 100, 0, 0, 14192, 0, 27593, 0, 'High General Abbendis');
INSERT INTO `creature_text` VALUES (27212, 0, 0, 'You\'re late, overseer.', 12, 0, 100, 51, 0, 0, 0, 26232, 0, 'Image of Loken');
INSERT INTO `creature_text` VALUES (27212, 1, 0, 'Destiny will not wait. Your craftsmen must increase their production of the war golems before the stone giant leader and his army reach Ulduar.', 12, 0, 100, 51, 0, 0, 0, 26233, 0, 'Image of Loken');
INSERT INTO `creature_text` VALUES (27212, 2, 0, 'Tell your rune-smiths to continue converting what stone giants you can. Those that will not submit must be destroyed.', 12, 0, 100, 51, 0, 0, 0, 26234, 0, 'Image of Loken');
INSERT INTO `creature_text` VALUES (27212, 3, 0, 'If the stone giants interfere with our work at Ulduar, I will hold you and your thane responsible. Mortals must not be allowed to come to the aid of the giants.', 12, 0, 100, 51, 0, 0, 0, 26235, 0, 'Image of Loken');
INSERT INTO `creature_text` VALUES (27212, 4, 0, 'Return to your duties, overseer. Be certain to impress upon your workers the urgency of their tasks.', 12, 0, 100, 51, 0, 0, 0, 26236, 0, 'Image of Loken');
INSERT INTO `creature_text` VALUES (27221, 0, 0, 'Let\'s see if this works.', 12, 0, 100, 1, 0, 0, 0, 26239, 0, 'Tormak the Scarred');
INSERT INTO `creature_text` VALUES (27221, 1, 0, 'You are among the Taunka of Camp Oneqwah, dwarf.', 12, 0, 100, 1, 0, 0, 0, 26251, 0, 'Tormak the Scarred');
INSERT INTO `creature_text` VALUES (27221, 2, 0, 'If you agree to help us against our mutual enemy, you will come to no harm here.', 12, 0, 100, 1, 0, 0, 0, 26252, 0, 'Tormak the Scarred');
INSERT INTO `creature_text` VALUES (27221, 3, 0, 'Think about it, prospector, but make up your mind swiftly. We must act soon.', 12, 0, 100, 25, 0, 0, 0, 26253, 0, 'Tormak the Scarred');
INSERT INTO `creature_text` VALUES (27221, 4, 0, 'I\'ll admit, dwarf, I was pleasantly surprised to see your plan succeed.', 12, 0, 100, 1, 0, 0, 0, 26871, 0, 'Tormak the Scarred');
INSERT INTO `creature_text` VALUES (27221, 5, 0, 'We can\'t just let you return to your companions and we can\'t take you with us...', 12, 0, 100, 1, 0, 0, 0, 26873, 0, 'Tormak the Scarred');
INSERT INTO `creature_text` VALUES (27221, 6, 0, 'He will live so long as he proves useful. I\'d be careful if I was you, dwarf.', 12, 0, 100, 273, 0, 0, 0, 26882, 0, 'Tormak the Scarred');
INSERT INTO `creature_text` VALUES (27227, 0, 0, 'Where... where am I?', 12, 0, 100, 0, 0, 0, 0, 26240, 0, 'Prospector Rokar');
INSERT INTO `creature_text` VALUES (27227, 1, 0, 'Wait a minute here! You promised to spare me if I helped you. I held up my end o\' the bargain!', 12, 0, 100, 5, 0, 0, 0, 26874, 0, 'Prospector Rokar');
INSERT INTO `creature_text` VALUES (27227, 2, 0, 'By the Light, Brann\'s alive? I\'d never help you capture him!', 12, 0, 100, 1, 0, 0, 0, 26879, 0, 'Prospector Rokar');
INSERT INTO `creature_text` VALUES (27234, 0, 0, 'You are being misled! The Onslaught is all lies! The Scourge and the Forsaken are not our enemies! Wake up!', 14, 0, 100, 5, 0, 0, 0, 26596, 0, 'Blacksmith Goodman');
INSERT INTO `creature_text` VALUES (27235, 0, 0, 'Renounce the Scarlet Onslaught! Don\'t listen to the lies of the high general and the grand admiral any longer!', 14, 0, 100, 5, 0, 0, 0, 26598, 0, 'Lead Cannoneer Zierhut');
INSERT INTO `creature_text` VALUES (27236, 0, 0, 'Abbendis is nothing but a harlot and Grand Admiral Westwind is selling her cheap like he sold us out!', 14, 0, 100, 5, 0, 0, 0, 26599, 0, 'Stable Master Mercer');
INSERT INTO `creature_text` VALUES (27237, 0, 0, 'High General Abbendis personally told me that it was a mistake to come north and that we\'re doomed! I urge you all to lay down your weapons and leave before it\'s too late!', 14, 0, 100, 5, 0, 0, 0, 26597, 0, 'Commander Jordan');
INSERT INTO `creature_text` VALUES (27238, 0, 0, 'My beautiful mill. You! You\'ll die for this!', 12, 0, 100, 0, 0, 0, 0, 26418, 0, 'Foreman Kaleiki');
INSERT INTO `creature_text` VALUES (27246, 0, 0, 'It is good that you have come my child.  The Light\'s protection has worn thin on you.', 12, 0, 100, 0, 0, 0, 0, 26271, 0, 'Bishop Street');
INSERT INTO `creature_text` VALUES (27246, 1, 0, 'It is done.  Serve the Light well.', 12, 0, 100, 0, 0, 0, 0, 26272, 0, 'Bishop Street');
INSERT INTO `creature_text` VALUES (27246, 2, 0, 'How dare you interrupt our righteous work?!', 12, 0, 100, 0, 0, 0, 0, 26405, 0, 'Bishop Street');
INSERT INTO `creature_text` VALUES (27247, 0, 0, 'Hey, who rang the bell?', 12, 0, 100, 0, 0, 0, 0, 26610, 0, 'Devout Bodyguard');
INSERT INTO `creature_text` VALUES (27248, 0, 0, 'Schlemiel!', 12, 0, 100, 5, 0, 0, 0, 26273, 0, 'Apothecary Vicky Levine');
INSERT INTO `creature_text` VALUES (27248, 1, 0, 'Apparently you flubbed this batch of blight. What do you have to say for yourself?', 12, 0, 100, 5, 0, 0, 0, 26275, 0, 'Apothecary Vicky Levine');
INSERT INTO `creature_text` VALUES (27248, 2, 0, 'Sorry? SORRY?! Here\'s sorry for you!', 12, 0, 100, 5, 0, 0, 0, 26279, 0, 'Apothecary Vicky Levine');
INSERT INTO `creature_text` VALUES (27248, 3, 0, 'Oops. I guess it IS working! That\'s odd.', 12, 0, 100, 6, 0, 0, 0, 26280, 0, 'Apothecary Vicky Levine');
INSERT INTO `creature_text` VALUES (27248, 4, 0, 'I\'m gonna need a cleanup and a new assistant over here!', 12, 0, 100, 5, 0, 0, 0, 26289, 0, 'Apothecary Vicky Levine');
INSERT INTO `creature_text` VALUES (27250, 0, 0, 'Huh?', 12, 0, 100, 6, 0, 0, 0, 26274, 0, 'Junior Apothecary Schlemiel');
INSERT INTO `creature_text` VALUES (27250, 1, 0, 'But... I followed your instructions precisely! Sorry?', 12, 0, 100, 6, 0, 0, 0, 26277, 0, 'Junior Apothecary Schlemiel');
INSERT INTO `creature_text` VALUES (27250, 2, 0, 'Eep!', 12, 0, 100, 34, 0, 1368, 0, 26281, 0, 'Junior Apothecary Schlemiel');
INSERT INTO `creature_text` VALUES (27299, 0, 0, 'Tell me if this hurts.', 12, 0, 100, 0, 0, 0, 0, 26296, 0, 'Initiate Greer');
INSERT INTO `creature_text` VALUES (27299, 0, 1, 'Let\'s try this one out...', 12, 0, 100, 0, 0, 0, 0, 26298, 0, 'Initiate Greer');
INSERT INTO `creature_text` VALUES (27299, 0, 2, 'I think I\'ve got it this time..', 12, 0, 100, 0, 0, 0, 0, 26297, 0, 'Initiate Greer');
INSERT INTO `creature_text` VALUES (27299, 1, 0, 'How\'d that feel?', 12, 0, 100, 0, 0, 0, 0, 26299, 0, 'Initiate Greer');
INSERT INTO `creature_text` VALUES (27299, 1, 1, 'Was that potent enough?', 12, 0, 100, 0, 0, 0, 0, 26302, 0, 'Initiate Greer');
INSERT INTO `creature_text` VALUES (27299, 1, 2, 'Do you think that will get some answers?', 12, 0, 100, 0, 0, 0, 0, 26300, 0, 'Initiate Greer');
INSERT INTO `creature_text` VALUES (27299, 2, 0, 'Hmm...', 12, 0, 100, 0, 0, 0, 0, 26304, 0, 'Initiate Greer');
INSERT INTO `creature_text` VALUES (27299, 2, 1, 'True, our enemies would show us no mercy.', 12, 0, 100, 0, 0, 0, 0, 26320, 0, 'Initiate Greer');
INSERT INTO `creature_text` VALUES (27299, 2, 2, 'True, but how far should we push?  At what point are we no better than our enemies?', 12, 0, 100, 0, 0, 0, 0, 26319, 0, 'Initiate Greer');
INSERT INTO `creature_text` VALUES (27300, 0, 0, 'You need more bite.  Do not show our enemies any mercy.', 12, 0, 100, 0, 0, 0, 0, 26305, 0, 'Initiate Vernon');
INSERT INTO `creature_text` VALUES (27300, 0, 1, 'The more pain we can inflict, the quicker we can get our answers.', 12, 0, 100, 0, 0, 0, 0, 26306, 0, 'Initiate Vernon');
INSERT INTO `creature_text` VALUES (27315, 0, 0, 'Are you sure you know how to fly this thing? Feels a little wobbly.', 12, 0, 100, 0, 0, 0, 0, 26359, 0, 'Helpless Wintergarde Villager');
INSERT INTO `creature_text` VALUES (27315, 0, 1, 'I don\'t mean to sound ungrateful, but could you fly a little closer to the ground? I hate heights!', 12, 0, 100, 0, 0, 0, 0, 26360, 0, 'Helpless Wintergarde Villager');
INSERT INTO `creature_text` VALUES (27315, 0, 2, 'I picked a bad day to stop drinking!', 12, 0, 100, 0, 0, 0, 0, 26345, 0, 'Helpless Wintergarde Villager');
INSERT INTO `creature_text` VALUES (27315, 0, 3, 'I\'m gettin\' a little woozy... Oooooof...', 12, 0, 100, 0, 0, 0, 0, 26364, 0, 'Helpless Wintergarde Villager');
INSERT INTO `creature_text` VALUES (27315, 0, 4, 'You saved my life! Thanks!', 12, 0, 100, 0, 0, 0, 0, 26358, 0, 'Helpless Wintergarde Villager');
INSERT INTO `creature_text` VALUES (27315, 0, 5, 'You are my guardian angel! Like a white knight you flew in from the heavens and lifted me from the pit of damnation!', 12, 0, 100, 0, 0, 0, 0, 26362, 0, 'Helpless Wintergarde Villager');
INSERT INTO `creature_text` VALUES (27315, 1, 0, 'How can I ever repay you for this, friend?', 12, 0, 100, 0, 0, 0, 0, 26363, 0, 'Helpless Wintergarde Villager');
INSERT INTO `creature_text` VALUES (27315, 1, 1, 'HURRAY!', 12, 0, 100, 0, 0, 0, 0, 26382, 0, 'Helpless Wintergarde Villager');
INSERT INTO `creature_text` VALUES (27315, 1, 2, 'Kindness is not lost with this one, Urik. Thank you, hero!', 12, 0, 100, 0, 0, 0, 0, 26383, 0, 'Helpless Wintergarde Villager');
INSERT INTO `creature_text` VALUES (27315, 1, 3, 'My shop\'s doors will always be open to you, friend.', 12, 0, 100, 0, 0, 0, 0, 26385, 0, 'Helpless Wintergarde Villager');
INSERT INTO `creature_text` VALUES (27315, 1, 4, 'Safe at last! Thank you, stranger!', 12, 0, 100, 0, 0, 0, 0, 26381, 0, 'Helpless Wintergarde Villager');
INSERT INTO `creature_text` VALUES (27315, 1, 5, 'Thanks for your help, hero!', 12, 0, 100, 0, 0, 0, 0, 26357, 0, 'Helpless Wintergarde Villager');
INSERT INTO `creature_text` VALUES (27315, 1, 6, 'We made it! We actually made it!', 12, 0, 100, 0, 0, 0, 0, 26384, 0, 'Helpless Wintergarde Villager');
INSERT INTO `creature_text` VALUES (27316, 0, 0, 'Stand back, $n, the beast might lash out and harm you.', 12, 7, 100, 396, 0, 0, 0, 26800, 0, 'Inquisitor Hallard');
INSERT INTO `creature_text` VALUES (27316, 1, 0, 'GODFREY! Hear me, fiend! Hear me, for I am the Light, here to deliver you from evil!', 12, 7, 100, 5, 0, 0, 0, 26801, 0, 'Inquisitor Hallard');
INSERT INTO `creature_text` VALUES (27316, 2, 0, 'Good. I have your attention, then, Godfrey?', 12, 7, 100, 396, 0, 0, 0, 26803, 0, 'Inquisitor Hallard');
INSERT INTO `creature_text` VALUES (27316, 3, 0, 'We can do this in one of two ways, Godfrey. First, I will simply ask you to tell me what the unholy markings etched upon the pages of this tome mean. What say you?', 12, 7, 100, 1, 0, 0, 0, 26804, 0, 'Inquisitor Hallard');
INSERT INTO `creature_text` VALUES (27316, 4, 0, 'Then it is option two.', 12, 7, 100, 1, 0, 0, 0, 26809, 0, 'Inquisitor Hallard');
INSERT INTO `creature_text` VALUES (27316, 5, 0, 'I say a prayer for you now, lost soul. May the Light take you gracefully.', 12, 7, 100, 68, 4000, 0, 0, 26810, 0, 'Inquisitor Hallard');
INSERT INTO `creature_text` VALUES (27316, 6, 0, 'Let the sermon begin.', 12, 7, 100, 1, 0, 0, 0, 26811, 0, 'Inquisitor Hallard');
INSERT INTO `creature_text` VALUES (27316, 7, 0, 'LIGHT TAKE YOU, BEAST!', 12, 7, 100, 0, 0, 0, 0, 26812, 0, 'Inquisitor Hallard');
INSERT INTO `creature_text` VALUES (27316, 8, 0, 'BURN IN HOLY FIRE!', 12, 7, 100, 0, 0, 0, 0, 26816, 0, 'Inquisitor Hallard');
INSERT INTO `creature_text` VALUES (27316, 9, 0, 'LET THE LIGHT IN, GODFREY GOODARD!', 12, 7, 100, 0, 0, 0, 0, 26814, 0, 'Inquisitor Hallard');
INSERT INTO `creature_text` VALUES (27316, 10, 0, 'May the power of Light compel you!', 12, 7, 100, 0, 0, 0, 0, 26813, 0, 'Inquisitor Hallard');
INSERT INTO `creature_text` VALUES (27316, 11, 0, 'I thought you would see the Light, Godfrey... Now speak quickly, fiend. What does it say?', 12, 7, 100, 396, 0, 0, 0, 26824, 0, 'Inquisitor Hallard');
INSERT INTO `creature_text` VALUES (27316, 12, 0, 'RUBBISH! Godfrey do you take me for a fool? Do not spew your Scourge propaganda at a man of the Light, heathen! Speak now or I will have the peasants craft a holy water bath and dip you into it like a dog with fleas!', 12, 7, 100, 6, 0, 0, 0, 26826, 0, 'Inquisitor Hallard');
INSERT INTO `creature_text` VALUES (27316, 13, 0, 'What? There is a mausoleum beneath the old village? What fools we are to not have noticed! We will find and strike down your master now, Godfrey. We will end this nightmare.', 12, 7, 100, 6, 0, 0, 0, 26829, 0, 'Inquisitor Hallard');
INSERT INTO `creature_text` VALUES (27316, 14, 0, 'You let us worry about how we kill the monster, Godfrey.', 12, 7, 100, 396, 0, 0, 0, 26830, 0, 'Inquisitor Hallard');
INSERT INTO `creature_text` VALUES (27316, 15, 0, 'Return to Halford with the information that the good mayor was kind enough to submit, $n. I will finish here and squeeze whatever else this wretch might know about Thel\'zan. Now, I will show Godfrey the rarely seen \"option three.\"', 12, 7, 100, 1, 0, 0, 0, 26833, 0, 'Inquisitor Hallard');
INSERT INTO `creature_text` VALUES (27316, 16, 0, 'Let\'s you and I have a chat about some things, Godfrey.', 12, 7, 100, 1, 0, 0, 0, 26834, 0, 'Inquisitor Hallard');
INSERT INTO `creature_text` VALUES (27328, 0, 0, 'Stay behind me, $r.  I will do my best to protect you.', 12, 0, 100, 0, 0, 0, 0, 26373, 0, 'Tur Ragepaw');
INSERT INTO `creature_text` VALUES (27328, 1, 0, 'My freedom means nothing if we fail to save Ursoc.  Make haste, $n.', 12, 0, 100, 0, 0, 0, 0, 26374, 0, 'Tur Ragepaw');
INSERT INTO `creature_text` VALUES (27328, 2, 0, 'I will watch over you and your allies, $r.', 12, 0, 100, 0, 0, 0, 0, 26375, 0, 'Tur Ragepaw');
INSERT INTO `creature_text` VALUES (27328, 3, 0, 'Ursoc, brother of Ursol and guardian of the furbolg tribes... show yourself!', 12, 0, 100, 0, 0, 0, 0, 26427, 0, 'Tur Ragepaw');
