INSERT INTO `creature_text` VALUES (27344, 0, 0, 'Guards!', 14, 6, 100, 0, 0, 0, 0, 4583, 0, 'Bat Handler Adeline');
INSERT INTO `creature_text` VALUES (27349, 0, 0, 'The %s moans but seems otherwise unaffected by the blight.', 16, 0, 100, 0, 0, 0, 0, 26389, 0, 'Scarlet Onslaught Prisoner');
INSERT INTO `creature_text` VALUES (27355, 0, 0, 'No... NO! What have you done?! So many ancient wyrms wasted... what magic could do this? ', 14, 0, 100, 1, 0, 0, 0, 26555, 0, 'Rothin the Decaying <Cult of the Damned>');
INSERT INTO `creature_text` VALUES (27355, 1, 0, 'Foolish errand $G boy:girl;... you will die for interrupting my work!', 14, 0, 100, 1, 0, 0, 0, 26547, 0, 'Rothin the Decaying <Cult of the Damned>');
INSERT INTO `creature_text` VALUES (27355, 2, 0, 'This is not the end... death only... strengthens... ', 14, 0, 100, 1, 0, 0, 0, 26669, 0, 'Rothin the Decaying <Cult of the Damned>');
INSERT INTO `creature_text` VALUES (27359, 0, 0, 'Good of you to come, champion! The beast almost had me!', 12, 7, 0, 0, 0, 0, 0, 26470, 0, 'Trapped Wintergarde Villager');
INSERT INTO `creature_text` VALUES (27359, 0, 1, 'Finally! A hero has come!', 12, 7, 0, 0, 0, 0, 0, 26469, 0, 'Trapped Wintergarde Villager');
INSERT INTO `creature_text` VALUES (27359, 0, 2, 'Thank you, kind soul.', 12, 7, 0, 0, 0, 0, 0, 26467, 0, 'Trapped Wintergarde Villager');
INSERT INTO `creature_text` VALUES (27359, 0, 3, 'I\'ll never forget you, friend.', 12, 7, 0, 0, 0, 0, 0, 26468, 0, 'Trapped Wintergarde Villager');
INSERT INTO `creature_text` VALUES (27364, 0, 0, 'Hey Celidh!  Do you have any pets you want me to take over to the stable?', 12, 7, 100, 0, 0, 0, 0, 26428, 0, 'Jimmy the Stable Boy');
INSERT INTO `creature_text` VALUES (27364, 1, 0, 'Let\'s go boy.', 12, 7, 100, 0, 0, 0, 0, 26432, 0, 'Jimmy the Stable Boy');
INSERT INTO `creature_text` VALUES (27364, 2, 0, 'If I were king, I\'d have a horse like you.  I\'d make sure you didn\'t get lost, and I bet you\'d make sure I never got lost.', 12, 7, 100, 0, 0, 0, 0, 26478, 0, 'Jimmy the Stable Boy');
INSERT INTO `creature_text` VALUES (27364, 3, 0, 'We\'d be best friends.  I bet me and Anduin would also be best friends, too.  Friends are important.  ', 12, 7, 100, 0, 0, 0, 0, 26479, 0, 'Jimmy the Stable Boy');
INSERT INTO `creature_text` VALUES (27364, 4, 0, 'I\'ll take you to some other horses.  You probably miss being around other horses.  They can be your friend too.', 12, 7, 100, 0, 0, 0, 0, 26486, 0, 'Jimmy the Stable Boy');
INSERT INTO `creature_text` VALUES (27373, 0, 0, 'I thank you all.  The druids of old were wise to tear down Vordrassil, for its roots seep deep into the dwelling of an ancient evil. ', 12, 0, 100, 0, 0, 0, 0, 26436, 0, 'Spirit of Ursoc');
INSERT INTO `creature_text` VALUES (27373, 1, 0, 'You know their kind as old gods.  Beware Yogg-Saron, the beast with a thousand maws.  His evil extends beyond Vordrassil\'s roots.', 12, 0, 100, 0, 0, 0, 0, 26437, 0, 'Spirit of Ursoc');
INSERT INTO `creature_text` VALUES (27376, 0, 0, 'What do you mean my time has come?! I\'ll kill you where you stand!', 12, 1, 100, 0, 0, 0, 0, 26449, 0, 'Deathguard Schneider before going hostile');
INSERT INTO `creature_text` VALUES (27376, 1, 0, 'Hey, over here!', 12, 0, 100, 0, 0, 0, 0, 53162, 0, 'Deathguard Schneider upon LoS');
INSERT INTO `creature_text` VALUES (27378, 0, 0, 'You can\'t possibly mean to.... I\'ll write you up for this, $c!', 12, 1, 100, 0, 0, 0, 0, 26450, 0, 'Senior Scrivener Barriga before going hostile');
INSERT INTO `creature_text` VALUES (27378, 1, 0, 'If you\'d be so kind, please let me out of here!', 12, 1, 100, 0, 0, 0, 0, 26452, 0, 'Senior Scrivener Barriga upon LoS');
INSERT INTO `creature_text` VALUES (27379, 0, 0, 'No! I beg you! Please don\'t kill me!', 12, 1, 100, 0, 0, 0, 0, 26454, 0, 'Engineer Burke before going hostile');
INSERT INTO `creature_text` VALUES (27379, 1, 0, 'Can someone let me out of here? I need to get back to Venomspite and finish my shift.', 12, 1, 100, 0, 0, 0, 0, 26453, 0, 'Engineer Burke upon LoS');
INSERT INTO `creature_text` VALUES (27381, 0, 0, 'What is the meaning of this?! Stop! I\'ll double whatever they\'re paying you!', 12, 1, 100, 0, 0, 0, 0, 26458, 0, 'Chancellor Amai before going hostile');
INSERT INTO `creature_text` VALUES (27381, 1, 0, 'I just arrived in Venomspite. How could I have possibly wronged you?', 12, 1, 100, 0, 0, 0, 0, 26459, 0, 'Chancellor Amai upon Los');
INSERT INTO `creature_text` VALUES (27383, 0, 0, 'I was... once like you. Mortal. Soft. Only flesh and bone. WEAK AND TIMID!', 14, 0, 100, 0, 0, 0, 0, 27157, 0, 'Thel zan the Duskbringer');
INSERT INTO `creature_text` VALUES (27383, 1, 0, 'Our lord, Arthas saw in me the frailty of man.', 14, 0, 100, 0, 0, 0, 0, 27159, 0, 'Thel zan the Duskbringer');
INSERT INTO `creature_text` VALUES (27383, 2, 0, '\"Serve me in life and I will promise to rend the weakness from your soul. To erase it from existence!\" To be chosen... blessed by the Lich King in such a way.', 14, 0, 100, 0, 0, 0, 0, 27160, 0, 'Thel zan the Duskbringer');
INSERT INTO `creature_text` VALUES (27383, 3, 0, 'So serve him I did... Were it not for me the glorious dread citadel of Naxxramas may never have returned to Northrend.', 14, 0, 100, 0, 0, 0, 0, 27161, 0, 'Thel zan the Duskbringer');
INSERT INTO `creature_text` VALUES (27383, 4, 0, 'But enough talk... You have fought hard and come from the distant reaches of this world to face the Duskbringer. I shall not disappoint...', 14, 0, 100, 0, 0, 0, 0, 27162, 0, 'Thel zan the Duskbringer');
INSERT INTO `creature_text` VALUES (27383, 5, 0, 'Just as our lord saved me, so too shall I save you!', 14, 0, 100, 0, 0, 0, 0, 27163, 0, 'Thel zan the Duskbringer');
INSERT INTO `creature_text` VALUES (27383, 6, 0, 'THIS WILL BE THE LAST TIME YOU UTTER THAT NAME, PALADIN! Breathe your dying breath!', 14, 0, 100, 0, 0, 0, 0, 27176, 0, 'Thel zan the Duskbringer');
INSERT INTO `creature_text` VALUES (27383, 7, 0, 'Rise, my minions! Your master commands it!', 14, 0, 100, 0, 0, 0, 0, 27164, 0, 'Thel zan the Duskbringer');
INSERT INTO `creature_text` VALUES (27389, 0, 0, 'By all means, don\'t assess the situation, you halfwit! Just jump into the fray!', 14, 0, 100, 0, 0, 13199, 0, 29527, 0, 'dalronn YELL_DALRONN_AGGRO');
INSERT INTO `creature_text` VALUES (27389, 1, 0, 'See... you... soon.', 14, 0, 100, 0, 0, 13200, 0, 29529, 0, 'dalronn YELL_DALRONN_SKA_DIED');
INSERT INTO `creature_text` VALUES (27389, 2, 0, 'There\'s no... greater... glory.', 14, 0, 100, 0, 0, 13201, 0, 29530, 0, 'dalronn YELL_DALRONN_DAL_DIEDFIRST');
INSERT INTO `creature_text` VALUES (27389, 3, 0, 'You may serve me yet.', 14, 0, 100, 0, 0, 13202, 0, 29526, 0, 'dalronn YELL_DALRONN_KILL');
INSERT INTO `creature_text` VALUES (27389, 4, 0, 'Skarvald, you incompetent slug! Return and make yourself useful!', 14, 0, 100, 0, 0, 13203, 0, 29528, 0, 'dalronn YELL_DALRONN_SKA_DIEDFIRST');
INSERT INTO `creature_text` VALUES (27390, 0, 0, 'Dalronn! See if you can muster the nerve to join my attack!', 14, 0, 100, 0, 0, 13229, 0, 29531, 0, 'skarvald YELL_SKARVALD_AGGRO');
INSERT INTO `creature_text` VALUES (27390, 1, 0, 'Not... over... yet.', 14, 0, 100, 0, 0, 13230, 0, 29534, 0, 'skarvald YELL_SKARVALD_DAL_DIED');
INSERT INTO `creature_text` VALUES (27390, 2, 0, 'A warrior\'s death.', 14, 0, 100, 0, 0, 13231, 0, 29535, 0, 'skarvald YELL_SKARVALD_SKA_DIEDFIRST');
INSERT INTO `creature_text` VALUES (27390, 3, 0, 'Jarggn olkt!', 14, 0, 100, 0, 0, 13232, 0, 29532, 0, 'skarvald YELL_SKARVALD_KILL');
INSERT INTO `creature_text` VALUES (27390, 4, 0, 'Pfagh! What sort of necromancer lets death stop him? I knew you were worthless!', 14, 0, 100, 0, 0, 13233, 0, 29533, 0, 'skarvald YELL_SKARVALD_DAL_DIEDFIRST');
INSERT INTO `creature_text` VALUES (27405, 0, 0, 'What\'s going on here, sarge?', 12, 0, 100, 0, 0, 0, 0, 26541, 0, 'Onslaught Footman');
INSERT INTO `creature_text` VALUES (27406, 0, 0, 'I don\'t know. Better kill them both to be on the safe side!', 12, 0, 100, 0, 0, 0, 0, 26542, 0, 'Onslaught Footman');
INSERT INTO `creature_text` VALUES (27411, 0, 0, 'Were you bitten?', 12, 7, 100, 6, 0, 0, 0, 26640, 0, 'Freed Alliance Scout Say1');
INSERT INTO `creature_text` VALUES (27411, 1, 1, 'Answer me!  Were you bloody bitten?', 14, 7, 100, 22, 0, 0, 0, 26641, 0, 'Freed Alliance Scout Yell');
INSERT INTO `creature_text` VALUES (27411, 2, 2, 'You don\'t even know, do you?  The trappers?  They\'re not human... we have to get out of here!  Come, quick!', 12, 7, 100, 0, 0, 0, 0, 26539, 0, 'Freed Alliance Scout Say2');
INSERT INTO `creature_text` VALUES (27411, 3, 3, 'Take Ducal\'s horse, he\'s one of them now!', 12, 7, 100, 0, 0, 0, 0, 26540, 0, 'Freed Alliance Scout Say3');
INSERT INTO `creature_text` VALUES (27411, 4, 4, 'There\'s lamp oil in the back - make use of it.  The fiends hate fire!', 12, 7, 100, 0, 0, 0, 0, 26644, 0, 'Freed Alliance Scout Say4');
INSERT INTO `creature_text` VALUES (27430, 0, 0, 'The Destructive Ward gains in power.', 41, 0, 100, 0, 0, 0, 0, 26601, 0, 'Destructive Ward');
INSERT INTO `creature_text` VALUES (27430, 1, 0, 'The Destructive Ward is fully charged!', 41, 0, 100, 0, 0, 0, 0, 26602, 0, 'Destructive Ward');
INSERT INTO `creature_text` VALUES (27439, 0, 0, 'I know a place nearby where we can speak in private, my child. Follow me.', 12, 0, 100, 1, 0, 0, 0, 26629, 0, 'High Abbot Landgren');
INSERT INTO `creature_text` VALUES (27439, 1, 0, 'Did you think that I could not see through your flimsy disguise, $N?', 12, 0, 100, 6, 0, 0, 0, 26631, 0, 'High Abbot Landgren');
INSERT INTO `creature_text` VALUES (27439, 2, 0, 'There is much that you do not understand, $R. The Master sees all.', 12, 0, 100, 1, 0, 0, 0, 26632, 0, 'High Abbot Landgren');
INSERT INTO `creature_text` VALUES (27439, 3, 0, 'He told me that you would come for me. I won\'t die by your hand, though. I have seen what you have done to my compatriots.', 12, 0, 100, 25, 0, 0, 0, 26633, 0, 'High Abbot Landgren');
INSERT INTO `creature_text` VALUES (27439, 4, 0, 'No. I will leave this world in a manner of my own choosing. And I will return, the grand admiral\'s will permitting!', 12, 0, 100, 274, 0, 0, 0, 26634, 0, 'High Abbot Landgren');
INSERT INTO `creature_text` VALUES (27439, 5, 0, 'AAAEEEEIIIiiiiiiiiiiiiiiiiiiiiiiiiiiii........................................', 12, 0, 100, 0, 0, 2943, 0, 26636, 0, 'High Abbot Landgren');
INSERT INTO `creature_text` VALUES (27447, 0, 0, 'There will be no mercy!', 14, 0, 0, 0, 0, 13649, 0, 31813, 0, '');
INSERT INTO `creature_text` VALUES (27447, 1, 1, 'Blast them! Destroy them!', 14, 0, 0, 0, 0, 13650, 0, 31814, 0, '');
INSERT INTO `creature_text` VALUES (27447, 2, 2, '%s calls an Azure Ring Captain!', 41, 0, 0, 0, 0, 0, 0, 33077, 0, '');
INSERT INTO `creature_text` VALUES (27447, 3, 3, 'They are... too strong! Underestimated their... fortitude.', 14, 0, 0, 0, 0, 13655, 0, 31819, 0, '');
INSERT INTO `creature_text` VALUES (27447, 4, 0, 'Intruders, your victory will be short-lived. I am Commander Varos Cloudstrider. My drakes control the skies and protect this conduit. I will see to it personally that the Oculus does not fall into your hands!', 14, 0, 100, 0, 0, 13648, 0, 31812, 3, 'Varos Cloudstrider SAY_VAROS_INTRO_TEXT');
INSERT INTO `creature_text` VALUES (27455, 0, 0, 'Captain, why are the guards not at their posts?', 12, 0, 100, 6, 0, 12725, 0, 26714, 0, 'Prince Arthas');
INSERT INTO `creature_text` VALUES (27455, 1, 0, 'Uther had my troops recalled? Damn it! If my warriors abandon me, I\'ll never defeat Mal\'Ganis. The ships must be burned before the men reach the shore!\n', 12, 0, 100, 5, 0, 12726, 0, 26716, 0, 'Prince Arthas');
INSERT INTO `creature_text` VALUES (27455, 2, 0, 'Burned down to their frames! No one goes home until our job here is done!\n', 12, 0, 100, 15, 0, 12727, 0, 26718, 0, 'Prince Arthas');
INSERT INTO `creature_text` VALUES (27455, 3, 0, 'Spare me, Muradin. You weren\'t there to see what Mal\'Ganis did to my homeland.\n', 12, 0, 100, 1, 0, 12728, 0, 26732, 0, 'Prince Arthas');
INSERT INTO `creature_text` VALUES (27455, 4, 0, 'Behold, Muradin, our salvation, Frostmourne.\n', 12, 0, 100, 1, 0, 12729, 0, 27207, 0, 'Prince Arthas');
INSERT INTO `creature_text` VALUES (27455, 5, 0, 'I would gladly bear any curse to save my homeland.\n', 12, 0, 100, 1, 0, 12730, 0, 27211, 0, 'Prince Arthas');
INSERT INTO `creature_text` VALUES (27455, 6, 0, 'Damn the men! Nothing shall prevent me from having my revenge, old friend. Not even you.\n', 12, 0, 100, 5, 0, 12731, 0, 27214, 0, 'Prince Arthas');
INSERT INTO `creature_text` VALUES (27455, 7, 0, 'Now, I call out to the spirits of this place. I will give anything or pay any price, if only you will help me save my people.\n', 12, 0, 100, 396, 0, 12732, 0, 27215, 0, 'Prince Arthas');
INSERT INTO `creature_text` VALUES (27463, 0, 0, 'Ahh.... better.', 12, 0, 100, 0, 0, 0, 0, 26657, 0, 'wounded skirmisher RANDOM_SAY_1');
INSERT INTO `creature_text` VALUES (27463, 0, 1, 'Whoa - I nearly died there. Thank you, $r!', 12, 0, 100, 0, 0, 0, 0, 26659, 0, 'wounded skirmisher RANDOM_SAY_2');
INSERT INTO `creature_text` VALUES (27463, 0, 2, 'Thank you, $c.', 12, 0, 100, 0, 0, 0, 0, 26656, 0, 'wounded skirmisher RANDOM_SAY_3');
INSERT INTO `creature_text` VALUES (27476, 0, 0, 'I apologize, emissary, but the prince is away on an errand. Wha brings you to this desolate place?', 12, 0, 100, 1, 3000, 12719, 0, 26709, 0, 'Valonforth 0  - Q: The Truth Shall Set Us Free');
INSERT INTO `creature_text` VALUES (27476, 1, 0, 'We\'re to just pick up and leave?', 12, 0, 100, 6, 1500, 12720, 0, 26711, 0, 'Valonforth 1  - Q: The Truth Shall Set Us Free');
INSERT INTO `creature_text` VALUES (27476, 2, 0, 'To hell with the undead! We\'ll cut our way through the woods, men!', 12, 0, 100, 22, 3000, 12721, 0, 26713, 0, 'Valonforth 2  - Q: The Truth Shall Set Us Free');
INSERT INTO `creature_text` VALUES (27476, 3, 0, 'Well, milord, your father had our troops recalled at Lord Uther\'s request.', 12, 0, 100, 1, 3000, 12722, 0, 26715, 0, 'Valonforth 3 - Q: The Truth Shall Set Us Free');
INSERT INTO `creature_text` VALUES (27480, 0, 0, 'Isn\'t that a bit much, lad?\n', 12, 0, 100, 6, 0, 12733, 0, 26717, 0, 'Muradin');
INSERT INTO `creature_text` VALUES (27480, 1, 0, 'You lied to your men and betrayed the mercenaries who fought for you. What\'s happening to you, Arthas? Is vengeance all that\'s important to you?\n', 12, 0, 100, 1, 0, 12734, 0, 26731, 0, 'Muradin');
INSERT INTO `creature_text` VALUES (27480, 2, 0, 'Hold, lad. There\'s an inscription on the dais. It\'s a warning. It says, \"Whomsoever takes up this blade shall wield power eternal. Just as the blade rends flesh, so must power scar the spirit.\" Oh, I should\'ve known. The blade is cursed! Let\'s get the hell out of here!\n', 12, 0, 100, 0, 0, 12735, 0, 27209, 0, 'Muradin');
INSERT INTO `creature_text` VALUES (27480, 3, 0, 'Leave it be, Arthas. Forget this business and lead your men home.\n', 12, 0, 100, 396, 0, 12736, 0, 27212, 0, 'Muradin');
INSERT INTO `creature_text` VALUES (27480, 4, 0, 'O\' my head... Wh... Where am I?\n', 12, 0, 100, 0, 0, 0, 0, 27217, 0, 'Muradin');
INSERT INTO `creature_text` VALUES (27480, 5, 0, 'Who... Who am I?\n', 12, 0, 100, 0, 0, 0, 0, 27218, 0, 'Muradin');
INSERT INTO `creature_text` VALUES (27482, 0, 0, 'I\'d nearly given up. You\'ve given me new life!', 12, 0, 50, 0, 0, 0, 0, 26684, 0, 'Wounded Westfall Infantry say text');
INSERT INTO `creature_text` VALUES (27482, 0, 1, 'Bless you, friend. I nearly expired....', 12, 0, 50, 0, 0, 0, 0, 26682, 0, 'Wounded Westfall Infantry say text');
INSERT INTO `creature_text` VALUES (27482, 0, 2, 'Without your help, I surely would have died....', 12, 0, 50, 0, 0, 0, 0, 26685, 0, 'Wounded Westfall Infantry say text');
INSERT INTO `creature_text` VALUES (27482, 0, 3, 'Thank you, $r!', 12, 0, 50, 0, 0, 0, 0, 26683, 0, 'Wounded Westfall Infantry say text');
INSERT INTO `creature_text` VALUES (27492, 0, 0, 'By royal edict, you men are to return to Lordaeron immediately. Lord Uther has convinced the king to recall this expedition.', 12, 0, 100, 25, 3500, 12723, 0, 26710, 0, 'Alliance Emissary 0 - Q: The Truth Shall Set Us Free');
INSERT INTO `creature_text` VALUES (27492, 1, 0, 'That\'s correct. My men report that the roads from here to the shore are held by the undead. You\'ll need to find an alternate route back to your ships.', 12, 0, 100, 378, 4000, 12724, 0, 26712, 0, 'Alliance Emissary 1 - Q: The Truth Shall Set Us Free');
INSERT INTO `creature_text` VALUES (27551, 0, 0, '%s goes into a frenzy!', 16, 0, 100, 0, 0, 0, 0, 38630, 0, 'Enraged Apparition');
INSERT INTO `creature_text` VALUES (27570, 0, 0, 'We\'re all gonna die!', 12, 0, 100, 0, 0, 0, 0, 26848, 0, 'venture co straggler SAY1');
INSERT INTO `creature_text` VALUES (27570, 0, 1, 'Gotta get out of here!', 12, 0, 100, 0, 0, 0, 0, 26847, 0, 'venture co straggler SAY2');
INSERT INTO `creature_text` VALUES (27570, 0, 2, 'No way I\'m stickin\' around!', 12, 0, 100, 0, 0, 0, 0, 26851, 0, 'venture co straggler SAY3');
INSERT INTO `creature_text` VALUES (27570, 0, 3, 'Forget this! I\'m going home!', 12, 0, 100, 0, 0, 0, 0, 26850, 0, 'venture co straggler SAY4');
INSERT INTO `creature_text` VALUES (27570, 0, 4, 'I didn\'t sign up for this!', 12, 0, 100, 0, 0, 0, 0, 26849, 0, 'venture co straggler SAY5');
INSERT INTO `creature_text` VALUES (27577, 0, 0, '%s growls.', 16, 0, 100, 0, 0, 409, 0, 26802, 0, 'Mayor Godfrey');
INSERT INTO `creature_text` VALUES (27577, 1, 0, '%s hisses!', 16, 0, 100, 0, 0, 410, 0, 26805, 0, 'Mayor Godfrey');
INSERT INTO `creature_text` VALUES (27577, 2, 0, 'Tell you nothing, preacher.', 12, 0, 100, 1, 0, 0, 0, 26806, 0, 'Mayor Godfrey');
INSERT INTO `creature_text` VALUES (27577, 3, 0, 'What can you do to me that Kel\'Thuzad has not? That the Lich King will not?', 12, 0, 100, 1, 0, 0, 0, 26807, 0, 'Mayor Godfrey');
INSERT INTO `creature_text` VALUES (27577, 4, 0, 'The book is your salvation, yes... but nothing will you know! NOTHING I SAY! NOTHING! ', 12, 0, 100, 1, 0, 0, 0, 26808, 0, 'Mayor Godfrey');
INSERT INTO `creature_text` VALUES (27577, 5, 0, 'No more LIGHT! NO MORE! I BEG YOU!', 12, 0, 100, 0, 0, 0, 0, 26822, 0, 'Mayor Godfrey');
INSERT INTO `creature_text` VALUES (27577, 6, 0, 'Never felt hurt like this!', 12, 0, 100, 0, 0, 0, 0, 26823, 0, 'Mayor Godfrey');
INSERT INTO `creature_text` VALUES (27577, 7, 0, 'It tells of the coming apocalypse. How this world will burn and be reborn from death.', 12, 0, 100, 1, 0, 0, 0, 26825, 0, 'Mayor Godfrey');
INSERT INTO `creature_text` VALUES (27577, 8, 0, 'NOOOO!!! I tell you! I tell you everything!', 12, 0, 100, 39, 0, 0, 0, 26827, 0, 'Mayor Godfrey');
INSERT INTO `creature_text` VALUES (27577, 9, 0, 'Humans... Beneath the earth of Wintergarde Village you built a mausoleum! Why do you think Naxxramas attacked that spot? Thel\'zan hides deep in your own crypt and sends a thousand-thousand corpses at you! Perish you will...', 12, 0, 100, 1, 0, 0, 0, 26828, 0, 'Mayor Godfrey');
INSERT INTO `creature_text` VALUES (27577, 10, 0, 'How? Humans truly are stupid, yes? Thel\'zan the Duskbringer! Thel\'zan the Lich! He who is born of human flesh and bone, sacrificed all for power, protected by the Lich King!', 12, 0, 100, 1, 0, 0, 0, 26831, 0, 'Mayor Godfrey');
INSERT INTO `creature_text` VALUES (27577, 11, 0, 'You cannot stop Thel\'zan! He bears the dark gift, bestowed upon him by the Lich King himself!', 12, 0, 100, 1, 0, 0, 0, 26832, 0, 'Mayor Godfrey');
INSERT INTO `creature_text` VALUES (27588, 0, 0, 'Without you we\'d be lost. Thanks for the ride!', 12, 7, 100, 0, 0, 0, 0, 26844, 0, '7th Legion Elite');
INSERT INTO `creature_text` VALUES (27588, 0, 1, 'For the Alliance and Lord Fordragon!', 12, 7, 100, 0, 0, 0, 0, 26841, 0, '7th Legion Elite');
INSERT INTO `creature_text` VALUES (27588, 0, 2, 'HOO-WAH! The cavalry has arrived!', 12, 7, 100, 0, 0, 0, 0, 26842, 0, '7th Legion Elite');
INSERT INTO `creature_text` VALUES (27588, 0, 3, 'Great driving, soldier! Not a scratch on us!', 12, 7, 100, 0, 0, 0, 0, 26843, 0, '7th Legion Elite');
INSERT INTO `creature_text` VALUES (27588, 1, 0, 'I think I see one of their plague wagons!', 12, 0, 100, 0, 0, 0, 0, 26971, 0, '7th Legion Elite');
INSERT INTO `creature_text` VALUES (27588, 1, 1, 'Did I ever tell you soldiers about the time I destroyed the Thandol Span? Yep, weren\'t no dark irons destroyed that bridge! Was me!', 12, 7, 100, 0, 0, 0, 0, 26972, 0, '7th Legion Elite');
INSERT INTO `creature_text` VALUES (27588, 1, 2, 'It doesn\'t make any sense. Why don\'t they just fly Naxxramas over Wintergarde Keep and blow it up? I mean, that\'s what I would do if I were Kel\'Thuzad.', 12, 7, 100, 0, 0, 0, 0, 26973, 0, '7th Legion Elite');
INSERT INTO `creature_text` VALUES (27588, 1, 3, 'I wonder if we\'ll ever solve the mystery of the strange ore. There\'s gotta be some use for it!', 12, 0, 100, 0, 0, 0, 0, 26974, 0, '7th Legion Elite');
INSERT INTO `creature_text` VALUES (27588, 1, 4, 'Hey, do any of you know McGoyver over at Valgarde? He\'s my uncle. You know what his title is? Pro. Yea, just \"Pro.\" I want to be a pro too.', 12, 0, 100, 0, 0, 0, 0, 26975, 0, '7th Legion Elite');
INSERT INTO `creature_text` VALUES (27588, 1, 5, 'Something straight up stinks in here! It\'s definitely not me. Gnomes smell like butter and sunshine. Not like those dwarves that smell like they were born from a trogg\'s armpit! None of you are dwarves, are you?', 12, 0, 100, 0, 0, 0, 0, 26976, 0, '7th Legion Elite');
INSERT INTO `creature_text` VALUES (27588, 1, 6, 'I used to know a dwarf that claimed gnomes evolved from beneath the earth. That we all started out as sand gnomes. What a load of nonsense! Sand gnomes? PUH-LEASE!', 12, 0, 100, 0, 0, 0, 0, 26977, 0, '7th Legion Elite');
INSERT INTO `creature_text` VALUES (27588, 1, 7, 'I\'ve never seen destruction like this...', 12, 0, 100, 0, 0, 0, 0, 26978, 0, '7th Legion Elite');
INSERT INTO `creature_text` VALUES (27588, 1, 8, 'Keep the chatter down, people. We need to stay alert!', 12, 0, 100, 0, 0, 0, 0, 26979, 0, '7th Legion Elite');
INSERT INTO `creature_text` VALUES (27588, 1, 9, 'The smell of death covers every inch of this place.', 12, 0, 100, 0, 0, 0, 0, 26980, 0, '7th Legion Elite');
INSERT INTO `creature_text` VALUES (27588, 1, 10, 'Driver, I hear you single handedly airlifted our villagers out of this hell-hole. Is that true?', 12, 0, 100, 0, 0, 0, 0, 26981, 0, '7th Legion Elite');
INSERT INTO `creature_text` VALUES (27588, 1, 11, 'So does anyone know anything about Thel\'zan? Who is he? How did he come to leading the Scourge armies on the ground?', 12, 0, 100, 0, 0, 0, 0, 26982, 0, '7th Legion Elite');
INSERT INTO `creature_text` VALUES (27588, 1, 12, 'Look at this mess... The Scourge will pay for this!', 12, 0, 100, 0, 0, 0, 0, 26983, 0, '7th Legion Elite');
INSERT INTO `creature_text` VALUES (27588, 1, 13, 'Where are all the corpses? What have they done to our people?', 12, 0, 100, 0, 0, 0, 0, 26984, 0, '7th Legion Elite');
INSERT INTO `creature_text` VALUES (27588, 1, 14, 'I hope I\'m alive to see Arthas get what\'s coming to him.', 12, 0, 100, 0, 0, 0, 0, 26985, 0, '7th Legion Elite');
INSERT INTO `creature_text` VALUES (27588, 2, 0, 'Reporting for duty, sir!', 12, 7, 100, 0, 0, 0, 0, 26954, 0, '7th Legion Elite');
INSERT INTO `creature_text` VALUES (27619, 0, 0, 'Rise, Arugal!  The power of the Lich King commands you!', 12, 0, 100, 0, 0, 0, 0, 26865, 0, 'Prince Valanar - Silverpine');
INSERT INTO `creature_text` VALUES (27624, 0, 0, 'Yes... he shall suit our needs perfectly.  Have him sent to Solstice Village when you\'re ready.  Arthas will not be pleased if we fall behind schedule.', 12, 0, 100, 0, 0, 0, 0, 26867, 0, 'Prince Theraldis - Silverpine');
INSERT INTO `creature_text` VALUES (27629, 0, 0, 'Are you ready for battle, $c?', 15, 0, 100, 0, 0, 0, 0, 29966, 0, 'Wyrmrest Defender');
INSERT INTO `creature_text` VALUES (27629, 0, 1, 'Take us into battle, $n!', 15, 0, 100, 0, 0, 0, 0, 26908, 0, 'Wyrmrest Defender');
INSERT INTO `creature_text` VALUES (27629, 0, 2, 'Shall we destabilize the shrine first or disable the enemy?', 15, 0, 100, 0, 0, 0, 0, 29964, 0, 'Wyrmrest Defender');
INSERT INTO `creature_text` VALUES (27629, 0, 3, 'We\'ve been expecting you, $n. Now we have the upper hand.', 15, 0, 100, 0, 0, 0, 0, 29965, 0, 'Wyrmrest Defender');
INSERT INTO `creature_text` VALUES (27629, 0, 4, 'I can\'t believe the blues would attack us. Let\'s give some of it back!', 15, 0, 100, 0, 0, 0, 0, 29961, 0, 'Wyrmrest Defender');
INSERT INTO `creature_text` VALUES (27629, 0, 5, 'I am ready. Let\'s defend the temple!', 15, 0, 100, 0, 0, 0, 0, 29960, 0, 'Wyrmrest Defender');
INSERT INTO `creature_text` VALUES (27629, 0, 6, 'Where to first?', 15, 0, 100, 0, 0, 0, 0, 29962, 0, 'Wyrmrest Defender');
INSERT INTO `creature_text` VALUES (27629, 0, 7, 'Your reputation precedes you, $n. This should be interesting.', 15, 0, 100, 0, 0, 0, 0, 29963, 0, 'Wyrmrest Defender');
INSERT INTO `creature_text` VALUES (27629, 1, 0, 'I never thought that I\'d be riding into battle with a $r on my back.', 15, 0, 100, 0, 0, 0, 0, 27376, 0, 'Wyrmrest Defender');
INSERT INTO `creature_text` VALUES (27629, 1, 1, 'Another blue down. We make a great team!', 15, 0, 100, 0, 0, 0, 0, 27374, 0, 'Wyrmrest Defender');
INSERT INTO `creature_text` VALUES (27629, 1, 2, 'With you and I working together, this battle will be over in no time.', 15, 0, 100, 0, 0, 0, 0, 27375, 0, 'Wyrmrest Defender');
INSERT INTO `creature_text` VALUES (27629, 1, 3, 'Malygos has clearly gone insane.', 15, 0, 100, 0, 0, 0, 0, 27380, 0, 'Wyrmrest Defender');
INSERT INTO `creature_text` VALUES (27629, 1, 4, 'Nice one, kid.', 15, 0, 100, 0, 0, 0, 0, 27373, 0, 'Wyrmrest Defender');
INSERT INTO `creature_text` VALUES (27629, 1, 5, 'I\'ve never seen anything like this before. I\'m glad that you\'re here.', 15, 0, 100, 0, 0, 0, 0, 27379, 0, 'Wyrmrest Defender');
INSERT INTO `creature_text` VALUES (27629, 1, 6, 'I\'m glad you saw that one!', 15, 0, 100, 0, 0, 0, 0, 27377, 0, 'Wyrmrest Defender');
INSERT INTO `creature_text` VALUES (27629, 1, 7, 'Just hang on. We\'ll get through this.', 15, 0, 100, 0, 0, 0, 0, 27378, 0, 'Wyrmrest Defender');
INSERT INTO `creature_text` VALUES (27629, 2, 0, 'We should get out of here. I need to renew!', 42, 0, 100, 0, 3000, 0, 0, 28879, 0, 'Wyrmrest Defender');
INSERT INTO `creature_text` VALUES (27631, 0, 0, '%s becomes enraged!', 16, 0, 100, 0, 0, 0, 0, 24144, 0, 'Angrathar Aberration on enrage');
INSERT INTO `creature_text` VALUES (27646, 0, 0, 'You won\'t eat me, will you?', 12, 0, 100, 0, 0, 0, 0, 26914, 0, 'Anya');
INSERT INTO `creature_text` VALUES (27646, 1, 0, 'Sister!  The mean men were going to take me to Badmoon Isle.  They wanted to turn me into one of them.', 12, 0, 100, 0, 0, 0, 0, 26918, 0, 'Anya');
INSERT INTO `creature_text` VALUES (27646, 2, 0, 'That\'s what I said!  Badmoon!  Sasha... don\'t ever leave me again!  First they took papa, then they took you... I don\'t want to be alone with them!', 12, 0, 100, 0, 0, 0, 0, 26919, 0, 'Anya');
INSERT INTO `creature_text` VALUES (27654, 0, 0, 'The prisoners shall not go free! The word of Malygos is law!', 14, 0, 100, 0, 0, 13594, 0, 31793, 0, 'drakos SAY_AGGRO');
INSERT INTO `creature_text` VALUES (27654, 1, 0, 'A fitting punishment.', 14, 0, 100, 0, 0, 13602, 0, 31801, 0, 'drakos SAY_KILL_1');
INSERT INTO `creature_text` VALUES (27654, 1, 1, 'Sentence executed!', 14, 0, 100, 0, 0, 13603, 0, 31802, 0, 'drakos SAY_KILL_2');
INSERT INTO `creature_text` VALUES (27654, 1, 2, 'Another casualty of war!', 14, 0, 100, 0, 0, 13604, 0, 31803, 0, 'drakos SAY_KILL_3');
INSERT INTO `creature_text` VALUES (27654, 2, 0, 'The war... goes on.', 14, 0, 100, 0, 0, 13605, 0, 31804, 0, 'drakos SAY_DEATH');
INSERT INTO `creature_text` VALUES (27654, 3, 0, 'It is too late to run!', 14, 0, 100, 0, 0, 13598, 0, 31797, 0, 'drakos SAY_PULL_1');
INSERT INTO `creature_text` VALUES (27654, 3, 1, 'Gather \'round....', 14, 0, 100, 0, 0, 13599, 0, 31798, 0, 'drakos SAY_PULL_2');
INSERT INTO `creature_text` VALUES (27654, 3, 2, 'None shall escape!', 14, 0, 100, 0, 0, 13600, 0, 31799, 0, 'drakos SAY_PULL_3');
INSERT INTO `creature_text` VALUES (27654, 3, 3, 'I condemn you to death!', 14, 0, 100, 0, 0, 13601, 0, 31800, 0, 'drakos SAY_PULL_4');
INSERT INTO `creature_text` VALUES (27654, 4, 0, 'Tremble, worms!', 14, 0, 100, 0, 0, 13595, 0, 31794, 0, 'drakos SAY_STOMP_1');
INSERT INTO `creature_text` VALUES (27654, 4, 1, 'I will crush you!', 14, 0, 100, 0, 0, 13596, 0, 31795, 0, 'drakos SAY_STOMP_2');
INSERT INTO `creature_text` VALUES (27654, 4, 2, 'Can you fly?', 14, 0, 100, 0, 0, 13597, 0, 31796, 0, 'drakos SAY_STOMP_3');
INSERT INTO `creature_text` VALUES (27655, 0, 0, 'What do we have here... those that would defy the Spell-Weaver? Those without foresight or understanding. How can I make you see? Malygos is saving the world from itself! Bah! You are hardly worth my time!', 14, 0, 100, 1, 0, 13635, 0, 27411, 0, 'Mage-Lord Urom - SAY_SUMMON_1');
INSERT INTO `creature_text` VALUES (27655, 1, 0, 'Clearly my pets failed. Perhaps another demonstration is in order.', 14, 0, 100, 1, 0, 13636, 0, 27413, 0, 'Mage-Lord Urom - SAY_SUMMON_2');
INSERT INTO `creature_text` VALUES (27655, 2, 0, 'Still you fight. Still you cling to misguided principles. If you survive, you\'ll find me in the center ring.', 14, 0, 100, 1, 0, 13637, 0, 27414, 0, 'Mage-Lord Urom - SAY_SUMMON_3');
INSERT INTO `creature_text` VALUES (27655, 3, 0, 'Poor blind fools!', 14, 0, 100, 0, 0, 13638, 0, 27412, 0, 'Mage-Lord Urom - SAY_AGGRO');
INSERT INTO `creature_text` VALUES (27655, 4, 0, '%s begins to cast Empowered Arcane Explosion!', 41, 0, 100, 0, 0, 0, 0, 27903, 0, 'Mage-Lord Urom - EMOTE_ARCANE_EXPLOSION');
INSERT INTO `creature_text` VALUES (27655, 5, 0, 'A taste... just a small taste... of the Spell-Weaver\'s power!', 14, 0, 100, 0, 0, 13639, 0, 31829, 0, 'Mage-Lord Urom - SAY_ARCANE_EXPLOSION');
INSERT INTO `creature_text` VALUES (27655, 5, 1, 'So much unstable energy... but worth the risk to destroy you!', 14, 0, 100, 0, 0, 13640, 0, 31830, 0, 'Mage-Lord Urom - SAY_ARCANE_EXPLOSION');
INSERT INTO `creature_text` VALUES (27655, 6, 0, 'Everything I have done... has been for Azeroth....', 14, 0, 100, 0, 0, 13644, 0, 31834, 0, 'Mage-Lord Urom - SAY_DEATH');
INSERT INTO `creature_text` VALUES (27655, 7, 0, 'If only you understood!', 14, 0, 100, 0, 0, 13641, 0, 31831, 0, 'Mage-Lord Urom - SAY_PLAYER_KILL');
INSERT INTO `creature_text` VALUES (27655, 7, 1, 'Now do you see? Do you?!', 14, 0, 100, 0, 0, 13642, 0, 31832, 0, 'Mage-Lord Urom - SAY_PLAYER_KILL');
INSERT INTO `creature_text` VALUES (27655, 7, 2, 'Unfortunate, but necessary.', 14, 0, 100, 0, 0, 13643, 0, 31833, 0, 'Mage-Lord Urom - SAY_PLAYER_KILL');
INSERT INTO `creature_text` VALUES (27656, 0, 0, 'Simpletons! You cannot comprehend the forces you have set in motion. The ley line conduit will not be disrupted! Your defeat shall be absolute!', 14, 0, 100, 0, 0, 13622, 0, 31841, 3, 'Ley-Guardian Eregos - SAY_SPAWN');
INSERT INTO `creature_text` VALUES (27656, 1, 0, 'You brash interlopers are out of your element! I will ground you!', 14, 0, 100, 0, 0, 13623, 0, 31873, 0, 'Ley-Guardian Eregos - SAY_AGGRO');
INSERT INTO `creature_text` VALUES (27656, 2, 0, 'Such insolence... such arrogance... must be PUNISHED!', 14, 0, 100, 0, 0, 13624, 0, 31874, 0, 'Ley-Guardian Eregos - SAY_ENRAGE');
INSERT INTO `creature_text` VALUES (27656, 3, 0, 'It\'s a long way down....', 14, 0, 100, 0, 0, 13628, 0, 31878, 0, 'Ley-Guardian Eregos - SAY_KILL');
INSERT INTO `creature_text` VALUES (27656, 3, 1, 'BACK to the earth with you!', 14, 0, 100, 0, 0, 13629, 0, 31879, 0, 'Ley-Guardian Eregos - SAY_KILL');
INSERT INTO `creature_text` VALUES (27656, 3, 2, 'Enjoy the fall!', 14, 0, 100, 0, 0, 13630, 0, 31880, 0, 'Ley-Guardian Eregos - SAY_KILL');
INSERT INTO `creature_text` VALUES (27656, 4, 0, 'Savor this small victory, foolish little creatures. You and your dragon allies have won this battle, but we will win... the Nexus War.', 14, 0, 100, 0, 0, 13631, 0, 31881, 0, 'Ley-Guardian Eregos - SAY_DEATH');
INSERT INTO `creature_text` VALUES (27656, 5, 0, 'We command the arcane! It shall not be used against us!', 14, 0, 100, 0, 0, 13626, 0, 31876, 0, 'Ley-Guardian Eregos - SAY_SHIELD');
INSERT INTO `creature_text` VALUES (27656, 5, 1, 'It is trivial to extinguish your fire!', 14, 0, 100, 0, 0, 13627, 0, 31877, 0, 'Ley-Guardian Eregos - SAY_SHIELD');
INSERT INTO `creature_text` VALUES (27656, 5, 2, 'No magic of nature will help you now!', 14, 0, 100, 0, 0, 13625, 0, 31875, 0, 'Ley-Guardian Eregos - SAY_SHIELD');
INSERT INTO `creature_text` VALUES (27658, 0, 0, 'Thank you for freeing us, mortals.  Beware, the blue flight is alerted to your presence.  Even now, Malygos sends Varos Cloudstrider and his ring guardians to defend the Oculus.  You will need our help to stand a chance.', 12, 0, 100, 1, 3500, 0, 0, 26937, 0, 'Belgaristrasz - On freed');
INSERT INTO `creature_text` VALUES (27683, 0, 0, 'I\'ve lived here and kept this shrine with the blessing of the Alexstrasza for longer than I can remember.  In its darkest hour, I\'ve failed... I fell at the feet of that damned death knight...  Bloodbane.', 12, 0, 100, 0, 0, 0, 0, 26939, 0, 'Remnant of Dahlia Suntouch');
INSERT INTO `creature_text` VALUES (27683, 1, 0, 'Dahlia spits on the ground at the thought of the name.', 16, 0, 100, 0, 0, 0, 0, 26943, 0, 'Remnant of Dahlia Suntouch');
INSERT INTO `creature_text` VALUES (27683, 2, 0, 'The Lich King is stretching his hand far now... fearlessly treading on the most sacred lands of all of the flights.  He intends to force us to action.', 12, 0, 100, 0, 0, 0, 0, 26940, 0, 'Remnant of Dahlia Suntouch');
INSERT INTO `creature_text` VALUES (27683, 3, 0, 'My time is done... I offered myself to Alexstrasza in life, but I have nothing left to offer in death.  She will need a new keeper for her shrine.', 12, 0, 100, 0, 0, 0, 0, 26941, 0, 'Remnant of Dahlia Suntouch');
INSERT INTO `creature_text` VALUES (27683, 4, 0, 'And thank you... thank you for freeing me from the shackles of the damned.  You are a hero... I know you will go on to great deeds.', 12, 0, 100, 0, 0, 0, 0, 26942, 0, 'Remnant of Dahlia Suntouch');
INSERT INTO `creature_text` VALUES (27683, 5, 0, 'The Remnant of Dahlia Suntouch fades away...', 16, 0, 100, 0, 0, 0, 0, 26944, 0, 'Remnant of Dahlia Suntouch');
INSERT INTO `creature_text` VALUES (27692, 0, 0, '%s flies away.', 16, 0, 100, 1, 0, 2858, 0, 25992, 0, 'Emerald - On take off');
INSERT INTO `creature_text` VALUES (27692, 1, 0, 'Welcome Friend.  Keep your head down and hold on tight!', 15, 0, 100, 1, 0, 0, 0, 28136, 0, 'Emerald - On welcome');
INSERT INTO `creature_text` VALUES (27692, 2, 0, 'Use Leeching Poison to damage enemies and keep me healed.  Touch the Nightmare is very powerful, but it hurts me, so only use it when I have a lot of health!', 15, 0, 100, 1, 0, 0, 0, 28255, 0, 'Emerald - On explaining abilities');
INSERT INTO `creature_text` VALUES (27692, 3, 0, 'Now that I am at my full power I can perform Dream Funnel.  You can use it to heal other drakes, but it drains my health, so make sure you\'re using Leeching Poison too!', 15, 0, 100, 1, 0, 0, 0, 28259, 0, 'Emerald - On ultimate ability unlocked');
INSERT INTO `creature_text` VALUES (27692, 4, 0, 'I\'m badly injured!  I can\'t take much more of this!', 15, 0, 100, 1, 0, 0, 0, 28257, 0, 'Emerald - On below 40%');
INSERT INTO `creature_text` VALUES (27692, 5, 0, '$3486W Centrifuge Constructs remain.  We must find and destroy them to disable Varos Cloudstrider\'s protective shield.  The constructs should be about halfway up The Oculus, on the Band of Acceleration.', 15, 0, 100, 0, 0, 0, 0, 29518, 0, 'Emerald - Say gps 10 constructs');
INSERT INTO `creature_text` VALUES (27692, 6, 0, 'One Centrifuge Construct remains.  We must find and destroy it to disable Varos Cloudstrider\'s protective shield.  The construct should be about halfway up The Oculus, on the Band of Acceleration.', 15, 0, 100, 0, 0, 0, 0, 29519, 0, 'Emerald - Say gps 1 construct');
INSERT INTO `creature_text` VALUES (27692, 7, 0, 'The shield is down; Varos is vulnerable!  Defeating him should disperse the Azure Ring Guardians and allow us access to the upper bands.  Varos is on a floating platform about halfway up The Oculus, on the Band of Acceleration.', 15, 0, 100, 0, 0, 0, 0, 29520, 0, 'Emerald - Say gps Varos');
INSERT INTO `creature_text` VALUES (27692, 8, 0, 'Mage-Lord Urom is our next target.  He should be hiding on one of the floating platforms on the Band of Transmutation, near the top of The Oculus.  Be warned, Urom is a powerful conjurer; he may not be alone.', 15, 0, 100, 0, 0, 0, 0, 29521, 0, 'Emerald - Say gps Lord Urom');
INSERT INTO `creature_text` VALUES (27692, 9, 0, 'Ley-Guardian Eregos is all that now stands between us and control of The Oculus.  He\'s flying above the Band of Alignment, at the very top of The Oculus.  Eregos is incredibly powerful; stay on my back when we engage him and leave the fighting to me.', 15, 0, 100, 0, 0, 0, 0, 29522, 0, 'Emerald - Say gps Eregos');
INSERT INTO `creature_text` VALUES (27692, 10, 0, 'We\'ve wrested control of The Oculus from the forces of Malygos.  You\'re not getting any lighter, little one.  My wings itch for the open sky.', 15, 0, 100, 0, 0, 0, 0, 29523, 0, 'Emerald - Say gps End');
INSERT INTO `creature_text` VALUES (27713, 0, 0, 'What is it?', 12, 0, 100, 0, 0, 0, 0, 27143, 0, '7th Legion Elite');
INSERT INTO `creature_text` VALUES (27713, 0, 1, 'Is this the end? If we stop this bastard now, is victory assured at the Wrathgate?', 12, 0, 100, 0, 0, 0, 0, 27146, 0, '7th Legion Elite');
INSERT INTO `creature_text` VALUES (27713, 0, 2, 'Let it be known that it has been an honor and a privilege fighting alongside all of you.', 12, 0, 100, 0, 0, 0, 0, 27148, 0, '7th Legion Elite');
INSERT INTO `creature_text` VALUES (27713, 0, 3, 'Death comes for us all eventually, but sometimes it comes a little faster than anticipated. Today we call that blind heroics... Tomorrow someone may call it stupidity.', 12, 0, 100, 0, 0, 0, 0, 27150, 0, '7th Legion Elite');
INSERT INTO `creature_text` VALUES (27713, 0, 4, 'Come on... Let\'s get this over with!', 12, 0, 100, 0, 0, 0, 0, 27149, 0, '7th Legion Elite');
INSERT INTO `creature_text` VALUES (27713, 0, 5, 'It looks like a gateway to hell!', 12, 0, 100, 0, 0, 0, 0, 27144, 0, '7th Legion Elite');
INSERT INTO `creature_text` VALUES (27713, 0, 6, 'I\'m getting a little worried. Where is this hero that is supposed to lead us into battle?', 12, 0, 100, 0, 0, 0, 0, 27147, 0, '7th Legion Elite');
INSERT INTO `creature_text` VALUES (27713, 0, 7, 'If I don\'t make it back to Wintergarde, let my family know that I did my best and that I love them...', 12, 0, 100, 0, 0, 0, 0, 27145, 0, '7th Legion Elite');
INSERT INTO `creature_text` VALUES (27713, 1, 0, 'It\'s the Highlord!', 12, 0, 100, 0, 0, 0, 0, 27178, 0, '7th Legion Elite');
INSERT INTO `creature_text` VALUES (27719, 0, 0, 'Ladies and gentlemen!  Well... let\'s just say you don\'t want to miss this one!', 14, 0, 100, 0, 0, 0, 0, 27032, 0, 'Grennix Shivwiggle - Quest Accepted');
INSERT INTO `creature_text` VALUES (27726, 0, 0, 'I used good judgment when you showed none.  I don\'t want to see you lead us into ruin, Krenna!', 12, 0, 100, 0, 0, 0, 0, 27027, 0, 'Gorgonna - Say 0');
INSERT INTO `creature_text` VALUES (27726, 1, 1, 'It doesn\'t have to end this way, you know?', 12, 0, 100, 0, 0, 0, 0, 27026, 0, 'Gorgonna - Say 1');
INSERT INTO `creature_text` VALUES (27726, 2, 2, 'We\'ll see about that.', 12, 0, 100, 0, 0, 0, 0, 27028, 0, 'Gorgonna - Say 2');
INSERT INTO `creature_text` VALUES (27726, 3, 3, 'Why... why sister?  Why did it have to come to this?', 12, 0, 100, 0, 0, 0, 0, 27033, 0, 'Gorgonna - Say 3');
INSERT INTO `creature_text` VALUES (27727, 0, 0, 'You went behind my back.  You neglected my orders.  You will pay the price.', 12, 0, 100, 0, 0, 0, 0, 27030, 0, 'Conqueror Krenna - Say 0');
INSERT INTO `creature_text` VALUES (27727, 1, 1, 'Enough talk.  By the time I\'m through with you, not even the worgs are going to want to go near your corpse.', 12, 0, 100, 0, 0, 0, 0, 27031, 0, 'Conqueror Krenna - Say 1');
INSERT INTO `creature_text` VALUES (27755, 0, 0, '%s flies away.', 16, 0, 100, 1, 0, 2858, 0, 25992, 0, 'Amber - On take off');
INSERT INTO `creature_text` VALUES (27755, 1, 0, 'Welcome Friend.  Keep your head down and hold on tight!', 15, 0, 100, 1, 0, 0, 0, 28136, 0, 'Amber - On welcome');
INSERT INTO `creature_text` VALUES (27755, 2, 0, 'Use Shock Lance to damage enemies.  If we get in trouble, Stop Time to freeze all enemies in place, then hit them with Shock Lance for massive damage!', 15, 0, 100, 1, 0, 0, 0, 28256, 0, 'Amber - On explaining abilities');
INSERT INTO `creature_text` VALUES (27755, 3, 0, 'Now that I am at my full power I can perform Temporal Rift.  You can use it to make enemies take extra damage and to get Shock Charges.  Save up Shock Charges and then Shock Lance for huge damage!', 15, 0, 100, 1, 0, 0, 0, 28258, 0, 'Amber - On ultimate ability unlocked');
INSERT INTO `creature_text` VALUES (27755, 4, 0, 'I\'m badly injured!  I can\'t take much more of this!', 15, 0, 100, 1, 0, 0, 0, 28257, 0, 'Amber - On below 40%');
