INSERT INTO `creature_text` VALUES (27756, 0, 0, '%s flies away.', 16, 0, 100, 1, 0, 2858, 0, 25992, 0, 'Ruby - On take off');
INSERT INTO `creature_text` VALUES (27756, 1, 0, 'Welcome Friend.  Keep your head down and hold on tight!', 15, 0, 100, 1, 0, 0, 0, 28136, 0, 'Ruby - On welcome');
INSERT INTO `creature_text` VALUES (27756, 2, 0, 'Use Searing Wrath to damage enemies and Evasive Maneuvers if I start taking damage.  Remember I need to build up Evasive Charges by taking damage to perform Evasive Maneuvers!', 15, 0, 100, 1, 0, 0, 0, 28254, 0, 'Ruby - On explaining abilities');
INSERT INTO `creature_text` VALUES (27756, 3, 0, 'Now that I am at my full power I can perform Martyr.  You can use it to protect other drakes, but I will take lots of damage, so make sure you\'re using Evasive Maneuvers too!', 15, 0, 100, 1, 0, 0, 0, 28260, 0, 'Ruby - On ultimate ability unlocked');
INSERT INTO `creature_text` VALUES (27756, 4, 0, 'I\'m badly injured!  I can\'t take much more of this!', 15, 0, 100, 1, 0, 0, 0, 28257, 0, 'Ruby - On below 40%');
INSERT INTO `creature_text` VALUES (27756, 5, 0, '$3486W Centrifuge Constructs remain.  We must find and destroy them to disable Varos Cloudstrider\'s protective shield.  The constructs should be about halfway up The Oculus, on the Band of Acceleration.', 15, 0, 100, 0, 0, 0, 0, 29518, 0, 'Ruby - Say gps 10 constructs');
