INSERT INTO `creature_text` VALUES (27923, 0, 0, 'Hang on to your hat, $n! To Scalawag we go!', 12, 0, 100, 0, 0, 0, 0, 27272, 0, 'Lou the Cabin Boy');
INSERT INTO `creature_text` VALUES (27923, 1, 0, 'YAAARRRRR! Here we be, matey! Scalawag Point!', 12, 0, 100, 0, 0, 0, 0, 27273, 0, 'Lou the Cabin Boy');
INSERT INTO `creature_text` VALUES (27928, 0, 0, 'You can leave my sight, squire, before I slay you. Again.', 12, 0, 100, 0, 0, 0, 0, 28104, 0, 'Siouxsie the Banshee');
INSERT INTO `creature_text` VALUES (27928, 0, 1, 'No. Away with you, worm.', 12, 0, 100, 0, 0, 0, 0, 28105, 0, 'Siouxsie the Banshee');
INSERT INTO `creature_text` VALUES (27928, 0, 2, 'Get away from me, Edwards, lest I behead you.', 12, 0, 100, 0, 0, 0, 0, 28102, 0, 'Siouxsie the Banshee');
INSERT INTO `creature_text` VALUES (27933, 0, 0, 'Enjoy the ride! It\'s a one way trip!', 12, 0, 100, 3, 0, 0, 0, 27337, 0, 'Alanya');
INSERT INTO `creature_text` VALUES (27951, 0, 0, 'Kill them Abbendis! Do not fail me!', 14, 0, 100, 25, 0, 14198, 0, 27354, 0, 'Admiral Barean Westwind');
INSERT INTO `creature_text` VALUES (27951, 1, 0, 'The grand admiral studies you a moment through narrowed eyes.', 16, 0, 100, 0, 0, 0, 0, 27355, 0, 'Admiral Barean Westwind');
INSERT INTO `creature_text` VALUES (27951, 2, 0, 'You served your purpose.', 12, 0, 100, 0, 0, 14199, 0, 27357, 0, 'Admiral Barean Westwind');
INSERT INTO `creature_text` VALUES (27951, 3, 0, 'I will see you in Icecrown, weakling... if you live that long!', 12, 0, 100, 0, 0, 14200, 0, 27358, 0, 'Admiral Barean Westwind');
INSERT INTO `creature_text` VALUES (27959, 0, 0, 'You think that you can get rid of me through meditation?', 15, 0, 100, 0, 0, 0, 0, 27368, 0, 'Your Inner Turmoil text 0');
INSERT INTO `creature_text` VALUES (27959, 1, 0, 'Fool! I will destroy you and finally become that which has been building inside of you all of these years!', 15, 0, 100, 0, 0, 0, 0, 27369, 0, 'Your Inner Turmoil text 1');
INSERT INTO `creature_text` VALUES (27959, 2, 0, 'You cannot defeat me. I\'m an inseparable part of you!', 15, 0, 100, 0, 0, 0, 0, 27370, 0, 'Your Inner Turmoil text 50%');
INSERT INTO `creature_text` VALUES (27959, 3, 0, 'NOOOOOOOoooooooooo!', 15, 0, 100, 0, 0, 0, 0, 27371, 0, 'Your Inner Turmoil text death');
INSERT INTO `creature_text` VALUES (27975, 0, 0, 'You shouldn\'t have come... now you will die!', 14, 0, 100, 0, 0, 13487, 0, 29563, 0, 'maiden of grief SAY_AGGRO');
INSERT INTO `creature_text` VALUES (27975, 1, 0, 'Why must it be this way?', 14, 0, 100, 0, 0, 13488, 0, 29564, 0, 'maiden of grief SAY_SLAY_1');
INSERT INTO `creature_text` VALUES (27975, 1, 1, 'You had it coming!', 14, 0, 100, 0, 0, 13489, 0, 29566, 0, 'maiden of grief SAY_SLAY_2');
INSERT INTO `creature_text` VALUES (27975, 1, 2, 'My burden grows heavier...', 14, 0, 100, 0, 0, 13490, 0, 29567, 0, 'maiden of grief SAY_SLAY_3');
INSERT INTO `creature_text` VALUES (27975, 1, 3, 'This is your fault!', 14, 0, 100, 0, 0, 13491, 0, 29568, 0, 'maiden of grief SAY_SLAY_4');
INSERT INTO `creature_text` VALUES (27975, 2, 0, 'I hope you all rot! I never... wanted... this.', 14, 0, 100, 0, 0, 13493, 0, 29574, 0, 'maiden of grief SAY_DEATH');
INSERT INTO `creature_text` VALUES (27975, 3, 0, 'So much lost time... that you\'ll never get back!', 14, 0, 100, 0, 0, 13492, 0, 29570, 0, 'maiden of grief SAY_STUN');
INSERT INTO `creature_text` VALUES (27977, 0, 0, 'Crush....', 14, 0, 100, 0, 0, 14176, 0, 29556, 0, 'krystallus SAY_AGGRO');
INSERT INTO `creature_text` VALUES (27977, 1, 0, 'Ha.... ha... hah....', 14, 0, 100, 0, 0, 14177, 0, 29558, 0, 'krystallus SAY_KILL');
INSERT INTO `creature_text` VALUES (27977, 2, 0, 'Uuuuhhhhhhhhhh......', 14, 0, 100, 0, 0, 14179, 0, 29560, 0, 'krystallus SAY_DEATH');
INSERT INTO `creature_text` VALUES (27977, 3, 0, 'Break.... you....', 14, 0, 100, 0, 0, 14178, 0, 29559, 0, 'krystallus SAY_SHATTER');
INSERT INTO `creature_text` VALUES (27978, 0, 0, 'Soft, vulnerable shells. Brief fragile lives. You cannot escape the curse of flesh!', 14, 0, 100, 0, 0, 14180, 0, 29582, 0, 'Sjonnir SAY_AGGRO');
INSERT INTO `creature_text` VALUES (27978, 1, 0, 'Flesh is no match for iron!', 14, 0, 100, 0, 0, 14182, 0, 29584, 0, 'Sjonnir SAY_SLAY_1');
INSERT INTO `creature_text` VALUES (27978, 1, 1, 'Armies of iron will smother the world!', 14, 0, 100, 0, 0, 14183, 0, 29585, 0, 'Sjonnir SAY_SLAY_2');
INSERT INTO `creature_text` VALUES (27978, 1, 2, 'Folvynn buul hrom onn!', 12, 0, 100, 0, 0, 0, 0, 29583, 0, 'Sjonnir SAY_SLAY_3');
INSERT INTO `creature_text` VALUES (27978, 2, 0, 'Loken will not rest... until the forge is retaken! You change... nothing!', 14, 0, 100, 0, 0, 14184, 0, 29586, 0, 'Sjonnir SAY_DEATH');
INSERT INTO `creature_text` VALUES (27986, 0, 0, 'Aye, I\'ll try it.', 12, 0, 100, 273, 0, 0, 0, 28549, 0, 'Hemet Nesingwary');
INSERT INTO `creature_text` VALUES (27986, 1, 0, 'That\'s exactly what I needed!', 12, 0, 100, 5, 0, 0, 0, 28550, 0, 'Hemet Nesingwary');
INSERT INTO `creature_text` VALUES (27986, 2, 0, 'It\'s got my vote! That\'ll put hair on your chest like nothing else will.', 12, 0, 100, 1, 0, 0, 0, 28551, 0, 'Hemet Nesingwary');
INSERT INTO `creature_text` VALUES (27986, 3, 0, 'What a fight you missed, Dorian! It was one for the ages!', 12, 0, 100, 5, 0, 0, 0, 28026, 0, 'Hemet Nesingwary');
INSERT INTO `creature_text` VALUES (27986, 4, 0, 'There we were, riding into battle at full speed on the back of Stampy...', 12, 0, 100, 5, 0, 0, 0, 28027, 0, 'Hemet Nesingwary');
INSERT INTO `creature_text` VALUES (27986, 5, 0, 'Well, lad, you can start by readin\' The Green Hills of Stranglethorn. Then, maybe you can join me on one of my safaris. What do you say?', 12, 0, 100, 0, 0, 0, 0, 34687, 0, 'Hemet Nesingwary');
INSERT INTO `creature_text` VALUES (27987, 0, 0, 'Over here, to the southeast. I see they got you too...', 14, 0, 100, 0, 0, 0, 0, 27610, 0, 'Monte Muzzleshot');
INSERT INTO `creature_text` VALUES (27990, 0, 0, 'You\'re too late, $n. Another visitor from Dalaran came asking after information about the same prismatic dragon blades.', 12, 0, 100, 1, 0, 0, 0, 37769, 0, 'Krasus');
INSERT INTO `creature_text` VALUES (27990, 1, 0, 'From your description, I\'m certain that the book I loaned our visitor could allow you to easily identify the weapon.', 12, 0, 100, 25, 0, 0, 0, 37770, 0, 'Krasus');
INSERT INTO `creature_text` VALUES (27990, 2, 0, 'I\'m afraid you\'ll have to ask the -- Well, perhaps Kalecgos can help.', 12, 0, 100, 1, 0, 0, 0, 37772, 0, 'Krasus');
INSERT INTO `creature_text` VALUES (27990, 3, 0, '$n may have found the remains of a prismatic blade, Kalecgos. Will you offer your help to our visitor?', 12, 0, 100, 1, 0, 0, 0, 37773, 0, 'Krasus');
INSERT INTO `creature_text` VALUES (27990, 4, 0, 'You believe our allies will not be able to control the power of the swords?', 12, 0, 100, 1, 0, 0, 0, 37778, 0, 'Krasus');
INSERT INTO `creature_text` VALUES (27990, 5, 0, 'As will we all.', 12, 0, 100, 25, 0, 0, 0, 37785, 0, 'Krasus');
INSERT INTO `creature_text` VALUES (27990, 6, 0, 'Please, mortal, speak with Arcanist Tybalin in Dalaran. He may be able to negotiate with the Sunreavers for access to the book.', 12, 0, 100, 1, 0, 0, 0, 37873, 0, 'Krasus');
INSERT INTO `creature_text` VALUES (27990, 7, 0, 'Please, mortal, seek out Magister Hathorel in Dalaran. He might be able to negotiate with the Silver Covenant for access to the book.', 12, 0, 100, 1, 0, 0, 0, 37874, 0, 'Krasus');
INSERT INTO `creature_text` VALUES (27990, 8, 0, 'Rest assured, Kekek, she is the queen of the dragons. We often take the forms of smaller beings when we work with them.', 12, 0, 0, 0, 0, 0, 0, 34665, 0, '');
INSERT INTO `creature_text` VALUES (27996, 0, 1, 'Across this vast expanse rest the bones of my ancestors. Their spirits have ascended to the Chamber of the Aspects.', 12, 0, 100, 0, 0, 0, 0, 27394, 0, 'WYRMREST VANQUISHER');
INSERT INTO `creature_text` VALUES (27996, 0, 2, 'Galakrond was the progenitor of dragonkind. It was from Galakrond that the Titans shaped the five Aspects.', 12, 0, 100, 0, 0, 0, 0, 27396, 0, 'WYRMREST VANQUISHER');
INSERT INTO `creature_text` VALUES (27996, 0, 3, 'It truly is a shame that the blue dragonflight has chosen such a heartless, cold path. It pains me greatly to do battle with Malygos\'s children.', 12, 0, 100, 0, 0, 0, 0, 27400, 0, 'WYRMREST VANQUISHER');
INSERT INTO `creature_text` VALUES (27996, 0, 4, 'The queen and her council have been speaking lately of a disaster at Ulduar. Something has happened in Storm Peaks!', 12, 0, 100, 0, 0, 0, 0, 27399, 0, 'WYRMREST VANQUISHER');
INSERT INTO `creature_text` VALUES (27996, 0, 5, 'Today is a good day, friend. Long has it been since I have left Wyrmrest Temple, free to fly across the great Dragonblight.', 12, 0, 100, 0, 0, 0, 0, 27398, 0, 'WYRMREST VANQUISHER');
INSERT INTO `creature_text` VALUES (27996, 0, 6, 'What the Scourge has done here will reverberate throughout our world. The aberrations must be stopped!', 12, 0, 100, 0, 0, 0, 0, 27395, 0, 'WYRMREST VANQUISHER');
INSERT INTO `creature_text` VALUES (27996, 1, 0, 'You must remember that I have yet to mature into an elder wyrm. I must eat to renew my strength!', 12, 0, 100, 0, 0, 0, 0, 27397, 0, 'WYRMREST VANQUISHER');
INSERT INTO `creature_text` VALUES (27996, 2, 0, 'I have been called back to Wyrmrest, ally. Farewell!', 12, 0, 100, 0, 0, 0, 0, 27404, 0, 'WYRMREST VANQUISHER');
INSERT INTO `creature_text` VALUES (28006, 0, 0, 'You think you\'ve won, mortal? Face the unbridled power of Antiok!', 14, 0, 100, 0, 0, 0, 0, 27415, 0, 'Antiok Yell1');
INSERT INTO `creature_text` VALUES (28006, 0, 1, 'Behold! The Scythe of Antiok!', 14, 0, 100, 0, 0, 0, 0, 27416, 0, 'Antiok Yell2');
INSERT INTO `creature_text` VALUES (28006, 1, 0, 'Soon, the bones of Galakrond will rise from their eternal slumber and wreak havoc upon this world!', 14, 0, 100, 0, 0, 0, 0, 27406, 0, 'Antiok Yell3');
INSERT INTO `creature_text` VALUES (28006, 1, 1, 'The Lich King demands more frost wyrms be sent to Angrathar! Meet his demands or face my wrath!', 14, 0, 100, 0, 0, 0, 0, 27408, 0, 'Antiok Yell4');
INSERT INTO `creature_text` VALUES (28006, 1, 2, 'Faster, dogs! We mustn\'t relent in our assault against the interlopers!', 14, 0, 100, 0, 0, 0, 0, 27405, 0, 'Antiok Yell5');
INSERT INTO `creature_text` VALUES (28006, 1, 3, 'Attackers are upon us! Let none through to this ancient grave!', 14, 0, 100, 0, 0, 0, 0, 27409, 0, 'Antiok Yell6');
INSERT INTO `creature_text` VALUES (28006, 1, 4, 'Hear me, minions! Hear your lord, Antiok! Double your efforts or pay the consequences of failure!', 14, 0, 100, 0, 0, 0, 0, 27407, 0, 'Antiok Yell7');
INSERT INTO `creature_text` VALUES (28012, 0, 0, 'The trickster Mage-Lord Urom protects the third ring.  He will appear alone and defenseless, but do not be fooled by appearances!  Urom is a powerful conjurer who commands a menagerie of Phantasmal creatures.  Seek him out above.', 12, 0, 0, 0, 0, 0, 0, 28079, 0, '');
INSERT INTO `creature_text` VALUES (28012, 1, 1, 'Your greatest challenge lies ahead.  Ley-Guardian Eregos is a Blue dragon of immense power. You will find him flying above the uppermost ring.', 12, 0, 0, 0, 0, 0, 0, 27389, 0, '');
INSERT INTO `creature_text` VALUES (28016, 0, 0, 'Ahh,  Back in Drak\'Tharon at last!', 12, 0, 100, 71, 0, 14006, 0, 27933, 0, 'Drakuru');
INSERT INTO `creature_text` VALUES (28016, 1, 0, 'Drakuru casts his gaze down on Drak\'Tharon Keep.', 16, 0, 100, 0, 0, 0, 0, 27934, 0, 'Drakuru');
INSERT INTO `creature_text` VALUES (28016, 2, 0, 'Ya done well, mon....', 12, 0, 100, 0, 0, 14007, 0, 27935, 0, 'Drakuru');
INSERT INTO `creature_text` VALUES (28016, 3, 0, 'Ya surpassed my greatest hopes.', 12, 0, 100, 0, 0, 14008, 0, 27954, 0, 'Drakuru');
INSERT INTO `creature_text` VALUES (28016, 4, 0, 'For your efforts, you be gettin\' da greatest of rewards....', 12, 0, 100, 0, 0, 14009, 0, 28118, 0, 'Drakuru');
INSERT INTO `creature_text` VALUES (28016, 5, 0, 'Revelation!', 12, 0, 100, 5, 0, 14010, 0, 28119, 0, 'Drakuru');
INSERT INTO `creature_text` VALUES (28016, 6, 0, 'Be still, friend, and behold da truth of things!', 12, 0, 100, 0, 0, 14011, 0, 28120, 0, 'Drakuru');
INSERT INTO `creature_text` VALUES (28016, 7, 0, 'Master, my mission be complete.', 12, 0, 100, 0, 0, 14012, 0, 28125, 0, 'Drakuru');
INSERT INTO `creature_text` VALUES (28016, 8, 0, 'With da help of these mortals, dis region been cleansed of all who oppose us.', 12, 0, 100, 0, 0, 14013, 0, 28127, 0, 'Drakuru');
INSERT INTO `creature_text` VALUES (28016, 9, 0, 'As you wish, master.', 12, 0, 100, 396, 0, 14014, 0, 28419, 0, 'Drakuru');
INSERT INTO `creature_text` VALUES (28016, 10, 0, 'Forgive my deception, mon. It all been for your own good.', 12, 0, 100, 396, 0, 14015, 0, 28420, 0, 'Drakuru');
INSERT INTO `creature_text` VALUES (28016, 11, 0, 'Ya needed ta prove yer worthiness....', 12, 0, 100, 396, 0, 14016, 0, 28421, 0, 'Drakuru');
INSERT INTO `creature_text` VALUES (28016, 12, 0, 'I be needin\' worthy underlings in Zul\'Drak, mon. Ones ready to wield real power.', 12, 0, 100, 396, 0, 14017, 0, 28422, 0, 'Drakuru');
INSERT INTO `creature_text` VALUES (28016, 13, 0, 'You see for yourself now. Da Lich King be generous to dem who obey.', 12, 0, 100, 396, 0, 14018, 0, 28423, 0, 'Drakuru');
INSERT INTO `creature_text` VALUES (28016, 14, 0, 'Follow dis path, mon, and soon you be tastin\' immortality!', 12, 0, 100, 396, 0, 14019, 0, 28424, 0, 'Drakuru');
INSERT INTO `creature_text` VALUES (28030, 0, 0, 'There is no escape. There is only my wrath!', 14, 0, 100, 0, 0, 0, 0, 29429, 0, 'Quetz\'lun');
INSERT INTO `creature_text` VALUES (28030, 0, 1, 'Eternal damnation is your future.', 14, 0, 100, 0, 0, 0, 0, 29427, 0, 'Quetz\'lun');
INSERT INTO `creature_text` VALUES (28030, 0, 2, 'My judgment is final.', 14, 0, 100, 0, 0, 0, 0, 29430, 0, 'Quetz\'lun');
INSERT INTO `creature_text` VALUES (28030, 0, 3, 'Beg for your life. Plead. It will do you no good!', 14, 0, 100, 0, 0, 0, 0, 29426, 0, 'Quetz\'lun');
INSERT INTO `creature_text` VALUES (28030, 0, 4, 'Burn in the fires of my fury!', 14, 0, 100, 0, 0, 0, 0, 29431, 0, 'Quetz\'lun');
INSERT INTO `creature_text` VALUES (28030, 0, 5, 'Die!', 14, 0, 100, 0, 0, 0, 0, 28793, 0, 'Quetz\'lun');
INSERT INTO `creature_text` VALUES (28030, 0, 6, 'You killed me and now I will kill you... over and over!', 14, 0, 100, 0, 0, 0, 0, 29428, 0, 'Quetz\'lun');
INSERT INTO `creature_text` VALUES (28030, 0, 7, 'I find you wanting.', 14, 0, 100, 0, 0, 0, 0, 29424, 0, 'Quetz\'lun');
INSERT INTO `creature_text` VALUES (28033, 0, 0, 'Finally, we have the parts to repair the Spirit of Gnomeregan.', 12, 0, 100, 1, 0, 0, 0, 29878, 0, 'Weslex Quickwrench');
INSERT INTO `creature_text` VALUES (28033, 1, 0, 'Let\'s see, just a tweak here and an adjustment there...', 12, 0, 100, 432, 0, 0, 0, 29879, 0, 'Weslex Quickwrench');
INSERT INTO `creature_text` VALUES (28033, 2, 0, 'And there she is, the Spirit of Gnomeregan!', 12, 0, 100, 25, 0, 0, 0, 29880, 0, 'Weslex Quickwrench');
INSERT INTO `creature_text` VALUES (28033, 3, 0, 'You can use her, but I don\'t want to hear any complaints about saw blades in your seat. I had to fix \'er with what we could find.', 12, 0, 100, 1, 0, 0, 0, 29881, 0, 'Weslex Quickwrench');
INSERT INTO `creature_text` VALUES (28041, 0, 0, 'Careful here, $c. These trolls killed their own snake god!', 15, 0, 50, 0, 0, 0, 0, 27449, 0, 'Argent Soldier whisper text');
INSERT INTO `creature_text` VALUES (28041, 0, 1, 'Watch your back. These Drakkari are a nasty lot.', 15, 0, 50, 0, 0, 0, 0, 27448, 0, 'Argent Soldier whisper text');
INSERT INTO `creature_text` VALUES (28041, 0, 2, 'These Drakkari are just bad news. We need to leave and head to Justice Keep!', 15, 0, 50, 0, 0, 0, 0, 27452, 0, 'Argent Soldier whisper text');
INSERT INTO `creature_text` VALUES (28041, 0, 3, 'See you around.', 15, 0, 50, 0, 0, 0, 0, 27447, 0, 'Argent Soldier whisper text');
INSERT INTO `creature_text` VALUES (28041, 0, 4, 'I wonder where we\'re headed to. And who\'s going to deal with these guys?', 15, 0, 50, 0, 0, 0, 0, 27451, 0, 'Argent Soldier whisper text');
INSERT INTO `creature_text` VALUES (28041, 0, 5, 'Right. I\'d better get back to the sergeant then.', 15, 0, 50, 0, 0, 0, 0, 27446, 0, 'Argent Soldier whisper text');
INSERT INTO `creature_text` VALUES (28041, 0, 6, '$n? I heard you were dead.', 15, 0, 50, 0, 0, 0, 0, 35005, 0, 'Argent Soldier whisper text');
INSERT INTO `creature_text` VALUES (28047, 0, 0, 'I\'m always up for something of Grimbooze\'s.', 12, 0, 100, 273, 0, 0, 0, 28554, 0, 'Hadrius Harlowe');
INSERT INTO `creature_text` VALUES (28047, 1, 0, 'Well, so far, it tastes like something my wife would drink...', 12, 0, 100, 5, 0, 0, 0, 28556, 0, 'Hadrius Harlowe');
INSERT INTO `creature_text` VALUES (28047, 2, 0, 'Now, there\'s the kick I\'ve come to expect from Grimbooze\'s drinks! I like it!', 12, 0, 100, 5, 0, 0, 0, 28557, 0, 'Hadrius Harlowe');
INSERT INTO `creature_text` VALUES (28054, 0, 0, 'Not the \'stache! Now I\'m asymmetrical!', 12, 0, 100, 5, 0, 0, 0, 27755, 0, 'Lucky Wilhelm');
INSERT INTO `creature_text` VALUES (28054, 0, 1, 'Ouch! That\'s it, I quit the target business!', 12, 0, 100, 0, 0, 0, 0, 27757, 0, 'Lucky Wilhelm');
INSERT INTO `creature_text` VALUES (28054, 0, 2, 'My ear! You grazed my ear!', 12, 0, 100, 0, 0, 0, 0, 27756, 0, 'Lucky Wilhelm');
INSERT INTO `creature_text` VALUES (28054, 1, 0, 'Good shot!', 12, 0, 100, 4, 0, 0, 0, 27778, 0, 'Lucky Wilhelm');
INSERT INTO `creature_text` VALUES (28068, 0, 0, 'Mmm, more mojo.', 14, 0, 100, 0, 0, 0, 0, 27531, 0, 'Prophet of Sseratus');
INSERT INTO `creature_text` VALUES (28068, 1, 0, 'NOOOOOOOOooooooo.........!', 14, 0, 100, 0, 0, 0, 0, 27532, 0, 'Prophet of Sseratus');
INSERT INTO `creature_text` VALUES (28068, 2, 0, 'They dare attack us? The fools! Do they not know we are now all-powerful?!', 14, 0, 100, 0, 0, 0, 0, 27509, 0, 'Prophet of Sseratus');
INSERT INTO `creature_text` VALUES (28068, 2, 1, 'With the power of the wild gods burning in our veins, none, not even the Lich King, can stand in our way!', 14, 0, 100, 0, 0, 0, 0, 27512, 0, 'Prophet of Sseratus');
INSERT INTO `creature_text` VALUES (28068, 2, 2, 'I see you, insect! Come closer.', 14, 0, 100, 0, 0, 0, 0, 27513, 0, 'Prophet of Sseratus');
INSERT INTO `creature_text` VALUES (28068, 2, 3, 'Kill these invaders who dare to defile our altar!', 14, 0, 100, 0, 0, 0, 0, 27510, 0, 'Prophet of Sseratus');
INSERT INTO `creature_text` VALUES (28068, 2, 4, 'I will destroy the Argent Crusade! I will crush the Scourge!', 14, 0, 100, 0, 0, 0, 0, 27514, 0, 'Prophet of Sseratus');
INSERT INTO `creature_text` VALUES (28068, 2, 5, 'Finish them and then we will move south to kill their brethren!', 14, 0, 100, 0, 0, 0, 0, 27511, 0, 'Prophet of Sseratus');
INSERT INTO `creature_text` VALUES (28068, 2, 6, 'The mojo of Sseratus is ours. Now all will be crushed under the might of the Drakkari!', 14, 0, 100, 0, 0, 0, 0, 27508, 0, 'Prophet of Sseratus');
INSERT INTO `creature_text` VALUES (28068, 2, 7, 'Drink deep my brothers!', 14, 0, 100, 0, 0, 0, 0, 27507, 0, 'Prophet of Sseratus');
INSERT INTO `creature_text` VALUES (28070, 0, 0, 'Now that\'s earnin\' your supper!', 14, 0, 100, 0, 0, 14244, 0, 30525, 0, 'brann SAY_KILL_1');
INSERT INTO `creature_text` VALUES (28070, 0, 1, 'Press on; that\'s the way!', 14, 0, 100, 0, 0, 14245, 0, 30526, 0, 'brann SAY_KILL_2');
INSERT INTO `creature_text` VALUES (28070, 0, 2, 'Keep it up now. Plenty of death-dealing for everyone!', 14, 0, 100, 0, 0, 14246, 0, 0, 0, 'brann SAY_KILL_3');
INSERT INTO `creature_text` VALUES (28070, 1, 0, 'I\'m all kinds of busted up... might not... make it.', 14, 0, 100, 0, 0, 14257, 0, 28121, 0, 'brann SAY_LOW_HEALTH');
INSERT INTO `creature_text` VALUES (28070, 2, 0, 'Not yet... not ye--', 14, 0, 100, 0, 0, 14258, 0, 30522, 0, 'brann SAY_DEATH');
INSERT INTO `creature_text` VALUES (28070, 3, 0, 'I\'m doin\' everything I can!', 14, 0, 100, 0, 0, 14260, 0, 30562, 0, 'brann SAY_PLAYER_DEATH_1');
INSERT INTO `creature_text` VALUES (28070, 3, 1, 'Light preserve ye!', 14, 0, 100, 0, 0, 14261, 0, 30563, 0, 'brann SAY_PLAYER_DEATH_2');
INSERT INTO `creature_text` VALUES (28070, 3, 2, 'I hope this is all worth it!', 14, 0, 100, 0, 0, 14262, 0, 30564, 0, 'brann SAY_PLAYER_DEATH_3');
INSERT INTO `creature_text` VALUES (28070, 4, 0, 'Time to get some answers! Let\'s get this show on the road!', 14, 0, 100, 0, 0, 14259, 0, 30561, 0, 'brann SAY_ESCORT_START');
INSERT INTO `creature_text` VALUES (28070, 5, 0, 'Don\'t worry! Ol\' Brann\'s got yer back! Keep that metal monstrosity busy, and I\'ll see if I can\'t sweet talk this machine into helping ye!', 14, 0, 100, 0, 0, 14274, 0, 31555, 0, 'brann SAY_SPAWN_DWARF');
INSERT INTO `creature_text` VALUES (28070, 6, 0, 'This is a wee bit trickier than before... Oh, bloody--incomin\'!', 14, 0, 100, 0, 0, 14275, 0, 27589, 0, 'brann SAY_SPAWN_TROGG');
INSERT INTO `creature_text` VALUES (28070, 7, 0, 'What in the name o\' Madoran did THAT do? Oh! Wait: I just about got it...', 14, 0, 100, 0, 0, 14276, 0, 27590, 0, 'brann SAY_SPAWN_OOZE');
INSERT INTO `creature_text` VALUES (28070, 8, 0, 'Ha, that did it! Help\'s a-comin\'! Take this, ya glowin\' iron brute!', 14, 0, 100, 0, 0, 14277, 0, 27591, 0, 'brann SAY_SPAWN_EARTHEN');
INSERT INTO `creature_text` VALUES (28070, 9, 0, 'Take a moment and relish this with me. Soon... all will be revealed. Okay then, let\'s do this!', 14, 0, 100, 0, 0, 14247, 0, 30523, 0, 'brann SAY_EVENT_INTRO_1');
INSERT INTO `creature_text` VALUES (28070, 10, 0, 'Now keep an eye out! I\'ll have this licked in two shakes of a--', 14, 0, 100, 0, 0, 14248, 0, 30552, 0, 'brann SAY_EVENT_INTRO_2');
INSERT INTO `creature_text` VALUES (28070, 11, 0, 'Oh, that doesn\'t sound good. We might have a complication or two...', 14, 0, 100, 0, 0, 14249, 0, 30553, 0, 'brann SAY_EVENT_A_1');
INSERT INTO `creature_text` VALUES (28070, 12, 0, 'Ah, you want to play hardball, eh? That\'s just my game!', 14, 0, 100, 0, 0, 14250, 0, 30554, 0, 'brann SAY_EVENT_A_3');
INSERT INTO `creature_text` VALUES (28070, 13, 0, 'Couple more minutes and I\'ll--', 14, 0, 100, 0, 0, 14251, 0, 30555, 0, 'brann SAY_EVENT_B_1');
INSERT INTO `creature_text` VALUES (28070, 14, 0, 'Heightened? What\'s the good news?', 14, 0, 100, 0, 0, 14252, 0, 30556, 0, 'brann SAY_EVENT_B_3');
INSERT INTO `creature_text` VALUES (28070, 15, 0, 'So that was the problem? Now I\'m makin\' progress...', 14, 0, 100, 0, 0, 14253, 0, 30557, 0, 'brann SAY_EVENT_C_1');
INSERT INTO `creature_text` VALUES (28070, 16, 0, 'Hang on! Nobody\'s gonna\' be sanitized as long as I have a say in it!', 14, 0, 100, 0, 0, 14254, 0, 30558, 0, 'brann SAY_EVENT_C_3');
INSERT INTO `creature_text` VALUES (28070, 17, 0, 'Ha! The old magic fingers finally won through! Now let\'s get down to--', 14, 0, 100, 0, 0, 14255, 0, 30559, 0, 'brann SAY_EVENT_D_1');
INSERT INTO `creature_text` VALUES (28070, 18, 0, 'Purge? No no no no no.. where did I-- Aha, this should do the trick...', 14, 0, 100, 0, 0, 14256, 0, 30560, 0, 'brann SAY_EVENT_D_3');
INSERT INTO `creature_text` VALUES (28070, 19, 0, 'Query? What do you think I\'m here for, tea and biscuits? Spill the beans already!', 14, 0, 100, 0, 0, 14263, 0, 30565, 0, 'brann SAY_EVENT_END_01');
INSERT INTO `creature_text` VALUES (28070, 20, 0, 'Tell me how the dwarves came to be, and start at the beginning!', 14, 0, 100, 0, 0, 14264, 0, 30566, 0, 'brann SAY_EVENT_END_02');
INSERT INTO `creature_text` VALUES (28070, 21, 0, 'Right, right... I know the earthen were made from stone to shape the deep regions o\' the world. But what about the anomalies? Matrix non-stabilizin\' and what-not?', 14, 0, 100, 0, 0, 14265, 0, 30567, 0, 'brann SAY_EVENT_END_04');
INSERT INTO `creature_text` VALUES (28070, 22, 0, 'Necrowhatinthe-- Speak bloody Common, will ye?', 14, 0, 100, 0, 0, 14266, 0, 30568, 0, 'brann SAY_EVENT_END_06');
INSERT INTO `creature_text` VALUES (28070, 23, 0, 'Old Gods, huh? So they zapped the earthen with this Curse of Flesh... and then what?', 14, 0, 100, 0, 0, 14267, 0, 30577, 0, 'brann SAY_EVENT_END_08');
INSERT INTO `creature_text` VALUES (28070, 24, 0, 'If they killed the Old Gods, Azeroth would\'ve been destroyed...', 14, 0, 100, 0, 0, 14268, 0, 30578, 0, 'brann SAY_EVENT_END_10');
INSERT INTO `creature_text` VALUES (28070, 25, 0, 'What protectors?', 14, 0, 100, 0, 0, 14269, 0, 30579, 0, 'brann SAY_EVENT_END_12');
INSERT INTO `creature_text` VALUES (28070, 26, 0, 'Aesir and Vanir... Okay, so the Forge o\' Wills started makin\' new earthen... but what happened to the old ones?', 14, 0, 100, 0, 0, 14270, 0, 30580, 0, 'brann SAY_EVENT_END_14');
INSERT INTO `creature_text` VALUES (28070, 27, 0, 'Hold everything! The Aesir and Vanir went to war? Why?', 14, 0, 100, 0, 0, 14271, 0, 30581, 0, 'brann SAY_EVENT_END_16');
INSERT INTO `creature_text` VALUES (28070, 28, 0, 'This \"Loken\" sounds like a nasty character. Glad we don\'t have to worry about the likes o\' him anymore. So... if I\'m understandin\' ye right, the original earthen eventually woke up from this stasis, and by that time the destabili-whatever had turned \'em into proper dwarves. Or at least... dwarf ancestors.', 14, 0, 100, 0, 0, 14272, 0, 30582, 0, 'brann SAY_EVENT_END_18');
INSERT INTO `creature_text` VALUES (28070, 29, 0, 'Well, now... that\'s a lot to digest. I\'m gonna need some time to take all this in. Thank ye.', 14, 0, 100, 0, 0, 14273, 0, 30583, 0, 'brann SAY_EVENT_END_20');
INSERT INTO `creature_text` VALUES (28070, 30, 0, 'Loken?! That\'s downright bothersome... We might\'ve neutralized the iron dwarves, but I\'d lay odds there\'s another machine somewhere else churnin\' out a whole mess o\' these iron vrykul!', 14, 0, 100, 0, 0, 14278, 0, 28591, 0, 'brann SAY_VICTORY_SJONNIR_1');
INSERT INTO `creature_text` VALUES (28070, 31, 0, 'I\'ll use the forge to make batches o\' earthen to stand guard... But our greatest challenge still remains: find and stop Loken!', 14, 0, 100, 0, 0, 14279, 0, 30584, 0, 'brann SAY_VICTORY_SJONNIR_2');
INSERT INTO `creature_text` VALUES (28070, 32, 0, 'I think it\'s time to see what\'s behind the door near the entrance. I\'m going to sneak over there, nice and quiet. Meet me at the door and I\'ll get us in.', 14, 0, 100, 0, 0, 0, 0, 31660, 0, 'brann SAY_ENTRANCE_MEET');
INSERT INTO `creature_text` VALUES (28076, 0, 0, '%s goes into a frenzy!', 16, 0, 100, 0, 0, 0, 0, 38630, 0, 'Frenzyheart Berserker');
INSERT INTO `creature_text` VALUES (28077, 0, 0, 'Dumb big-tongue lover! You not friend of Frenzyheart no more. Frenzyheart will get you good.', 14, 0, 100, 0, 0, 0, 0, 27798, 0, 'Frenzyheart Tracker');
INSERT INTO `creature_text` VALUES (28078, 0, 0, '%s goes into a frenzy!', 16, 0, 100, 0, 0, 0, 0, 38630, 0, 'Frenzyheart Ravager');
INSERT INTO `creature_text` VALUES (28090, 0, 0, 'We\'ll cleanse this place! Arthas beware!', 12, 0, 100, 0, 0, 0, 0, 27500, 0, 'crusade recruit SAY_RECRUIT_1');
INSERT INTO `creature_text` VALUES (28090, 0, 1, 'You\'re right! We can do this!', 12, 0, 100, 0, 0, 0, 0, 27497, 0, 'crusade recruit SAY_RECRUIT_2');
INSERT INTO `creature_text` VALUES (28090, 0, 2, 'You\'re right! What was I thinking? Bring on the Scourge!', 12, 0, 100, 0, 0, 0, 0, 27496, 0, 'crusade recruit SAY_RECRUIT_3');
INSERT INTO `creature_text` VALUES (28092, 0, 0, 'Omega Rune deployed.  Activation successful.', 12, 0, 100, 51, 0, 0, 0, 33004, 0, 'The Etymidian');
INSERT INTO `creature_text` VALUES (28095, 0, 0, 'No! You steal kill! Now you owe Gekgek!', 12, 0, 100, 0, 0, 0, 0, 27605, 0, 'Tracker Gekgek');
INSERT INTO `creature_text` VALUES (28095, 1, 0, 'Dumb! Don\'t shoot at it! Gekgek want trophy in one piece!', 12, 0, 100, 0, 0, 0, 0, 28492, 0, 'Tracker Gekgek');
INSERT INTO `creature_text` VALUES (28095, 2, 0, 'No let it get away! We got it this time.', 12, 0, 33, 0, 0, 0, 0, 28493, 0, 'Tracker Gekgek');
INSERT INTO `creature_text` VALUES (28095, 2, 1, 'Careful! No let it scratch you like last time.', 12, 0, 33, 0, 0, 0, 0, 28495, 0, 'Tracker Gekgek');
INSERT INTO `creature_text` VALUES (28095, 2, 2, 'Ahh! It\'s coming right for us!', 12, 0, 34, 0, 0, 0, 0, 27754, 0, 'Tracker Gekgek');
INSERT INTO `creature_text` VALUES (28103, 0, 0, 'Urgreth begins to emanate a powerful dark energy!', 16, 0, 100, 0, 0, 0, 0, 29932, 0, 'Urgreth of the Thousand Tombs');
INSERT INTO `creature_text` VALUES (28105, 0, 0, 'My treasure! You no steal from Tartek, dumb big-tongue traitor thing.', 14, 0, 100, 0, 0, 0, 0, 27893, 0, 'Warlord Tartek');
INSERT INTO `creature_text` VALUES (28105, 1, 0, 'Tartek and nasty dragon going to kill you! You so dumb.', 14, 0, 100, 0, 0, 0, 0, 27895, 0, 'Warlord Tartek');
INSERT INTO `creature_text` VALUES (28113, 0, 0, 'Please take... my shinies. All done...', 12, 0, 100, 0, 0, 0, 0, 28630, 0, 'Mosswalker Victim');
INSERT INTO `creature_text` VALUES (28113, 0, 1, 'We not do anything... to them... I no understand.', 12, 0, 100, 0, 0, 0, 0, 28903, 0, 'Mosswalker Victim');
INSERT INTO `creature_text` VALUES (28113, 0, 2, 'Use my shinies... make weather good again... make undead things go away.', 12, 0, 100, 0, 0, 0, 0, 28901, 0, 'Mosswalker Victim');
INSERT INTO `creature_text` VALUES (28113, 0, 3, 'We gave shinies to shrine... we not greedy... why this happen?', 12, 0, 100, 0, 0, 0, 0, 28900, 0, 'Mosswalker Victim');
INSERT INTO `creature_text` VALUES (28113, 0, 4, 'I do something bad? I sorry....', 12, 0, 100, 0, 0, 0, 0, 28904, 0, 'Mosswalker Victim');
INSERT INTO `creature_text` VALUES (28113, 1, 0, 'We saved. You nice, dryskin.', 12, 0, 100, 0, 0, 0, 0, 28631, 0, 'Mosswalker Victim');
INSERT INTO `creature_text` VALUES (28113, 1, 1, 'Maybe you make weather better too?', 12, 0, 100, 0, 0, 0, 0, 28906, 0, 'Mosswalker Victim');
INSERT INTO `creature_text` VALUES (28113, 1, 2, 'You save us! Yay for you!', 12, 0, 100, 0, 0, 0, 0, 28909, 0, 'Mosswalker Victim');
INSERT INTO `creature_text` VALUES (28113, 1, 3, 'Thank you! You good!', 12, 0, 100, 0, 0, 0, 0, 28905, 0, 'Mosswalker Victim');
INSERT INTO `creature_text` VALUES (28113, 3, 0, 'The mosswalker victim groans in pain.', 16, 0, 100, 0, 0, 0, 0, 28638, 0, 'Mosswalker Victim');
INSERT INTO `creature_text` VALUES (28115, 0, 0, 'You feel a massive jolt of energy as your body binds with that of Soo-holu.', 42, 0, 100, 0, 0, 0, 0, 29003, 0, 'Soo-holu to Player');
INSERT INTO `creature_text` VALUES (28116, 0, 0, 'You feel rage rise within you as your body binds with Kartak.', 42, 0, 100, 0, 0, 0, 0, 29005, 0, 'Kartak the Abominable to Player');
INSERT INTO `creature_text` VALUES (28120, 0, 0, 'Ooh! Shinies!', 12, 0, 18, 0, 0, 0, 0, 28261, 0, 'Lafoo');
INSERT INTO `creature_text` VALUES (28120, 0, 1, 'Maybe more shiny bugs! We like bugs!', 12, 0, 17, 0, 0, 0, 0, 28267, 0, 'Lafoo');
INSERT INTO `creature_text` VALUES (28120, 0, 2, 'Treasure for Lafoo! Treasure for shrine!', 12, 0, 17, 0, 0, 0, 0, 28265, 0, 'Lafoo');
INSERT INTO `creature_text` VALUES (28120, 0, 3, 'Dig, dig, dig...', 12, 0, 16, 0, 0, 0, 0, 28266, 0, 'Lafoo');
INSERT INTO `creature_text` VALUES (28120, 0, 4, 'Crunchy bugs so delicious. Want?', 12, 0, 16, 0, 0, 0, 0, 28291, 0, 'Lafoo');
INSERT INTO `creature_text` VALUES (28120, 0, 5, 'That weird. Someone took our bugs. You have maybe?', 12, 0, 16, 0, 0, 0, 0, 28303, 0, 'Lafoo');
INSERT INTO `creature_text` VALUES (28120, 1, 0, 'No come back this time!', 12, 0, 100, 0, 0, 0, 0, 28277, 0, 'Lafoo');
INSERT INTO `creature_text` VALUES (28120, 2, 0, 'One day maybe you can be Soo-dryskin. Like?', 12, 0, 20, 0, 0, 0, 0, 28293, 0, 'Lafoo');
INSERT INTO `creature_text` VALUES (28120, 2, 1, 'You have pie? Someone gave us pie year ago. Really yummy.', 12, 0, 20, 0, 0, 0, 0, 28306, 0, 'Lafoo');
INSERT INTO `creature_text` VALUES (28120, 2, 2, 'Big comfy tree. Good home.', 12, 0, 20, 0, 0, 0, 0, 28290, 0, 'Lafoo');
INSERT INTO `creature_text` VALUES (28120, 2, 3, 'Never met Great Ones. Nope, just met their shrines.', 12, 0, 20, 0, 0, 0, 0, 28289, 0, 'Lafoo');
INSERT INTO `creature_text` VALUES (28120, 2, 4, 'You got funny looking eyeballs.', 12, 0, 20, 0, 0, 0, 0, 28286, 0, 'Lafoo');
INSERT INTO `creature_text` VALUES (28120, 3, 0, 'Home good, but not stay long! More adventures!', 12, 0, 100, 0, 0, 0, 0, 28340, 0, 'Lafoo');
INSERT INTO `creature_text` VALUES (28120, 4, 0, 'Hiss... hiss... dead. All dead.', 12, 0, 100, 0, 0, 0, 0, 28279, 0, 'Lafoo');
INSERT INTO `creature_text` VALUES (28121, 0, 0, 'Shiny things! Shiny things! Not as shiny as my crystal, but still shiny!', 12, 0, 25, 0, 0, 0, 0, 28308, 0, 'Jaloot');
INSERT INTO `creature_text` VALUES (28121, 0, 1, 'More treasures! Treasures of all kinds! Yay treasures!', 12, 0, 25, 0, 0, 0, 0, 28307, 0, 'Jaloot');
INSERT INTO `creature_text` VALUES (28121, 0, 2, 'Can never have too many shiny crystals! Nope!', 12, 0, 25, 0, 0, 0, 0, 28310, 0, 'Jaloot');
INSERT INTO `creature_text` VALUES (28121, 0, 3, 'We are fast digger! Really fast! See... all done! That was fast, huh?', 12, 0, 25, 0, 0, 0, 0, 28309, 0, 'Jaloot');
INSERT INTO `creature_text` VALUES (28121, 1, 0, 'You like that? Bet not! Bet not cause you dead! Hahah!', 12, 0, 34, 0, 0, 0, 0, 28332, 0, 'Jaloot');
INSERT INTO `creature_text` VALUES (28121, 1, 1, 'Vic...tor...ious! Dun nun nun dun da da!', 12, 0, 33, 0, 0, 0, 0, 28338, 0, 'Jaloot');
INSERT INTO `creature_text` VALUES (28121, 1, 2, 'Killing Spree!', 12, 0, 33, 0, 0, 0, 0, 28334, 0, 'Jaloot');
INSERT INTO `creature_text` VALUES (28121, 2, 0, 'Mistcaller really good at controlling weather with shrine. They know just which crystals to use! Really good!', 12, 0, 16, 0, 0, 0, 0, 28330, 0, 'Jaloot');
INSERT INTO `creature_text` VALUES (28121, 2, 1, 'Ever trip over own feet? We have. Kind of hurt. No do it, not fun. Nope.', 12, 0, 16, 0, 0, 0, 0, 28920, 0, 'Jaloot');
INSERT INTO `creature_text` VALUES (28121, 2, 2, 'Want us to help with something? What we help with? We good at helping.', 12, 0, 16, 0, 0, 0, 0, 28922, 0, 'Jaloot');
INSERT INTO `creature_text` VALUES (28121, 2, 3, 'Hello! You\'re pretty strong and nice and stuff. I like you. Lets stay friends, okay?', 12, 0, 16, 0, 0, 0, 0, 28921, 0, 'Jaloot');
INSERT INTO `creature_text` VALUES (28121, 2, 4, 'Misty. Very misty. Seem sort of misty to you? Maybe not so much.', 12, 0, 16, 0, 0, 0, 0, 28327, 0, 'Jaloot');
INSERT INTO `creature_text` VALUES (28121, 2, 5, 'Lets go jump off tree! Fun fun!', 12, 0, 15, 0, 0, 0, 0, 28341, 0, 'Jaloot');
INSERT INTO `creature_text` VALUES (28121, 3, 0, 'Chase the hatchlings! Chase the hatchlings! So fast!', 12, 0, 34, 0, 0, 0, 0, 28342, 0, 'Jaloot');
INSERT INTO `creature_text` VALUES (28121, 3, 1, 'Great Ones keep home safe because they like Oracles I think! They know we good and nice.', 12, 0, 33, 0, 0, 0, 0, 28344, 0, 'Jaloot');
INSERT INTO `creature_text` VALUES (28121, 3, 2, 'Home home home... place of the shinies!', 12, 0, 33, 0, 0, 0, 0, 28339, 0, 'Jaloot');
INSERT INTO `creature_text` VALUES (28121, 4, 0, 'We know you have dragon thing! We not scared! We going to get you and dragon thing!', 12, 0, 34, 0, 0, 0, 0, 28352, 0, 'Jaloot');
INSERT INTO `creature_text` VALUES (28121, 4, 1, 'Teach you to steal our treasures! Our treasures are... ours, okay?!', 12, 0, 33, 0, 0, 0, 0, 28317, 0, 'Jaloot');
INSERT INTO `creature_text` VALUES (28121, 4, 2, 'Time to hide puppy-men! We coming for you... better get in your hill things!', 12, 0, 33, 0, 0, 0, 0, 28353, 0, 'Jaloot');
INSERT INTO `creature_text` VALUES (28121, 5, 0, 'Tartek no match for you! You got the skills. Yeah!', 12, 0, 100, 0, 0, 0, 0, 28315, 0, 'Jaloot');
INSERT INTO `creature_text` VALUES (28122, 0, 0, 'What the heck are shiny rabbits doing here anyway?', 12, 0, 25, 0, 0, 0, 0, 28366, 0, 'Moodle');
INSERT INTO `creature_text` VALUES (28122, 0, 1, 'Here I am, digging up some useless bauble from the ground at the will of someone I just recently met... does that seem right to you?', 12, 0, 25, 0, 0, 0, 0, 28368, 0, 'Moodle');
INSERT INTO `creature_text` VALUES (28122, 0, 2, 'Hey... maybe we\'ll find some more useless bugs.', 12, 0, 25, 0, 0, 0, 0, 28365, 0, 'Moodle');
INSERT INTO `creature_text` VALUES (28122, 0, 3, 'Yay... more dirt.', 12, 0, 25, 0, 0, 0, 0, 28363, 0, 'Moodle');
INSERT INTO `creature_text` VALUES (28122, 1, 0, 'I can\'t believe... they\'re all... dead.', 12, 0, 50, 0, 0, 0, 0, 28377, 0, 'Moodle');
INSERT INTO `creature_text` VALUES (28122, 1, 1, 'Another falls to our onslaught.', 12, 0, 50, 0, 0, 0, 0, 28384, 0, 'Moodle');
INSERT INTO `creature_text` VALUES (28122, 2, 0, 'There some reason why we\'re still here? I\'d love to hear it.', 12, 0, 33, 0, 0, 0, 0, 28393, 0, 'Moodle');
INSERT INTO `creature_text` VALUES (28122, 2, 1, 'Yeah, let\'s go now.', 12, 0, 33, 0, 0, 0, 0, 28391, 0, 'Moodle');
INSERT INTO `creature_text` VALUES (28122, 2, 2, 'I\'m done here.', 12, 0, 34, 0, 0, 0, 0, 28392, 0, 'Moodle');
INSERT INTO `creature_text` VALUES (28122, 3, 0, 'Going to go take a nap? I apologize in advance for accidentally stepping on your face while you sleep.', 12, 0, 33, 0, 0, 0, 0, 28396, 0, 'Moodle');
INSERT INTO `creature_text` VALUES (28122, 3, 1, 'Planning to sit on your butt some more, or are we going to go do something that\'s actually useful?', 12, 0, 34, 0, 0, 0, 0, 28396, 0, 'Moodle');
INSERT INTO `creature_text` VALUES (28122, 3, 2, 'Here I am, brain the size of a planet, and they ask me to take you to the other village. Call that job satisfaction? \'Cause I don\'t.', 12, 0, 33, 0, 0, 0, 0, 28396, 0, 'Moodle');
INSERT INTO `creature_text` VALUES (28122, 4, 0, 'The Mosswalkers were good people... they did not deserve this.', 12, 0, 17, 0, 0, 0, 0, 28382, 0, 'Moodle');
INSERT INTO `creature_text` VALUES (28122, 4, 1, 'I know I talk down to them, but I never want to see any of my people die.', 12, 0, 17, 0, 0, 0, 0, 28382, 0, 'Moodle');
INSERT INTO `creature_text` VALUES (28122, 4, 2, 'It\'s good to see some of the mosswalker were more resilient than I thought.', 12, 0, 17, 0, 0, 0, 0, 28382, 0, 'Moodle');
INSERT INTO `creature_text` VALUES (28122, 4, 3, 'It\'s hard to believe any of them survived this.', 12, 0, 17, 0, 0, 0, 0, 28382, 0, 'Moodle');
INSERT INTO `creature_text` VALUES (28122, 4, 4, 'There are more that might still be alive! We need to keep moving.', 12, 0, 16, 0, 0, 0, 0, 28382, 0, 'Moodle');
INSERT INTO `creature_text` VALUES (28122, 4, 5, 'Where are they dragging those bodies? What are they doing to them?', 12, 0, 16, 0, 0, 0, 0, 28382, 0, 'Moodle');
INSERT INTO `creature_text` VALUES (28122, 5, 0, 'This fight\'s all yours!', 12, 0, 10, 0, 0, 0, 0, 28873, 0, 'Moodle');
INSERT INTO `creature_text` VALUES (28122, 6, 0, 'It\'s a small comfort to see that they haven\'t brought any back in undeath.', 12, 0, 100, 0, 0, 0, 0, 28400, 0, 'Moodle');
INSERT INTO `creature_text` VALUES (28136, 0, 0, 'Thank you, friend. Were it not for your aid I would be no more.', 12, 0, 100, 0, 0, 0, 0, 27521, 0, 'Crusader Jonothan');
INSERT INTO `creature_text` VALUES (28136, 1, 0, '<sigh>... This is the end of me.', 12, 0, 100, 1, 0, 0, 0, 27520, 0, '');
INSERT INTO `creature_text` VALUES (28139, 0, 0, 'I wanna see!', 12, 0, 100, 396, 0, 0, 0, 29461, 0, 'Frenzyheart Pup');
INSERT INTO `creature_text` VALUES (28139, 1, 0, 'Show me too!', 12, 0, 100, 1, 0, 0, 0, 29462, 0, 'Frenzyheart Pup');
INSERT INTO `creature_text` VALUES (28139, 2, 0, 'Metalhead ate Rejek! Run!', 12, 0, 100, 1, 0, 0, 0, 29465, 0, 'Frenzyheart Pup');
INSERT INTO `creature_text` VALUES (28142, 0, 0, '<sigh>... Well, I suppose it could be worse.', 12, 0, 100, 0, 0, 0, 0, 27523, 0, 'Crusader Lamoof');
INSERT INTO `creature_text` VALUES (28142, 1, 0, '<sigh>... This is the end of me.', 12, 0, 100, 1, 0, 0, 0, 27520, 0, '');
