INSERT INTO `creature_text` VALUES (28406, 0, 4, 'Here come the tears...', 12, 0, 100, 0, 0, 0, 0, 29264, 0, 'dk initiate SAY_DUEL_E');
INSERT INTO `creature_text` VALUES (28406, 0, 5, 'You have challenged death itself!', 12, 0, 100, 0, 0, 0, 0, 29262, 0, 'dk initiate SAY_DUEL_F');
INSERT INTO `creature_text` VALUES (28406, 0, 6, 'The Lich King will see his true champion on this day!', 12, 0, 100, 0, 0, 0, 0, 29268, 0, 'dk initiate SAY_DUEL_G');
