INSERT INTO `creature_text` VALUES (28451, 0, 0, 'What a fight you missed, Dorian! It was one for the ages!', 12, 0, 100, 0, 0, 0, 0, 28026, 0, 'Hemet');
INSERT INTO `creature_text` VALUES (28451, 1, 0, 'There we were, riding into battle at full speed on the back of Stampy...', 12, 0, 100, 0, 0, 0, 0, 28027, 0, 'Hemet');
INSERT INTO `creature_text` VALUES (28451, 2, 0, 'Everything was shaking all over the place. I could hardly steady my gun.', 12, 0, 100, 0, 0, 0, 0, 28028, 0, 'Hemet');
INSERT INTO `creature_text` VALUES (28451, 3, 0, 'And then we saw her, the fearsome beast!', 12, 0, 100, 0, 0, 0, 0, 28029, 0, 'Hemet');
INSERT INTO `creature_text` VALUES (28451, 4, 0, 'It took all of $n\'s skill to maintain control of Stampy.', 12, 0, 100, 0, 0, 0, 0, 28030, 0, 'Hemet');
INSERT INTO `creature_text` VALUES (28451, 5, 0, 'The battle was a haze of blood, lead, and sweat. The broodmother\'s attacks came faster and faster!', 12, 0, 100, 0, 0, 0, 0, 28032, 0, 'Hemet');
INSERT INTO `creature_text` VALUES (28451, 6, 0, 'But, in the end, $n and I prevailed through our wits and teamwork. And now we have this glorious trophy as a memento of our victory.', 12, 0, 100, 0, 0, 0, 0, 28033, 0, 'Hemet');
INSERT INTO `creature_text` VALUES (28451, 7, 0, 'Do I detect a bit of jealousy, Dorian?', 12, 0, 100, 0, 0, 0, 0, 28036, 0, 'Hemet');
INSERT INTO `creature_text` VALUES (28472, 0, 0, 'As disciples of blood, you strive to master the very lifeforce of your enemies.', 12, 0, 100, 1, 0, 0, 0, 29867, 0, 'Lord Thorval');
INSERT INTO `creature_text` VALUES (28472, 1, 0, 'Be it by blade or incantation, blood feeds our attacks and weakens our foes.', 12, 0, 100, 1, 0, 0, 0, 29868, 0, 'Lord Thorval');
INSERT INTO `creature_text` VALUES (28472, 2, 0, 'True masters learn to make blood serve more than just their strength in battle.', 12, 0, 100, 1, 0, 0, 0, 29869, 0, 'Lord Thorval');
INSERT INTO `creature_text` VALUES (28472, 3, 0, 'Stripping energy from our foes, both fighting and fallen, allows us to persevere where lesser beigns falls exhausted.', 12, 0, 100, 1, 0, 0, 0, 29870, 0, 'Lord Thorval');
INSERT INTO `creature_text` VALUES (28472, 4, 0, 'And every foe that falls, energy sapped and stolen, only further fuels our assault.', 12, 0, 100, 1, 0, 0, 0, 29871, 0, 'Lord Thorval');
INSERT INTO `creature_text` VALUES (28472, 5, 0, 'As masters of blood, we know battle without end...', 12, 0, 100, 1, 0, 0, 0, 29872, 0, 'Lord Thorval');
INSERT INTO `creature_text` VALUES (28472, 6, 0, 'We know hunger never to be quenched...', 12, 0, 100, 1, 0, 0, 0, 29873, 0, 'Lord Thorval');
INSERT INTO `creature_text` VALUES (28472, 7, 0, 'We know power never to be overcome...', 12, 0, 100, 1, 0, 0, 0, 29874, 0, 'Lord Thorval');
INSERT INTO `creature_text` VALUES (28472, 8, 0, 'As masters of blood, we are masters of life and death itself. Agains us, even hope falls drained and lifeless.', 12, 0, 100, 1, 0, 0, 0, 29875, 0, 'Lord Thorval');
