INSERT INTO `creature_text` VALUES (28683, 0, 2, 'You\'ll be hanging at the gallows soon...', 12, 0, 100, 0, 0, 0, 0, 28677, 0, 'Stable Master Kitrik');
INSERT INTO `creature_text` VALUES (28683, 0, 3, 'How many more of you do I need to send back to the Lich King in pieces before he\'ll stop?', 12, 0, 100, 0, 0, 0, 0, 28678, 0, 'Stable Master Kitrik');
INSERT INTO `creature_text` VALUES (28683, 1, 0, 'And keep the change, ya filthy animal!', 12, 0, 100, 0, 0, 0, 0, 28702, 0, 'Stable Master Kitrik');
INSERT INTO `creature_text` VALUES (28683, 1, 1, 'Perhaps you\'ll think twice about stealin\' one of Kitrik\'s horses next time, eh?', 12, 0, 100, 0, 0, 0, 0, 28703, 0, 'Stable Master Kitrik');
INSERT INTO `creature_text` VALUES (28683, 1, 2, 'Is that all you got?', 12, 0, 100, 0, 0, 0, 0, 28704, 0, 'Stable Master Kitrik');
INSERT INTO `creature_text` VALUES (28683, 1, 3, 'What a colossal waste of time.', 12, 0, 100, 0, 0, 0, 0, 28705, 0, 'Stable Master Kitrik');
INSERT INTO `creature_text` VALUES (28683, 1, 4, 'At least make me work for it next time.', 12, 0, 100, 0, 0, 0, 0, 28706, 0, 'Stable Master Kitrik');
