INSERT INTO `creature_text` VALUES (28859, 0, 0, 'Lesser beings, intruding here! A shame that your excess courage does not compensate for your stupidity!', 14, 0, 100, 0, 10000, 14512, 0, 31759, 0, 'Malygos - Intro');
INSERT INTO `creature_text` VALUES (28859, 0, 1, 'None but the blue dragonflight are welcome here. Perhaps this is the work of Alexstrasza? Well, then, she has sent you to your deaths!', 14, 0, 100, 0, 11000, 14513, 0, 31760, 0, 'Malygos - Intro');
INSERT INTO `creature_text` VALUES (28859, 0, 2, 'What could you hope to accomplish? To storm brazenly into my domain... to employ magic... against ME?', 14, 0, 100, 0, 13000, 14514, 0, 31761, 0, 'Malygos - Intro');
INSERT INTO `creature_text` VALUES (28859, 0, 3, 'I am without limits here. The rules of your cherished reality do not apply. In this realm, I am in control!', 14, 0, 100, 0, 10000, 14515, 0, 31762, 0, 'Malygos - Intro');
INSERT INTO `creature_text` VALUES (28859, 0, 4, 'I give you one chance. Pledge fealty to me, and perhaps I will not slaughter you for your insolence.', 14, 0, 100, 0, 7000, 14516, 0, 31763, 0, 'Malygos - Intro');
INSERT INTO `creature_text` VALUES (28859, 1, 0, 'My patience has reached its limit. I will be rid of you!', 14, 0, 100, 0, 4000, 14517, 0, 31764, 0, 'Malygos - Start phase 1');
INSERT INTO `creature_text` VALUES (28859, 2, 0, 'You will not succeed while I draw breath!', 14, 0, 100, 0, 3000, 14518, 0, 31781, 0, 'Malygos - Begin to cast Deep Breath');
INSERT INTO `creature_text` VALUES (28859, 3, 0, 'Your stupidity has finally caught up with you!', 14, 0, 100, 0, 3250, 14519, 0, 31767, 0, 'Malygos - Killed Player (Phase 1)');
INSERT INTO `creature_text` VALUES (28859, 3, 1, 'More artifacts to confiscate.', 14, 0, 100, 0, 2800, 14520, 0, 31768, 0, 'Malygos - Killed Player (Phase 1)');
INSERT INTO `creature_text` VALUES (28859, 3, 2, 'How very naive.', 14, 0, 100, 0, 4800, 14521, 0, 31769, 0, 'Malygos - Killed Player (Phase 1)');
INSERT INTO `creature_text` VALUES (28859, 4, 0, 'I had hoped to end your lives quickly, but you have proven more... resilient than I anticipated. Nonetheless, your efforts are in vain. It is you reckless, careless mortals who are to blame for this war. I do what I must, and if it means your extinction.... then SO BE IT!!', 14, 0, 100, 0, 22900, 14522, 0, 31772, 0, 'Malygos - End Phase One');
INSERT INTO `creature_text` VALUES (28859, 5, 0, 'Few have experienced the pain I will now inflict upon you!', 14, 0, 100, 0, 5500, 14523, 0, 31765, 0, 'Malygos - Start phase 2');
INSERT INTO `creature_text` VALUES (28859, 6, 0, 'I will teach you ignorant children just how little you know of magic.', 14, 0, 100, 0, 7000, 14524, 0, 31773, 0, 'Malygos - Anti-Magic Shell');
INSERT INTO `creature_text` VALUES (28859, 7, 0, 'Watch helplessly as your hopes are swept away!', 14, 0, 100, 0, 4000, 14525, 0, 31774, 0, 'Malygos - Magic Blast');
INSERT INTO `creature_text` VALUES (28859, 8, 0, 'Your energy will be put to good use.', 14, 0, 100, 0, 2000, 14526, 0, 31775, 0, 'Malygos - Killed Player (Phase 2)');
INSERT INTO `creature_text` VALUES (28859, 8, 1, 'I am the Spell-Weaver! My power is infinite!', 14, 0, 100, 0, 5200, 14527, 0, 31776, 0, 'Malygos - Killed Player (Phase 2)');
INSERT INTO `creature_text` VALUES (28859, 8, 2, 'Your spirit will linger here forever!', 14, 0, 100, 0, 3800, 14528, 0, 31777, 0, 'Malygos - Killed Player (Phase 2)');
INSERT INTO `creature_text` VALUES (28859, 9, 0, 'ENOUGH! If you intend to reclaim Azeroth\'s magic, then you shall have it!', 14, 0, 100, 0, 7000, 14529, 0, 31778, 0, 'Malygos - End Phase 2');
INSERT INTO `creature_text` VALUES (28859, 10, 0, 'Now your benefactors make their appearance, but they are too late! The powers contained here are sufficient to destroy the world ten times over. What do you think they will do to you?', 14, 0, 100, 0, 13000, 14530, 0, 31779, 0, 'Intro Phase 3');
INSERT INTO `creature_text` VALUES (28859, 11, 0, 'Submit!', 14, 0, 100, 0, 1000, 14531, 0, 31780, 0, 'Malygos - Start phase 3');
INSERT INTO `creature_text` VALUES (28859, 12, 0, '%s takes a deep breath...', 41, 0, 100, 0, 10000, 0, 0, 33657, 0, 'Malygos - Surge of Power warning (Phase 2)');
INSERT INTO `creature_text` VALUES (28859, 13, 0, 'The powers at work here exceed anything you could possibly imagine!', 14, 0, 100, 0, 5000, 14532, 0, 31782, 0, 'Malygos - Surge of Power');
INSERT INTO `creature_text` VALUES (28859, 14, 0, 'I am unstoppable!', 14, 0, 100, 0, 2000, 14533, 0, 31783, 0, 'Malygos - Buffed by a spark');
