INSERT INTO `creature_text` VALUES (28902, 2, 0, 'I WILL KILL YOU, $n!', 14, 0, 100, 0, 0, 0, 0, 29041, 0, 'Warlord Zol\'Maz');
INSERT INTO `creature_text` VALUES (28907, 0, 0, 'The sky itself feeds upon the suffering of the conquered!\n', 12, 0, 100, 5, 0, 0, 0, 29048, 0, 'Prince Valanar');
INSERT INTO `creature_text` VALUES (28907, 1, 0, 'The Scarlet Crusade is powerless to stop us!\n', 12, 0, 100, 5, 0, 0, 0, 29049, 0, 'Prince Valanar');
INSERT INTO `creature_text` VALUES (28907, 2, 0, 'The corpses of those that try only serve to feed our expanding host!', 12, 0, 100, 1, 0, 0, 0, 29050, 0, 'Prince Valanar');
INSERT INTO `creature_text` VALUES (28907, 3, 0, 'The Scarlet fleet lies in ruin upon the sundered coast!\n', 12, 0, 100, 25, 0, 0, 0, 29051, 0, 'Prince Valanar');
INSERT INTO `creature_text` VALUES (28907, 4, 0, 'With each enemy slain, our strength grows! We mustn\'t relent! \n', 12, 0, 100, 5, 0, 0, 0, 29052, 0, 'Prince Valanar');
INSERT INTO `creature_text` VALUES (28907, 5, 0, 'The Lich King has spoken to each of you! Let his words resonate among you once more!', 12, 0, 100, 25, 0, 0, 0, 29053, 0, 'Prince Valanar');
INSERT INTO `creature_text` VALUES (28907, 6, 0, 'ALL. MUST. DIE.', 12, 0, 100, 22, 0, 0, 0, 29054, 0, 'Prince Valanar');
INSERT INTO `creature_text` VALUES (28907, 7, 0, 'Leave no survivors in your wake, brothers and sisters!', 12, 0, 100, 274, 0, 0, 0, 29055, 0, 'Prince Valanar');
INSERT INTO `creature_text` VALUES (28907, 8, 0, 'No mercy for the weak!', 12, 0, 100, 15, 0, 0, 0, 29056, 0, 'Prince Valanar');
INSERT INTO `creature_text` VALUES (28907, 9, 0, 'TERROR!', 12, 0, 100, 5, 0, 0, 0, 29057, 0, 'Prince Valanar');
INSERT INTO `creature_text` VALUES (28907, 10, 0, 'CHAOS!', 12, 0, 100, 22, 0, 0, 0, 29058, 0, 'Prince Valanar');
INSERT INTO `creature_text` VALUES (28907, 11, 0, 'DESTRUCTION!', 12, 0, 100, 15, 0, 0, 0, 29059, 0, 'Prince Valanar');
INSERT INTO `creature_text` VALUES (28907, 12, 0, 'With the approaching darkness comes the end of the Scarlet Crusade. You will battle once more! For the Scourge! For the Lich King!', 12, 0, 100, 5, 0, 0, 0, 29060, 0, 'Prince Valanar');
INSERT INTO `creature_text` VALUES (28907, 13, 0, 'SUFFER WELL, DEATH KNIGHTS!', 12, 0, 100, 71, 0, 9006, 0, 29061, 0, 'Prince Valanar');
INSERT INTO `creature_text` VALUES (28912, 0, 0, 'I\'ll need to get my runeblade and armor... Just need a little more time.', 12, 0, 100, 0, 0, 0, 0, 29201, 0, 'koltira deathweaver SAY_BREAKOUT1');
INSERT INTO `creature_text` VALUES (28912, 1, 0, 'I\'m still weak, but I think I can get an anti-magic barrier up. Stay inside it or you\'ll be destroyed by their spells.', 12, 0, 100, 0, 0, 0, 0, 29203, 0, 'koltira deathweaver SAY_BREAKOUT2');
INSERT INTO `creature_text` VALUES (28912, 2, 0, 'Maintaining this barrier will require all of my concentration. Kill them all!', 12, 0, 100, 0, 0, 0, 0, 29205, 0, 'koltira deathweaver SAY_BREAKOUT3');
INSERT INTO `creature_text` VALUES (28912, 3, 0, 'There are more coming. Defend yourself! Don\'t fall out of the anti-magic field! They\'ll tear you apart without its protection!', 12, 0, 100, 0, 0, 0, 0, 29207, 0, 'koltira deathweaver SAY_BREAKOUT4');
INSERT INTO `creature_text` VALUES (28912, 4, 0, 'I can\'t keep this barrier up much longer... Where is that coward?', 12, 0, 100, 0, 0, 0, 0, 29208, 0, 'koltira deathweaver SAY_BREAKOUT5');
INSERT INTO `creature_text` VALUES (28912, 5, 0, 'The High Inquisitor comes! Be ready, death knight! Do not let him draw you out of the protective bounds of my anti-magic field! Kill him and take his head!', 12, 0, 100, 0, 0, 0, 0, 29210, 0, 'koltira deathweaver SAY_BREAKOUT6');
INSERT INTO `creature_text` VALUES (28912, 6, 0, 'Stay in the anti-magic field! Make them come to you!', 12, 0, 100, 0, 0, 0, 0, 29225, 0, 'koltira deathweaver SAY_BREAKOUT7');
INSERT INTO `creature_text` VALUES (28912, 7, 0, 'The death of the High Inquisitor of New Avalon will not go unnoticed. You need to get out of here at once! Go, before more of them show up. I\'ll be fine on my own.', 12, 0, 100, 0, 0, 0, 0, 29239, 0, 'koltira deathweaver SAY_BREAKOUT8');
INSERT INTO `creature_text` VALUES (28912, 8, 0, 'I\'ll draw their fire, you make your escape behind me.', 12, 0, 100, 0, 0, 0, 0, 29240, 0, 'koltira deathweaver SAY_BREAKOUT9');
INSERT INTO `creature_text` VALUES (28912, 9, 0, 'Your High Inquisitor is nothing more than a pile of meat, Crusaders! There are none beyond the grasp of the Scourge!', 14, 0, 100, 0, 0, 0, 0, 29241, 0, 'koltira deathweaver SAY_BREAKOUT10');
INSERT INTO `creature_text` VALUES (28913, 0, 0, 'What, Orbaz? That he\'s a blood elf?', 12, 0, 100, 1, 0, 0, 0, 29192, 0, 'Tharassian');
INSERT INTO `creature_text` VALUES (28913, 1, 0, 'In life we were hated enemies - this is true...', 12, 0, 100, 1, 0, 0, 0, 29193, 0, 'Tharassian');
INSERT INTO `creature_text` VALUES (28913, 2, 0, 'But in death... We are the children of the damned. The orphaned sons and daughters of the Scourge.', 12, 0, 100, 1, 0, 0, 0, 29194, 0, 'Tharassian');
INSERT INTO `creature_text` VALUES (28913, 3, 0, 'In death we are brothers.', 12, 0, 100, 1, 0, 0, 0, 29195, 0, 'Tharassian');
INSERT INTO `creature_text` VALUES (28914, 0, 0, 'Make haste, $n. Use the portal!', 12, 0, 100, 25, 0, 0, 0, 29443, 0, 'Orbaz Bloodlane');
INSERT INTO `creature_text` VALUES (28914, 1, 0, 'Why do you care, Thassarian?', 12, 0, 100, 1, 0, 0, 0, 29189, 0, 'Orbaz Bloodbane');
INSERT INTO `creature_text` VALUES (28914, 2, 0, 'His weakness led to his capture.', 12, 0, 100, 1, 0, 0, 0, 29190, 0, 'Orbaz Bloodbane');
INSERT INTO `creature_text` VALUES (28914, 3, 0, 'Only the strong should survive. Not to mention...', 12, 0, 100, 1, 0, 0, 0, 29191, 0, 'Orbaz Bloodbane');
INSERT INTO `creature_text` VALUES (28914, 4, 0, 'To hell with you, Thassarian.', 12, 0, 100, 25, 0, 0, 0, 29196, 0, 'Orbaz Bloodbane');
INSERT INTO `creature_text` VALUES (28917, 0, 0, 'Daddy!', 12, 0, 100, 0, 0, 0, 0, 29007, 0, 'Yara');
INSERT INTO `creature_text` VALUES (28921, 1, 0, '%s moves up the tunnel!', 41, 0, 100, 0, 0, 0, 0, 29852, 3, 'Hadronox HADRONOX_EMOTE_MOVE');
INSERT INTO `creature_text` VALUES (28922, 1, 0, 'The gate has been breached! Quickly, divert forces to deal with these invaders!', 14, 0, 100, 0, 0, 13941, 0, 29473, 3, 'Anub\'ar Crusher CRUSHER_SAY_AGGRO');
INSERT INTO `creature_text` VALUES (28922, 2, 0, '%s goes into a frenzy!', 41, 0, 100, 0, 0, 0, 0, 10645, 3, 'Anub\'ar Crusher CRUSHER_EMOTE_FRENZY');
INSERT INTO `creature_text` VALUES (28923, 0, 0, 'I have witnessed the rise and fall of empires... the birth and extinction of entire species... Over countless millennia the foolishness of mortals has remained the only constant. Your presence here confirms this.', 14, 0, 100, 0, 0, 14160, 0, 30536, 0, 'Loken');
INSERT INTO `creature_text` VALUES (28923, 1, 0, 'My master has shown me the future, and you have no place in it. Azeroth will be reborn in darkness. Yogg-Saron shall be released! The Pantheon shall fall!', 14, 0, 100, 0, 0, 14161, 0, 30537, 0, 'Loken');
INSERT INTO `creature_text` VALUES (28923, 2, 0, 'What hope is there for you? None!', 14, 0, 100, 0, 0, 14162, 0, 30535, 0, 'Loken');
INSERT INTO `creature_text` VALUES (28923, 3, 0, 'You cannot hide from fate!', 14, 0, 100, 0, 0, 14163, 0, 30538, 0, 'Loken');
INSERT INTO `creature_text` VALUES (28923, 3, 1, 'Come closer. I will make it quick.', 14, 0, 100, 0, 0, 14164, 0, 30539, 0, 'Loken');
INSERT INTO `creature_text` VALUES (28923, 3, 2, 'Your flesh cannot hold out for long.', 14, 0, 100, 0, 0, 14165, 0, 30540, 0, 'Loken');
INSERT INTO `creature_text` VALUES (28923, 4, 0, 'Only mortal...', 14, 0, 100, 0, 0, 14166, 0, 30541, 0, 'Loken');
INSERT INTO `creature_text` VALUES (28923, 4, 1, 'I... am... FOREVER!', 14, 0, 100, 0, 0, 14167, 0, 30542, 0, 'Loken');
INSERT INTO `creature_text` VALUES (28923, 4, 2, 'What little time you had, you wasted!', 14, 0, 100, 0, 0, 14168, 0, 30543, 0, 'Loken');
INSERT INTO `creature_text` VALUES (28923, 5, 0, 'You stare blindly into the abyss!', 14, 0, 100, 0, 0, 14169, 0, 30545, 0, 'Loken');
INSERT INTO `creature_text` VALUES (28923, 6, 0, 'Your ignorance is profound. Can you not see where this path leads?', 14, 0, 100, 0, 0, 14170, 0, 30546, 0, 'Loken');
INSERT INTO `creature_text` VALUES (28923, 7, 0, 'You cross the precipice of oblivion!', 14, 0, 100, 0, 0, 14171, 0, 30547, 0, 'Loken');
INSERT INTO `creature_text` VALUES (28923, 8, 0, 'My death... heralds the end of this world.', 14, 0, 100, 0, 0, 14172, 0, 30544, 0, 'Loken');
INSERT INTO `creature_text` VALUES (28923, 9, 0, '%s begins to cast Lightning Nova!', 41, 0, 100, 0, 0, 0, 0, 29865, 0, 'Loken');
INSERT INTO `creature_text` VALUES (28936, 0, 0, 'What is the \"Crimson Dawn?\"', 12, 0, 100, 0, 0, 0, 0, 29133, 0, 'Scarlet Commander');
INSERT INTO `creature_text` VALUES (28936, 0, 1, 'Tell me what you know about \"Crimson Dawn\" or the beatings will continue!\'', 12, 0, 100, 0, 0, 0, 0, 29134, 0, 'Scarlet Commander');
INSERT INTO `creature_text` VALUES (28936, 0, 2, 'I\'m through being courteous with your kind, human! What is the \"Crimson Dawn?\"', 12, 0, 100, 0, 0, 0, 0, 29135, 0, 'Scarlet Commander');
INSERT INTO `creature_text` VALUES (28936, 0, 3, '\"Crimson Dawn!\" What is it! Speak!', 12, 0, 100, 0, 0, 0, 0, 29136, 0, 'Scarlet Commander');
INSERT INTO `creature_text` VALUES (28936, 0, 4, 'I can keep this up for a very long time, Scarlet dog! Tell me about the \"Crimson Dawn!\"', 12, 0, 100, 0, 0, 0, 0, 29137, 0, 'Scarlet Commander');
INSERT INTO `creature_text` VALUES (28936, 0, 5, 'I\'ll tear the secrets from your soul! Tell me about the \"Crimson Dawn\" and your life may be spared!', 12, 0, 100, 0, 0, 0, 0, 29138, 0, 'Scarlet Commander');
INSERT INTO `creature_text` VALUES (28936, 0, 6, 'Is your life worth so little? Just tell me what I need to know about \"Crimson Dawn\" and I\'ll end your suffering quickly.', 12, 0, 100, 0, 0, 0, 0, 29139, 0, 'Scarlet Commander');
INSERT INTO `creature_text` VALUES (28936, 0, 7, 'I grow tired of these games, human. Tell me of the \"Crimson Dawn\" or I will tear your spine out with my bare hands!', 12, 0, 100, 0, 0, 0, 0, 29140, 0, 'Scarlet Commander');
INSERT INTO `creature_text` VALUES (28936, 1, 0, 'I\'ll tell you nothing, Scourge filth!', 12, 0, 100, 0, 0, 0, 0, 29141, 0, 'Scarlet Commander');
INSERT INTO `creature_text` VALUES (28936, 1, 1, 'You\'\'ll have to kill me, monster. I will tell you NOTHING!', 12, 0, 100, 0, 0, 0, 0, 29142, 0, 'Scarlet Commander');
INSERT INTO `creature_text` VALUES (28936, 1, 2, 'Argh... The pain... The pain is almost as unbearable as the lashings I received in grammar school when I was but a child.', 12, 0, 100, 0, 0, 0, 0, 29143, 0, 'Scarlet Commander');
INSERT INTO `creature_text` VALUES (28936, 1, 3, 'You... You\'ll get nothing... And like it...', 12, 0, 100, 0, 0, 0, 0, 29144, 0, 'Scarlet Commander');
INSERT INTO `creature_text` VALUES (28936, 1, 4, 'Do you think this to be pain? To be suffering? HAH! Destroy this mortal shell so that I may ascend to the heavens!', 12, 0, 100, 0, 0, 0, 0, 29145, 0, 'Scarlet Commander');
INSERT INTO `creature_text` VALUES (28936, 1, 5, 'You hit like a girl. Honestly. Is that the best you can do?', 12, 0, 100, 0, 0, 0, 0, 29146, 0, 'Scarlet Commander');
INSERT INTO `creature_text` VALUES (28936, 1, 6, 'The crimson what? Never heard of it...', 12, 0, 100, 0, 0, 0, 0, 29147, 0, 'Scarlet Commander');
INSERT INTO `creature_text` VALUES (28936, 1, 7, 'The pain is momentary. It pales in comparison to what the High General would do to me...', 12, 0, 100, 0, 0, 0, 0, 29148, 0, 'Scarlet Commander');
INSERT INTO `creature_text` VALUES (28936, 1, 8, 'You know nothing of pain, monster!', 12, 0, 100, 0, 0, 0, 0, 29155, 0, 'Scarlet Commander');
INSERT INTO `creature_text` VALUES (28936, 1, 9, 'HAH! I\'ve survived three separate inquisitions! This is nothing in comparison!', 12, 0, 100, 0, 0, 0, 0, 29156, 0, 'Scarlet Commander');
INSERT INTO `creature_text` VALUES (28936, 1, 10, 'Look out! There\'s a bee loose and it\'s on a stinging rampage!', 12, 0, 100, 0, 0, 0, 0, 29157, 0, 'Scarlet Commander');
INSERT INTO `creature_text` VALUES (28936, 1, 11, 'That one tickled...', 12, 0, 100, 0, 0, 0, 0, 29158, 0, 'Scarlet Commander');
INSERT INTO `creature_text` VALUES (28936, 1, 12, 'I\'m going to send you back to the Lich King in pieces!', 12, 0, 100, 0, 0, 0, 0, 29159, 0, 'Scarlet Commander');
INSERT INTO `creature_text` VALUES (28936, 1, 13, 'You\'ll be hanging in the gallows shortly, Scourge fiend!', 12, 0, 100, 0, 0, 0, 0, 29160, 0, 'Scarlet Commander');
INSERT INTO `creature_text` VALUES (28936, 1, 14, 'I used to work for Grand Inquisitor Isillien! Your idea of pain is a normal mid-afternoon for me!', 12, 0, 100, 0, 0, 0, 0, 29161, 0, 'Scarlet Commander');
INSERT INTO `creature_text` VALUES (28936, 1, 15, 'ARGH! You burned my last good tabard!', 12, 0, 100, 0, 0, 0, 0, 29162, 0, 'Scarlet Commander');
INSERT INTO `creature_text` VALUES (28936, 2, 0, 'I\'ll tell you everything! STOP! PLEASE!', 12, 0, 100, 20, 0, 0, 0, 29149, 0, 'Scarlet Commander');
INSERT INTO `creature_text` VALUES (28936, 3, 0, 'We... We have only been told that the \"Crimson Dawn\" is an awakening. You... You see, the Light speaks to the High General. It is the Light...', 12, 0, 100, 1, 0, 0, 0, 29150, 0, 'Scarlet Commander');
INSERT INTO `creature_text` VALUES (28936, 4, 0, 'The Light that guides us. This movement was set in motion before you came... We... We do as we are told. It is what must be done.', 12, 0, 100, 1, 0, 0, 0, 29151, 0, 'Scarlet Commander');
INSERT INTO `creature_text` VALUES (28936, 5, 0, 'I know very litte else... The High General chooses who may go and who must stay behind. There\'\'s nothing else... You must believe me!', 12, 0, 100, 1, 0, 0, 0, 29152, 0, 'Scarlet Commander');
INSERT INTO `creature_text` VALUES (28936, 6, 0, 'LIES! The pain you are about to endure will be talked about for years to come!', 12, 0, 100, 0, 0, 0, 0, 29163, 0, 'Scarlet Commander');
INSERT INTO `creature_text` VALUES (28936, 7, 0, 'NO! PLEASE! There is one more thing that I forgot to mention... A courier comes soon... From Hearthglen. It...', 12, 0, 100, 431, 0, 0, 0, 29153, 0, 'Scarlet Commander');
INSERT INTO `creature_text` VALUES (28936, 8, 0, '%s dies from the torture.', 16, 0, 100, 0, 0, 0, 0, 29154, 0, 'Scarlet Commander');
INSERT INTO `creature_text` VALUES (28936, 9, 0, 'Scourge filth! DIE!, Scourge filth! DIE!', 12, 0, 100, 0, 0, 0, 0, 28597, 0, 'Scarlet Commander');
INSERT INTO `creature_text` VALUES (28936, 9, 1, 'The Crusade will be victorious!, The Crusade will be victorious!', 12, 0, 100, 0, 0, 0, 0, 28598, 0, 'Scarlet Commander');
INSERT INTO `creature_text` VALUES (28936, 9, 2, 'I will present your head to Abbendis myself!', 12, 0, 100, 0, 0, 0, 0, 28599, 0, 'Scarlet Commander');
INSERT INTO `creature_text` VALUES (28936, 9, 3, 'You\'re not welcome in these lands, monster!', 12, 0, 100, 0, 0, 0, 0, 28600, 0, 'Scarlet Commander');
INSERT INTO `creature_text` VALUES (28936, 9, 4, 'Let the purging begin!, Let the purging begin!', 12, 0, 100, 0, 0, 0, 0, 28601, 0, 'Scarlet Commander');
INSERT INTO `creature_text` VALUES (28936, 9, 5, 'By the Light be cleansed!, By the Light be cleansed!', 12, 0, 100, 0, 0, 0, 0, 28602, 0, 'Scarlet Commander');
INSERT INTO `creature_text` VALUES (28939, 0, 0, 'What is the \"Crimson Dawn?\"', 12, 0, 100, 0, 0, 0, 0, 29133, 0, 'Scarlet Preacher');
