INSERT INTO `creature_text` VALUES (28996, 1, 0, 'And now, Akali, my master will have your blood and I will take some of your power as well!', 14, 0, 100, 397, 0, 0, 0, 29180, 0, 'Prophet of Akali');
INSERT INTO `creature_text` VALUES (28996, 2, 0, 'It is done. Run back to your masters. Run back to Har\'koa. Tell her that I am coming!', 14, 0, 100, 14, 0, 0, 0, 29183, 0, 'Prophet of Akali');
