INSERT INTO `creature_text` VALUES (29076, 1, 0, 'What\'s this!? This isn\'t a tree at all! Guards! Guards!', 12, 0, 100, 0, 0, 0, 0, 29415, 0, 'scarlet courier SAY_TREE2');
INSERT INTO `creature_text` VALUES (29112, 0, 0, 'Flattery will get you nowhere.', 12, 0, 100, 1, 0, 0, 0, 29650, 0, 'Gothik the Harvester');
INSERT INTO `creature_text` VALUES (29112, 1, 0, 'Don\'t be ridiculous. Where would you even go if I did spare you? We just finished eradicating your civilization, remember?', 12, 0, 100, 1, 0, 0, 0, 29651, 0, 'Gothik the Harvester');
INSERT INTO `creature_text` VALUES (29112, 2, 0, 'Oh yes. Immensely. The pain will propably render you unconcious - hence the vat of slime you\'re floating in!', 12, 0, 100, 1, 0, 0, 0, 0, 0, 'Gothik the Harvester');
INSERT INTO `creature_text` VALUES (29112, 3, 0, 'That\'s how you know it\'s working.', 12, 0, 100, 1, 0, 0, 0, 29648, 0, 'Gothik the Harvester');
INSERT INTO `creature_text` VALUES (29112, 4, 0, 'You idiot! That\'s What I\'m Doing Right Now!', 12, 0, 100, 5, 0, 0, 0, 0, 0, 'Gothik the Harvester');
INSERT INTO `creature_text` VALUES (29112, 5, 0, 'I AM going to kill you. What\'s the rush?', 12, 0, 100, 6, 0, 0, 0, 29649, 0, 'Gothik the Harvester');
INSERT INTO `creature_text` VALUES (29113, 0, 0, 'We need more flesh!', 14, 0, 100, 5, 0, 0, 0, 29634, 0, 'Noth the Plaguebringer');
INSERT INTO `creature_text` VALUES (29113, 0, 1, 'Double your efforts! The final assault draws near!', 14, 0, 100, 5, 0, 0, 0, 29639, 0, 'Noth the Plaguebringer');
INSERT INTO `creature_text` VALUES (29120, 0, 0, 'Eternal agony awaits you!', 14, 0, 100, 0, 0, 14054, 0, 32308, 0, 'anub arak SAY_AGGRO');
INSERT INTO `creature_text` VALUES (29120, 1, 0, 'Soon the master\'s voice will call to you.', 14, 0, 100, 0, 0, 14057, 0, 32311, 0, 'anub arak SAY_SLAY_1');
INSERT INTO `creature_text` VALUES (29120, 1, 1, 'You have made your choice.', 14, 0, 100, 0, 0, 14056, 0, 32310, 0, 'anub arak SAY_SLAY_2');
INSERT INTO `creature_text` VALUES (29120, 1, 2, 'You shall experience my torment first hand!', 14, 0, 100, 0, 0, 14055, 0, 32309, 0, 'anub arak SAY_SLAY_3');
INSERT INTO `creature_text` VALUES (29120, 2, 0, 'Ahhh... RAAAAAGH! Never thought... I would be free of him....', 14, 0, 100, 0, 0, 14069, 0, 32317, 0, 'anub arak SAY_DEATH');
INSERT INTO `creature_text` VALUES (29120, 3, 0, 'Your armor is useless against my locusts!', 14, 0, 100, 0, 0, 14060, 0, 32314, 0, 'anub arak SAY_LOCUST_1');
INSERT INTO `creature_text` VALUES (29120, 3, 1, 'Uunak-hissss tik-k-k-k-k!', 14, 0, 100, 0, 0, 14067, 0, 32315, 0, 'anub arak SAY_LOCUST_2');
INSERT INTO `creature_text` VALUES (29120, 3, 2, 'A pestilence upon you!', 14, 0, 100, 0, 0, 14068, 0, 32316, 0, 'anub arak SAY_LOCUST_3');
INSERT INTO `creature_text` VALUES (29120, 4, 0, 'Auum na-l ak-k-k-k, isshhh.', 14, 0, 100, 0, 0, 14058, 0, 32312, 0, 'anub arak SAY_SUBMERGE_1');
INSERT INTO `creature_text` VALUES (29120, 4, 1, 'Come forth, my brethren! Feast on their flesh!', 14, 0, 100, 0, 0, 14059, 0, 32313, 0, 'anub arak SAY_SUBMERGE_2');
INSERT INTO `creature_text` VALUES (29120, 5, 0, 'I was king of this empire once, long ago. In life I stood as champion. In death I returned as conqueror. Now I protect the kingdom once more. Ironic, yes? ', 14, 0, 100, 0, 0, 14053, 0, 0, 0, 'anub arak SAY_INTRO');
INSERT INTO `creature_text` VALUES (29173, 0, 0, 'Soldiers of the Scourge, stand ready! Prepare to unleash your fury upon the Argent Dawn!', 14, 0, 100, 0, 0, 14677, 0, 29694, 0, 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES (29173, 1, 0, 'The sky weeps at the glorious devastation of these lands! Soon, Azeroth\'s futile tears will rain down upon us!', 14, 0, 100, 0, 0, 14678, 0, 29696, 0, 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES (29173, 2, 0, 'Death knights of Acherus, the death march begins!', 14, 0, 100, 0, 0, 14681, 0, 29697, 0, 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES (29173, 3, 0, 'Soldiers of the Scourge, death knights of Acherus, minions of the darkness: hear the call of the Highlord!', 14, 0, 100, 0, 0, 14679, 0, 29699, 0, 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES (29173, 4, 0, 'RISE!', 14, 0, 100, 0, 0, 14680, 0, 31198, 0, 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES (29173, 5, 0, 'The skies turn red with the blood of the fallen! The Lich King watches over us, minions! Leave only ashes and misery in your destructive wake!', 14, 0, 100, 0, 0, 14682, 0, 29722, 0, 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES (29173, 8, 0, 'Kneel before the Highlord!', 12, 0, 100, 0, 0, 14683, 0, 29727, 0, 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES (29173, 8, 1, 'You stand no chance!', 12, 0, 100, 0, 0, 14684, 0, 29728, 0, 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES (29173, 8, 2, 'The Scourge will destroy this place!', 12, 0, 100, 0, 0, 14685, 0, 29729, 0, 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES (29173, 8, 3, 'Your life is forfeit.', 12, 0, 100, 0, 0, 14686, 0, 62815, 0, 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES (29173, 8, 4, 'Life is meaningless without suffering!', 12, 0, 100, 0, 0, 14687, 0, 29731, 0, 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES (29173, 8, 5, 'How much longer will your forces hold out?', 12, 0, 100, 0, 0, 14688, 0, 29732, 0, 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES (29173, 8, 6, 'The Argent Dawn is finished!', 12, 0, 100, 0, 0, 14689, 0, 29733, 0, 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES (29173, 8, 7, 'Spare no one!', 12, 0, 100, 0, 0, 14690, 0, 29734, 0, 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES (29173, 8, 8, 'What is this?! My... I cannot strike...', 12, 0, 100, 0, 0, 14691, 0, 29735, 0, 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES (29173, 8, 9, 'OBEY ME, BLADE!', 14, 0, 100, 0, 0, 14692, 0, 29736, 0, 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES (29173, 8, 10, 'You will do as I command! I am in control here!', 12, 0, 100, 0, 0, 14693, 0, 29737, 0, 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES (29173, 8, 11, 'I... I cannot... The blade fights me...', 12, 0, 100, 0, 0, 14694, 0, 29738, 0, 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES (29173, 8, 12, 'What is happening to me?', 12, 0, 100, 0, 0, 14695, 0, 29739, 0, 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES (29173, 8, 13, 'Power... wanes...', 12, 0, 100, 0, 0, 14696, 0, 29740, 0, 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES (29173, 8, 14, 'Ashbringer defies me...', 12, 0, 100, 0, 0, 14697, 0, 29741, 0, 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES (29173, 8, 15, 'Minions, come to my aid!', 12, 0, 100, 0, 0, 14698, 0, 29742, 0, 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES (29173, 26, 0, 'Stand down, death knights. We have lost. The Light... this place... no hope...', 12, 0, 100, 0, 0, 14699, 0, 29760, 0, 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES (29173, 30, 0, 'Save your breath, old man. It might be the last you ever draw.', 12, 0, 100, 0, 0, 14700, 0, 29761, 0, 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES (29173, 32, 0, 'Father!', 12, 0, 100, 0, 0, 14701, 0, 29767, 0, 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES (29173, 33, 0, 'Argh... what... is...', 12, 0, 100, 0, 0, 14702, 0, 29768, 0, 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES (29173, 43, 0, 'You... betrayed me. You betrayed us all, monster! Face the might of Mograine!', 14, 0, 100, 0, 0, 14707, 0, 29781, 0, 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES (29173, 51, 0, 'That day is not today...', 12, 0, 100, 0, 0, 14708, 0, 29824, 0, 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES (29173, 52, 0, 'Tirion!', 14, 0, 100, 0, 0, 14709, 0, 29801, 0, 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES (29173, 67, 0, 'So too do the Knights of the Ebon Blade. Although our kind has no place in your world, we will fight to bring an end to the Lich King. This I vow!', 12, 0, 100, 0, 0, 14710, 0, 29851, 0, 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES (29173, 68, 0, 'Thousands of Scourge rise up at the Highlord\'s command.', 16, 0, 100, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (29173, 69, 0, 'The army marches towards Light\'s Hope Chapel.', 16, 0, 100, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (29173, 70, 0, 'After over a hundred Defenders of the Light fall, Highlord Tirion Fordring arrives.', 16, 0, 100, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (29173, 72, 0, '%s kneels in defeat before Tirion Fordring.', 16, 0, 100, 0, 0, 0, 0, 29759, 0, 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES (29173, 77, 0, '%s becomes himself again...and is now angry.', 16, 0, 100, 0, 0, 0, 0, 0, 0, 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES (29173, 81, 0, '%s throws the Corrupted Ashbringer to Tirion, who catches it. Tirion becomes awash with Light, and the Ashbringer is cleansed.', 16, 0, 100, 0, 0, 0, 0, 0, 0, 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES (29173, 82, 0, '%s collapses.', 16, 0, 100, 0, 0, 0, 0, 29802, 0, 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES (29173, 85, 0, 'Light washes over the chapel ? the Light of Dawn is uncovered.', 16, 0, 100, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (29175, 24, 0, 'You cannot win, Darion!', 14, 0, 100, 0, 0, 14584, 0, 29743, 0, 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES (29175, 25, 0, 'Bring them before the chapel!', 14, 0, 100, 0, 0, 14585, 0, 29756, 0, 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES (29175, 27, 0, 'Have you learned nothing, boy? You have become all that your father fought against! Like that coward, Arthas, you allowed yourself to be consumed by the darkness, the hate... feeding upon the misery of those you tortured and killed!', 12, 0, 100, 0, 0, 14586, 0, 29744, 0, 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES (29175, 28, 0, 'Your master knows what lies beneath the chapel. That is why he dares not show his face! He\'s sent you and your death knights to meet their doom, Darion.', 12, 0, 100, 0, 0, 14587, 0, 29745, 0, 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES (29175, 29, 0, 'What you are feeling right now is the anguish of a thousand lost souls! Souls that you and your master brought here! The Light will tear you apart, Darion!', 12, 0, 100, 0, 0, 14588, 0, 29746, 0, 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES (29175, 46, 0, 'You\'re a damned monster, Arthas!', 12, 0, 100, 0, 0, 14589, 0, 29783, 0, 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES (29175, 53, 0, 'ARTHAS!!!!', 14, 0, 100, 0, 0, 14591, 0, 29803, 0, 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES (29175, 55, 0, 'Your end.', 14, 0, 100, 0, 0, 14592, 0, 29861, 0, 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES (29175, 59, 0, 'Rise, Darion, and listen...\n', 12, 0, 100, 0, 0, 14593, 0, 29835, 0, 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES (29175, 60, 0, 'We have all been witness to a terrible tragedy. The blood of good men has been shed upon this soil! Honorable knights, slain defending their lives -- our lives!', 12, 0, 100, 0, 0, 14594, 0, 29843, 0, 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES (29175, 61, 0, 'And while such things can never be forgotten, we must remain vigilant in our cause!\n', 12, 0, 100, 0, 0, 14595, 0, 29845, 0, 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES (29175, 62, 0, 'The Lich King must answer for what he has done, and must not be allowed to cause further destruction in our world.', 12, 0, 100, 0, 0, 14596, 0, 29846, 0, 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES (29175, 63, 0, 'I make a promise to you now, brothers and sisters: the Lich King will be defeated! On this day, I call for a union.', 12, 0, 100, 0, 0, 14597, 0, 29847, 0, 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES (29175, 64, 0, 'The Argent Dawn and the Order of the Silver Hand will come together as one! We will succeed where so many before us have failed!\n', 12, 0, 100, 0, 0, 14598, 0, 29848, 0, 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES (29175, 65, 0, 'We will take the fight to Arthas, and we will tear down the walls of Icecrown!', 12, 0, 100, 0, 0, 14599, 0, 29849, 0, 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES (29175, 66, 0, 'The Argent Crusade comes for you, Arthas!\n', 14, 0, 100, 0, 0, 14600, 0, 29850, 0, 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES (29175, 79, 0, '%s gasps for air.', 16, 0, 100, 0, 0, 0, 0, 37502, 0, 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES (29175, 83, 0, '%s charges towards the Lich King, Ashbringer in hand and strikes the Lich King.', 16, 0, 100, 0, 0, 0, 0, 0, 0, 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES (29176, 6, 0, 'Scourge armies approach!', 14, 0, 100, 0, 0, 0, 0, 29724, 0, 'Korfax, Champion of the Light');
INSERT INTO `creature_text` VALUES (29178, 7, 0, 'Stand fast, brothers and sisters! The Light will prevail!', 14, 0, 100, 0, 0, 14487, 0, 29723, 0, 'Lord Maxwell Tyrosus');
INSERT INTO `creature_text` VALUES (29178, 49, 0, 'ATTACK!!!', 14, 0, 100, 0, 0, 14488, 0, 61812, 0, 'Lord Maxwell Tyrosus');
INSERT INTO `creature_text` VALUES (29183, 42, 0, 'Touching...', 14, 0, 100, 0, 0, 14803, 0, 29779, 0, 'The Lich King');
INSERT INTO `creature_text` VALUES (29183, 44, 0, 'He is mine now...', 12, 0, 100, 0, 0, 14805, 0, 29780, 0, 'The Lich King');
INSERT INTO `creature_text` VALUES (29183, 45, 0, 'Pathetic...', 12, 0, 100, 0, 0, 14804, 0, 29782, 0, 'The Lich King');
INSERT INTO `creature_text` VALUES (29183, 47, 0, 'You were right, Fordring. I did send them in to die. Their lives are meaningless, but yours...', 12, 0, 100, 0, 0, 14806, 0, 29784, 0, 'The Lich King');
INSERT INTO `creature_text` VALUES (29183, 48, 0, 'How simple it was to draw the great Tirion Fordring out of hiding. You\'ve left yourself exposed, paladin. Nothing will save you.', 12, 0, 100, 0, 0, 14807, 0, 29785, 0, 'The Lich King');
INSERT INTO `creature_text` VALUES (29183, 50, 0, 'APOCALYPSE!', 14, 0, 100, 0, 0, 14808, 0, 38049, 0, 'The Lich King');
INSERT INTO `creature_text` VALUES (29183, 54, 0, 'What is this?', 14, 0, 100, 0, 0, 14809, 0, 32584, 0, 'The Lich King');
INSERT INTO `creature_text` VALUES (29183, 56, 0, 'Impossible...', 14, 0, 100, 0, 0, 14810, 0, 38080, 0, 'The Lich King');
INSERT INTO `creature_text` VALUES (29183, 57, 0, 'This... is not over!', 14, 0, 100, 0, 0, 14811, 0, 29826, 0, 'The Lich King');
INSERT INTO `creature_text` VALUES (29183, 58, 0, 'When next we meet, it won\'t be on holy ground, paladin.', 14, 0, 100, 0, 0, 14812, 0, 29827, 0, 'The Lich King');
INSERT INTO `creature_text` VALUES (29183, 78, 0, '%s casts a spell on Tirion.', 16, 0, 100, 0, 0, 0, 0, 0, 0, 'The Lich King');
INSERT INTO `creature_text` VALUES (29183, 80, 0, '%s casts a powerful spell, killing the Defenders and knocking back the others.', 16, 0, 100, 0, 0, 0, 0, 0, 0, 'The Lich King');
INSERT INTO `creature_text` VALUES (29183, 84, 0, '%s disappears. Tirion walks over to where Darion lay', 16, 0, 100, 0, 0, 0, 0, 0, 0, 'The Lich King');
INSERT INTO `creature_text` VALUES (29193, 0, 0, 'You\'re a monster!', 12, 0, 100, 0, 0, 0, 0, 29644, 0, 'Scarlet Deserter');
INSERT INTO `creature_text` VALUES (29193, 1, 0, 'Please! Spare me! I...', 12, 0, 100, 0, 0, 0, 0, 29645, 0, 'Scarlet Deserter');
INSERT INTO `creature_text` VALUES (29193, 2, 0, 'Wi... Will it hurt?', 12, 0, 100, 0, 0, 0, 0, 29641, 0, 'Scarlet Deserter');
INSERT INTO `creature_text` VALUES (29193, 3, 0, 'It tingles...', 12, 0, 100, 0, 0, 0, 0, 29642, 0, 'Scarlet Deserter');
INSERT INTO `creature_text` VALUES (29193, 4, 0, 'Why don\'t you Just Kill me Already', 12, 0, 100, 0, 0, 0, 0, 0, 0, 'Scarlet Deserter');
INSERT INTO `creature_text` VALUES (29193, 5, 0, 'Is it too late to change my mind? How about you just kill me instead?', 12, 0, 100, 0, 0, 0, 0, 29643, 0, 'Scarlet Deserter');
INSERT INTO `creature_text` VALUES (29193, 6, 0, 'The horror! THE HORROR!', 12, 0, 100, 0, 0, 0, 0, 29646, 0, 'Scarlet Deserter');
INSERT INTO `creature_text` VALUES (29196, 0, 0, 'As disciples of blood, you strive to master the very lifeforce of your enemies.', 12, 0, 100, 1, 0, 0, 0, 29867, 0, 'Lord Thorval - (Part 1 of Speech To Deciples)');
INSERT INTO `creature_text` VALUES (29196, 1, 0, 'Be it by blade or incantation, blood feeds our attacks and weakens our foes.', 12, 0, 100, 1, 0, 0, 0, 29868, 0, 'Lord Thorval - (Part 2 of Speech To Deciples)');
INSERT INTO `creature_text` VALUES (29196, 2, 0, 'True masters learn to make blood serve more than just their strength in battle.', 12, 0, 100, 1, 0, 0, 0, 29869, 0, 'Lord Thorval - (Part 3 of Speech To Deciples)');
INSERT INTO `creature_text` VALUES (29196, 3, 0, 'Stripping energy from our foes, both fighting and fallen, allows us to persevere where lesser beings fall exhausted.', 12, 0, 100, 1, 0, 0, 0, 29870, 0, 'Lord Thorval - (Part 4 of Speech To Deciples)');
INSERT INTO `creature_text` VALUES (29196, 4, 0, 'And every foe that falls, energy sapped and stolen, only further fuels our assault.', 12, 0, 100, 1, 0, 0, 0, 29871, 0, 'Lord Thorval - (Part 5 of Speech To Deciples)');
INSERT INTO `creature_text` VALUES (29196, 5, 0, 'As masters of blood, we know battle without end...', 12, 0, 100, 1, 0, 0, 0, 29872, 0, 'Lord Thorval - (Part 6 of Speech To Deciples)');
INSERT INTO `creature_text` VALUES (29196, 6, 0, 'We know hunger never to be quenched...', 12, 0, 100, 1, 0, 0, 0, 29873, 0, 'Lord Thorval - (Part 7 of Speech To Deciples)');
INSERT INTO `creature_text` VALUES (29196, 7, 0, 'We know power never to be overcome...', 12, 0, 100, 1, 0, 0, 0, 29874, 0, 'Lord Thorval - (Part 8 of Speech To Deciples)');
INSERT INTO `creature_text` VALUES (29196, 8, 0, 'As masters of blood, we are masters of life and death itself.  Against us, even hope falls drained and lifeless.', 12, 0, 100, 1, 0, 0, 0, 29875, 0, 'Lord Thorval - (Part 9 of Speech To Deciples)');
INSERT INTO `creature_text` VALUES (29204, 71, 0, '%s flees.', 16, 0, 100, 0, 0, 0, 0, 29758, 0, 'Orbaz');
INSERT INTO `creature_text` VALUES (29227, 31, 0, 'My son! My dear, beautiful boy!', 12, 0, 100, 0, 0, 14493, 0, 29762, 0, 'Highlord Alexandros Mograine');
INSERT INTO `creature_text` VALUES (29227, 36, 0, 'Nothing could have kept me away from here, Darion. Not from my home and family.', 12, 0, 100, 0, 0, 14494, 0, 29773, 0, 'Highlord Alexandros Mograine');
INSERT INTO `creature_text` VALUES (29227, 38, 0, 'Darion Mograine, you are barely of age to hold a sword, let alone battle the undead hordes of Lordaeron! I could not bear losing you. Even the thought...\n', 12, 0, 100, 0, 0, 14495, 0, 29775, 0, 'Highlord Alexandros Mograine');
INSERT INTO `creature_text` VALUES (29227, 40, 0, 'My son, there will come a day when you will command the Ashbringer and, with it, mete out justice across this land. I have no doubt that when that day finally comes, you will bring pride to our people and that Lordaeron will be a better place because of you. But, my son, that day is not today.\n', 12, 0, 100, 0, 0, 14496, 0, 29777, 0, 'Highlord Alexandros Mograine');
INSERT INTO `creature_text` VALUES (29227, 41, 0, 'Do not forget...', 12, 0, 100, 0, 0, 14497, 0, 29778, 0, 'Highlord Alexandros Mograine');
INSERT INTO `creature_text` VALUES (29227, 73, 0, '%s arrives.', 16, 0, 100, 0, 0, 0, 0, 0, 0, 'Highlord Alexandros Mograine');
INSERT INTO `creature_text` VALUES (29227, 76, 0, '%s disappears, and the Lich King appears.', 16, 0, 100, 0, 0, 0, 0, 0, 0, 'Alexandros');
INSERT INTO `creature_text` VALUES (29228, 34, 0, 'Father, you have returned!', 12, 0, 100, 0, 0, 14703, 0, 29769, 0, 'Darion Mograine');
INSERT INTO `creature_text` VALUES (29228, 35, 0, 'You have been gone a long time, Father. I thought...', 12, 0, 100, 0, 0, 14704, 0, 29770, 0, 'Darion Mograine');
INSERT INTO `creature_text` VALUES (29228, 37, 0, 'Father, I wish to join you in the war against the undead. I want to fight! I can sit idle no longer!', 12, 0, 100, 0, 0, 14705, 0, 29774, 0, 'Darion Mograine');
INSERT INTO `creature_text` VALUES (29228, 39, 0, 'If I die, Father, I would rather it be on my feet, standing in defiance against the undead legions! If I die, let me die with you!', 12, 0, 100, 0, 0, 14706, 0, 29776, 0, 'Darion Mograine');
INSERT INTO `creature_text` VALUES (29228, 74, 0, '%s becomes a shade of his past, and walks up to his father.', 16, 0, 100, 0, 0, 0, 0, 0, 0, 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES (29228, 75, 0, '%s hugs his father.', 16, 0, 100, 0, 0, 0, 0, 29771, 0, 'Darion Mograine');
INSERT INTO `creature_text` VALUES (29260, 1, 0, 'Sparklight\'s vehicle emporium is open for business!', 14, 0, 100, 0, 0, 0, 0, 30230, 0, 'Rigger Sparklight');
INSERT INTO `creature_text` VALUES (29262, 1, 0, 'Rigspark\'s vehicle shop is open for business!', 14, 0, 100, 0, 0, 0, 0, 30229, 0, 'Gorgril Rigspark');
INSERT INTO `creature_text` VALUES (29266, 0, 0, 'It seems my freedom must be bought with blood!', 14, 0, 100, 0, 0, 14499, 0, 31805, 0, 'Xevozz SAY_AGGRO');
INSERT INTO `creature_text` VALUES (29266, 1, 0, 'Nothing personal.', 14, 0, 100, 0, 0, 14504, 0, 31808, 0, 'Xevozz SAY_SLAY_1');
INSERT INTO `creature_text` VALUES (29266, 1, 1, 'Business concluded.', 14, 0, 100, 0, 0, 14505, 0, 31809, 0, 'Xevozz SAY_SLAY_2');
