INSERT INTO `creature_text` VALUES (29572, 1, 0, 'No.', 12, 0, 100, 0, 0, 0, 0, 39548, 0, '');
INSERT INTO `creature_text` VALUES (29572, 2, 0, 'STOP! It isn\'t worth it. I\'ll tell you where he is.', 12, 0, 100, 0, 0, 0, 0, 30245, 0, '');
INSERT INTO `creature_text` VALUES (29572, 3, 0, 'On the south end of the island is a cave -- a hidden hollow. The grand admiral has holed himself up in there, preparing for the final battle against the Lich King.', 12, 0, 100, 0, 0, 0, 0, 30247, 0, '');
INSERT INTO `creature_text` VALUES (29572, 4, 0, 'AAAEEEEIIIiiiiiiiiiiiiiiiiiiiiiiiiiiii........................................', 14, 0, 100, 0, 0, 0, 0, 30250, 0, '');
INSERT INTO `creature_text` VALUES (29573, 0, 0, '%s gathers its mojo and surges forward!', 16, 0, 100, 0, 0, 0, 0, 30717, 0, 'Drakkari Elemental - Mojo');
INSERT INTO `creature_text` VALUES (29573, 1, 0, 'As the last remnants of the %s seep into the ground, the altar set into the floor nearby begins to glow faintly.', 16, 0, 100, 0, 0, 0, 0, 31226, 0, 'Drakkari Elemental - Death');
INSERT INTO `creature_text` VALUES (29579, 0, 0, 'I... I can understand you now! Well, now that we can talk to each other, you have some explaining to do!', 12, 0, 100, 5, 0, 0, 0, 30624, 0, 'Brann Bronzebeard text0');
INSERT INTO `creature_text` VALUES (29579, 1, 0, 'How did you get my communicator from the Explorers\' League?', 12, 0, 100, 5, 0, 0, 0, 30625, 0, 'Brann Bronzebeard text1');
INSERT INTO `creature_text` VALUES (29579, 2, 0, 'If the Explorers\' League sent men, I didn\'t see any trace of them. I found your note buried in a camp overrun by iron dwarves.', 12, 1, 100, 0, 0, 0, 0, 30622, 0, 'Player text2');
INSERT INTO `creature_text` VALUES (29579, 3, 0, 'You have my thanks for dispatching the iron dwarves. But why would the Horde have an interest in me?', 12, 0, 100, 6, 0, 0, 0, 30626, 0, 'Brann Bronzebeard text3');
INSERT INTO `creature_text` VALUES (29579, 4, 0, 'A scout found the remains of your journal in Thor Modan. We\'ve been looking for you ever since.', 12, 1, 100, 0, 0, 0, 0, 30623, 0, 'Player text4');
INSERT INTO `creature_text` VALUES (29579, 5, 0, 'That wouldn\'t be Scout Vor\'takh, would it? Even I know of his reputation for ruthlessness. He means to abduct me, then?', 12, 0, 100, 5, 0, 0, 0, 30627, 0, 'Brann Bronzebeard text5');
INSERT INTO `creature_text` VALUES (29579, 6, 0, 'If you\'ve seen the journal, then you know what I\'ve been discovering. The titans\' own creations are at war with each other!', 12, 0, 100, 5, 0, 0, 0, 30628, 0, 'Brann Bronzebeard text6');
INSERT INTO `creature_text` VALUES (29579, 7, 0, 'Loken and his iron dwarf minions have ousted the Earthen from Ulduar and taken over the city.', 12, 0, 100, 5, 0, 0, 0, 30629, 0, 'Brann Bronzebeard text7');
INSERT INTO `creature_text` VALUES (29579, 8, 0, 'If we spend our time and strength fighting with each other, Loken will use Ulduar\'s resources to destroy both Horde and Alliance.', 12, 0, 100, 6, 0, 0, 0, 30630, 0, 'Brann Bronzebeard text8');
INSERT INTO `creature_text` VALUES (29579, 9, 0, 'Speak to the commander at your post, $g lad:lass;, and persuade him to abandon Vor\'takh\'s foolish plan.', 12, 0, 100, 6, 0, 0, 0, 30631, 0, 'Brann Bronzebeard Text10');
