INSERT INTO `creature_text` VALUES (30299, 1, 0, 'You may have killed Valduran, but that will not stop me from completing the colossus.', 12, 0, 100, 1, 0, 0, 0, 30973, 0, 'Overseer Narvir');
INSERT INTO `creature_text` VALUES (30313, 0, 0, 'Temple of Invention analysis commencing.', 12, 0, 100, 15, 0, 0, 0, 31498, 0, 'Mobile Databank');
INSERT INTO `creature_text` VALUES (30313, 1, 0, 'Temple of Invention verified to be intact.', 12, 0, 100, 15, 0, 0, 0, 31499, 0, 'Mobile Databank');
INSERT INTO `creature_text` VALUES (30313, 2, 0, 'Mechanical servants appear to have turned against each other. Several attendants have been piled together in an unorganized manner.', 12, 0, 100, 15, 0, 0, 0, 31500, 0, 'Mobile Databank');
INSERT INTO `creature_text` VALUES (30313, 3, 0, 'Remaining mechagnome guardians corrupted by unknown source.', 12, 0, 100, 15, 0, 0, 0, 31501, 0, 'Mobile Databank');
INSERT INTO `creature_text` VALUES (30313, 4, 0, 'Watcher Mimir verified to no longer be present. Analysis complete.', 12, 0, 100, 15, 0, 0, 0, 31502, 0, 'Mobile Databank');
INSERT INTO `creature_text` VALUES (30313, 5, 0, 'Temple of Winter analysis commencing.', 12, 0, 100, 15, 0, 0, 0, 31504, 0, 'Mobile Databank');
INSERT INTO `creature_text` VALUES (30313, 6, 0, 'Temple of Winter verified to be in tact.', 12, 0, 100, 15, 0, 0, 0, 31506, 0, 'Mobile Databank');
INSERT INTO `creature_text` VALUES (30313, 7, 0, 'Temple guardians verified to be deceased. Sulfurous odor suggests that death resulted from a fire-based entity.', 12, 0, 100, 15, 0, 0, 0, 31507, 0, 'Mobile Databank');
INSERT INTO `creature_text` VALUES (30313, 8, 0, 'Previously established cold weather patterns at the temple have ceased.', 12, 0, 100, 15, 0, 0, 0, 31508, 0, 'Mobile Databank');
INSERT INTO `creature_text` VALUES (30313, 9, 0, 'Watcher Hodir verified to no longer be present. Analysis complete.', 12, 0, 100, 15, 0, 0, 0, 31509, 0, 'Mobile Databank');
INSERT INTO `creature_text` VALUES (30313, 10, 0, 'Temple of Life analysis commencing.', 12, 0, 100, 15, 0, 0, 0, 31515, 0, 'Mobile Databank');
INSERT INTO `creature_text` VALUES (30313, 11, 0, 'Temple of Life verified to be damaged beyond repair.', 12, 0, 100, 15, 0, 0, 0, 31512, 0, 'Mobile Databank');
INSERT INTO `creature_text` VALUES (30313, 12, 0, 'Evidence indicates a significant battle. The opponent of Watcher Freya estimated to be of similar size and strength to Watcher Freya.', 12, 0, 100, 15, 0, 0, 0, 31513, 0, 'Mobile Databank');
INSERT INTO `creature_text` VALUES (30313, 13, 0, 'Temple guardians are no longer present. Plant forms associated with temple are deceased.', 12, 0, 100, 15, 0, 0, 0, 31514, 0, 'Mobile Databank');
INSERT INTO `creature_text` VALUES (30313, 14, 0, 'Watcher Freya verified to no longer be present. Analysis complete.', 12, 0, 100, 15, 0, 0, 0, 31510, 0, 'Mobile Databank');
INSERT INTO `creature_text` VALUES (30313, 15, 0, 'Temple of Order analysis commencing.', 12, 0, 100, 15, 0, 0, 0, 31516, 0, 'Mobile Databank');
INSERT INTO `creature_text` VALUES (30313, 16, 0, 'Temple of Order verified to be in tact.', 12, 0, 100, 15, 0, 0, 0, 31517, 0, 'Mobile Databank');
INSERT INTO `creature_text` VALUES (30313, 17, 0, 'No indications of struggle are present. No guardians are present.', 12, 0, 100, 15, 0, 0, 0, 31518, 0, 'Mobile Databank');
INSERT INTO `creature_text` VALUES (30313, 18, 0, 'Watcher Tyr verified to no longer be present. Analysis complete.', 12, 0, 100, 15, 0, 0, 0, 31519, 0, 'Mobile Databank');
INSERT INTO `creature_text` VALUES (30331, 0, 0, 'Hold on, little $r.', 12, 0, 100, 0, 0, 0, 0, 31000, 0, 'King Jokkum - Mount up');
INSERT INTO `creature_text` VALUES (30331, 1, 0, 'Thorim! Come, show yourself!', 14, 0, 100, 0, 0, 0, 0, 31018, 0, 'King Jokkum - Conversation start');
INSERT INTO `creature_text` VALUES (30331, 2, 0, 'The deeds of your $r servant defy $g his:her; stature, Stormlord. $g His:Her; efforts have succeeded in softening the hearts of my people.', 12, 0, 100, 396, 0, 0, 0, 31051, 0, 'King Jokkum - Conversation 20 sec in');
INSERT INTO `creature_text` VALUES (30331, 3, 0, 'Never have such humble words come from mighty Thorim. I shall deliver your words to Dun Niffelem.', 12, 0, 100, 396, 0, 0, 0, 31052, 0, 'King Jokkum - Conversation 40 sec in');
INSERT INTO `creature_text` VALUES (30331, 4, 0, 'The events of that dark day are hereby forgiven by my people.  They shall never again be spoken of.', 12, 0, 100, 396, 0, 0, 0, 31053, 0, 'King Jokkum - Conversation 48 sec in');
INSERT INTO `creature_text` VALUES (30331, 5, 0, 'To signify our reforged friendship, I have something which belongs to you...', 12, 0, 100, 0, 0, 0, 0, 31054, 0, 'King Jokkum - Conversation 57 sec in');
INSERT INTO `creature_text` VALUES (30331, 6, 0, 'As the great explosion filled the region, my father cast a rune at the great hammer that it might not be had by our enemies. It was his final act...', 12, 0, 100, 396, 0, 0, 0, 31057, 0, 'King Jokkum - Conversation 63 sec in');
INSERT INTO `creature_text` VALUES (30331, 7, 0, 'We welcome the opportunity to fight by your side, mighty Thorim.', 12, 0, 100, 0, 0, 0, 0, 31059, 0, 'King Jokkum - Conversation 75 sec in');
INSERT INTO `creature_text` VALUES (30331, 8, 0, 'I must return to Dun Niffilem. We shall speak again soon, Stormlord.', 12, 0, 100, 396, 0, 0, 0, 31060, 0, 'King Jokkum - Conversation 80 sec in');
INSERT INTO `creature_text` VALUES (30344, 0, 0, 'FIRE! FIRE!', 14, 0, 100, 0, 0, 16721, 0, 36993, 0, 'High Captain Justin Bartlett - Fire');
INSERT INTO `creature_text` VALUES (30344, 1, 0, 'Quickly, climb aboard! We mustn\'t tarry here! There\'s no telling when this whole mountainside will collapse.', 14, 0, 100, 0, 0, 16722, 0, 37213, 0, 'High Captain Justin Bartlett - Final');
INSERT INTO `creature_text` VALUES (30377, 0, 0, 'You are dealing with a being that holds within it the consciousness of the most cunning, intelligent, and ruthless individuals to ever live.', 12, 0, 100, 1, 0, 0, 0, 31005, 0, 'The Ebon Watcher');
INSERT INTO `creature_text` VALUES (30377, 1, 0, 'The Lich King is unlike any foe that you have ever faced, Highlord. Though you bested him upon the holy ground of Light\'s Hope Chapel, you tread now upon his domain.\n', 12, 0, 100, 1, 0, 0, 0, 31006, 0, 'The Ebon Watcher');
INSERT INTO `creature_text` VALUES (30377, 2, 0, 'You cannot win. Not like this...\n', 12, 0, 100, 274, 0, 0, 0, 31007, 0, 'The Ebon Watcher');
INSERT INTO `creature_text` VALUES (30377, 3, 0, 'Nothing. There is nothing that you can do while the Light binds you. It controls you wholly, shackling you to the ground with its virtues.', 12, 0, 100, 274, 0, 0, 0, 31009, 0, 'The Ebon Watcher');
INSERT INTO `creature_text` VALUES (30377, 4, 0, '%s shakes his head.', 16, 0, 100, 0, 0, 0, 0, 31011, 0, 'The Ebon Watcher');
INSERT INTO `creature_text` VALUES (30377, 5, 0, 'Look upon the field, Highlord. The Lich King has halted your advance completely and won the upper hand!', 12, 0, 100, 25, 0, 0, 0, 31012, 0, 'The Ebon Watcher');
INSERT INTO `creature_text` VALUES (30377, 6, 0, 'The breach you created was sealed with Nerubian webbing almost as quickly as it was opened.', 12, 0, 100, 1, 0, 0, 0, 31013, 0, 'The Ebon Watcher');
INSERT INTO `creature_text` VALUES (30377, 7, 0, 'Your soldiers are being used as living shields to stave off artillery fire in the Valley of Echoes, allowing the forces of the Lich King to assault your base without impediment.', 12, 0, 100, 1, 0, 0, 0, 31014, 0, 'The Ebon Watcher');
INSERT INTO `creature_text` VALUES (30377, 8, 0, 'The Lich King knows your boundaries, Highlord. He knows that you will not fire on your own men. Do you not understand? He has no boundaries. No rules to abide. \n', 12, 0, 100, 1, 0, 0, 0, 31015, 0, 'The Ebon Watcher');
INSERT INTO `creature_text` VALUES (30377, 9, 0, 'Then you have lost, Highlord.\n', 12, 0, 100, 1, 0, 0, 0, 31017, 0, 'The Ebon Watcher');
INSERT INTO `creature_text` VALUES (30381, 0, 0, 'And it very nearly didn\'t remember you. $N fought bravely against the North Wind to protect your story.', 12, 0, 100, 1, 0, 0, 0, 32748, 0, 'Xarantaur');
INSERT INTO `creature_text` VALUES (30381, 1, 0, 'Your battle with the wind was hundreds of years ago, Stormhoof. You weakened it so much that it has been silent all these years.', 12, 0, 100, 273, 0, 0, 0, 32750, 0, 'Xarantaur');
INSERT INTO `creature_text` VALUES (30381, 2, 0, 'But the North Wind did not silently accept defeat. Instead, it gathered power and bided its time, deciding the best revenge was to destroy your memory, and with it, your village.', 12, 0, 100, 1, 0, 0, 0, 32751, 0, 'Xarantaur');
INSERT INTO `creature_text` VALUES (30381, 3, 0, 'The wind used its minions to distort the history of this place, changing the stories your people told, destroying the spirits of the honored dead.', 12, 0, 100, 1, 0, 0, 0, 32752, 0, 'Xarantaur');
INSERT INTO `creature_text` VALUES (30381, 4, 0, 'It would\'ve succeeded if $N and I hadn\'t tracked the North Wind\'s historical corruptions to their source: your ancient battle.', 12, 0, 100, 1, 0, 0, 0, 32754, 0, 'Xarantaur');
INSERT INTO `creature_text` VALUES (30381, 5, 0, 'With my help, $N travelled back in time to your battle, and ensured that history was set right. The North Wind\'s fate was sealed, and your story restored.', 12, 0, 100, 1, 0, 0, 0, 32755, 0, 'Xarantaur');
INSERT INTO `creature_text` VALUES (30381, 6, 0, 'Rest well, hero.', 12, 0, 100, 273, 0, 0, 0, 32762, 0, 'Xarantaur');
INSERT INTO `creature_text` VALUES (30381, 7, 0, 'This isn\'t right at all, $n. In the tale Wind Tamer Barah told me, Stormhoof was a hero, not a villain.', 12, 0, 100, 6, 0, 0, 0, 31041, 0, 'Xarantaur');
INSERT INTO `creature_text` VALUES (30381, 8, 0, 'The North Wind is the ancient enemy of the taunka, a ruler among the elements, and the very force which bleeds the life from these ancient people.', 12, 0, 100, 1, 0, 0, 0, 31044, 0, 'Xarantaur');
INSERT INTO `creature_text` VALUES (30381, 9, 0, 'They would rather die than bow before the North Wind. Something is amiss here.', 12, 0, 100, 274, 0, 0, 0, 31046, 0, 'Xarantaur');
INSERT INTO `creature_text` VALUES (30381, 10, 0, 'The Lorehammer...', 12, 0, 100, 25, 0, 0, 0, 31049, 0, 'Xarantaur');
INSERT INTO `creature_text` VALUES (30381, 11, 0, 'It confirms our suspicions. The threads of time have been severed and rewoven in the wrong pattern. But, how?', 12, 0, 100, 1, 0, 0, 0, 31050, 0, 'Xarantaur');
INSERT INTO `creature_text` VALUES (30381, 12, 0, 'There\'s no doubt that something has distorted the true history of Stormhoof and the warriors of Camp Tunka\'lo. But, what?', 12, 0, 100, 1, 0, 0, 0, 31259, 0, 'Xarantaur');
INSERT INTO `creature_text` VALUES (30381, 13, 0, 'No! The true voice of history will triumph. We will restore all you have destroyed!', 12, 0, 100, 25, 0, 0, 0, 31263, 0, 'Xarantaur');
INSERT INTO `creature_text` VALUES (30381, 14, 0, '$n, we must hurry and discover when the North Wind has started distorting the timeline. Only then can we defeat him and correct it.', 12, 0, 100, 1, 0, 0, 0, 31264, 0, 'Xarantaur');
INSERT INTO `creature_text` VALUES (30388, 0, 0, 'The horn! Use the horn on it while it\'s weak!', 14, 0, 100, 0, 0, 0, 0, 31183, 0, 'Stormhoof');
INSERT INTO `creature_text` VALUES (30388, 1, 0, 'Go to the center of the temple and use the Horn of Elemental Fury!', 41, 0, 100, 0, 0, 0, 0, 36207, 0, 'Stormhoof');
INSERT INTO `creature_text` VALUES (30390, 0, 0, 'King Jokkum, you have summoned me?', 12, 0, 100, 0, 0, 0, 0, 31001, 0, 'Thorim  - Conversation 18 sec in');
INSERT INTO `creature_text` VALUES (30390, 1, 0, 'Jokkum, son of Arngrim, I have always regretted my actions here. In my grief, I brought great harm to those closest to me.', 12, 0, 100, 0, 0, 0, 0, 31003, 0, 'Thorim  - Conversation 26 sec in');
INSERT INTO `creature_text` VALUES (30390, 2, 0, 'I would ask your forgiveness for the suffering I have caused you and your people.', 12, 0, 100, 0, 0, 0, 0, 31062, 0, 'Thorim - Conversation 32 sec in');
INSERT INTO `creature_text` VALUES (30390, 3, 0, 'Krolmir... I thank you Jokkum. I hadn\'t dared hope it still existed. It shall soon see glorious battle once again!', 12, 0, 100, 396, 0, 0, 0, 31063, 0, 'Thorim - Conversation 68 sec in');
INSERT INTO `creature_text` VALUES (30395, 0, 0, 'My brother, Stormhoof, was a far greater warrior than I. While I trained to be the chieftain of our clan, he prepared for a larger quest.', 12, 0, 100, 1, 0, 0, 0, 31151, 0, 'Chieftain Swiftspear');
INSERT INTO `creature_text` VALUES (30395, 1, 0, 'How could he be in danger? All he wanted was to give us power over the elements that made life harsh for us here.', 12, 0, 100, 1, 0, 0, 0, 31152, 0, 'Chieftain Swiftspear');
INSERT INTO `creature_text` VALUES (30395, 2, 0, 'He sought an artifact... a horn, I think. He left the village on a long journey in search of it.', 12, 0, 100, 6, 0, 0, 0, 31153, 0, 'Chieftain Swiftspear');
INSERT INTO `creature_text` VALUES (30395, 3, 0, 'I know that a terrible enemy pursued him after he won the horn, but I... I don\'t recall... what happened... next.', 12, 0, 100, 5, 0, 0, 0, 31154, 0, 'Chieftain Swiftspear');
INSERT INTO `creature_text` VALUES (30395, 4, 0, 'It is as though my memories are shrouded in mist. I cannot even recall what became of my brother. Is this how you mean that he is in danger?', 12, 0, 100, 274, 0, 0, 0, 31155, 0, 'Chieftain Swiftspear');
INSERT INTO `creature_text` VALUES (30395, 5, 0, 'The disturbances in the tomb, they must be involved. Look around you, $n. Do you see them?', 12, 0, 100, 25, 0, 0, 0, 31156, 0, 'Chieftain Swiftspear');
INSERT INTO `creature_text` VALUES (30395, 6, 0, 'Yes, they are at fault, but I am powerless to stop them. Will you warn the people of Tunka\'lo, stranger, that their past and their ancestors are in danger?', 12, 0, 100, 1, 0, 0, 0, 31157, 0, 'Chieftain Swiftspear');
INSERT INTO `creature_text` VALUES (30396, 0, 0, 'You seem eager to join your beloved Sif, brother.', 12, 0, 100, 0, 0, 0, 0, 31082, 0, 'Loken');
INSERT INTO `creature_text` VALUES (30396, 1, 0, 'ENOUGH!', 14, 0, 100, 15, 0, 0, 0, 31126, 0, 'Loken');
INSERT INTO `creature_text` VALUES (30396, 2, 0, 'Looks like you can still best me in a fair fight, little brother.', 12, 0, 100, 1, 0, 0, 0, 31110, 0, 'Loken');
INSERT INTO `creature_text` VALUES (30396, 3, 0, 'Unfortunately for you.... this fight is anything but fair!', 12, 0, 100, 1, 0, 0, 0, 31111, 0, 'Loken');
INSERT INTO `creature_text` VALUES (30396, 4, 0, 'Thanks to your little friend, you\'ve left the Temple of Storms, where you were at your strongest.  Instead we fight closer to my master\'s power.', 12, 0, 100, 0, 0, 0, 0, 31112, 0, 'Loken');
INSERT INTO `creature_text` VALUES (30396, 5, 0, 'Minions!  Take my brother and his drake to their new dwellings.  Make sure the beast makes it alive.  I have special plans for the broodmother.', 12, 0, 100, 0, 0, 0, 0, 31128, 0, 'Loken');
INSERT INTO `creature_text` VALUES (30396, 6, 0, 'As for your life, mortal.  I will be generous.  After all... why would I destroy my most useful servant?  I waited for you for weeks inside that Hyldnir mine.', 12, 0, 100, 0, 0, 0, 0, 31127, 0, 'Loken');
INSERT INTO `creature_text` VALUES (30396, 7, 0, 'The shape wasn\'t mine, of course.  And had you not been so reckless, you would\'ve seen past my illusion.  But you came through for me, and for that... I must thank you!', 12, 0, 100, 0, 0, 0, 0, 31133, 0, 'Loken');
INSERT INTO `creature_text` VALUES (30399, 0, 0, 'It is time I put an end to my brother\'s madness.', 12, 0, 100, 0, 0, 0, 0, 31078, 0, 'Thorim');
INSERT INTO `creature_text` VALUES (30399, 1, 0, 'Loken!!', 14, 0, 100, 15, 0, 0, 0, 31079, 0, 'Thorim');
INSERT INTO `creature_text` VALUES (30399, 2, 0, 'Stand up and fight me, you worthless traitor!  ', 14, 0, 100, 53, 0, 0, 0, 31080, 0, 'Thorim');
INSERT INTO `creature_text` VALUES (30399, 3, 0, 'Do not dare...', 12, 0, 100, 0, 0, 0, 0, 31083, 0, 'Thorim');
INSERT INTO `creature_text` VALUES (30399, 4, 0, 'Speak...', 12, 0, 100, 0, 0, 0, 0, 31116, 0, 'Thorim');
INSERT INTO `creature_text` VALUES (30399, 5, 0, 'Her name!!', 12, 0, 100, 0, 0, 0, 0, 31117, 0, 'Thorim');
INSERT INTO `creature_text` VALUES (30401, 0, 0, 'King Stormheart is putting you to the test, eh? He must see something in you to begin with or I doubt he\'d put you through such a sacred ritual.', 12, 0, 100, 0, 0, 0, 0, 30942, 0, 'Velog Icebellow');
INSERT INTO `creature_text` VALUES (30401, 1, 0, 'I know you\'re new to our kind, so I\'ll catch you up a bit while we\'re on our way over.', 12, 0, 100, 0, 0, 0, 0, 30943, 0, 'Velog Icebellow');
INSERT INTO `creature_text` VALUES (30401, 2, 0, 'Years back, my father and several other frostborn were returning from a trek across Dragonblight. There was a heavy blizzard... far worse than we\'ve ever seen since.', 12, 0, 100, 0, 0, 0, 0, 30944, 0, 'Velog Icebellow');
INSERT INTO `creature_text` VALUES (30401, 3, 0, 'They crossed a trail of blood-soaked snow and followed it to find a dwarf wandering and speaking in a dialect they couldn\'t make out... and not a dwarf of our kind mind you, but a mountain dwarf - something our kind had never seen before.', 12, 0, 100, 0, 0, 0, 0, 30945, 0, 'Velog Icebellow');
INSERT INTO `creature_text` VALUES (30401, 4, 0, 'The dwarf seemed lost, having no memory of where he came from, or even of his own name. Not being the kind to leave a dwarven cousin to die in the snow, my father\'s party took him in and continued back towards Frosthold.', 12, 0, 100, 0, 0, 0, 0, 30946, 0, 'Velog Icebellow');
INSERT INTO `creature_text` VALUES (30401, 5, 0, 'Not long later, out of nowhere, the snow burst before them and a jormungar the size of Veranus herself came down upon their party... one of them was swallowed whole before they even had time to react.', 12, 0, 100, 0, 0, 0, 0, 30947, 0, 'Velog Icebellow');
INSERT INTO `creature_text` VALUES (30401, 6, 0, 'My father thought they were all doomed... but behind him, a furious roar rumbled across the snow, and he turned to see the mountain dwarf growing in size, his skin taking on a stone-like texture, and his hands sizzling with lightning.', 12, 0, 100, 0, 0, 0, 0, 30948, 0, 'Velog Icebellow');
INSERT INTO `creature_text` VALUES (30401, 7, 0, 'The dwarf barreled forward with a sound like rolling thunder and hurled a shining metal hammer, lightning coursing over its surface, directly into the jormungar\'s throat.', 12, 0, 100, 0, 0, 0, 0, 30949, 0, 'Velog Icebellow');
INSERT INTO `creature_text` VALUES (30401, 8, 0, 'The jormungar collapsed instantly, its head barely still attached to its convulsing body. My father turned to the dwarf in awe and raised a fist in praise...', 12, 0, 100, 0, 0, 0, 0, 30950, 0, 'Velog Icebellow');
INSERT INTO `creature_text` VALUES (30401, 9, 0, 'The stranger having no name of his own, my father deemed \"Yorg,\" a name reserved for champions of legend. Years later, he now stands before us as Yorg Stormheart, King of the Frostborn.', 12, 0, 100, 0, 0, 0, 0, 30952, 0, 'Velog Icebellow');
INSERT INTO `creature_text` VALUES (30401, 10, 0, 'King Stormheart has trained us well... turned us into even more fearsome warriors than we could have boasted during the time of our war with the Frost Giants.', 12, 0, 100, 0, 0, 0, 0, 30953, 0, 'Velog Icebellow');
INSERT INTO `creature_text` VALUES (30401, 11, 0, 'And as one of the fiercest tests put upon a warrior of the frostborn, we are made to face a creature far larger than ourselves--giants, dragons, jormungar--as a testament to the fact that size will never be our weakness.', 12, 0, 100, 0, 0, 0, 0, 30955, 0, 'Velog Icebellow');
INSERT INTO `creature_text` VALUES (30401, 12, 0, 'This is the test put before you this day. Return to us only once The Iron Watcher is dead, and be revered as a warrior of the frostborn.', 14, 0, 100, 0, 0, 0, 0, 30956, 0, 'Velog Icebellow');
INSERT INTO `creature_text` VALUES (30401, 13, 0, 'He is slow from the rust of the ages... be quick on your feet and he will not best you. You have King Stormheart\'s favor - do not disappoint.', 14, 0, 100, 0, 0, 0, 0, 31343, 0, 'Velog Icebellow');
INSERT INTO `creature_text` VALUES (30403, 0, 0, 'As you jump into Nergeld, the rotting flesh of the construct comes to life.', 16, 0, 100, 0, 0, 0, 0, 31120, 0, 'Nergeld');
INSERT INTO `creature_text` VALUES (30403, 1, 0, '%s falls to pieces.', 16, 0, 100, 0, 0, 0, 0, 31132, 0, 'Nergeld to Dr. Terrible');
INSERT INTO `creature_text` VALUES (30404, 0, 0, 'WHO DARES?! I AM GOING TO FLAY THE FLESH FROM YOUR BONES EVEN IF I HAVE TO DESTROY NERGELD TO GET TO YOU!', 14, 0, 100, 0, 0, 0, 0, 31134, 0, 'Dr. Terrible');
INSERT INTO `creature_text` VALUES (30404, 1, 0, 'Now the nightmare\'s real.Now Dr. Terrible is here.To make you quake with fear.To make the whole world kneel.And I won\'t feel ...a thing', 12, 0, 100, 0, 0, 0, 0, 0, 0, 'Dr. Terrible');
INSERT INTO `creature_text` VALUES (30404, 2, 0, 'I\'LL RISE AGAIN AND WHEN I DO I WILL HAVE MY REVENGE!', 14, 0, 100, 0, 0, 0, 0, 31173, 0, 'Dr. Terrible to Nergeld');
INSERT INTO `creature_text` VALUES (30405, 0, 0, 'By all the gods... it can\'t be... Muradin?', 12, 0, 100, 0, 0, 0, 0, 31032, 0, 'Brann');
INSERT INTO `creature_text` VALUES (30405, 1, 0, 'Come on boy, there\'s no mistak\'n it - it\'s definately you. Don\'t ya recognize your younger brother?', 12, 0, 100, 0, 0, 0, 0, 31033, 0, 'Brann');
INSERT INTO `creature_text` VALUES (30405, 2, 0, 'I can\'t believe this! You were dead! All accounts said so... what happened, Muradin. How did you get here?', 12, 0, 100, 0, 0, 0, 0, 31034, 0, 'Brann');
INSERT INTO `creature_text` VALUES (30405, 3, 0, 'Indeed! Magni will be so happy to see you too! He\'s gotten nothing but bad news for a long time now, but this changes everything!', 12, 0, 100, 0, 0, 0, 0, 31035, 0, 'Brann');
INSERT INTO `creature_text` VALUES (30405, 4, 0, 'He\'s here in Northrend, brother, looking for you. A seer in Wintergarde brought word that you were not dead, and he left Ironforge immediately to come find you.', 12, 0, 100, 0, 0, 0, 0, 31036, 0, 'Brann');
INSERT INTO `creature_text` VALUES (30405, 5, 0, 'Speaking of which...', 12, 0, 100, 0, 0, 0, 0, 31087, 0, 'Brann');
INSERT INTO `creature_text` VALUES (30405, 6, 0, 'That\'s in the past, Muradin. Regrets won\'t change anything.', 12, 0, 100, 0, 0, 0, 0, 31096, 0, 'Brann');
INSERT INTO `creature_text` VALUES (30405, 7, 0, 'Aye, be safe Muradin. I\'d join you, but I\'m on top of the most amazing discovery the world has yet seen. I can\'t abandon it now.', 12, 0, 100, 0, 0, 0, 0, 31102, 0, 'Brann');
INSERT INTO `creature_text` VALUES (30407, 0, 0, 'Let\'s get out of here! Quickly!', 12, 0, 100, 0, 0, 0, 0, 31115, 0, 'Captured Crusader Start');
INSERT INTO `creature_text` VALUES (30407, 1, 0, 'Faster, hero! We must get back to the Argent Vanguard and warn them of the impending attack!', 12, 0, 100, 0, 0, 0, 0, 31122, 0, 'Captured Crusader Middle');
INSERT INTO `creature_text` VALUES (30407, 1, 1, 'There are hundreds of them! Maybe thousands! They will attack at any moment! I\'m sure of it!', 12, 0, 100, 0, 0, 0, 0, 31123, 0, 'Captured Crusader Middle');
INSERT INTO `creature_text` VALUES (30407, 1, 2, 'Had you not come along I would have died for sure!', 12, 0, 100, 0, 0, 0, 0, 31124, 0, 'Captured Crusader Middle');
INSERT INTO `creature_text` VALUES (30407, 1, 3, 'They\'re planning a huge attack! We need to warn Highlord Fordring!', 12, 0, 100, 2, 0, 0, 0, 31121, 0, 'Captured Crusader Middle');
INSERT INTO `creature_text` VALUES (30407, 2, 0, 'Thank you, hero.', 12, 0, 100, 2, 0, 0, 0, 31130, 0, 'Captured Crusader End');
INSERT INTO `creature_text` VALUES (30407, 2, 1, 'Thank you, friend, but we\'re not out of trouble yet!', 12, 0, 100, 2, 0, 0, 0, 31114, 0, 'Captured Crusader End');
INSERT INTO `creature_text` VALUES (30408, 0, 0, 'What\'s that? You talkin\' to me, lad?', 12, 0, 100, 0, 0, 0, 0, 31037, 0, 'Yorg');
INSERT INTO `creature_text` VALUES (30408, 1, 0, 'My brother... yes... I do have brothers...', 12, 0, 100, 0, 0, 0, 0, 31038, 0, 'Yorg');
INSERT INTO `creature_text` VALUES (30408, 2, 0, 'Muradin clutches his head and reels for a moment as the memories rush back to him.', 16, 0, 100, 0, 0, 0, 0, 31039, 0, 'Yorg');
INSERT INTO `creature_text` VALUES (30408, 3, 0, '...Brann?', 12, 0, 100, 0, 0, 0, 0, 31040, 0, 'Yorg');
INSERT INTO `creature_text` VALUES (30408, 4, 0, 'I... I dunno, Brann. I\'ve been \'ere a long time... all I \'ave of me life before this place are flashes and nightmares.', 12, 0, 100, 0, 0, 0, 0, 31042, 0, 'Yorg');
INSERT INTO `creature_text` VALUES (30408, 5, 0, 'It\'s good te see you though, brother. More than words can say.', 12, 0, 100, 0, 0, 0, 0, 31043, 0, 'Yorg');
INSERT INTO `creature_text` VALUES (30408, 6, 0, 'Magn! Forgive me, the memories are comin\' back slowly, brother.', 12, 0, 100, 0, 0, 0, 0, 31045, 0, 'Yorg');
INSERT INTO `creature_text` VALUES (30408, 7, 0, 'The frostborn have been very good to me. They\'re strong people.', 12, 0, 100, 0, 0, 0, 0, 31048, 0, 'Yorg');
INSERT INTO `creature_text` VALUES (30408, 8, 0, 'Not much, Magni. I\'ve had nightmares of a human... tall... light hair... death black armor. His name rests on the tip of me tongue, but...', 12, 0, 100, 0, 0, 0, 0, 31089, 0, 'Yorg');
INSERT INTO `creature_text` VALUES (30408, 9, 0, '...Arthas.', 12, 0, 100, 0, 0, 0, 0, 31091, 0, 'Yorg');
INSERT INTO `creature_text` VALUES (30408, 10, 0, 'Aye, I know. I watched him turn... I watched him give up all that was right and I didn\'t lift a hand... I didn\'t even consider it until it was too late.', 12, 0, 100, 0, 0, 0, 0, 31094, 0, 'Yorg');
INSERT INTO `creature_text` VALUES (30408, 11, 0, 'No... no they won\'t. But I can make this right. I have te. I\'m goin\' after the boy. I\'ll make\'m answer for everything he\'s done.', 12, 0, 100, 0, 0, 0, 0, 31097, 0, 'Yorg');
INSERT INTO `creature_text` VALUES (30408, 12, 0, 'I\'m sure, Magni. I\'ll see this through, don\'t ya worry.', 12, 0, 100, 0, 0, 0, 0, 31100, 0, 'Yorg');
INSERT INTO `creature_text` VALUES (30408, 13, 0, 'Go Brann. Bring back an epic tale when we meet again. Farewell for now, brothers...', 12, 0, 100, 0, 0, 0, 0, 31103, 0, 'Yorg');
INSERT INTO `creature_text` VALUES (30409, 0, 0, 'You\'ve come for the doctor\'s plans! You\'ll only find death!', 12, 0, 0, 0, 0, 0, 0, 31119, 0, 'Apprentice Osterkilgr - Say 0');
INSERT INTO `creature_text` VALUES (30409, 1, 0, 'The doctor entrusted me with the plans to Nergeld, our flesh giant amalgamation made entirely of vargul! It will be the most powerful creation of its kind and a whole legion of them will be created to destroy your pitiful forces!', 12, 0, 0, 0, 0, 0, 0, 31090, 0, 'Apprentice Osterkilgr - Say 1');
INSERT INTO `creature_text` VALUES (30411, 0, 0, 'Look, Lagnus, I consider you a capable man, but my patience is wearing thin. I know that Muradin is here, can you point me to him or not?', 14, 0, 100, 0, 0, 0, 0, 31084, 0, 'Magni');
INSERT INTO `creature_text` VALUES (30411, 1, 0, 'Brother! There you are! I can barely believe my eyes... you\'re alive!', 14, 0, 100, 0, 0, 0, 0, 31085, 0, 'Magni');
INSERT INTO `creature_text` VALUES (30411, 2, 0, 'It\'s so good to see you again, Muradin. And what\'s this I heard about you being a King in your own right now? The Bronzebeards were always destined to greatness.', 12, 0, 100, 0, 0, 0, 0, 31086, 0, 'Magni');
INSERT INTO `creature_text` VALUES (30411, 3, 0, 'So it seems! And you haven\'t lost any muscle yourself. Do you remember anything of what happened, Muradin? Fate as turned ill in your absence.', 12, 0, 100, 0, 0, 0, 0, 31088, 0, 'Magni');
INSERT INTO `creature_text` VALUES (30411, 4, 0, 'Magni nods.', 16, 0, 100, 0, 0, 0, 0, 31092, 0, 'Magni');
INSERT INTO `creature_text` VALUES (30411, 5, 0, 'He\'s not the boy of your memories anymore, Muradin. He\'s become something else entirely.', 12, 0, 100, 0, 0, 0, 0, 31093, 0, 'Magni');
INSERT INTO `creature_text` VALUES (30411, 6, 0, 'Are you sure Muradin? I just got you back after years of thinking you were dead. I do not want to lose you again.', 12, 0, 100, 0, 0, 0, 0, 31099, 0, 'Magni');
INSERT INTO `creature_text` VALUES (30411, 7, 0, 'So be it then. I have to return to my people, brothers. Come back to me in one piece.', 12, 0, 100, 0, 0, 0, 0, 31101, 0, 'Magni');
INSERT INTO `creature_text` VALUES (30411, 8, 0, '...farewell brother.', 12, 0, 100, 0, 0, 0, 0, 31129, 0, 'Magni');
INSERT INTO `creature_text` VALUES (30449, 0, 0, 'You pose no threat, lesser beings! Give me your worst!', 14, 0, 100, 0, 0, 14133, 0, 32002, 0, 'vesperon SAY_VESPERON_AGGRO');
INSERT INTO `creature_text` VALUES (30449, 1, 0, 'The least you could do is put up a fight!', 14, 0, 100, 0, 0, 14134, 0, 32003, 0, 'vesperon SAY_VESPERON_SLAY_1');
INSERT INTO `creature_text` VALUES (30449, 1, 1, 'Was that the best you can do?', 14, 0, 100, 0, 0, 14135, 0, 32004, 0, 'vesperon SAY_VESPERON_SLAY_2');
INSERT INTO `creature_text` VALUES (30449, 2, 0, 'I still have some... fight... in me-e-e...', 14, 0, 100, 0, 0, 14140, 0, 32009, 0, 'vesperon SAY_VESPERON_DEATH');
INSERT INTO `creature_text` VALUES (30449, 3, 0, 'I will pick my teeth with your bones!', 14, 0, 100, 0, 0, 14136, 0, 32005, 0, 'vesperon SAY_VESPERON_BREATH');
INSERT INTO `creature_text` VALUES (30449, 4, 0, 'Father was right about you, Sartharion. You ARE a weakling.', 14, 0, 100, 0, 0, 14139, 0, 32008, 0, 'vesperon SAY_VESPERON_RESPOND');
INSERT INTO `creature_text` VALUES (30449, 5, 0, 'Aren\'t you tricky? I have a few tricks of my own...', 14, 0, 100, 0, 0, 14137, 0, 32006, 0, 'vesperon SAY_VESPERON_SPECIAL_1');
INSERT INTO `creature_text` VALUES (30449, 5, 1, 'Unlike you, I have many talents!', 14, 0, 100, 0, 0, 14138, 0, 32007, 0, 'vesperon SAY_VESPERON_SPECIAL_2');
INSERT INTO `creature_text` VALUES (30449, 6, 0, 'You pose no threat, lesser beings! Give me your worst!', 41, 0, 100, 0, 0, 0, 0, 32002, 0, 'Vesperon WHISPER_OPEN_PORTAL');
INSERT INTO `creature_text` VALUES (30449, 7, 0, 'A Vesperon Disciple appears in the Twilight!', 41, 0, 100, 0, 0, 0, 0, 32960, 0, 'Vesperon - WHISPER_OPENED_PORTAL');
INSERT INTO `creature_text` VALUES (30451, 0, 0, 'I fear nothing! Least of all you!', 14, 0, 100, 0, 0, 14111, 0, 31985, 0, 'shadron SAY_SHADRON_AGGRO');
INSERT INTO `creature_text` VALUES (30451, 1, 0, 'You are insignificant.', 14, 0, 100, 0, 0, 14112, 0, 31986, 0, 'shadron SAY_SHADRON_SLAY_1');
INSERT INTO `creature_text` VALUES (30451, 1, 1, 'Such mediocre resistance...', 14, 0, 100, 0, 0, 14113, 0, 31987, 0, 'shadron SAY_SHADRON_SLAY_2');
INSERT INTO `creature_text` VALUES (30451, 2, 0, 'We... are superior! How could this... be?', 14, 0, 100, 0, 0, 14118, 0, 31984, 0, 'shadron SAY_SHADRON_DEATH');
INSERT INTO `creature_text` VALUES (30451, 3, 0, 'You are easily bested!', 14, 0, 100, 0, 0, 14114, 0, 31988, 0, 'shadron SAY_SHADRON_BREATH');
INSERT INTO `creature_text` VALUES (30451, 4, 0, 'I will take pity on you, Sartharion, just this once.', 14, 0, 100, 0, 0, 14117, 0, 31991, 0, 'shadron SAY_SHADRON_RESPOND');
INSERT INTO `creature_text` VALUES (30451, 5, 0, 'Father taught me well.', 14, 0, 100, 0, 0, 14115, 0, 31989, 0, 'shadron SAY_SHADRON_SPECIAL_1');
INSERT INTO `creature_text` VALUES (30451, 5, 1, 'On your knees!', 14, 0, 100, 0, 0, 14116, 0, 21749, 0, 'shadron SAY_SHADRON_SPECIAL_2');
INSERT INTO `creature_text` VALUES (30451, 6, 0, '%s begins to open a Twilight Portal!', 41, 0, 100, 0, 0, 0, 0, 32554, 0, 'Shadron WHISPER_OPEN_PORTAL');
INSERT INTO `creature_text` VALUES (30451, 7, 0, 'A Shadron Disciple appears in the Twilight!', 41, 0, 100, 0, 0, 0, 0, 32958, 0, 'Shadron - WHISPER_OPENED_PORTAL');
INSERT INTO `creature_text` VALUES (30452, 0, 0, 'You have no place here! Your place... is among the departed!', 14, 0, 100, 0, 0, 14122, 0, 31993, 0, 'tenebron SAY_TENEBRON_AGGRO');
INSERT INTO `creature_text` VALUES (30452, 1, 0, 'No contest.', 14, 0, 100, 0, 0, 14123, 0, 31994, 0, 'tenebron SAY_TENEBRON_SLAY_1');
INSERT INTO `creature_text` VALUES (30452, 1, 1, 'Typical. Just as I was having fun...', 14, 0, 100, 0, 0, 14124, 0, 31995, 0, 'tenebron SAY_TENEBRON_SLAY_2');
INSERT INTO `creature_text` VALUES (30452, 2, 0, 'I should not... have held back...', 14, 0, 100, 0, 0, 14129, 0, 32000, 0, 'tenebron SAY_TENEBRON_DEATH');
INSERT INTO `creature_text` VALUES (30452, 3, 0, 'To darkness I condemn you.', 14, 0, 100, 0, 0, 14125, 0, 31996, 0, 'tenebron SAY_TENEBRON_BREATH');
INSERT INTO `creature_text` VALUES (30452, 4, 0, 'It is amusing to watch you struggle. Very well, witness how it is done!', 14, 0, 100, 0, 0, 14128, 0, 31999, 0, 'tenebron SAY_TENEBRON_RESPOND');
INSERT INTO `creature_text` VALUES (30452, 5, 0, 'Arrogant little creatures, to challenge powers you do not yet understand.', 14, 0, 100, 0, 0, 14126, 0, 31997, 0, 'tenebron SAY_TENEBRON_SPECIAL_1');
INSERT INTO `creature_text` VALUES (30452, 5, 1, 'I am no mere dragon. You will find that I am much, much more.', 14, 0, 100, 0, 0, 14127, 0, 31998, 0, 'tenebron SAY_TENEBRON_SPECIAL_2');
INSERT INTO `creature_text` VALUES (30452, 6, 0, 'Tenebron begins to hatch eggs in the Twilight!', 41, 0, 100, 0, 0, 0, 0, 32959, 0, 'Tenebron WHISPER_OPEN_PORTAL');
INSERT INTO `creature_text` VALUES (30452, 7, 0, 'Tenebron begins to hatch eggs in the Twilight!', 41, 0, 100, 0, 0, 0, 0, 32959, 0, 'Tenebron WHISPER_OPENED_PORTAL');
INSERT INTO `creature_text` VALUES (30461, 0, 0, '%s lets out a bellowing roar as she descends upon the nest.', 16, 0, 100, 15, 0, 0, 0, 31158, 0, 'Veranus');
INSERT INTO `creature_text` VALUES (30462, 0, 0, 'Look out below!', 14, 0, 100, 0, 0, 0, 0, 31159, 0, 'Thorim');
INSERT INTO `creature_text` VALUES (30462, 1, 0, 'Easy there, girl!  Don\'t you recognize your old master?', 12, 0, 100, 0, 0, 0, 0, 31160, 0, 'Thorim');
INSERT INTO `creature_text` VALUES (30462, 2, 0, 'I will see you at the Temple of Storms.  Looks like I\'m going to have to break her in again.', 12, 0, 100, 0, 0, 0, 0, 31161, 0, 'Thorim');
INSERT INTO `creature_text` VALUES (30474, 0, 0, 'Fool! You will never defeat me!', 14, 0, 100, 0, 0, 0, 0, 31180, 0, 'North Wind');
INSERT INTO `creature_text` VALUES (30474, 1, 0, 'I will never allow you to use the Horn of Elemental Fury against us! Die, weakling!', 14, 0, 100, 0, 0, 0, 0, 31181, 0, 'North Wind');
INSERT INTO `creature_text` VALUES (30475, 0, 0, 'Bethod\'s Sword?!! Very well, $r, I accept your challenge. But first, you must best my champions. Volgur! Squash this insect!', 14, 0, 100, 15, 2000, 0, 0, 31209, 0, 'Thane');
INSERT INTO `creature_text` VALUES (30475, 1, 0, 'Brita, create a pincushion of this puny $c!', 14, 0, 100, 22, 2000, 0, 0, 31211, 0, 'Thane');
INSERT INTO `creature_text` VALUES (30475, 2, 0, 'You may have defeated my champions, $c. I am something else entirely.', 14, 0, 100, 66, 2000, 0, 0, 31212, 0, 'Thane');
INSERT INTO `creature_text` VALUES (30475, 3, 0, 'Pray to your gods for a quick death.', 14, 0, 100, 53, 2000, 0, 0, 31213, 0, 'Thane');
INSERT INTO `creature_text` VALUES (30479, 0, 0, 'This is Camp Tunka\'lo, the village ruled by my brother, but I... I don\'t remember you.', 12, 0, 100, 6, 0, 0, 0, 32747, 0, 'Stormhoof\'s Spirit');
INSERT INTO `creature_text` VALUES (30479, 1, 0, 'The North Wind? It still troubles my people?', 12, 0, 100, 6, 0, 0, 0, 32749, 0, 'Stormhoof\'s Spirit');
INSERT INTO `creature_text` VALUES (30479, 2, 0, 'You both have my thanks and that of the village, though many will never know of your deeds.', 12, 0, 100, 2, 0, 0, 0, 32756, 0, 'Stormhoof\'s Spirit');
INSERT INTO `creature_text` VALUES (30479, 3, 0, 'I cannot remain here. I must return to my cairn where I belong, but when I see the ancestors again, it is I who will tell your story.', 12, 0, 100, 1, 0, 0, 0, 32759, 0, 'Stormhoof\'s Spirit');
INSERT INTO `creature_text` VALUES (30501, 0, 0, 'Thane Illskar, you were challenged to honorable combat and failed.', 14, 0, 100, 0, 2000, 0, 0, 31215, 0, 'Arbiter');
INSERT INTO `creature_text` VALUES (30501, 1, 0, 'You have been found wanting and are judged accordingly. Now rise, Thane Illskar the Damned!', 14, 0, 100, 0, 2000, 0, 0, 31216, 0, 'Arbiter');
INSERT INTO `creature_text` VALUES (30523, 0, 0, 'NOOOOOOOOOOOOOOOOOO!', 14, 0, 100, 15, 2000, 0, 0, 31227, 0, 'Illskar the Damned');
INSERT INTO `creature_text` VALUES (30549, 0, 0, 'You seek death?', 14, 0, 100, 0, 0, 14571, 0, 13051, 0, 'rivendare SAY_RIVE_AGGRO1');
INSERT INTO `creature_text` VALUES (30549, 0, 1, 'None shall pass!', 14, 0, 100, 0, 0, 14572, 0, 34133, 0, 'rivendare SAY_RIVE_AGGRO2');
INSERT INTO `creature_text` VALUES (30549, 0, 2, 'Be still!', 14, 0, 100, 0, 0, 14573, 0, 13053, 0, 'rivendare SAY_RIVE_AGGRO3');
INSERT INTO `creature_text` VALUES (30549, 1, 2, 'Life is meaningless. It is in death that we are truly tested.', 14, 0, 100, 0, 0, 14579, 0, 13060, 0, 'rivendare SAY_RIVE_TAUNT3');
INSERT INTO `creature_text` VALUES (30549, 2, 0, 'Bow to the might of the Scourge!', 14, 0, 100, 0, 0, 14576, 0, 13057, 0, 'rivendare SAY_RIVE_SPECIAL');
INSERT INTO `creature_text` VALUES (30549, 3, 0, 'You will find no peace in death.', 14, 0, 30, 0, 0, 14574, 0, 13055, 0, 'rivendare SAY_RIVE_SLAY1');
INSERT INTO `creature_text` VALUES (30549, 3, 1, 'The master\'s will is done.', 14, 0, 30, 0, 0, 14575, 0, 13056, 0, 'rivendare SAY_RIVE_SLAY2');
INSERT INTO `creature_text` VALUES (30549, 4, 0, 'Death... will not stop me.', 14, 0, 100, 0, 0, 14580, 0, 33116, 0, 'rivendare SAY_RIVE_DEATH');
INSERT INTO `creature_text` VALUES (30549, 5, 0, 'Enough prattling. Let them come. We shall grind their bones to dust.', 14, 0, 100, 0, 0, 14577, 0, 13058, 3, 'rivendare SAY_RIVE_TAUNT1');
INSERT INTO `creature_text` VALUES (30549, 6, 0, 'Conserve your anger. Harness your rage. You will all have outlets for your frustrations soon enough.', 14, 0, 100, 0, 0, 14578, 0, 13059, 3, 'rivendare SAY_RIVE_TAUNT2');
INSERT INTO `creature_text` VALUES (30550, 0, 0, 'Do not trouble yourself, little tauren. Neither Stormhoof, nor you and your ally will stand in my way.', 12, 0, 100, 1, 0, 0, 0, 31260, 0, 'Image of the North Wind');
INSERT INTO `creature_text` VALUES (30550, 1, 0, 'Even now, my power grows. I have long waited for the day when my revenge would be complete.', 12, 0, 100, 1, 0, 0, 0, 31261, 0, 'Image of the North Wind');
INSERT INTO `creature_text` VALUES (30550, 2, 0, 'Stormhoof and his kin will be erased from history and these lands will be restored to their rightful owner!', 12, 0, 100, 1, 0, 0, 0, 31262, 0, 'Image of the North Wind');
INSERT INTO `creature_text` VALUES (30551, 0, 0, 'This whole situation seems a bit paranoid, don\'t you think?', 12, 0, 100, 1, 0, 0, 0, 31324, 0, 'Hearthsinger Forresten - LINE_FORREST_1');
INSERT INTO `creature_text` VALUES (30551, 1, 0, 'Thank the Light for that.', 12, 0, 100, 1, 0, 0, 0, 32573, 0, 'Hearthsinger Forresten - LINE_FORREST_2');
INSERT INTO `creature_text` VALUES (30552, 0, 0, 'It\'s a strange order, you can\'t deny. Suspicious food? Under that definition, you should arrest Belfast!', 12, 0, 100, 1, 0, 0, 0, 31326, 0, 'Fras Siabi - LINE_FRAS_1');
INSERT INTO `creature_text` VALUES (30552, 1, 0, '%s nods.', 16, 0, 100, 273, 0, 0, 0, 32574, 0, 'Fras Siabi - LINE_FRAS_2');
INSERT INTO `creature_text` VALUES (30553, 0, 0, 'Orders are orders. If the Prince says jump...', 12, 0, 100, 1, 0, 0, 0, 31325, 0, 'Footman James - LINE_JAMES_1');
INSERT INTO `creature_text` VALUES (30553, 1, 0, 'Don\'t worry too much. By the time I went off duty, we hadn\'t found a scrap of befouled grain here.', 12, 0, 100, 1, 0, 0, 0, 32572, 0, 'Footman James - LINE_JAMES_2');
INSERT INTO `creature_text` VALUES (30561, 0, 0, 'The soldiers are doing important work. The safety of the people is more important, Mal, if you\'re interested in your customers living to spend another day.', 12, 0, 100, 1, 0, 0, 0, 32571, 0, 'Gryan Stoutmantle - LINE_GRYAN_1');
INSERT INTO `creature_text` VALUES (30570, 0, 0, 'The Stone Crow\'s closed today, sorry. There are too many health concerns, and I\'m not about to have Ed under fire for supposedly making people sick.', 12, 0, 100, 1, 0, 0, 0, 31724, 0, 'Emery Neill TALK_FLUFF 1');
INSERT INTO `creature_text` VALUES (30570, 0, 1, 'I hate to disappoint people, but the Stone Crow\'s closed. If the kids from the orphanage were evacuated, I don\'t see why we should be open, either.', 12, 0, 100, 1, 0, 0, 0, 31729, 0, 'Emery Neill TALK_FLUFF 2');
INSERT INTO `creature_text` VALUES (30571, 0, 0, 'What were you doing in my cellar? There\'s a food scare going on, and the last thing I need is strangers rummaging around in my goods! Shoo!', 12, 0, 100, 5, 0, 0, 0, 31323, 0, 'Michael Belfast - SAY_CELLAR');
INSERT INTO `creature_text` VALUES (30571, 0, 1, 'Hey! Stop rooting around in my cellar! Clear out!', 12, 0, 100, 5, 0, 0, 0, 31322, 0, 'Michael Belfast - SAY_CELLAR');
INSERT INTO `creature_text` VALUES (30571, 1, 0, 'I HEARD THAT! No more ale for you! Not a drop!', 12, 0, 100, 22, 0, 0, 0, 31327, 0, 'Michael Belfast - LINE_BELFAST_1');
INSERT INTO `creature_text` VALUES (30631, 0, 0, 'Damn it, Olakin, get your arse back here! There\'s work to be done!', 12, 0, 100, 5, 0, 0, 0, 32195, 0, 'Darkrider Arly to Crusader Olakin Sainrith');
INSERT INTO `creature_text` VALUES (30632, 0, 0, 'Your entrails will make a fine necklace.', 12, 0, 100, 0, 0, 13535, 0, 30500, 0, 'Mjordin Water Magus');
INSERT INTO `creature_text` VALUES (30632, 0, 1, 'I\'ll eat your heart!', 12, 0, 100, 0, 0, 13695, 0, 30508, 0, 'Mjordin Water Magus');
INSERT INTO `creature_text` VALUES (30632, 0, 2, 'Haraak foln!', 12, 0, 100, 0, 0, 13541, 0, 30506, 0, 'Mjordin Water Magus');
INSERT INTO `creature_text` VALUES (30632, 0, 3, 'I\'ll eat your heart!', 12, 0, 100, 0, 0, 13542, 0, 30508, 0, 'Mjordin Water Magus');
INSERT INTO `creature_text` VALUES (30632, 0, 4, 'Sniveling pig!', 12, 0, 100, 0, 0, 13539, 0, 30504, 0, 'Mjordin Water Magus');
INSERT INTO `creature_text` VALUES (30632, 0, 5, 'I spit on you!', 12, 0, 100, 0, 0, 13538, 0, 30503, 0, 'Mjordin Water Magus');
INSERT INTO `creature_text` VALUES (30658, 0, 0, 'Prison guards, we are leaving! These adventurers are taking over! Go go go!', 14, 0, 100, 5, 0, 0, 0, 31474, 3, 'Lieutenant Sinclari - Intro 1');
INSERT INTO `creature_text` VALUES (30658, 1, 0, 'I\'m locking the door. Good luck, and thank you for doing this.', 12, 0, 100, 396, 0, 0, 0, 31475, 3, 'Lieutenant Sinclari - Intro 2');
INSERT INTO `creature_text` VALUES (30658, 2, 0, 'You did it! You held the Blue Dragonflight back and defeated their commander. Amazing work!', 12, 0, 100, 396, 0, 0, 0, 31694, 3, 'Lieutenant Sinclari - Outro');
INSERT INTO `creature_text` VALUES (30725, 0, 0, 'You come to die!', 12, 0, 100, 0, 0, 13537, 0, 30502, 0, 'Jotunheim Sleep-Watcher');
INSERT INTO `creature_text` VALUES (30725, 0, 1, 'I spit on you!', 12, 0, 100, 0, 0, 13537, 0, 30503, 0, 'Jotunheim Sleep-Watcher');
INSERT INTO `creature_text` VALUES (30725, 0, 2, 'Your entrails will make a fine necklace.', 12, 0, 100, 0, 0, 13535, 0, 30500, 0, 'Jotunheim Sleep-Watcher');
INSERT INTO `creature_text` VALUES (30824, 0, 0, 'FIRE! FIRE!', 14, 0, 100, 0, 0, 16732, 0, 38681, 0, 'Sky-Reaver Korm Blackscar - Fire');
INSERT INTO `creature_text` VALUES (30824, 1, 0, 'Get on board, now! This whole mountainside could collapse at any moment.', 14, 0, 100, 0, 0, 16733, 0, 37212, 0, 'Sky-Reaver Korm Blackscar - Final');
INSERT INTO `creature_text` VALUES (30835, 0, 0, 'My lady.', 12, 0, 100, 0, 0, 0, 0, 31446, 0, 'Shadow Cultist');
INSERT INTO `creature_text` VALUES (30835, 1, 0, 'There is word from Jotunheim. The sleep-watchers there believe that they have found someone of significance.', 12, 0, 100, 0, 0, 0, 0, 31476, 0, 'Shadow Cultist');
INSERT INTO `creature_text` VALUES (30835, 2, 0, 'The cultist practically sputters.', 16, 0, 100, 0, 0, 0, 0, 31477, 0, 'Shadow Cultist');
INSERT INTO `creature_text` VALUES (30835, 3, 0, 'Look like, my lady? A vrykul, I suppose. They did not actually show him to me. Ever since The Shadow Vault....', 12, 0, 100, 0, 0, 0, 0, 31478, 0, 'Shadow Cultist');
INSERT INTO `creature_text` VALUES (30835, 4, 0, 'A name? Oh, yes, the name! I believe it was Iskalder.', 12, 0, 100, 0, 0, 0, 0, 31479, 0, 'Shadow Cultist');
INSERT INTO `creature_text` VALUES (30835, 5, 0, 'Right away, my lady.', 12, 0, 100, 2, 0, 0, 0, 31480, 0, 'Shadow Cultist');
INSERT INTO `creature_text` VALUES (30836, 0, 0, 'Report.', 12, 0, 100, 1, 0, 0, 0, 31481, 0, 'Vardmadra');
INSERT INTO `creature_text` VALUES (30836, 1, 0, 'Describe this vrykul. What does he look like?', 12, 0, 100, 1, 0, 0, 0, 31482, 0, 'Vardmadra');
INSERT INTO `creature_text` VALUES (30836, 2, 0, 'I am not interested in excuses. Perhaps they gave you a name?', 12, 0, 100, 1, 0, 0, 0, 31483, 0, 'Vardmadra');
INSERT INTO `creature_text` VALUES (30836, 3, 0, 'Iskalder?! You fool! Have you no idea who that is? He\'s only the greatest vrykul warrior who ever lived!', 12, 0, 100, 1, 0, 0, 0, 31484, 0, 'Vardmadra');
INSERT INTO `creature_text` VALUES (30836, 4, 0, 'Return to Jotunheim and tell them to keep him asleep until I arrive. I will judge this vrykul with my own eyes.', 12, 0, 100, 1, 0, 0, 0, 31485, 0, 'Vardmadra');
INSERT INTO `creature_text` VALUES (30838, 1, 1, 'You challenge death itself! Come then, \"King.\" Face the might of Mograine!', 12, 0, 100, 0, 0, 0, 0, 31455, 0, 'Mograine\"s arrival');
INSERT INTO `creature_text` VALUES (30838, 2, 1, 'I will see you back at the Valley of Echoes. Suffer well, ally.', 12, 0, 100, 0, 0, 0, 0, 31456, 0, 'Opens portal');
INSERT INTO `creature_text` VALUES (30839, 1, 1, 'Too weak to finish your own battles? Very well then...', 12, 0, 100, 0, 0, 0, 0, 31489, 0, 'Jayde arrival');
INSERT INTO `creature_text` VALUES (30839, 2, 2, 'Anything else you need help with before I go? Right, I thought not...', 12, 0, 100, 0, 0, 0, 0, 31490, 0, 'Oppens portal and dissapears');
INSERT INTO `creature_text` VALUES (30840, 1, 1, 'Say hello to my little friend!', 12, 0, 100, 0, 0, 0, 0, 31486, 0, 'Munch arrival');
INSERT INTO `creature_text` VALUES (30840, 2, 2, 'Arise, Melt!', 12, 0, 100, 0, 0, 0, 0, 31487, 0, 'Summon Melts');
INSERT INTO `creature_text` VALUES (30840, 3, 3, 'We\'re done here. I\'m out.', 12, 0, 100, 0, 0, 0, 0, 31488, 0, 'Munch oppening portal');
INSERT INTO `creature_text` VALUES (30884, 1, 1, 'It\'s Iskalder! Quickly, use The Bone Witch\'s Amulet!', 41, 0, 100, 0, 100, 0, 0, 31523, 0, 'Originaly it as the Bonne witch guide');
INSERT INTO `creature_text` VALUES (30891, 0, 0, 'The Blessed Banner of the Crusade has been planted.\nDefend the banner from all attackers!', 41, 0, 100, 0, 0, 0, 0, 31539, 0, 'Blessed Banner of the Crusade');
INSERT INTO `creature_text` VALUES (30897, 0, 0, 'Threat index threshold exceeded.  Celestial archive aborted. Security level heightened.', 14, 0, 100, 0, 0, 13761, 0, 29576, 0, 'brann SAY_EVENT_B_2_MARN');
INSERT INTO `creature_text` VALUES (30897, 1, 0, 'Additional background is relevant to your query: following global combat between Aesir and Vanir--', 14, 0, 100, 0, 0, 13762, 0, 30606, 0, 'brann SAY_EVENT_END_15_MARN');
INSERT INTO `creature_text` VALUES (30897, 2, 0, 'Unknown. Data suggests that impetus for global combat originated with prime designate Loken, who neutralized all remaining Aesir and Vanir, affecting termination of conflict. Prime designate Loken then initiated stasis of several seed races, including earthen, giants and vrykul, at designated holding facilities.', 14, 0, 100, 0, 0, 13763, 0, 30607, 0, 'brann SAY_EVENT_END_17_MARN');
INSERT INTO `creature_text` VALUES (30897, 3, 0, 'Essentially that is correct.', 14, 0, 100, 0, 0, 13764, 0, 30608, 0, 'brann SAY_EVENT_END_19_MARN');
INSERT INTO `creature_text` VALUES (30898, 0, 0, 'Security breach in progress. Analysis of historical archives transferred to lower-priority queue. Countermeasures engaged.', 14, 0, 100, 0, 0, 13756, 0, 29575, 0, 'brann SAY_EVENT_A_2_KADD');
INSERT INTO `creature_text` VALUES (30898, 1, 0, 'Accessing... Creators arrived to extirpate symbiotic infection. Assessment revealed that Old God infestation had grown malignant. Excising parasites would result in loss of host--', 14, 0, 100, 0, 0, 13757, 0, 30603, 0, 'brann SAY_EVENT_END_09_KADD');
INSERT INTO `creature_text` VALUES (30898, 2, 0, 'Correct. Creators neutralized parasitic threat and contained it within the host. Forge of Wills and other systems were instituted to create new earthen. Safeguards were implemented, and protectors were appointed.', 14, 0, 100, 0, 0, 13758, 0, 30604, 0, 'brann SAY_EVENT_END_11_KADD');
INSERT INTO `creature_text` VALUES (30898, 3, 0, 'Designations: Aesir and Vanir. Or in the common nomenclature, storm and earth giants. Sentinel Loken designated supreme. Dragon Aspects appointed to monitor evolution on Azeroth.', 14, 0, 100, 0, 0, 13759, 0, 30605, 0, 'brann SAY_EVENT_END_13_KADD');
INSERT INTO `creature_text` VALUES (30899, 0, 0, 'Warning: life form pattern not recognized. Archival processing terminated.  Continued interference will result in targeted response.', 14, 0, 100, 0, 0, 13765, 0, 29561, 0, 'brann SAY_EVENT_INTRO_3_ABED');
INSERT INTO `creature_text` VALUES (30899, 1, 0, 'Critical threat index. Void analysis diverted. Initiating sanitization protocol.', 14, 0, 100, 0, 0, 13767, 0, 29577, 0, 'brann SAY_EVENT_C_2_ABED');
INSERT INTO `creature_text` VALUES (30899, 2, 0, 'Alert: security fail-safes deactivated. Beginning memory purge and... ', 14, 0, 100, 0, 0, 13768, 0, 29579, 0, 'brann SAY_EVENT_D_2_ABED');
INSERT INTO `creature_text` VALUES (30899, 3, 0, 'System online. Life form pattern recognized.  Welcome, Branbronzan.  Query?', 14, 0, 100, 0, 0, 13769, 0, 29580, 0, 'brann SAY_EVENT_D_4_ABED');
INSERT INTO `creature_text` VALUES (30899, 4, 0, 'Accessing prehistoric data... retrieved. In the beginning the earthen were created to--', 14, 0, 100, 0, 0, 13770, 0, 30611, 0, 'brann SAY_EVENT_END_03_ABED');
INSERT INTO `creature_text` VALUES (30899, 5, 0, 'Accessing... In the early stages of it\'s development cycle, Azeroth suffered infection by parasitic necrophotic symbiotes.', 14, 0, 100, 0, 0, 13771, 0, 30612, 0, 'brann SAY_EVENT_END_05_ABED');
INSERT INTO `creature_text` VALUES (30899, 6, 0, 'Designation: Old Gods. Old Gods rendered all systems, including earthen, defenseless in order to facilitate assimilation. This matrix destabilization has been termed \"the Curse of Flesh\". Effects of destabilization increased over time.', 14, 0, 100, 0, 0, 13772, 0, 30613, 0, 'brann SAY_EVENT_END_07_ABED');
INSERT INTO `creature_text` VALUES (30899, 7, 0, 'Acknowledged, Branbronzan. Session terminated.', 14, 0, 100, 0, 0, 13773, 0, 30614, 0, 'brann SAY_EVENT_END_21_ABED');
INSERT INTO `creature_text` VALUES (30920, 0, 0, 'What little geist want?', 12, 0, 100, 0, 0, 0, 0, 31614, 0, 'Lumbering Atrocity');
INSERT INTO `creature_text` VALUES (30920, 0, 1, 'Present?', 12, 0, 100, 0, 0, 0, 0, 31609, 0, 'Lumbering Atrocity');
