INSERT INTO `creature_text` VALUES (30920, 0, 3, 'What bomb thing for?', 12, 0, 100, 0, 0, 0, 0, 31610, 0, 'Lumbering Atrocity');
INSERT INTO `creature_text` VALUES (30920, 0, 4, 'Want me to deliver somewhere?', 12, 0, 100, 0, 0, 0, 0, 31611, 0, 'Lumbering Atrocity');
INSERT INTO `creature_text` VALUES (30920, 0, 5, 'I not sure this safe, little geist.', 12, 0, 100, 0, 0, 0, 0, 31612, 0, 'Lumbering Atrocity');
INSERT INTO `creature_text` VALUES (30920, 0, 6, 'This no go here.', 12, 0, 100, 0, 0, 0, 0, 31613, 0, 'Lumbering Atrocity');
INSERT INTO `creature_text` VALUES (30920, 0, 7, 'For me?', 12, 0, 100, 0, 0, 0, 0, 31608, 0, 'Lumbering Atrocity');
INSERT INTO `creature_text` VALUES (30924, 0, 0, 'Now fight me, $n! Kill Iskalder!', 14, 0, 100, 0, 0, 1167, 0, 31540, 0, 'Possessed Iskalder');
INSERT INTO `creature_text` VALUES (30945, 0, 0, 'Iskalder, there you are. What is this? Engaged in battle already?', 14, 0, 100, 457, 0, 13824, 0, 31528, 0, 'Vardmadra');
INSERT INTO `creature_text` VALUES (30945, 1, 0, 'NO! How is this possible?', 14, 0, 100, 457, 0, 13825, 0, 31530, 0, 'Vardmadra');
INSERT INTO `creature_text` VALUES (30945, 2, 0, 'I know not how this was possible, but you must still be judged Iskalder. Wait... what is this?', 14, 0, 100, 457, 0, 13824, 0, 31531, 0, 'Vardmadra');
INSERT INTO `creature_text` VALUES (30945, 3, 0, 'Stay away from me creature! Do not touch me!', 14, 0, 100, 0, 0, 1168, 0, 31533, 0, 'Vardmadra to Nightswood');
INSERT INTO `creature_text` VALUES (30945, 4, 0, 'Ahahahahahaha! It is done. Return to my cave. We have much to discuss!', 14, 0, 100, 457, 0, 1167, 0, 31534, 0, 'Vardmadra to ');
INSERT INTO `creature_text` VALUES (30955, 0, 0, 'This? This is me taking control of you. This is me setting up my final revenge!', 14, 0, 100, 457, 0, 13824, 0, 31532, 0, 'Lady Nightswood');
INSERT INTO `creature_text` VALUES (30956, 0, 0, 'I knew that you would come.', 14, 0, 100, 0, 0, 0, 0, 31835, 0, 'Sapph');
INSERT INTO `creature_text` VALUES (30956, 1, 0, 'You have some fight in you. I\'m impressed.', 14, 0, 100, 0, 0, 0, 0, 31852, 0, 'Sapph');
INSERT INTO `creature_text` VALUES (30956, 2, 0, 'Welcome to the world of the unliving.', 14, 0, 100, 0, 0, 0, 0, 31858, 0, 'Sapph');
INSERT INTO `creature_text` VALUES (30956, 3, 0, 'Well fought...', 14, 0, 100, 0, 0, 0, 0, 31853, 0, 'Sapph');
INSERT INTO `creature_text` VALUES (30992, 1, 0, 'Oh, bravo. Do you feel proud of yourself now that you\'ve unleashed this terrible mistake?', 12, 0, 100, 21, 0, 0, 0, 31550, 0, 'Doctor Sabnok');
INSERT INTO `creature_text` VALUES (30992, 2, 0, 'I already had him scheduled for disposal, but I suppose we can speed up the process a bit.', 12, 0, 100, 1, 0, 0, 0, 31551, 0, 'Doctor Sabnok');
INSERT INTO `creature_text` VALUES (30992, 3, 0, 'It\'s a pity so many fine, and reusable, parts will go to waste... ', 12, 0, 100, 11, 0, 0, 0, 31553, 0, 'Doctor Sabnok');
INSERT INTO `creature_text` VALUES (30993, 1, 0, 'Patches get revenge on Doctor Sabnok!', 12, 0, 100, 51, 0, 0, 0, 31556, 0, '\"Patches\"');
INSERT INTO `creature_text` VALUES (30993, 2, 0, 'Patches not going to be cut open or sewn up no more!', 12, 0, 100, 1, 0, 0, 0, 31554, 0, '\"Patches\"');
INSERT INTO `creature_text` VALUES (30994, 0, 0, 'No remedy seems to work - the entire city has sickened...', 12, 0, 100, 0, 0, 0, 0, 31583, 0, 'Magistrate Barthilas');
INSERT INTO `creature_text` VALUES (30994, 0, 1, 'The soldiers are spreading panic with the rumors of bad food. Neighbors are accusing one another of poison. The city will fall into bedlam!', 12, 0, 100, 0, 0, 0, 0, 31584, 0, 'Magistrate Barthilas');
INSERT INTO `creature_text` VALUES (30994, 0, 2, 'Everyone is falling ill - this is an epidemic!', 12, 0, 100, 0, 0, 0, 0, 31582, 0, 'Magistrate Barthilas');
INSERT INTO `creature_text` VALUES (30994, 1, 0, 'I pray the illness I\'m feeling is due to stress ulcers...', 12, 0, 100, 0, 0, 0, 0, 31587, 0, 'Magistrate Barthilas');
INSERT INTO `creature_text` VALUES (30994, 1, 1, 'How can I possibly help the city in such a widespread crisis?', 12, 0, 100, 0, 0, 0, 0, 31581, 0, 'Magistrate Barthilas');
INSERT INTO `creature_text` VALUES (30994, 1, 2, 'I\'m at a loss. What can one simple man do in the face of disaster?', 12, 0, 100, 0, 0, 0, 0, 31586, 0, 'Magistrate Barthilas');
INSERT INTO `creature_text` VALUES (30997, 0, 0, 'Why, hello again!', 12, 0, 100, 0, 0, 0, 0, 32022, 0, 'Chromie #3 RP5_LINE_CHROMIE0');
INSERT INTO `creature_text` VALUES (31003, 0, 0, 'BY THE LIGHT! Those damned monsters! Look at what they\'ve done to our people!', 12, 0, 100, 5, 0, 0, 0, 31560, 0, 'Crusader Lord Dalfors');
INSERT INTO `creature_text` VALUES (31003, 1, 0, 'Burn it down, boys. Burn it all down.', 12, 0, 100, 1, 0, 0, 0, 31561, 0, 'Crusader Lord Dalfors');
INSERT INTO `creature_text` VALUES (31003, 2, 0, 'Let \'em come. They\'ll pay for what they\'ve done!', 12, 0, 100, 1, 0, 0, 0, 31562, 0, 'Crusader Lord Dalfors');
INSERT INTO `creature_text` VALUES (31003, 3, 0, 'We\'ve done it, lads! We\'ve taken the pinnacle from the Scourge! Report to Father Gustav at once and tell him the good news! We\'re gonna get to buildin\' and settin\' up! Go!', 14, 0, 100, 71, 0, 0, 0, 31573, 0, 'Crusader Lord Dalfors');
INSERT INTO `creature_text` VALUES (31013, 0, 0, 'Leave no survivors!', 14, 0, 100, 0, 0, 14793, 0, 31567, 0, 'The Lich King');
INSERT INTO `creature_text` VALUES (31013, 1, 0, 'Cower before my terrible creations!', 14, 0, 100, 0, 0, 14800, 0, 31568, 0, 'The Lich King');
INSERT INTO `creature_text` VALUES (31013, 2, 0, 'Feast my children! Feast upon the flesh of the living!', 14, 0, 100, 0, 0, 14801, 0, 31569, 0, 'The Lich King');
INSERT INTO `creature_text` VALUES (31013, 3, 0, 'Lay down your arms and surrender your souls!', 14, 0, 100, 0, 0, 14802, 0, 31570, 0, 'The Lich King');
INSERT INTO `creature_text` VALUES (31016, 1, 0, 'You dare to challenge me? You haven\'t earned the right!', 14, 0, 100, 25, 0, 15633, 0, 31597, 0, 'Overthane Balargarde to Safirdrang');
INSERT INTO `creature_text` VALUES (31016, 2, 0, 'Vardmadra?! Did the Lich King send you personally?', 14, 0, 100, 66, 0, 15634, 0, 31599, 0, 'Overthane Balargarde to Possessed Vardmadra');
INSERT INTO `creature_text` VALUES (31016, 3, 0, 'Very well. I will dispatch these creatures. It is only an inconvenience. Prepare to die!', 14, 0, 100, 0, 0, 15635, 0, 31600, 0, 'Overthane Balargarde to Possessed Vardmadra');
INSERT INTO `creature_text` VALUES (31016, 4, 0, 'Safirdrang, let them feel the chill of Icecrown!', 14, 0, 100, 0, 0, 15636, 0, 31601, 0, 'Overthane Balargarde to 0');
INSERT INTO `creature_text` VALUES (31016, 5, 0, 'STOP! Kneel you fools, it\'s the Lich King!', 14, 0, 100, 0, 0, 15637, 0, 31627, 0, 'Overthane Balargarde to The Lich King');
INSERT INTO `creature_text` VALUES (31016, 6, 0, 'But, my lord...?', 14, 0, 100, 0, 0, 15638, 0, 31635, 0, 'Overthane Balargarde to The Lich King');
INSERT INTO `creature_text` VALUES (31016, 7, 0, 'DIE DOGS!', 14, 0, 100, 0, 0, 15639, 0, 31637, 0, 'Overthane Balargarde to The Lich King');
INSERT INTO `creature_text` VALUES (31017, 0, 0, 'Enough, Michael. Business is hurting enough with this scare as it is. We can use every copper.', 12, 0, 100, 1, 0, 0, 0, 32560, 0, 'Mal Corricks - LINE_MAL_1');
INSERT INTO `creature_text` VALUES (31017, 1, 0, '%s grudgingly nods.', 16, 0, 100, 273, 0, 0, 0, 32569, 0, 'Mal Corricks - LINE_MAL_2');
INSERT INTO `creature_text` VALUES (31017, 2, 0, 'I can\'t argue with that.', 12, 0, 100, 1, 0, 0, 0, 32570, 0, 'Mal Corricks - LINE_MAL_3');
INSERT INTO `creature_text` VALUES (31021, 0, 0, 'What\'s the commotion outside?', 12, 0, 100, 1, 0, 0, 0, 31730, 0, 'Sophie Aaren TALK_FLUFF 1');
INSERT INTO `creature_text` VALUES (31021, 0, 1, 'Is something going on? I hear angry voices.', 12, 0, 100, 1, 0, 0, 0, 31731, 0, 'Sophie Aaren TALK_FLUFF 2');
INSERT INTO `creature_text` VALUES (31021, 0, 2, 'Everything\'s been so strange lately...', 12, 0, 100, 1, 0, 0, 0, 31732, 0, 'Sophie Aaren TALK_FLUFF 3');
INSERT INTO `creature_text` VALUES (31027, 0, 0, 'Look, I\'ll sell you shields today, but no maces. The last thing I want is for some innocent to get brained by one of my weapons.', 12, 0, 100, 1, 0, 0, 0, 31721, 0, 'Leeka Turner TALK_FLUFF 1');
INSERT INTO `creature_text` VALUES (31027, 0, 1, 'I don\'t know how much longer I\'ll be open today with all the commotion outside. If you need something repaired, I\'ll take a quick look.', 12, 0, 100, 1, 0, 0, 0, 31722, 0, 'Leeka Turner TALK_FLUFF 2');
INSERT INTO `creature_text` VALUES (31027, 0, 2, 'I hope you\'re not coming in here fixing for a fight. There are too many angry people on the streets today.', 12, 0, 100, 1, 0, 0, 0, 31720, 0, 'Leeka Turner TALK_FLUFF 3');
INSERT INTO `creature_text` VALUES (31029, 0, 0, 'He\'s on his way!', 14, 0, 100, 457, 0, 15643, 0, 31595, 0, 'Possessed Vardmadra to Player');
INSERT INTO `creature_text` VALUES (31029, 1, 0, 'Wrong, Balargarde. You WILL accept this challenge!', 14, 0, 100, 457, 0, 15644, 0, 31598, 0, 'Possessed Vardmadra to Overthane Balargarde');
INSERT INTO `creature_text` VALUES (31029, 2, 0, 'My lord.', 14, 0, 100, 457, 0, 15645, 0, 31631, 0, 'Possessed Vardmadra to The Lich King');
INSERT INTO `creature_text` VALUES (31029, 3, 0, 'But...!', 14, 0, 100, 0, 0, 15646, 0, 31633, 0, 'Possessed Vardmadra to The Lich King');
INSERT INTO `creature_text` VALUES (31033, 0, 0, 'There goes the hero of the Vanguard!', 12, 0, 100, 66, 0, 0, 0, 31385, 0, 'Crusader of Virtue');
INSERT INTO `creature_text` VALUES (31033, 0, 1, '%s cheers at you.', 16, 1, 100, 71, 0, 0, 0, 25275, 0, 'Crusader of Virtue');
INSERT INTO `creature_text` VALUES (31033, 0, 2, 'We couldn\'t have done it without you, $g sir:ma\'am;.', 12, 0, 100, 66, 0, 0, 0, 31384, 0, 'Crusader of Virtue');
INSERT INTO `creature_text` VALUES (31033, 0, 3, 'You honor us with your presence, $n.', 12, 0, 100, 66, 0, 0, 0, 31386, 0, 'Crusader of Virtue');
INSERT INTO `creature_text` VALUES (31033, 0, 4, 'Thank you, $n. From the bottom of my heart. Thank you.', 12, 0, 100, 66, 0, 0, 0, 31388, 0, 'Crusader of Virtue');
INSERT INTO `creature_text` VALUES (31033, 0, 5, 'Call on me anytime, $n.', 12, 0, 100, 66, 0, 0, 0, 31389, 0, 'Crusader of Virtue');
INSERT INTO `creature_text` VALUES (31036, 0, 0, 'The first of the Horde towers has fallen! Destroy all three and we will hasten their retreat!', 14, 7, 100, 0, 0, 0, 0, 33299, 0, 'Commander Zanneth');
INSERT INTO `creature_text` VALUES (31036, 1, 0, 'The second tower has fallen! Destroy the final tower and we will hasten their retreat!', 14, 7, 100, 0, 0, 0, 0, 33300, 0, 'Commander Zanneth');
INSERT INTO `creature_text` VALUES (31036, 2, 0, 'The Horde towers have fallen! We have forced their hand. Finish off the remaining forces!', 14, 7, 100, 0, 0, 0, 0, 33301, 0, 'Commander Zanneth');
INSERT INTO `creature_text` VALUES (31036, 3, 0, 'Show those animals no mercy, $n!', 12, 7, 100, 0, 0, 0, 0, 33101, 0, 'Commander Zanneth');
INSERT INTO `creature_text` VALUES (31057, 0, 0, 'Hello. Master Fras Siabi is not in at this time. Please come back tomorrow to Fras Siabi\'s Premium Tobacco! Beep!', 12, 0, 100, 0, 0, 0, 0, 31716, 0, 'Mechanical Greeter RY7R');
INSERT INTO `creature_text` VALUES (31083, 0, 0, 'Honor guard stay where you are.', 14, 0, 100, 1, 0, 15600, 0, 31628, 0, 'The Lich King to 0');
INSERT INTO `creature_text` VALUES (31083, 1, 0, 'Vardmadra. I\'d wondered where you disappeared to. How is Iskalder?', 14, 0, 100, 6, 0, 15601, 0, 31629, 0, 'The Lich King to 0');
INSERT INTO `creature_text` VALUES (31083, 2, 0, 'I see through your disguise, Lady Nightswood. YOU THINK THAT YOU CAN FOOL ME?!', 14, 0, 100, 5, 0, 15602, 0, 31632, 0, 'The Lich King to 0');
INSERT INTO `creature_text` VALUES (31083, 3, 0, 'You may continue your combat, overthane.', 14, 0, 100, 25, 0, 15603, 0, 31634, 0, 'The Lich King to 0');
INSERT INTO `creature_text` VALUES (31083, 4, 0, 'But nothing! Finish them! DO NOT FAIL ME, BALARGARDE!', 14, 0, 100, 5, 0, 15604, 0, 31636, 0, 'The Lich King to Overthane Balargarde');
INSERT INTO `creature_text` VALUES (31083, 5, 0, 'You have bested one of my finest, but your efforts are for naught.', 14, 0, 100, 1, 0, 15605, 0, 31693, 0, 'The Lich King to 0');
INSERT INTO `creature_text` VALUES (31083, 6, 0, 'The frozen heart of Icecrown awaits....', 14, 0, 100, 1, 0, 15606, 0, 31695, 0, 'The Lich King to 0');
INSERT INTO `creature_text` VALUES (31091, 0, 0, 'The first of the Alliance towers has fallen! Destroy all three and we will hasten their retreat!', 14, 1, 100, 0, 0, 0, 0, 33296, 0, 'Commander Dardosh');
INSERT INTO `creature_text` VALUES (31091, 1, 0, 'Lok\'tar! The second tower falls! Destroy the final tower and we will hasten their retreat!', 14, 1, 100, 0, 0, 0, 0, 33297, 0, 'Commander Dardosh');
INSERT INTO `creature_text` VALUES (31091, 2, 0, 'The Alliance towers have fallen! We have forced their hand. Finish off the remaining forces!', 14, 1, 100, 0, 0, 0, 0, 33298, 0, 'Commander Dardosh');
INSERT INTO `creature_text` VALUES (31091, 3, 0, 'Show those animals no mercy, $n!', 12, 7, 100, 0, 0, 0, 0, 33101, 0, 'Commander Dardosh');
INSERT INTO `creature_text` VALUES (31108, 0, 0, 'Stop the Horde from retrieving the embers, $n. We cannot risk them having the advantage when the battle resumes!', 12, 7, 100, 0, 0, 0, 0, 33102, 0, 'Siege Master Stouthandle');
INSERT INTO `creature_text` VALUES (31109, 0, 0, 'Destroy their foul machines of war, $n!', 12, 7, 100, 0, 0, 0, 0, 33103, 0, 'Senior Demolitionist Legoso');
INSERT INTO `creature_text` VALUES (31125, 0, 0, '%s goes into a berserker rage!', 16, 0, 100, 0, 0, 0, 0, 34057, 0, 'archavon EMOTE_BERSERK');
INSERT INTO `creature_text` VALUES (31125, 1, 0, '%s lunges for $n!', 41, 0, 100, 0, 0, 0, 0, 33085, 0, 'Archavon the Stone Watcher Leap Emote');
INSERT INTO `creature_text` VALUES (31126, 0, 0, 'Ah, you\'ve finally arrived Prince Arthas. You\'re here just in the nick of time.', 12, 0, 100, 0, 0, 0, 0, 27811, 0, 'culling SAY_PHASE302');
INSERT INTO `creature_text` VALUES (31126, 1, 0, 'There\'s no need for you to understand, Arthas. All you need to do is die.', 12, 0, 100, 0, 0, 0, 0, 27815, 0, 'culling SAY_PHASE305');
INSERT INTO `creature_text` VALUES (31134, 0, 0, 'We finish this now, champions of Kirin Tor!', 14, 0, 100, 0, 0, 13947, 0, 31723, 0, 'cyanigosa SAY_AGGRO');
INSERT INTO `creature_text` VALUES (31134, 1, 0, 'I will end the Kirin Tor!', 14, 0, 100, 0, 0, 13952, 0, 31725, 0, 'cyanigosa SAY_SLAY_1');
INSERT INTO `creature_text` VALUES (31134, 1, 1, 'Dalaran WILL fall!', 14, 0, 100, 0, 0, 13953, 0, 31726, 0, 'cyanigosa SAY_SLAY_2');
INSERT INTO `creature_text` VALUES (31134, 1, 2, 'So ends you defiance of the Spell-Weaver.', 14, 0, 100, 0, 0, 13954, 0, 31727, 0, 'cyanigosa SAY_SLAY_3');
INSERT INTO `creature_text` VALUES (31134, 2, 0, 'Perhaps... we have... underestimated... you.', 14, 0, 100, 0, 0, 13955, 0, 31728, 0, 'cyanigosa SAY_DEATH');
INSERT INTO `creature_text` VALUES (31134, 3, 0, 'A valiant defense, but this city must be razed. I will fulfill Malygos\'s wishes myself!', 14, 0, 100, 396, 0, 13946, 0, 31718, 0, 'cyanigosa SAY_SPAWN');
INSERT INTO `creature_text` VALUES (31134, 4, 0, 'Am I interrupting?', 14, 0, 100, 0, 0, 13951, 0, 0, 0, 'cyanigosa SAY_DISRUPTION');
INSERT INTO `creature_text` VALUES (31134, 5, 0, 'Shiver and die!', 14, 0, 100, 0, 0, 13948, 0, 0, 0, 'cyanigosa SAY_BREATH_ATTACK');
INSERT INTO `creature_text` VALUES (31134, 6, 0, 'The world has forgotten what true magic is! Let this be a reminder!', 14, 0, 100, 0, 0, 13949, 0, 0, 0, 'cyanigosa SAY_SPECIAL_ATTACK_1');
INSERT INTO `creature_text` VALUES (31134, 6, 1, 'Who among you can withstand my power?', 14, 0, 100, 0, 0, 13950, 0, 31740, 0, 'cyanigosa SAY_SPECIAL_ATTACK_2');
INSERT INTO `creature_text` VALUES (31135, 0, 0, 'Valhalas is yours to win or die in, $n. But whatever you do, stay within the bounds of the arena. To flee is to lose and be dishonored.', 12, 0, 0, 0, 0, 0, 0, 31744, 0, 'Greiff quest 1 say 1');
INSERT INTO `creature_text` VALUES (31135, 1, 1, '$n and $g his : her; comrades in arms have chosen to accept honorable combat within the sacred confines of Valhalas.', 14, 0, 0, 0, 0, 0, 0, 31745, 0, 'Greiff quest 1 say 2');
INSERT INTO `creature_text` VALUES (31135, 2, 2, 'There can be only one outcome to such a battle: death for one side or the other. Let $n prove $g himself : herself; upon the bones of those outsiders who have fallen before!', 14, 0, 0, 0, 0, 0, 0, 31746, 0, 'Greiff quest 1 say 3');
INSERT INTO `creature_text` VALUES (31135, 3, 3, 'The fallen heroes of Valhalas emerge from the ground to do battle once more!', 41, 0, 0, 0, 0, 14998, 0, 31756, 0, 'Greiff quest 1 say 4');
INSERT INTO `creature_text` VALUES (31135, 4, 4, '$n has defeated the fallen heroes of Valhalas battles past. This is only a beginning, but it will suffice.', 14, 0, 0, 0, 0, 14998, 0, 31755, 0, 'Greiff quest 1 say 5');
INSERT INTO `creature_text` VALUES (31135, 5, 5, 'Prepare yourself. Khit\'rix will be entering Valhalas from the southeast. Remember, do not leave the ring or you will lose the battle.', 12, 0, 0, 0, 0, 0, 0, 31771, 0, 'Greiff quest 2 say 1');
INSERT INTO `creature_text` VALUES (31135, 6, 6, '$n has accepted the challenge of Khit\'rix the Dark Master. May the gods show mercy upon $g him : her; for Khit\'rix surely will not.', 14, 0, 0, 0, 0, 0, 0, 31770, 0, 'Greiff quest 2 say 2');
INSERT INTO `creature_text` VALUES (31135, 7, 7, 'Khit\'rix the Dark Master skitters into Valhalas from the southeast!', 41, 0, 0, 0, 0, 14998, 0, 32753, 0, 'Greiff quest 2 say 3');
INSERT INTO `creature_text` VALUES (31135, 8, 8, 'Khit\'rix the Dark Master has been defeated by $n and $g his : her; band of companions. Let the next challenge be issued!', 14, 0, 0, 0, 0, 14998, 0, 31820, 0, 'Greiff quest 2 sasy 4');
INSERT INTO `creature_text` VALUES (31135, 9, 9, 'Sigrid Iceborn has returned to the heights of Jotunheim to prove herself against $n.  When last they met, $n bested her in personal combat.  Let us see the outcome of this match.', 14, 0, 0, 0, 0, 0, 0, 31892, 0, 'Greiff quest 3 say 1');
INSERT INTO `creature_text` VALUES (31135, 10, 10, 'Circling Valhalas, Sigrid Iceborn approaches to seek her revenge!', 41, 0, 0, 0, 0, 14998, 0, 32757, 0, 'Greiff quest 3 say 2');
INSERT INTO `creature_text` VALUES (31135, 11, 11, '$n has defeated Sigrid Iceborn for a second time.  Well, this time $g he : she; did it with the help of $g his : her; friends, but a win is a win!', 14, 0, 0, 0, 0, 14998, 0, 31921, 0, 'Greiff quest 3 say 3');
INSERT INTO `creature_text` VALUES (31135, 12, 12, 'Carnage is coming!  Remember, no matter what you do, do NOT leave the battle ring or I will disqualify you and your group.', 12, 0, 0, 0, 0, 0, 0, 31885, 0, 'Greiff quest 4 say 1');
INSERT INTO `creature_text` VALUES (31135, 13, 13, 'From the bowels of the Underhalls comes Carnage.  Brave and foolish $n has accepted the challenge.  $G He : She; and $g his : her; group stand ready to face the monstrosity.', 14, 0, 0, 0, 0, 0, 0, 31930, 0, 'Greiff quest 4 say 2');
INSERT INTO `creature_text` VALUES (31135, 14, 14, 'Lumbering in from the south, the smell of Carnage precedes him!', 41, 0, 0, 0, 0, 14998, 0, 32758, 0, 'Greiff quest 4 say 3');
INSERT INTO `creature_text` VALUES (31135, 15, 15, 'The horror known as Carnage is no more.  Could it be that $n is truly worthy of battle in Valhalas?  We shall see.', 14, 0, 0, 0, 0, 14998, 0, 31931, 0, 'Greiff quest 4 say 4');
INSERT INTO `creature_text` VALUES (31135, 16, 16, 'Look to the southeast and you will see the thane upon the platform near Gjonner the Merciless when he shows himself. Let him come down.  Stay within the ring of Valhalas.', 12, 0, 0, 0, 0, 0, 0, 31938, 0, 'Greiff quest 5 say 1');
INSERT INTO `creature_text` VALUES (31135, 17, 17, 'Thane Banahogg returns to Valhalas for the first time in ages to prove that the vrykul are the only beings worthy to fight within its sacred ring.  Will $n prove him wrong?', 14, 0, 0, 0, 0, 0, 0, 31939, 0, 'Greiff quest 5 say 2');
INSERT INTO `creature_text` VALUES (31135, 18, 18, 'Thane Banahogg appears upon the overlook to the southeast!', 41, 0, 0, 0, 0, 14998, 0, 32760, 0, 'Greiff quest 5 say 3');
INSERT INTO `creature_text` VALUES (31135, 19, 19, 'Thane Banahogg the Deathblow has fallen to $n and $g his : her; fighting companions.  $G He : She; has but one challenge ahead of $g him : her;.  Who will it be?', 14, 0, 0, 0, 0, 14998, 0, 31940, 0, 'Greiff quest 5 say 4');
INSERT INTO `creature_text` VALUES (31135, 20, 20, 'It\'s too late to run now.  Do not leave the ring.  Die bravely, $n!', 12, 0, 0, 0, 0, 0, 0, 31949, 0, 'Greiff quest 6 say 1');
INSERT INTO `creature_text` VALUES (31135, 21, 21, 'From the depths of Icecrown Citadel, one of the Lich King\'s chosen comes to put an end to the existence of $n and $g his : her; friends.', 14, 0, 0, 0, 0, 0, 0, 31951, 0, 'Greiff quest 6 say 2');
INSERT INTO `creature_text` VALUES (31135, 22, 22, 'Warriors of Jotunheim, I present to you, Blood Prince Sandoval!', 14, 0, 0, 0, 0, 0, 0, 31955, 0, 'Greiff quest 6 say 3');
INSERT INTO `creature_text` VALUES (31135, 23, 23, 'Without warning, Prince Sandoval magically appears within Valhalas!', 41, 0, 0, 0, 0, 14998, 0, 32761, 0, 'Greiff quest 6 say 4');
INSERT INTO `creature_text` VALUES (31135, 24, 24, 'The unthinkable has happened... $n has slain Prince Sandoval!', 14, 0, 0, 0, 0, 0, 0, 31945, 0, 'Greiff quest 6 say 5');
INSERT INTO `creature_text` VALUES (31135, 25, 25, 'In defeating him, $g he : she; and $g his : her; fighting companions have proven themselves worthy of battle in this most sacred place of vrykul honor.', 14, 0, 0, 0, 0, 0, 0, 31946, 0, 'Greiff quest 6 say 6');
INSERT INTO `creature_text` VALUES (31135, 26, 26, 'ALL HAIL $n, CHAMPION OF VALHALAS!', 14, 0, 0, 0, 0, 14998, 0, 31948, 0, 'Greiff quest 6 say 7');
INSERT INTO `creature_text` VALUES (31135, 27, 27, 'You were not prepared!', 14, 0, 0, 0, 0, 0, 0, 31753, 0, 'Greiff kill');
INSERT INTO `creature_text` VALUES (31161, 0, 0, 'I knew that you would come.', 14, 0, 100, 0, 0, 0, 0, 31835, 0, 'Sapph');
INSERT INTO `creature_text` VALUES (31161, 1, 0, 'You have some fight in you. I\'m impressed.', 14, 0, 100, 0, 0, 0, 0, 31852, 0, 'Sapph');
INSERT INTO `creature_text` VALUES (31161, 2, 0, 'Welcome to the world of the unliving.', 14, 0, 100, 0, 0, 0, 0, 31858, 0, 'Sapph');
INSERT INTO `creature_text` VALUES (31161, 3, 0, 'Well fought...', 14, 0, 100, 0, 0, 0, 0, 31853, 0, 'Sapph');
INSERT INTO `creature_text` VALUES (31222, 0, 0, 'When I am done here, I am going to mount your heads upon the walls of Azjol-Nerub!', 14, 0, 0, 0, 0, 0, 0, 31836, 0, 'Dark Master say 1');
INSERT INTO `creature_text` VALUES (31235, 0, 0, 'Thank the Light for granting me another chance. And thank you, $n.', 12, 0, 100, 1, 0, 0, 0, 31826, 0, 'Crusader Olakin Sainrith');
INSERT INTO `creature_text` VALUES (31235, 1, 0, 'Without your help, I would\'ve been doomed to a life of undeath among the Lich King\'s gruesome creations.', 12, 0, 100, 1, 0, 0, 0, 31827, 0, 'Crusader Olakin Sainrith');
INSERT INTO `creature_text` VALUES (31235, 2, 0, 'There will be time for a proper thanks later, but there is still a battle to be fought!', 12, 0, 100, 25, 0, 0, 0, 31828, 0, 'Crusader Olakin Sainrith');
INSERT INTO `creature_text` VALUES (31237, 0, 0, 'You shouldn\'t have done that!  He\'s not going to like it.', 15, 0, 100, 0, 0, 0, 0, 32338, 0, 'Matthias Lehner - On quest accept');
INSERT INTO `creature_text` VALUES (31238, 0, 0, 'Come, Cloudwing. Let\'s do a lap.', 12, 0, 100, 0, 0, 0, 0, 32247, 0, 'Hira Snowdawn');
INSERT INTO `creature_text` VALUES (31238, 0, 1, 'Feeling as restless as I am, Cloudwing? Then let us fly!', 12, 0, 100, 0, 0, 0, 0, 32252, 0, 'Hira Snowdawn');
INSERT INTO `creature_text` VALUES (31238, 0, 2, 'I\'m bored, Cloudwing. Go, go!', 12, 0, 100, 0, 0, 0, 0, 32251, 0, 'Hira Snowdawn');
INSERT INTO `creature_text` VALUES (31238, 1, 0, '%s sighs softly and rolls her shoulders.', 16, 0, 100, 0, 0, 0, 0, 32249, 0, 'Hira Snowdawn');
INSERT INTO `creature_text` VALUES (31238, 1, 1, '%s looks around Krasus\' Landing, bored.', 16, 0, 100, 0, 0, 0, 0, 32244, 0, 'Hira Snowdawn');
INSERT INTO `creature_text` VALUES (31238, 1, 2, '%s yawns quietly into the palm of her hand.', 16, 0, 100, 0, 0, 0, 0, 32245, 0, 'Hira Snowdawn');
INSERT INTO `creature_text` VALUES (31242, 0, 0, 'Ah, there you are! Remember me? Of course you do! Wait right there, I\'m coming down.', 14, 0, 0, 0, 0, 0, 0, 31882, 0, 'Sigrid Iceborn say 1');
INSERT INTO `creature_text` VALUES (31242, 1, 0, 'I told you I\'d be better prepared when next we met. I\'ve fought and won the Hyldsmeet, trained at Valkyrion, and here I am.  Come and get some!', 14, 0, 0, 0, 0, 0, 0, 31883, 0, 'Sigrid Iceborn say 2');
INSERT INTO `creature_text` VALUES (31254, 0, 0, 'The undead are upon us!', 12, 0, 10, 0, 0, 0, 0, 32027, 0, 'Lordaeron Footsoldier to Ghoulish Minion');
INSERT INTO `creature_text` VALUES (31254, 0, 1, 'The rumors were true!  The prince has gone mad!', 12, 0, 10, 0, 0, 0, 0, 32029, 0, 'Lordaeron Footsoldier to Ghoulish Minion');
INSERT INTO `creature_text` VALUES (31258, 0, 0, 'I will take pleasure in gutting you!', 12, 0, 100, 0, 0, 13533, 0, 30498, 0, 'Ymirheim Chosen Warrior');
INSERT INTO `creature_text` VALUES (31258, 0, 1, 'I\'ll eat your heart!', 12, 0, 100, 1, 0, 13542, 0, 30508, 0, 'Ymirheim Chosen Warrior');
INSERT INTO `creature_text` VALUES (31277, 0, 0, 'ENOUGH!  You tiny insects are not worthy to do battle within this sacred place!  You do not fight with honor!  You do not even ascend! ', 14, 0, 0, 0, 0, 0, 0, 31935, 0, 'Thane say 1');
INSERT INTO `creature_text` VALUES (31277, 1, 0, 'Fight me and die you cowards!', 14, 0, 0, 0, 0, 0, 0, 31936, 0, 'Thane say 2');
INSERT INTO `creature_text` VALUES (31277, 2, 0, 'Haraak foln!', 12, 0, 0, 0, 0, 0, 0, 30506, 0, 'Thane say 3');
INSERT INTO `creature_text` VALUES (31279, 1, 0, 'The Light\'s blessing be upon you for aiding me in my time of need, $n.', 12, 0, 100, 0, 0, 0, 0, 31941, 0, 'Father Kamaros');
INSERT INTO `creature_text` VALUES (31279, 2, 0, 'I\'ve had my fill of this place. Let us depart.', 12, 0, 100, 0, 0, 0, 0, 31942, 0, 'Father Kamaros');
INSERT INTO `creature_text` VALUES (31279, 3, 0, 'You must tell my brothers that I live.', 12, 0, 100, 0, 0, 0, 0, 31944, 0, 'Father Kamaros');
INSERT INTO `creature_text` VALUES (31279, 4, 0, 'I have you to thank for my life. I will return to my comrades and spread word of your bravery. Fight the Scourge with all the strength you can muster, and we will be by your side.', 12, 0, 100, 0, 0, 0, 0, 31943, 0, 'Father Kamaros');
INSERT INTO `creature_text` VALUES (31283, 0, 0, 'Ah, the hand of the Ebon Blade has finally come to claim me. I tremble in fear.', 14, 0, 100, 0, 0, 0, 0, 31959, 0, 'Orbaz Bloodbane');
INSERT INTO `creature_text` VALUES (31283, 1, 0, 'Hah! That\'s all you\'ve got? I\'m surprised you even made it to the citadel.', 14, 0, 100, 0, 0, 0, 0, 31961, 0, 'Orbaz Bloodbane');
INSERT INTO `creature_text` VALUES (31283, 2, 0, 'The Lich King has given me power beyond your wildest nightmares. Behold!', 14, 0, 100, 0, 0, 0, 0, 31962, 0, 'Orbaz Bloodbane');
INSERT INTO `creature_text` VALUES (31283, 3, 0, 'It all ends here.', 14, 0, 100, 0, 0, 0, 0, 31963, 0, 'Orbaz Bloodbane');
INSERT INTO `creature_text` VALUES (31283, 4, 0, '%s chuckles knowingly.', 16, 0, 100, 0, 0, 0, 0, 31966, 0, 'Orbaz Bloodbane');
INSERT INTO `creature_text` VALUES (31283, 5, 0, 'The Lich King shall extinquish... every... last... one... of you.', 14, 0, 100, 0, 0, 0, 0, 31967, 0, 'Orbaz Bloodbane');
INSERT INTO `creature_text` VALUES (31283, 6, 0, 'There you are Darion! I was wondering when you\'d crawl forth!', 14, 0, 100, 0, 0, 0, 0, 31964, 0, 'Orbaz Bloodbane');
INSERT INTO `creature_text` VALUES (31290, 0, 0, 'Orbaz Bloodbane, self proclaimed hand of the Lich King.', 14, 0, 100, 0, 0, 0, 0, 31975, 0, 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES (31290, 1, 0, 'Couldn\'t wait for me to be out of the way so that you could sieze control?', 14, 0, 100, 0, 0, 0, 0, 31977, 0, 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES (31290, 2, 0, 'You\'ll never have what it takes to lead anything, Orbaz, not even a legion of mindless skeletons.', 14, 0, 100, 0, 0, 0, 0, 31983, 0, 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES (31290, 3, 0, 'Good riddance.', 14, 0, 100, 0, 0, 0, 0, 31978, 0, 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES (31301, 0, 0, 'You dare to think you have thwarted my will? That you have broken my plans?', 12, 0, 100, 25, 0, 0, 0, 31973, 0, 'LK SAY 1');
INSERT INTO `creature_text` VALUES (31301, 1, 0, 'You have accomplished nothing!', 12, 0, 100, 22, 0, 0, 0, 31974, 0, 'LK SAY 2');
INSERT INTO `creature_text` VALUES (31301, 2, 0, 'Your feeble resistance has only hastened your doom.', 12, 0, 100, 25, 0, 0, 0, 31976, 0, 'LK SAY 3');
INSERT INTO `creature_text` VALUES (31301, 3, 0, 'So spout your platitudes and flail until you are spent. In the end, you will be mine, and Morbidus will be the harbinger of your death!', 12, 0, 100, 22, 0, 0, 0, 31979, 0, 'LK SAY 4');
INSERT INTO `creature_text` VALUES (31301, 4, 0, 'Morbidus, show them what happens to those who defy the will of their king!', 12, 0, 100, 25, 0, 0, 0, 31981, 0, 'LK SAY 5');
INSERT INTO `creature_text` VALUES (31306, 0, 0, 'Knights of the Ebon Blade, prepare for battle!', 12, 0, 100, 1, 0, 0, 0, 32015, 0, 'DHAKAR SAY 1');
INSERT INTO `creature_text` VALUES (31333, 0, 0, 'No, my beloved.', 12, 0, 100, 1, 0, 0, 0, 32040, 0, 'Alexstrasza the Life-Binder to Player');
INSERT INTO `creature_text` VALUES (31333, 1, 0, 'They must not discover the fate of the young paladin. Not yet.', 12, 11, 100, 5, 0, 0, 0, 32041, 0, 'Alexstrasza the Life-Binder to Player');
INSERT INTO `creature_text` VALUES (31333, 2, 0, 'Come to me, $n.', 15, 0, 100, 0, 0, 0, 0, 32048, 0, 'Alexstrasza the Life-Binder to Player');
INSERT INTO `creature_text` VALUES (31334, 0, 0, 'My Queen, do they know?', 12, 0, 100, 6, 0, 0, 0, 32038, 0, 'Korialstrasz to Player');
INSERT INTO `creature_text` VALUES (31334, 1, 0, '%s nods.', 16, 0, 100, 0, 0, 0, 0, 32046, 0, 'Korialstrasz to Player');
INSERT INTO `creature_text` VALUES (31334, 2, 0, 'They will not.', 12, 0, 100, 1, 0, 0, 0, 32047, 0, 'Korialstrasz to Player');
INSERT INTO `creature_text` VALUES (31397, 0, 0, 'AHAHAHAHA... you\'ll join us soon enough!', 14, 0, 100, 0, 0, 0, 0, 32473, 0, 'Saronite Mine Slave');
INSERT INTO `creature_text` VALUES (31397, 0, 1, 'I don\'t want to leave! I want to stay here!', 14, 0, 100, 0, 0, 0, 0, 32471, 0, 'Saronite Mine Slave');
INSERT INTO `creature_text` VALUES (31397, 0, 2, 'NO! You\'re wrong! The voices in my head are beautiful!', 14, 0, 100, 0, 0, 0, 0, 32470, 0, 'Saronite Mine Slave');
INSERT INTO `creature_text` VALUES (31397, 0, 3, 'My life for you!', 14, 0, 100, 0, 0, 0, 0, 32467, 0, 'Saronite Mine Slave');
INSERT INTO `creature_text` VALUES (31397, 0, 4, 'I\'m coming, master!', 14, 0, 100, 0, 0, 0, 0, 32466, 0, 'Saronite Mine Slave');
INSERT INTO `creature_text` VALUES (31397, 0, 5, 'I won\'t leave!', 14, 0, 100, 0, 0, 0, 0, 32469, 0, 'Saronite Mine Slave');
INSERT INTO `creature_text` VALUES (31397, 0, 6, 'I\'ll never return. The whole reason for my existence awaits below!', 14, 0, 100, 0, 0, 0, 0, 32472, 0, 'Saronite Mine Slave');
INSERT INTO `creature_text` VALUES (31397, 0, 7, 'I must get further underground to where he is. I must jump!', 14, 0, 100, 0, 0, 0, 0, 32474, 0, 'Saronite Mine Slave');
INSERT INTO `creature_text` VALUES (31397, 1, 0, 'Please, no more.', 12, 1, 100, 0, 0, 0, 0, 32506, 0, 'Saronite Mine Slave');
INSERT INTO `creature_text` VALUES (31397, 1, 1, 'Hit me again and you\'ll regret it!', 12, 1, 100, 0, 0, 0, 0, 32507, 0, 'Saronite Mine Slave');
INSERT INTO `creature_text` VALUES (31397, 1, 2, 'Ow!', 12, 1, 100, 0, 0, 0, 0, 32508, 0, 'Saronite Mine Slave');
INSERT INTO `creature_text` VALUES (31397, 1, 3, 'I\'m sorry. I\'ll work harder.', 12, 1, 100, 0, 0, 0, 0, 32509, 0, 'Saronite Mine Slave');
INSERT INTO `creature_text` VALUES (31397, 1, 4, 'Soon the master will reclaim me!', 12, 1, 100, 0, 0, 0, 0, 32510, 0, 'Saronite Mine Slave');
INSERT INTO `creature_text` VALUES (31412, 0, 0, 'Kor\'kron, stand down!', 12, 0, 100, 0, 0, 16222, 0, 32286, 0, 'Thrall - herald of war');
INSERT INTO `creature_text` VALUES (31412, 1, 0, 'Jaina...', 12, 0, 100, 0, 0, 16223, 0, 32287, 0, 'Thrall - herald of war');
INSERT INTO `creature_text` VALUES (31412, 2, 0, 'Jaina, what happened at the Wrathgate. It was a betrayal from within...', 12, 0, 100, 0, 0, 16224, 0, 32289, 0, 'Thrall - herald of war');
INSERT INTO `creature_text` VALUES (31412, 3, 0, 'The Horde has lost the Undercity.', 12, 0, 100, 1, 0, 16225, 0, 32292, 0, 'Thrall - herald of war');
INSERT INTO `creature_text` VALUES (31412, 4, 0, 'We now prepare to lay siege to the city and bring the perpetrators of this unforgivable crime to justice.', 12, 0, 100, 378, 0, 16226, 0, 32293, 0, 'Thrall - herald of war');
INSERT INTO `creature_text` VALUES (31412, 5, 0, 'If we are forced into a conflict, the Lich King will destroy our divided forces in Northrend.', 12, 0, 100, 378, 0, 16227, 0, 32294, 0, 'Thrall - herald of war');
INSERT INTO `creature_text` VALUES (31412, 6, 0, 'We will make this right, Jaina. Tell your king all that you have learned here.', 12, 0, 100, 378, 0, 16228, 0, 32295, 0, 'Thrall - herald of war');
INSERT INTO `creature_text` VALUES (31412, 7, 0, 'Kor\'kron, prepare transport to the Undercity.', 12, 0, 100, 378, 0, 16229, 0, 32300, 0, 'Thrall - herald of war');
INSERT INTO `creature_text` VALUES (31418, 0, 0, 'Thrall, what has happened? The King is preparing for war...', 12, 0, 100, 0, 0, 16129, 0, 32288, 0, 'Jaina - herald of war');
INSERT INTO `creature_text` VALUES (31418, 1, 0, 'I will deliver this information to King Wrynn, Thrall, but...', 12, 0, 100, 378, 0, 16130, 0, 32296, 0, 'Jaina - herald of war');
INSERT INTO `creature_text` VALUES (31418, 2, 0, 'Bolvar was like a brother to him. In the King\'s absence, Bolvar kept the Alliance united. He found strength for our people in our darkest hours. He watched over Anduin, raising him as his own.', 12, 0, 100, 378, 0, 16131, 0, 32297, 0, 'Jaina - herald of war');
INSERT INTO `creature_text` VALUES (31418, 3, 0, 'I fear that the rage will consume him, Thrall. I remain hopeful that reason will prevail, but we must prepare for the worst... for war.', 12, 0, 100, 378, 0, 16132, 0, 32298, 0, 'Jaina - herald of war');
INSERT INTO `creature_text` VALUES (31418, 4, 0, 'Farewell, Warchief. I pray that the next time we meet it will be as allies.', 12, 0, 100, 0, 0, 16133, 0, 32299, 0, 'Jaina - herald of war');
INSERT INTO `creature_text` VALUES (31419, 0, 0, 'Lady Proudmoore, the Warchief speaks the truth. This subterfuge was set in motion by Varimathras and Grand Apothecary Putress. It was not the Horde\'s doing.', 12, 0, 100, 0, 0, 16315, 0, 32290, 0, 'Sylvanas - herald of war');
INSERT INTO `creature_text` VALUES (31419, 1, 0, 'As the combined Horde and Alliance forces began their assault upon the Wrath Gate, an uprising broke out in the Undercity. Varimathras and hordes of his demonic brethren attacked. Hundreds of my people were slain in the coup. I barely managed to escape with my life.', 12, 0, 100, 0, 0, 16316, 0, 32291, 0, 'Sylvanas - herald of war');
INSERT INTO `creature_text` VALUES (31423, 0, 0, 'Why is Thrall allowing this to happen to our city?', 12, 0, 100, 0, 0, 0, 0, 32239, 0, 'Kaja - herald of war');
INSERT INTO `creature_text` VALUES (31425, 0, 0, 'Where is the Warchief?', 12, 0, 100, 0, 0, 0, 0, 32236, 0, 'olvia - herald of war');
INSERT INTO `creature_text` VALUES (31425, 1, 0, 'What are all these Forsaken doing here?', 12, 0, 100, 0, 0, 0, 0, 32233, 0, 'olvia - herald of war');
INSERT INTO `creature_text` VALUES (31426, 0, 0, 'Guards!', 14, 6, 100, 0, 0, 0, 0, 4583, 0, 'Doras');
INSERT INTO `creature_text` VALUES (31427, 0, 0, 'What is going on?', 12, 0, 100, 0, 0, 0, 0, 45914, 0, 'felika - herald of war');
INSERT INTO `creature_text` VALUES (31427, 1, 0, 'This is an outrage!', 12, 0, 100, 0, 0, 0, 0, 46620, 0, 'felika - herald of war');
INSERT INTO `creature_text` VALUES (31428, 0, 0, 'You will pay for what you\'ve done! For the Argent Crusade!', 12, 0, 100, 0, 0, 0, 0, 32198, 0, 'OLAKIN SAY 1');
INSERT INTO `creature_text` VALUES (31428, 1, 0, 'Look! What\'s a frostwyrm doing here? It\'s coming straight for us.', 12, 0, 100, 25, 0, 0, 0, 32173, 0, 'Crusader Olakin Sainrith to Player');
INSERT INTO `creature_text` VALUES (31429, 0, 0, 'We demand answers!', 12, 0, 100, 0, 0, 0, 0, 32235, 0, 'Sana - herald of war');
INSERT INTO `creature_text` VALUES (31430, 0, 0, 'What is going on?', 12, 0, 100, 0, 0, 0, 0, 32232, 0, 'auctioneer thathung - herald of war');
INSERT INTO `creature_text` VALUES (31431, 0, 0, 'SILENCE!', 12, 0, 100, 0, 0, 0, 0, 32240, 0, 'Overlord runthak - herald of war');
INSERT INTO `creature_text` VALUES (31431, 1, 0, 'We are on the brink of all out war with the Alliance!', 12, 0, 100, 0, 0, 0, 0, 32241, 0, 'Overlord runthak - herald of war');
INSERT INTO `creature_text` VALUES (31431, 2, 0, 'Tragic events have unfolded in Northrend. The Warchief is doing all that he can to keep us safe.', 12, 0, 100, 0, 0, 0, 0, 32242, 0, 'Overlord runthak - herald of war');
