INSERT INTO `creature_text` VALUES (31814, 4, 0, 'Your motives are pure. The keystone\'s parts shall be reunited. Go to Ulduar and learn the answers to your questions, Brann Bronzebeard.', 12, 0, 100, 0, 0, 0, 0, 32390, 0, 'A Distant Voice');
