INSERT INTO `creature_text` VALUES (32184, 3, 0, 'I call your bluff.  You\'re a paladin after all.  Your obsession with redemption goes beyond the inane.', 12, 0, 100, 1, 0, 0, 0, 32593, 0, 'The Lich King');
INSERT INTO `creature_text` VALUES (32184, 4, 0, 'You surely wouldn\'t destroy humanity\'s only chance to redeem its most wayward son.  You\'d sooner die!', 12, 0, 100, 1, 0, 0, 0, 32594, 0, 'The Lich King');
