INSERT INTO `creature_text` VALUES (32239, 6, 0, 'That might be, but I don\'t need to stand on holy ground to run that disembodied heart of yours with the Ashbringer.', 12, 0, 100, 1, 0, 0, 0, 32589, 0, 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES (32239, 7, 0, 'The heart... the last remaining vestige of your humanity.  I had to stop it from being destroyed.  I had to see for myself.  And at last I\'m sure...', 12, 0, 100, 1, 0, 0, 0, 32590, 0, 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES (32239, 8, 0, 'Only shadows from the past remain.  There\'s nothing left to redeem!', 12, 0, 100, 0, 0, 0, 0, 32595, 0, 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES (32241, 0, 0, 'Tirion\'s down!  Defend him with your lives!', 14, 0, 100, 0, 0, 0, 0, 32687, 0, 'Disguised Crusader');
INSERT INTO `creature_text` VALUES (32273, 0, 0, 'How dare you interfere with our work!', 14, 0, 100, 0, 0, 0, 0, 32639, 3, 'Infinite Corruptor - SAY_AGGRO');
