INSERT INTO `creature_text` VALUES (32295, 0, 0, 'I did what I had to, brother. You gave me no alternative.', 14, 0, 100, 0, 4000, 14406, 0, 32626, 0, 'Alexstrasza - Yell text 1');
INSERT INTO `creature_text` VALUES (32295, 1, 0, 'And so ends the Nexus War. ', 14, 0, 100, 0, 4000, 14407, 0, 32627, 0, 'Alexstrasza - Yell text 2');
INSERT INTO `creature_text` VALUES (32295, 2, 0, 'This resolution pains me deeply, but the destruction, the monumental loss of life had to end. Regardless of Malygos\' recent transgressions, I will mourn his loss. He was once a guardian, a protector. This day, one of the world\'s mightiest has fallen.', 14, 0, 100, 0, 24000, 14408, 0, 32628, 0, 'Alexstrasza - Yell text 3');
INSERT INTO `creature_text` VALUES (32295, 3, 0, 'The red dragonflight will take on the burden of mending the devastation wrought on Azeroth. Return home to your people and rest. Tomorrow will bring you new challenges, and you must be ready to face them. Life... goes on.', 14, 0, 100, 0, 22000, 14409, 0, 32629, 0, 'Alexstrasza - Yell text 4');
INSERT INTO `creature_text` VALUES (32311, 0, 0, 'Take courage, crusaders.  You do not fight alone!', 14, 0, 100, 0, 0, 0, 0, 32708, 0, 'Koltira Deathweaver');
INSERT INTO `creature_text` VALUES (32311, 1, 0, 'The Lich King is badly hurt.  We ought to stay behind and finish him.', 12, 0, 100, 0, 0, 0, 0, 32725, 0, 'Koltira Deathweaver');
INSERT INTO `creature_text` VALUES (32312, 0, 0, 'Quick, through the portal!  He won\'t stay down for long.', 12, 0, 100, 0, 0, 0, 0, 32707, 0, 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES (32312, 1, 0, 'Patience... we will get our chance soon enough.  Be content that for once, it is Tirion who is in our debt.', 12, 0, 100, 0, 0, 0, 0, 32726, 0, 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES (32331, 1, 0, 'Whoa! You\'re me, but from the future! Hey, my equipment got an upgrade! Cool!', 15, 0, 100, 396, 0, 0, 0, 32764, 0, 'Past You');
INSERT INTO `creature_text` VALUES (32331, 2, 0, 'Here come the Infinites! I\'ve got to keep the hourglass safe. Can you help?', 15, 0, 100, 396, 0, 0, 0, 32769, 0, 'Past You');
INSERT INTO `creature_text` VALUES (32331, 3, 0, 'This equipment looks cool and all, but couldn\'t we have done a little better? Are you even raiding?', 15, 0, 100, 396, 0, 0, 0, 32770, 0, 'Past You');
INSERT INTO `creature_text` VALUES (32331, 4, 0, 'So, how does it all turn out in Icecrown?', 15, 0, 100, 396, 0, 0, 0, 32771, 0, 'Past You');
INSERT INTO `creature_text` VALUES (32331, 5, 0, 'It might help if you could tell me how many of these guys we\'re going to fight.  You can remember that, right, grandpa?', 15, 0, 100, 396, 0, 0, 0, 32775, 0, 'Past You');
INSERT INTO `creature_text` VALUES (32331, 6, 0, 'Chromie said that if I don\'t do this just right, I might wink out of existence. If I go, then you go!', 15, 0, 100, 396, 0, 0, 0, 32772, 0, 'Past You');
INSERT INTO `creature_text` VALUES (32331, 7, 0, 'I just want you to know that if we get through this alive, I\'m making sure that we turn out better than you. No offense.', 15, 0, 100, 396, 0, 0, 0, 32776, 0, 'Past You');
INSERT INTO `creature_text` VALUES (32331, 8, 0, 'Looks like I\'m an underachiever.', 15, 0, 100, 396, 0, 0, 0, 32773, 0, 'Past You');
INSERT INTO `creature_text` VALUES (32331, 9, 0, 'Wait a minute! If you\'re here, then that means that in the not-so-distant future I\'m going to be you helping me? Are we stuck in a time loop?!', 15, 0, 100, 396, 0, 0, 0, 32777, 0, 'Past You');
INSERT INTO `creature_text` VALUES (32331, 10, 0, 'I think I\'m going to turn to drinking after this.', 15, 0, 100, 396, 0, 0, 0, 32774, 0, 'Past You');
INSERT INTO `creature_text` VALUES (32331, 11, 0, 'What the heck? Nozdormu is up there!', 15, 0, 100, 396, 0, 0, 0, 32781, 0, 'Past You');
