INSERT INTO `creature_text` VALUES (32865, 0, 0, 'Interlopers! You mortals who dare to interfere with my sport will pay... Wait--you...', 14, 0, 100, 0, 0, 15733, 0, 33145, 0, 'Thorim SAY_AGGRO_1');
INSERT INTO `creature_text` VALUES (32865, 1, 0, 'I remember you... In the mountains... But you... what is this? Where am--', 14, 0, 100, 0, 0, 15734, 0, 33270, 0, 'Thorim SAY_AGGRO_2');
INSERT INTO `creature_text` VALUES (32865, 2, 0, 'Do not hold back! Destroy them!', 14, 0, 100, 0, 0, 15736, 0, 34241, 0, 'Thorim SAY_SPECIAL');
INSERT INTO `creature_text` VALUES (32865, 3, 0, 'Impertinent whelps, you dare challenge me atop my pedestal? I will crush you myself!', 14, 0, 100, 0, 0, 15738, 0, 33148, 0, 'Thorim SAY_JUMPDOWN');
INSERT INTO `creature_text` VALUES (32865, 4, 0, 'Can\'t you at least put up a fight!?', 14, 0, 100, 0, 0, 15739, 0, 34239, 0, 'Thorim SAY_SLAY_1');
INSERT INTO `creature_text` VALUES (32865, 4, 1, 'Pathetic.', 14, 0, 100, 0, 0, 15740, 0, 35768, 0, 'Thorim SAY_SLAY_2');
INSERT INTO `creature_text` VALUES (32865, 5, 0, 'My patience has reached its limit!', 14, 0, 100, 0, 0, 15741, 0, 33365, 0, 'Thorim SAY_BERSERK');
INSERT INTO `creature_text` VALUES (32865, 6, 0, 'Failures! Weaklings!', 14, 0, 100, 0, 0, 15742, 0, 33274, 0, 'Thorim SAY_WIPE');
INSERT INTO `creature_text` VALUES (32865, 7, 0, 'Stay your arms! I yield!', 14, 0, 100, 0, 0, 15743, 0, 33948, 0, 'Thorim SAY_DEATH');
INSERT INTO `creature_text` VALUES (32865, 8, 0, 'I feel as though I am awakening from a nightmare, but the shadows in this place yet linger.', 14, 0, 100, 0, 0, 15744, 0, 33949, 0, 'Thorim SAY_END_NORMAL_1');
INSERT INTO `creature_text` VALUES (32865, 9, 0, 'Sif... was Sif here? Impossible--she died by my brother\'s hand. A dark nightmare indeed....', 14, 0, 100, 0, 0, 15745, 0, 33950, 0, 'Thorim SAY_END_NORMAL_2');
INSERT INTO `creature_text` VALUES (32865, 10, 0, 'I need time to reflect.... I will aid your cause if you should require it. I owe you at least that much. Farewell.', 14, 0, 100, 0, 0, 15746, 0, 33951, 0, 'Thorim SAY_END_NORMAL_3');
INSERT INTO `creature_text` VALUES (32865, 11, 0, 'You! Fiend! You are not my beloved! Be gone!', 14, 0, 100, 0, 0, 15747, 0, 33952, 0, 'Thorim SAY_END_HARD_1');
INSERT INTO `creature_text` VALUES (32865, 12, 0, 'Behold the hand behind all the evil that has befallen Ulduar, left my kingdom in ruins, corrupted my brother, and slain my wife.', 14, 0, 100, 0, 0, 15748, 0, 33953, 0, 'Thorim SAY_END_HARD_2');
INSERT INTO `creature_text` VALUES (32865, 13, 0, 'And now it falls to you, champions, to avenge us all. The task before you is great, but I will lend you my aid as I am able. You must prevail.', 14, 0, 100, 0, 0, 15749, 0, 33954, 0, 'Thorim SAY_END_HARD_3');
INSERT INTO `creature_text` VALUES (32867, 0, 0, 'You will not defeat the Assembly of Iron so easily, invaders!', 14, 0, 100, 0, 0, 15674, 0, 34321, 0, 'Steelbreaker - Aggro');
INSERT INTO `creature_text` VALUES (32867, 1, 0, 'So fragile and weak!', 14, 0, 100, 0, 0, 15675, 0, 34322, 0, 'Steelbreaker - Slay 1');
INSERT INTO `creature_text` VALUES (32867, 1, 1, 'Flesh... such a hindrance.', 14, 0, 100, 0, 0, 15676, 0, 34323, 0, 'Steelbreaker - Slay 2');
INSERT INTO `creature_text` VALUES (32867, 2, 0, 'You seek the secrets of Ulduar? Then take them!', 14, 0, 100, 0, 0, 15677, 0, 34324, 0, 'Steelbreaker - Power');
INSERT INTO `creature_text` VALUES (32867, 3, 0, 'My death only serves to hasten your demise.', 14, 0, 100, 0, 0, 15678, 0, 34325, 0, 'Steelbreaker - Death');
INSERT INTO `creature_text` VALUES (32867, 4, 0, 'Impossible...', 14, 0, 100, 0, 0, 15679, 0, 38080, 0, 'Steelbreaker - Encounter defeated');
INSERT INTO `creature_text` VALUES (32867, 5, 0, 'This meeting of the Assembly of Iron is adjourned!', 14, 0, 100, 0, 0, 15680, 0, 34335, 0, 'Steelbreaker - Berserk');
INSERT INTO `creature_text` VALUES (32868, 0, 0, 'Don\'t move, little adventurer. I have the death you seek.', 12, 0, 100, 0, 0, 0, 0, 34476, 0, 'on Aggro Text');
INSERT INTO `creature_text` VALUES (32871, 0, 0, 'Translocation complete. Commencing planetary analysis of Azeroth.', 12, 0, 0, 0, 0, 15405, 0, 34026, 3, 'Algalon the Observer - SAY_ALGALON_INTRO_1');
INSERT INTO `creature_text` VALUES (32871, 1, 0, 'Stand back, mortals. I\'m not here to fight you.', 12, 0, 0, 0, 0, 15406, 0, 34027, 3, 'Algalon the Observer - SAY_ALGALON_INTRO_2');
INSERT INTO `creature_text` VALUES (32871, 2, 0, 'It is in the universe\'s best interest to re-originate this planet should my analysis find systemic corruption. Do not interfere.', 12, 0, 0, 0, 0, 15407, 0, 34028, 3, 'Algalon the Observer - SAY_ALGALON_INTRO_3');
INSERT INTO `creature_text` VALUES (32871, 3, 0, 'Your actions are illogical. All possible results for this encounter have been calculated. The Pantheon will receive the Observer\'s message regardless of outcome.', 14, 0, 0, 0, 0, 15386, 0, 33995, 3, 'Algalon the Observer - SAY_ALGALON_START_TIMER');
INSERT INTO `creature_text` VALUES (32871, 4, 0, 'See your world through my eyes: A universe so vast as to be immeasurable - incomprehensible even to your greatest minds.', 14, 0, 0, 1, 0, 15390, 0, 33998, 3, 'Algalon the Observer - SAY_ALGALON_AGGRO');
INSERT INTO `creature_text` VALUES (32871, 5, 0, 'The stars come to my aid!', 14, 0, 0, 0, 0, 15392, 0, 33245, 3, 'Algalon the Observer - SAY_ALGALON_COLLAPSING_STAR');
INSERT INTO `creature_text` VALUES (32871, 6, 0, '%s begins to Summon Collapsing Stars!', 41, 0, 0, 0, 0, 0, 0, 33244, 3, 'Algalon the Observer - EMOTE_ALGALON_COLLAPSING_STAR');
INSERT INTO `creature_text` VALUES (32871, 7, 0, 'Witness the fury of the cosmos!', 14, 0, 0, 0, 0, 15396, 0, 33269, 3, 'Algalon the Observer - SAY_ALGALONG_BIG_BANG');
INSERT INTO `creature_text` VALUES (32871, 8, 0, '%s begins to cast Big Bang!', 41, 0, 0, 0, 0, 0, 0, 34465, 3, 'Algalon the Observer - EMOTE_ALGALON_BIG_BANG');
INSERT INTO `creature_text` VALUES (32871, 9, 0, 'You are out of time.', 14, 0, 0, 0, 0, 15394, 0, 34001, 3, 'Algalon the Observer - SAY_ALGALON_ASCEND');
INSERT INTO `creature_text` VALUES (32871, 10, 0, '%s begins to cast Cosmic Smash!', 41, 0, 0, 0, 0, 0, 0, 34500, 3, 'Algalon the Observer - EMOTE_ALGALON_COSMIC_SMASH');
INSERT INTO `creature_text` VALUES (32871, 11, 0, 'Behold the tools of creation!', 14, 0, 0, 0, 0, 15397, 0, 33994, 3, 'Algalon the Observer - SAY_ALGALON_PHASE_TWO');
INSERT INTO `creature_text` VALUES (32871, 12, 0, 'I have seen worlds bathed in the Makers\' flames, their denizens fading without as much as a whimper. Entire planetary systems born and razed in the time that it takes your mortal hearts to beat once. Yet all throughout, my own heart devoid of emotion... of empathy. I. Have. Felt. Nothing. A million-million lives wasted. Had they all held within them your tenacity? Had they all loved life as you do?', 14, 0, 0, 1, 0, 15393, 0, 34000, 3, 'Algalon the Observer - SAY_ALGALON_OUTRO_1');
INSERT INTO `creature_text` VALUES (32871, 13, 0, 'Perhaps it is your imperfections... that which grants you free will... that allows you to persevere against all cosmically calculated odds. You prevail where the Titan\'s own perfect creations have failed.', 14, 0, 0, 1, 0, 15401, 0, 34012, 3, 'Algalon the Observer - SAY_ALGALON_OUTRO_2');
INSERT INTO `creature_text` VALUES (32871, 14, 0, 'I\'ve rearranged the reply code - your planet will be spared. I cannot be certain of my own calculations anymore.', 14, 0, 0, 1, 0, 15402, 0, 34013, 3, 'Algalon the Observer - SAY_ALGALON_OUTRO_3');
INSERT INTO `creature_text` VALUES (32871, 15, 0, 'I lack the strength to transmit the signal. You must... hurry... find a place of power... close to the skies.', 14, 0, 0, 1, 0, 15403, 0, 34016, 3, 'Algalon the Observer - SAY_ALGALON_OUTRO_4');
INSERT INTO `creature_text` VALUES (32871, 16, 0, 'Do not worry about my fate, Bronzen. If the signal is not transmitted in time, re-origination will proceed regardless. Save... your world...', 14, 0, 0, 1, 0, 15404, 0, 34018, 3, 'Algalon the Observer - SAY_ALGALON_OUTRO_5');
INSERT INTO `creature_text` VALUES (32871, 17, 0, 'Analysis complete. There is partial corruption in the planet\'s life-support systems as well as complete corruption in most of the planet\'s defense mechanisms.', 12, 0, 0, 0, 0, 15398, 0, 34006, 3, 'Algalon the Observer - SAY_ALGALON_DESPAWN_1');
INSERT INTO `creature_text` VALUES (32871, 18, 0, 'Begin uplink: Reply Code: \'Omega.\' Planetary re-origination requested.', 12, 0, 0, 0, 0, 15399, 0, 34014, 3, 'Algalon the Observer - SAY_ALGALON_DESPAWN_2');
INSERT INTO `creature_text` VALUES (32871, 19, 0, 'Farewell, mortals. Your bravery is admirable, for such flawed creatures.', 12, 0, 0, 0, 0, 15400, 0, 34015, 3, 'Algalon the Observer - SAY_ALGALON_DESPAWN_3');
INSERT INTO `creature_text` VALUES (32871, 20, 0, 'Loss of life unavoidable.', 14, 0, 0, 0, 0, 15387, 0, 33996, 3, 'Algalon the Observer - SAY_ALGALON_KILL');
INSERT INTO `creature_text` VALUES (32871, 20, 1, 'I do what I must.', 14, 0, 0, 0, 0, 15388, 0, 33997, 3, 'Algalon the Observer - SAY_ALGALON_KILL');
