INSERT INTO `creature_text` VALUES (32927, 1, 0, 'The world suffers yet another insignificant loss.', 14, 0, 100, 0, 0, 15658, 0, 34329, 0, 'Runemaster Molgeim - Slay 1');
INSERT INTO `creature_text` VALUES (32927, 1, 1, 'Death is the price of your arrogance.', 14, 0, 100, 0, 0, 15659, 0, 34330, 0, 'Runemaster Molgeim - Slay 2');
