INSERT INTO `creature_text` VALUES (33288, 0, 0, 'BOW DOWN BEFORE THE GOD OF DEATH!', 14, 0, 100, 0, 0, 0, 0, 34357, 0, 'Yogg-Saron - SAY_YOGG_SARON_SPAWN');
INSERT INTO `creature_text` VALUES (33288, 1, 0, 'MADNESS WILL CONSUME YOU!', 14, 0, 100, 0, 0, 15756, 0, 34361, 0, 'Yogg-Saron - SAY_YOGG_SARON_MADNESS');
INSERT INTO `creature_text` VALUES (33288, 2, 0, 'Portals open into %s\'s mind!', 41, 0, 100, 0, 0, 0, 0, 34037, 0, 'Yogg-Saron - EMOTE_YOGG_SARON_MADNESS');
INSERT INTO `creature_text` VALUES (33288, 3, 0, 'Look upon the true face of death, and know that your end comes soon!', 14, 0, 100, 0, 0, 15755, 0, 34360, 0, 'Yogg-Saron - SAY_YOGG_SARON_PHASE_3');
INSERT INTO `creature_text` VALUES (33288, 4, 0, 'Eternal suffering awaits!', 14, 0, 100, 0, 0, 15758, 0, 34362, 0, 'Yogg-Saron - SAY_YOGG_SARON_DEAFENING_ROAR');
INSERT INTO `creature_text` VALUES (33288, 5, 0, '%s opens his mouth wide!', 41, 0, 100, 0, 0, 0, 0, 34022, 0, 'Yogg-Saron - EMOTE_YOGG_SARON_DEAFENING_ROAR');
INSERT INTO `creature_text` VALUES (33288, 6, 0, 'Your fate is sealed. The end of days is finally upon you and ALL who inhabit this miserable little seedling. Uulwi ifis halahs gag erh\'ongg w\'ssh.', 14, 0, 100, 0, 0, 15761, 0, 34365, 0, 'Yogg-Saron - SAY_YOGG_SARON_DEATH');
INSERT INTO `creature_text` VALUES (33288, 7, 0, '%s prepares to unleash Empowering Shadows!', 41, 0, 100, 0, 0, 0, 0, 34170, 0, 'Yogg-Saron - EMOTE_YOGG_SARON_EMPOWERING_SHADOWS');
INSERT INTO `creature_text` VALUES (33288, 8, 0, '%s goes berserk, extinguishing all life!', 41, 0, 100, 0, 0, 0, 0, 33993, 0, 'Yogg-Saron - EMOTE_YOGG_SARON_EXTINGUISH_ALL_LIFE');
INSERT INTO `creature_text` VALUES (33292, 0, 0, '%s begins to boil upon touching $n!', 16, 0, 100, 0, 0, 0, 0, 35163, 0, 'Ominous Cloud - EMOTE_OMINOUS_CLOUD_PLAYER_TOUCH');
INSERT INTO `creature_text` VALUES (33293, 0, 0, 'New toys? For me? I promise I won\'t break them this time!', 14, 0, 100, 0, 0, 15724, 0, 33508, 0, 'XT-002 Deconstructor SAY_AGGRO');
INSERT INTO `creature_text` VALUES (33293, 1, 0, 'So tired. I will rest for just a moment!', 14, 0, 100, 0, 0, 15725, 0, 33446, 0, 'XT-002 Deconstructor SAY_HEART_OPENED');
INSERT INTO `creature_text` VALUES (33293, 2, 0, 'I\'m ready to play!', 14, 0, 100, 0, 0, 15726, 0, 33448, 0, 'XT-002 Deconstructor SAY_HEART_CLOSED');
INSERT INTO `creature_text` VALUES (33293, 3, 0, 'NO! NO! NO! NO! NO!', 14, 0, 100, 0, 0, 15727, 0, 33453, 0, 'XT-002 Deconstructor SAY_TYMPANIC_TANTRUM');
INSERT INTO `creature_text` VALUES (33293, 4, 0, 'I... I think I broke it.', 14, 0, 100, 0, 0, 15728, 0, 33509, 0, 'XT-002 Deconstructor SAY_SLAY_1');
INSERT INTO `creature_text` VALUES (33293, 4, 1, 'I guess it doesn\'t bend that way.', 14, 0, 100, 0, 0, 15729, 0, 33510, 0, 'XT-002 Deconstructor SAY_SLAY_2');
INSERT INTO `creature_text` VALUES (33293, 5, 0, 'I\'m tired of these toys. I don\'t want to play anymore!', 14, 0, 100, 0, 0, 15730, 0, 33512, 0, 'XT-002 Deconstructor SAY_BERSERK');
INSERT INTO `creature_text` VALUES (33293, 6, 0, 'You are bad... Toys... Very... Baaaaad ', 14, 0, 100, 0, 0, 15731, 0, 33511, 0, 'XT-002 Deconstructor SAY_DEATH');
INSERT INTO `creature_text` VALUES (33293, 7, 0, 'Time for a new game! My old toys will fight my new toys!', 14, 0, 100, 0, 0, 15732, 0, 0, 0, 'XT-002 Deconstructor SAY_SUMMON');
INSERT INTO `creature_text` VALUES (33293, 8, 0, '%s\'s heart is exposed and leaking energy.', 41, 0, 100, 0, 0, 0, 0, 33447, 0, 'XT-002 Deconstructor EMOTE_HEART_OPENED');
INSERT INTO `creature_text` VALUES (33293, 9, 0, '%s\'s heart is severed from his body.', 41, 0, 100, 0, 0, 0, 0, 34023, 0, 'XT-002 Deconstructor EMOTE_HEART_CLOSED');
INSERT INTO `creature_text` VALUES (33293, 10, 0, '%s begins to cause the earth to quake.', 41, 0, 100, 0, 0, 0, 0, 33454, 0, 'XT-002 Deconstructor EMOTE_TYMPANIC_TANTRUM');
INSERT INTO `creature_text` VALUES (33293, 11, 0, '%s consumes a scrap bot to repair himself!', 41, 0, 100, 0, 0, 0, 0, 33856, 0, 'XT-002 Deconstructor EMOTE_SCRAPBOT');
INSERT INTO `creature_text` VALUES (33303, 0, 0, 'Thank you, mortal, for freeing me from this curse. I beg you, take this blade.', 12, 0, 100, 1, 0, 0, 0, 33436, 0, 'Maiden of Winter\'s Breath Lake');
INSERT INTO `creature_text` VALUES (33303, 1, 0, 'It has brought me naught but ill. Mayhap you can find someone who will contain its power.', 12, 0, 100, 1, 0, 0, 0, 33437, 0, 'Maiden of Winter\'s Breath Lake');
INSERT INTO `creature_text` VALUES (33306, 0, 0, 'Ready yourself!', 12, 1, 100, 0, 0, 0, 0, 33757, 0, 'Argent Valiant');
INSERT INTO `creature_text` VALUES (33306, 0, 1, 'Prepare yourself!', 12, 1, 100, 0, 0, 0, 0, 33755, 0, 'Argent Valiant');
