INSERT INTO `creature_text` VALUES (33306, 2, 1, 'That was a well fought battle. I yield to you.', 12, 1, 100, 0, 0, 0, 0, 33776, 0, 'Argent Valiant');
INSERT INTO `creature_text` VALUES (33306, 2, 2, 'It would seem I underestimated your skills. Well done.', 12, 1, 100, 0, 0, 0, 0, 33775, 0, 'Argent Valiant');
INSERT INTO `creature_text` VALUES (33306, 2, 3, 'I have been defeated. Good fight!', 12, 1, 100, 0, 0, 0, 0, 33760, 0, 'Argent Valiant');
INSERT INTO `creature_text` VALUES (33350, 0, 0, 'Oh, my! I wasn\'t expecting company! The workshop is such a mess. How embarrassing.', 14, 0, 100, 0, 0, 15611, 0, 34298, 0, 'Mimiron SAY_AGGRO');
INSERT INTO `creature_text` VALUES (33350, 1, 0, 'Now, why would you go and do something like that? Didn\'t you see the sign that said, \"DO NOT PUSH THIS BUTTON!\"? How will we finish testing with the self-destruct mechanism active?', 14, 0, 100, 0, 0, 15629, 0, 34244, 0, 'Mimiron SAY_HARDMODE_ON');
INSERT INTO `creature_text` VALUES (33350, 2, 0, 'We haven\'t much time, friends! You\'re going to help me test out my latest and greatest creation. Now, before you change your minds, remember that you kind of owe it to me after the mess you made with the XT-002.', 14, 0, 100, 0, 0, 15612, 0, 33636, 0, 'Mimiron SAY_MKII_ACTIVATE');
INSERT INTO `creature_text` VALUES (33350, 3, 0, 'MEDIC!', 14, 0, 100, 0, 0, 15613, 0, 38771, 0, 'Mimiron SAY_MKII_SLAY_1');
INSERT INTO `creature_text` VALUES (33350, 3, 1, 'I can fix that... or  maybe not! Sheesh, what a mess...', 14, 0, 100, 0, 0, 15614, 0, 34300, 0, 'Mimiron SAY_MKII_SLAY_2');
INSERT INTO `creature_text` VALUES (33350, 4, 0, 'WONDERFUL! Positively marvelous results! Hull integrity at 98.9 percent! Barely a dent! Moving right along.', 14, 0, 100, 0, 0, 15615, 0, 33638, 0, 'Mimiron SAY_MKII_DEATH');
INSERT INTO `creature_text` VALUES (33350, 5, 0, 'Behold the VX-001 Anti-personnel Assault Cannon! You might want to take cover.', 14, 0, 100, 0, 0, 15616, 0, 33639, 0, 'Mimiron SAY_VX001_ACTIVATE');
INSERT INTO `creature_text` VALUES (33350, 6, 0, 'Fascinating. I think they call that a \"clean kill\".', 14, 0, 100, 0, 0, 15617, 0, 34301, 0, 'Mimiron SAY_VX001_SLAY_1');
INSERT INTO `creature_text` VALUES (33350, 6, 1, 'Note to self: Cannon highly effective against flesh.', 14, 0, 100, 0, 0, 15618, 0, 34302, 0, 'Mimiron SAY_VX001_SLAY_2');
INSERT INTO `creature_text` VALUES (33350, 7, 0, 'Thank you, friends! Your efforts have yielded some fantastic data! Now, where did I put-- oh, there it is.', 14, 0, 100, 0, 0, 15619, 0, 33668, 0, 'Mimiron SAY_VX001_DEATH');
INSERT INTO `creature_text` VALUES (33350, 8, 0, 'Isn\'t it beautiful? I call it the magnificent aerial command unit!', 14, 0, 100, 0, 0, 15620, 0, 33669, 0, 'Mimiron SAY_AERIAL_ACTIVATE');
INSERT INTO `creature_text` VALUES (33350, 9, 0, 'Outplayed!', 14, 0, 100, 0, 0, 15621, 0, 34303, 0, 'Mimiron SAY_AERIAL_SLAY_1');
INSERT INTO `creature_text` VALUES (33350, 9, 1, 'You can do better than that!', 14, 0, 100, 0, 0, 15622, 0, 34304, 0, 'Mimiron SAY_AERIAL_SLAY_2');
INSERT INTO `creature_text` VALUES (33350, 10, 0, 'Preliminary testing phase complete. Now comes the true test!', 14, 0, 100, 0, 0, 15623, 0, 33966, 0, 'Mimiron SAY_AERIAL_DEATH');
INSERT INTO `creature_text` VALUES (33350, 11, 0, 'Gaze upon its magnificence! Bask in its glorious, um, glory! I present you with... V-07-TR-0N!', 14, 0, 100, 0, 0, 15624, 0, 34033, 0, 'Mimiron SAY_V07TRON_ACTIVATE');
INSERT INTO `creature_text` VALUES (33350, 12, 0, 'Prognosis: Negative!', 14, 0, 100, 0, 0, 15625, 0, 34448, 0, 'Mimiron SAY_V07TRON_SLAY_1');
INSERT INTO `creature_text` VALUES (33350, 12, 1, 'You\'re not going to get up from that one, friend.', 14, 0, 100, 0, 0, 15626, 0, 34449, 0, 'Mimiron SAY_V07TRON_SLAY_2');
INSERT INTO `creature_text` VALUES (33350, 13, 0, 'It would appear that I\'ve made a slight miscalculation. I allowed my mind to be corrupted by the fiend in the prison, overriding my primary directive. All systems seem to be functional now. Clear.', 14, 0, 100, 0, 0, 15627, 0, 34086, 0, 'Mimiron SAY_V07TRON_DEATH');
INSERT INTO `creature_text` VALUES (33350, 14, 0, 'Oh, my! It would seem that we are out of time, my friends.', 14, 0, 100, 0, 0, 15628, 0, 34305, 0, 'Mimiron SAY_BERSERK');
INSERT INTO `creature_text` VALUES (33356, 0, 0, 'I want that one killed! The one right there that doesn\'t look like the others!', 14, 0, 50, 0, 0, 0, 0, 33481, 0, 'say Text');
INSERT INTO `creature_text` VALUES (33356, 0, 1, 'Smash them! Destroy them! Lok\'tar ogar!', 14, 0, 50, 0, 0, 0, 0, 33479, 0, 'say Text');
INSERT INTO `creature_text` VALUES (33356, 0, 2, 'There is no blood easier spilt than that of a puny night elf!', 14, 0, 50, 0, 0, 0, 0, 33483, 0, 'say Text');
INSERT INTO `creature_text` VALUES (33356, 1, 0, 'Pathetic weakling. You will die quickly!', 12, 0, 100, 0, 0, 0, 0, 33475, 0, 'say Text');
