INSERT INTO `creature_text` VALUES (33626, 0, 0, 'Not a bit. For the life of me, I can\'t figure out how this thing works.', 12, 0, 100, 0, 0, 0, 0, 34183, 0, 'Hired Engineer to Player');
INSERT INTO `creature_text` VALUES (33626, 1, 0, 'I know, I know... I haven\'t given up yet. Don\'t get your hopes up though, this technology is way beyond me.', 12, 0, 100, 0, 0, 0, 0, 34187, 0, 'Hired Engineer to Player');
INSERT INTO `creature_text` VALUES (33662, 0, 0, 'Any luck?', 12, 0, 100, 0, 0, 0, 0, 34184, 0, 'Kirin Tor Battle-Mage to Player');
INSERT INTO `creature_text` VALUES (33662, 1, 0, 'Keep at it. If our suspicions are right and this is a transporter, it could prove extremely useful in our assault.', 12, 0, 100, 0, 0, 0, 0, 34186, 0, 'Kirin Tor Battle-Mage to Player');
INSERT INTO `creature_text` VALUES (33686, 0, 0, 'I was constructed to serve as a repository for essential information regarding this complex. My primary functions include communicating the status of the frontal defense systems and assessing the status of the entity that this complex was built to imprison.', 12, 0, 0, 0, 0, 0, 0, 33703, 0, 'Norgannon SAY_EVENT_1');
INSERT INTO `creature_text` VALUES (33686, 1, 0, 'Access to the interior of the complex is currently restricted. Primary defensive emplacements are active. Secondary systems are currently non-active.', 12, 0, 0, 0, 0, 0, 0, 33705, 0, 'Norgannon SAY_EVENT_2');
INSERT INTO `creature_text` VALUES (33686, 2, 0, 'Compromise of complex detected, security override enabled - query permitted.', 12, 0, 0, 0, 0, 0, 0, 33707, 0, 'Norgannon SAY_EVENT_3');
INSERT INTO `creature_text` VALUES (33686, 3, 0, 'Primary defensive emplacements consist of iron constructs and Storm Beacons, which will generate additional constructs as necessary. Secondary systems consist of orbital defense emplacements.', 12, 0, 0, 0, 0, 0, 0, 33712, 0, 'Norgannon SAY_EVENT_4');
INSERT INTO `creature_text` VALUES (33686, 4, 0, 'Entity designate: Yogg-Saron. Security has been compromised. Prison operational status unknown. Unable to contact Watchers for notification purposes.', 12, 0, 0, 0, 0, 0, 0, 33711, 0, 'Norgannon SAY_EVENT_5');
INSERT INTO `creature_text` VALUES (33696, 0, 0, '%s nods.', 16, 0, 100, 0, 0, 0, 0, 33715, 0, 'Archmage Rhydian to Player');
INSERT INTO `creature_text` VALUES (33696, 1, 0, '%s whispers something to Brann.', 16, 0, 100, 0, 0, 0, 0, 33716, 0, 'Archmage Rhydian to Player');
INSERT INTO `creature_text` VALUES (33701, 0, 0, 'I heard a story or two of a Lore Keeper in Uldaman that fit your description. Do you serve a similar purpose?', 12, 0, 100, 0, 0, 0, 0, 33702, 0, 'High Explorer Dellorah to Player');
INSERT INTO `creature_text` VALUES (33701, 1, 0, 'Frontal defense systems? Is there something I should let Brann know before he has anyone attempt to enter the complex?', 12, 0, 100, 0, 0, 0, 0, 33704, 0, 'High Explorer Dellorah to Player');
INSERT INTO `creature_text` VALUES (33701, 2, 0, 'Can you detail the nature of these defense systems?', 12, 0, 100, 0, 0, 0, 0, 33706, 0, 'High Explorer Dellorah to Player');
INSERT INTO `creature_text` VALUES (33701, 3, 0, 'Got it. At least we don\'t have to deal with those orbital emplacements.', 12, 0, 100, 0, 0, 0, 0, 33708, 0, 'High Explorer Dellorah to Player');
INSERT INTO `creature_text` VALUES (33701, 4, 0, 'Rhydian, make sure you let Brann and Archmage Pentarus know about those defenses immediately.', 12, 0, 100, 0, 0, 0, 0, 33709, 0, 'High Explorer Dellorah to Player');
INSERT INTO `creature_text` VALUES (33701, 5, 0, 'And you mentioned an imprisoned entity? What is the nature of this entity and what is its status?', 12, 0, 100, 0, 0, 0, 0, 33710, 0, 'High Explorer Dellorah to Player');
INSERT INTO `creature_text` VALUES (33701, 6, 0, 'Yogg-Saron is here? It sounds like we really will have our hands full then.', 12, 0, 100, 0, 0, 0, 0, 33713, 0, 'High Explorer Dellorah to Player');
