INSERT INTO `creature_text` VALUES (34249, 4, 0, 'The Sapphire of Sky is located at the separate room in the lower section of the northern chamber.', 15, 0, 100, 0, 0, 0, 0, 34651, 0, 'Sentinel Huntress');
INSERT INTO `creature_text` VALUES (34249, 5, 0, 'The Black Feather Quill is located down the ramp in the lower section of the southern chamber.', 15, 0, 100, 0, 0, 0, 0, 34652, 0, 'Sentinel Huntress');
INSERT INTO `creature_text` VALUES (34249, 6, 0, 'The Raven Claw Talisman is located on the ledge in the upper section of the northern chamber.', 15, 0, 100, 0, 0, 0, 0, 34653, 0, 'Sentinel Huntress');
INSERT INTO `creature_text` VALUES (34249, 7, 0, 'Oben Rageclaw resides at the double bridges in the western chamber, near the hallway that leads out of the Barrow Den.', 15, 0, 100, 0, 0, 0, 0, 34654, 0, 'Sentinel Huntress');
INSERT INTO `creature_text` VALUES (34295, 0, 0, 'I am free of the bonds of the earth never to return again!', 14, 0, 50, 0, 0, 0, 0, 34507, 0, 'say Text');
INSERT INTO `creature_text` VALUES (34295, 0, 1, 'Like moths to the flame!', 14, 0, 50, 0, 0, 0, 0, 34509, 0, 'say Text');
INSERT INTO `creature_text` VALUES (34295, 0, 2, 'Scorch the ground, boil the water, and ignite the skies!', 14, 0, 50, 0, 0, 0, 0, 34504, 0, 'say Text');
INSERT INTO `creature_text` VALUES (34295, 0, 3, 'There is no escape! There is only the fiery embrace of my lava!', 14, 0, 50, 0, 0, 0, 0, 34502, 0, 'say Text');
INSERT INTO `creature_text` VALUES (34295, 0, 4, 'Death rises from below and erupts from above! There is no escape!', 14, 0, 50, 0, 0, 0, 0, 34503, 0, 'say Text');
INSERT INTO `creature_text` VALUES (34295, 0, 5, 'The Earthen Ring cannot withstand my fury!', 14, 0, 50, 0, 0, 0, 0, 34505, 0, 'say Text');
INSERT INTO `creature_text` VALUES (34295, 0, 6, 'Destroy them my minions!', 14, 0, 50, 0, 0, 0, 0, 34506, 0, 'say Text');
INSERT INTO `creature_text` VALUES (34295, 0, 7, 'ASHENVALE WILL BURN!', 14, 0, 50, 0, 0, 0, 0, 34508, 0, 'say Text');
INSERT INTO `creature_text` VALUES (34320, 0, 0, 'The %s\'s strong jaws tear into the food.', 16, 0, 100, 0, 0, 0, 0, 34521, 0, 'Venomhide Hatchling');
