INSERT INTO `creature_text` VALUES (34386, 1, 0, 'We remember this visit, yes we do. Never forget what you learn from your new friend, little Roo. You got great future ahead.', 12, 0, 0, 0, 0, 0, 0, 35492, 0, '');
INSERT INTO `creature_text` VALUES (34387, 1, 0, 'No worry, Kekek. You grow up, be strong for Frenzyheart. You lead people back to old home, where there no big-tongue babies.', 12, 0, 0, 0, 0, 0, 0, 35493, 0, '');
INSERT INTO `creature_text` VALUES (34413, 0, 0, '%s becomes enraged!', 16, 0, 100, 0, 0, 0, 0, 10677, 0, 'combat Enrage');
INSERT INTO `creature_text` VALUES (34489, 1, 0, 'Wanna see what I can do?', 12, 0, 0, 0, 0, 0, 0, 34617, 0, '');
INSERT INTO `creature_text` VALUES (34489, 2, 0, 'Now dance together!', 12, 0, 0, 0, 0, 0, 0, 34619, 0, '');
