INSERT INTO `creature_text` VALUES (34490, 2, 0, 'Got you good!', 12, 0, 0, 0, 0, 0, 0, 34621, 0, '');
INSERT INTO `creature_text` VALUES (34496, 0, 0, 'In the name of our dark master. For the Lich King. You. Will. Die.', 14, 0, 100, 0, 0, 16272, 0, 35738, 0, 'Eydis Darkbane - Aggro');
