INSERT INTO `creature_text` VALUES (34981, 0, 6, 'Protect me, please!', 12, 0, 100, 1, 0, 0, 0, 35234, 0, 'Frightened Citizen to Player');
INSERT INTO `creature_text` VALUES (34990, 0, 0, 'Your beasts will be no match for my champions, Tirion!', 14, 0, 0, 0, 0, 16069, 0, 35693, 0, 'King Varian Wrynn - Northrend Beasts Outro');
INSERT INTO `creature_text` VALUES (34990, 1, 0, 'The Alliance doesn\'t need the help of a demon lord to deal with Horde filth! Come, pig!', 14, 0, 100, 5, 0, 16064, 0, 35688, 0, 'King Varian Wrynn - Lord Jaraxxus Outro');
INSERT INTO `creature_text` VALUES (34990, 2, 0, 'Our honor has been besmirched! They make wild claims and false accusations against us. I demand justice! Allow my champions to fight in place of your knights, Tirion. We challenge the Horde!', 14, 0, 100, 5, 0, 16066, 0, 35690, 0, 'King Varian Wrynn - Faction Champions Intro');
INSERT INTO `creature_text` VALUES (34990, 3, 0, 'Fight for the glory of the Alliance, heroes! Honor your king and your people!', 14, 0, 100, 5, 0, 16065, 0, 35689, 0, 'King Varian Wrynn - Faction Champions Intro');
INSERT INTO `creature_text` VALUES (34990, 4, 0, 'GLORY TO THE ALLIANCE!', 14, 0, 100, 0, 0, 16067, 0, 35691, 0, 'King Varian Wrynn - Victory');
INSERT INTO `creature_text` VALUES (34990, 5, 0, 'Not even the Lich King\'s most powerful minions can stand against the Alliance. All hail our victors!', 14, 0, 0, 0, 0, 16068, 0, 35692, 0, 'King Varian Wrynn - Faction Champions Outro');
INSERT INTO `creature_text` VALUES (34990, 6, 0, 'Hardly a challenge.', 14, 0, 100, 274, 0, 16061, 0, 35685, 0, 'King Varian Wrynn - Faction Champions Kill Player');
INSERT INTO `creature_text` VALUES (34990, 6, 1, 'HAH!', 14, 0, 100, 5, 0, 16060, 0, 35684, 0, 'King Varian Wrynn - Faction Champions Kill Player');
INSERT INTO `creature_text` VALUES (34990, 6, 2, 'Is this the best the Horde has to offer?', 14, 0, 100, 6, 0, 16063, 0, 35687, 0, 'King Varian Wrynn  - Faction Champions Kill Player');
INSERT INTO `creature_text` VALUES (34990, 6, 3, 'Worthless scrub.', 14, 0, 100, 25, 0, 16062, 0, 35686, 0, 'King Varian Wrynn  - Faction Champions Kill Player');
INSERT INTO `creature_text` VALUES (34990, 50, 0, 'I have no taste for these games, Tirion. Still... I trust they will perform admirably.', 12, 0, 100, 1, 0, 0, 0, 35322, 0, 'King Varian Wrynn');
INSERT INTO `creature_text` VALUES (34990, 51, 0, 'Don\'t just stand there; kill him!', 14, 0, 100, 22, 0, 0, 0, 35550, 0, 'King Varian Wrynn');
INSERT INTO `creature_text` VALUES (34990, 52, 0, 'I did not come here to watch animals tear at each other senselessly, Tirion.', 12, 0, 100, 1, 0, 0, 0, 35328, 0, 'King Varian Wrynn');
INSERT INTO `creature_text` VALUES (34990, 53, 0, 'You fought well.', 14, 0, 100, 66, 0, 0, 0, 35795, 0, 'King Varian Wrynn - SAY_KNIGHT_OUTRO');
INSERT INTO `creature_text` VALUES (34994, 0, 0, 'Fight well, Horde! Lok\'tar Ogar!', 14, 0, 100, 22, 0, 0, 0, 35326, 0, 'Thrall - SAY_THRALL_GRAND_CHAMPIONS_INTRO');
INSERT INTO `creature_text` VALUES (34994, 1, 0, 'Well done, Horde!', 14, 0, 100, 66, 0, 0, 0, 35794, 0, 'Thrall - SAY_THRALL_KNIGHT_OUTRO');
INSERT INTO `creature_text` VALUES (34994, 2, 0, 'Garrosh, enough.', 12, 0, 100, 396, 0, 0, 0, 35325, 0, 'Thrall');
INSERT INTO `creature_text` VALUES (34995, 0, 0, 'I\'ve seen more worthy challenges in the Ring of Blood. You waste our time, paladin.', 14, 0, 100, 1, 0, 16026, 0, 35707, 0, 'Garrosh Hellscream  - Northrend Beasts Outro');
INSERT INTO `creature_text` VALUES (34995, 1, 0, 'Treacherous Alliance dogs! You summon a demon lord against warriors of the Horde? Your deaths will be swift!', 14, 0, 100, 5, 0, 16021, 0, 35699, 0, 'Garrosh Hellscream - Lord Jaraxxus Outro');
INSERT INTO `creature_text` VALUES (34995, 2, 0, 'The Horde demands justice! We challenge the Alliance! Allow us to battle in place of your knights, paladin. We will show these dogs what it means to insult the Horde.', 14, 0, 100, 1, 0, 16023, 0, 35703, 0, 'Garrosh Hellscream - Faction Champions Intro');
INSERT INTO `creature_text` VALUES (34995, 3, 0, 'Show them no mercy, Horde champions! LOK\'TAR OGAR! ', 14, 0, 0, 0, 0, 16022, 0, 35701, 0, 'Garrosh - Faction Champions Intro');
INSERT INTO `creature_text` VALUES (34995, 4, 0, 'That was just a taste of what the future brings. FOR THE HORDE!', 14, 0, 100, 1, 0, 16024, 0, 35705, 0, 'Garrosh Hellscream - Faction Champions Victory');
INSERT INTO `creature_text` VALUES (34995, 5, 0, 'Do you still question the might of the Horde, paladin? We will take on all comers!', 14, 0, 100, 1, 0, 16025, 0, 35706, 0, 'Garrosh Hellscream - Faction Champions Outro');
INSERT INTO `creature_text` VALUES (34995, 6, 0, 'Weakling!', 14, 0, 100, 0, 0, 16017, 0, 35694, 0, 'Garrosh Hellscream - Faction Champions Kill Player');
INSERT INTO `creature_text` VALUES (34995, 6, 1, 'Pathetic.', 14, 0, 100, 0, 0, 16018, 0, 35696, 0, 'Garrosh Hellscream - Faction Champions Kill Player');
INSERT INTO `creature_text` VALUES (34995, 6, 2, 'Overpowered...', 14, 0, 100, 0, 0, 16019, 0, 35697, 0, 'Garrosh Hellscream - Faction Champions Kill Player');
INSERT INTO `creature_text` VALUES (34995, 6, 3, 'Lok\'tar!', 14, 0, 100, 0, 0, 16020, 0, 37739, 0, 'Garrosh Hellscream - Faction Champions Kill Player');
INSERT INTO `creature_text` VALUES (34995, 50, 0, 'Finally, a fight worth watching.', 12, 0, 100, 396, 0, 0, 0, 35327, 0, 'Garrosh Hellscream - SAY_GARROSH_GRAND_CHAMPIONS_INTRO');
INSERT INTO `creature_text` VALUES (34995, 51, 0, 'Tear him apart!', 14, 0, 100, 22, 0, 0, 0, 35551, 0, 'Garrosh Hellscream');
INSERT INTO `creature_text` VALUES (34995, 52, 0, 'Admirably? Hah! I will enjoy watching your weak little champions fail, human.', 14, 0, 100, 22, 0, 0, 0, 35324, 0, 'Garrosh Hellscream');
INSERT INTO `creature_text` VALUES (34996, 0, 0, 'Welcome, champions! You have heard the call of the Argent Crusade and you have boldly answered! It is here, in the Crusaders\' Coliseum, that you will face your greatest challenges. Those of you who survive the rigors of the coliseum will join the Argent Crusade on its march to Icecrown Citadel.', 14, 0, 100, 0, 0, 16036, 0, 35708, 0, 'Highlord Tirion Fordring - Welcome');
INSERT INTO `creature_text` VALUES (34996, 1, 0, 'Hailing from the deepest, darkest caverns of the Storm Peaks, Gormok the Impaler! Battle on, heroes!', 14, 0, 100, 0, 0, 16038, 0, 35710, 0, 'Highlord Tirion Fordring - Summing Gormok the Impaler');
INSERT INTO `creature_text` VALUES (34996, 2, 0, 'Steel yourselves, heroes, for the twin terrors, Acidmaw and Dreadscale, enter the arena!', 14, 0, 100, 0, 0, 16039, 0, 35711, 0, 'Highlord Tirion Fordring - Summing Acidmaw and Dreadscale');
INSERT INTO `creature_text` VALUES (34996, 3, 0, 'The air itself freezes with the introduction of our next combatant, Icehowl! Kill or be killed, champions!', 14, 0, 100, 0, 0, 16040, 0, 35712, 0, 'Highlord Tirion Fordring Summing Icehowl');
INSERT INTO `creature_text` VALUES (34996, 4, 0, 'The monstrous menagerie has been vanquished!', 14, 0, 100, 0, 0, 16041, 0, 35713, 0, 'Highlord Tirion Fordring - Northrend Beasts Done');
INSERT INTO `creature_text` VALUES (34996, 5, 0, 'Tragic... They fought valiantly, but the beasts of Northrend triumphed. Let us observe a moment of silence for our fallen heroes.', 14, 0, 0, 0, 0, 16042, 0, 35714, 0, 'Highlord Tirion Fordring - Northrend Beasts FAIL');
INSERT INTO `creature_text` VALUES (34996, 6, 0, 'Grand Warlock Wilfred Fizzlebang will summon forth your next challenge. Stand by for his entry.', 14, 0, 100, 0, 0, 16043, 0, 35715, 0, 'Highlord Tirion Fordring - Summing Wilfred Fizzlebang');
INSERT INTO `creature_text` VALUES (34996, 7, 0, 'Quickly, heroes, destroy the demon lord before it can open a portal to its twisted demonic realm!', 14, 0, 100, 5, 0, 16044, 0, 35716, 0, 'Highlord Tirion Fordring to Wilfred Fizzlebang - Lord Jaraxxus Intro');
INSERT INTO `creature_text` VALUES (34996, 8, 0, 'The loss of Wilfred Fizzlebang, while unfortunate, should be a lesson to those that dare dabble in dark magic. Alas, you are victorious and now must face the next challenge.', 14, 0, 100, 0, 0, 16045, 0, 35717, 0, 'Highlord Tirion Fordring - Lord Jaraxxus Outro');
INSERT INTO `creature_text` VALUES (34996, 9, 0, 'Everyone calm down! Compose yourselves! There is no conspiracy at play here! The warlock acted on his own volition, outside of influences from the Alliance. The tournament must go on!', 14, 0, 100, 5, 0, 16046, 0, 35718, 0, 'Highlord Tirion Fordring - Lord Jaraxxus Outro');
INSERT INTO `creature_text` VALUES (34996, 10, 0, 'The next battle will be against the Argent Crusade\'s most powerful knights! Only by defeating them will you be deemed worthy...', 14, 0, 100, 0, 0, 16047, 0, 35719, 0, 'Highlord Tirion Fordring - Faction Champions Intro');
INSERT INTO `creature_text` VALUES (34996, 11, 0, 'Very well. I will allow it. Fight with honor!', 14, 0, 100, 1, 0, 16048, 0, 35720, 0, 'Highlord Tirion Fordring - Faction Champions Intro');
INSERT INTO `creature_text` VALUES (34996, 12, 0, 'A shallow and tragic victory. We are weaker as a whole from the losses suffered today. Who but the Lich King could benefit from such foolishness? Great warriors have lost their lives. And for what? The true threat looms ahead - the Lich King awaits us all in death.', 14, 0, 100, 0, 0, 16049, 0, 35721, 0, 'Highlord Tirion Fordring - Faction Champions Outro');
INSERT INTO `creature_text` VALUES (34996, 13, 0, 'Only by working together will you overcome the final challenge. From the depths of Icecrown come two of the Scourge\'s most powerful lieutenants: fearsome val\'kyr, winged harbingers of the Lich King!', 14, 0, 100, 0, 0, 16050, 0, 35722, 0, 'Highlord Tirion Fordring - Twin Val\'kyr Intro');
INSERT INTO `creature_text` VALUES (34996, 14, 0, 'Let the games begin!', 14, 0, 100, 0, 0, 16037, 0, 35709, 0, 'Highlord Tirion Fordring - Twin Val\'kyr Intro');
INSERT INTO `creature_text` VALUES (34996, 15, 0, 'A mighty blow has been dealt to the Lich King! You have proven yourselves as able bodied champions of the Argent Crusade. Together we will strike against Icecrown Citadel and destroy what remains of the Scourge! There is no challenge that we cannot face united!', 14, 0, 100, 5, 0, 16051, 0, 35723, 0, 'Highlord Tirion Fordring - Anub\'arak Intro');
INSERT INTO `creature_text` VALUES (34996, 16, 0, 'Arthas! You are hopelessly outnumbered! Lay down Frostmourne and I will grant you a just death.', 14, 0, 100, 25, 0, 16052, 0, 35724, 0, 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES (34996, 50, 0, 'Welcome, champions. Today, before the eyes of your leaders and peers, you will prove yourselves worthy combatants.', 14, 0, 100, 1, 0, 0, 0, 35321, 0, 'Highlord Tirion Fordring - SAY_TIRION_GRAND_CHAMPION_INTRO_1');
INSERT INTO `creature_text` VALUES (34996, 51, 0, 'You will first be facing three of the Grand Champions of the Tournament! These fierce contenders have beaten out all others to reach the pinnacle of skill in the joust.', 14, 0, 100, 0, 0, 0, 0, 35330, 0, 'Highlord Tirion Fordring - SAY_TIRION_GRAND_CHAMPION_INTRO_2');
INSERT INTO `creature_text` VALUES (34996, 52, 0, 'Begin!', 14, 0, 100, 0, 0, 8574, 0, 35331, 0, 'Highlord Tirion Fordring - SAY_TIRION_GRAND_CHAMPION_INTRO_3');
INSERT INTO `creature_text` VALUES (34996, 53, 0, 'Well fought! Your next challenge comes from the Crusade\'s own ranks. You will be tested against their considerable prowess.', 14, 0, 100, 0, 0, 15882, 0, 35541, 0, 'Highlord Tirion Fordring - SAY_TIRION_PALETRESS_INTRO_1');
INSERT INTO `creature_text` VALUES (34996, 54, 0, 'You may begin!', 14, 0, 100, 22, 0, 8574, 0, 35677, 0, 'Highlord Tirion Fordring - SAY_TIRION_PALETRESS_INTRO_2');
INSERT INTO `creature_text` VALUES (34996, 55, 0, 'Well done. You have proven yourself today-', 14, 0, 100, 0, 0, 0, 0, 35544, 0, 'Highlord Tirion Fordring - SAY_TIRION_KNIGHT_INTRO_2');
INSERT INTO `creature_text` VALUES (34996, 56, 0, 'What is the meaning of this?', 14, 0, 100, 0, 0, 0, 0, 35547, 0, 'Highlord Tirion Fordring - SAY_TIRION_KNIGHT_INTRO_2');
INSERT INTO `creature_text` VALUES (34996, 57, 0, 'My congratulations, champions. Through trials both planned and unexpected, you have triumphed.', 14, 0, 100, 0, 0, 15882, 0, 35796, 0, 'Highlord Tirion Fordring - SAY_TIRION_KNIGHT_OUTRO_1');
INSERT INTO `creature_text` VALUES (34996, 58, 0, 'Go now and rest; you\'ve earned it.', 14, 0, 100, 0, 0, 0, 0, 35797, 0, 'Highlord Tirion Fordring - SAY_TIRION_KNIGHT_OUTRO_2');
INSERT INTO `creature_text` VALUES (35003, 0, 0, 'All aboard the gunship! Man the guns!', 14, 0, 100, 0, 0, 0, 0, 35241, 0, 'Horde Gunship Captain');
INSERT INTO `creature_text` VALUES (35004, 0, 0, 'Entering the arena, a paladin who is no stranger to the battlefield or tournament ground, the Grand Champion of the Argent Crusade, Eadric the Pure!', 12, 0, 100, 0, 0, 8574, 0, 35542, 0, 'Jaeren Sunsworn - SAY_JAEREN_EADRIC_INTRO');
INSERT INTO `creature_text` VALUES (35004, 1, 0, 'The next combatant is second to none in her passion for upholding the Light. I give you Argent Confessor Paletress!', 14, 0, 100, 0, 0, 8574, 0, 35543, 0, 'Jaeren Sunsworn - SAY_JAEREN_PALETRESS_INTRO');
INSERT INTO `creature_text` VALUES (35004, 2, 0, 'The Sunreavers are proud to present their representatives in this trial by combat.', 14, 0, 100, 396, 0, 8574, 0, 35260, 0, 'Jaeren Sunsworn - SAY_JAEREN_GRAND_CHAMPIONS_INTRO_1');
INSERT INTO `creature_text` VALUES (35004, 3, 0, 'Coming out of the gate is Colosos, the towering Grand Champion of the Exodar!', 12, 0, 100, 0, 0, 8573, 0, 35247, 0, 'Jaeren Sunsworn - SAY_JAEREN_GRAND_CHAMPIONS_INTRO_2');
INSERT INTO `creature_text` VALUES (35004, 4, 0, 'Entering the arena is the Grand Champion of Darnassus, the skilled sentinel Jaelyne Evensong!', 12, 0, 100, 0, 0, 8573, 0, 35249, 0, 'Jaeren Sunsworn - SAY_JAEREN_GRAND_CHAMPIONS_INTRO_2');
INSERT INTO `creature_text` VALUES (35004, 5, 0, 'Here comes the small but deadly Ambrose Boltspark, Grand Champion of Gnomeregan!', 12, 0, 100, 0, 0, 8573, 0, 35248, 0, 'Jaeren Sunsworn - SAY_JAEREN_GRAND_CHAMPIONS_INTRO_2');
INSERT INTO `creature_text` VALUES (35004, 6, 0, 'Proud and strong, give a cheer for Marshal Jacob Alerius, the Grand Champion of Stormwind!', 12, 0, 100, 0, 0, 8573, 0, 35245, 0, 'Jaeren Sunsworn - SAY_JAEREN_GRAND_CHAMPIONS_INTRO_2');
INSERT INTO `creature_text` VALUES (35004, 7, 0, 'The might of the dwarves is represented today by the Grand Champion of Ironforge, Lana Stouthammer!', 12, 0, 100, 0, 0, 8573, 0, 35246, 0, 'Jaeren Sunsworn - SAY_JAEREN_GRAND_CHAMPIONS_INTRO_2');
INSERT INTO `creature_text` VALUES (35004, 8, 0, 'What\'s that, up near the rafters?', 12, 0, 100, 25, 0, 0, 0, 35545, 0, 'Jaeren Sunsworn - SAY_JAEREN_KNIGHT_INTRO');
INSERT INTO `creature_text` VALUES (35005, 0, 0, 'Entering the arena, a paladin who is no stranger to the battlefield or tournament ground, the Grand Champion of the Argent Crusade, Eadric the Pure!', 14, 0, 100, 0, 0, 8574, 0, 35542, 0, 'Arelas Brightstar - SAY_EADRIC_INTRO');
INSERT INTO `creature_text` VALUES (35005, 1, 0, 'The next combatant is second to none in her passion for upholding the Light. I give you Argent Confessor Paletress!', 14, 0, 100, 0, 0, 8574, 0, 35543, 0, 'Arelas Brightstar - SAY_ARELAS_PALETRESS_INTRO');
INSERT INTO `creature_text` VALUES (35005, 2, 0, 'The Silver Covenant is pleased to present their contenders for this event, Highlord.', 14, 0, 100, 396, 0, 8574, 0, 35259, 0, 'Arelas Brightstar - SAY_ARELAS_GRAND_CHAMPIONS_INTRO_1');
INSERT INTO `creature_text` VALUES (35005, 3, 0, 'Coming out of the gate is Eressea Dawnsinger, skilled mage and Grand Champion of Silvermoon!\n', 12, 0, 100, 0, 0, 8573, 0, 35338, 0, 'Arelas Brightstar - SAY_ARELAS_GRAND_CHAMPIONS_INTRO_2');
INSERT INTO `creature_text` VALUES (35005, 4, 0, 'Entering the arena is the lean and dangerous Zul\'tore, Grand Champion of Sen\'jin!\n', 12, 0, 100, 0, 0, 8573, 0, 35335, 0, 'Arelas Brightstar - SAY_ARELAS_GRAND_CHAMPIONS_INTRO_2');
INSERT INTO `creature_text` VALUES (35005, 5, 0, 'Presenting the fierce Grand Champion of Orgrimmar, Mokra the Skullcrusher!\n', 12, 0, 100, 0, 0, 8573, 0, 35334, 0, 'Arelas Brightstar - SAY_ARELAS_GRAND_CHAMPIONS_INTRO_2');
INSERT INTO `creature_text` VALUES (35005, 6, 0, 'Representing the tenacity of the Forsaken, here is the Grand Champion of the Undercity, Deathstalker Visceri!\n', 12, 0, 100, 0, 0, 8573, 0, 35337, 0, 'Arelas Brightstar - SAY_ARELAS_GRAND_CHAMPIONS_INTRO_2');
INSERT INTO `creature_text` VALUES (35005, 7, 0, 'Tall in the saddle of his kodo, here is the venerable Runok Wildmane, Grand Champion of Thunder Bluff!\n', 12, 0, 100, 0, 0, 8573, 0, 35336, 0, 'Arelas Brightstar - SAY_ARELAS_GRAND_CHAMPIONS_INTRO_2');
INSERT INTO `creature_text` VALUES (35005, 8, 0, 'What\'s that, up near the rafters?', 12, 0, 100, 25, 0, 0, 0, 35545, 0, 'Arelas Brightstar - SAY_ARELAS_KNIGHT_INTRO');
