INSERT INTO `creature_text` VALUES (35053, 0, 0, 'Make sure you pull plenty of moolah out of the bank. I don\'t want you buying any of that cheap clothing you usually like to wear. This party\'s important!', 12, 0, 100, 1, 0, 0, 0, 49023, 0, 'say Text');
INSERT INTO `creature_text` VALUES (35053, 1, 0, 'You\'re the most handsome boyfriend a girl could hope for. Knock\'em dead, honey!', 12, 0, 100, 1, 0, 0, 0, 49012, 0, 'say Text');
INSERT INTO `creature_text` VALUES (35053, 2, 0, 'You are dresed Impress! Use your new powers below to make your party guests happy!', 41, 0, 100, 0, 0, 0, 0, 35444, 0, 'say Text');
