INSERT INTO `creature_text` VALUES (35198, 0, 0, 'Why, what brings you all the way over here, young $r? Please be cautious. Our foes stream from a hole in the mountains to the east - they have done irreparable harm to this land.', 14, 0, 100, 0, 0, 0, 0, 35480, 0, 'on Aggro Text');
