INSERT INTO `creature_text` VALUES (35200, 0, 0, 'We be here courtesy o\' the trade prince.', 12, 0, 50, 0, 0, 0, 0, 35450, 0, 'on Aggro Text');
INSERT INTO `creature_text` VALUES (35200, 0, 1, 'Let\'s have a dance!', 12, 0, 50, 0, 0, 0, 0, 35449, 0, 'on Aggro Text');
INSERT INTO `creature_text` VALUES (35200, 0, 2, 'Who be in charge here?', 12, 0, 50, 0, 0, 0, 0, 35454, 0, 'on Aggro Text');
INSERT INTO `creature_text` VALUES (35222, 0, 0, 'Is that you, $n? What are you doing away from headquarters? Get back to work!', 14, 0, 100, 5, 0, 19546, 0, 37243, 0, 'Trade Prince Gallywix to Player');
INSERT INTO `creature_text` VALUES (35222, 0, 1, 'Drink Kaja\'Cola!', 14, 0, 100, 5, 0, 19549, 0, 37246, 0, 'Trade Prince Gallywix to Player');
INSERT INTO `creature_text` VALUES (35222, 0, 2, 'What\'s this I hear about a party? I wasn\'t invited!', 14, 0, 100, 6, 0, 19543, 0, 37240, 0, 'Trade Prince Gallywix to Player');
INSERT INTO `creature_text` VALUES (35222, 0, 3, 'You\'re all lazy! Get back to work. Especially you, $n!', 14, 0, 100, 25, 0, 19544, 0, 37241, 0, 'Trade Prince Gallywix to Player');
INSERT INTO `creature_text` VALUES (35222, 0, 4, 'All hail the greatest trade prince on Azeroth... me!', 14, 0, 100, 1, 0, 19547, 0, 37244, 0, 'Trade Prince Gallywix to Player');
INSERT INTO `creature_text` VALUES (35222, 0, 5, 'Thank you for your hard work and dedication. Unpaid overtime is approved for all!', 14, 0, 100, 1, 0, 19548, 0, 37245, 0, 'Trade Prince Gallywix to Player');
INSERT INTO `creature_text` VALUES (35222, 0, 6, 'It\'s demotions for everyone if the Sharks beat the Buccaneers!', 14, 0, 100, 1, 0, 19545, 0, 37242, 0, 'Trade Prince Gallywix to Player');
INSERT INTO `creature_text` VALUES (35229, 0, 0, '%s becomes enraged!', 16, 0, 100, 0, 0, 0, 0, 10677, 0, 'combat Enrage');
INSERT INTO `creature_text` VALUES (35230, 0, 0, 'Let\'s round up as many of them as we can.  Every worgen chasing us is one less worgen chasing the survivors!', 12, 0, 100, 0, 0, 19696, 0, 35498, 0, 'Lord Darius Crowley to Player');
INSERT INTO `creature_text` VALUES (35230, 1, 0, 'You\'ll never catch us, you blasted mongrels!', 14, 0, 100, 0, 0, 19696, 0, 35497, 0, 'Lord Darius Crowley to Player');
INSERT INTO `creature_text` VALUES (35294, 0, 0, 'I not gonna take it anymore,mon!', 12, 0, 50, 0, 0, 0, 0, 49041, 0, 'on Aggro Text');
INSERT INTO `creature_text` VALUES (35294, 0, 1, 'Kezan for da trolls!', 12, 0, 50, 0, 0, 0, 0, 49042, 0, 'on Aggro Text');
INSERT INTO `creature_text` VALUES (35294, 0, 2, 'Ya got some nerve,mon!', 12, 0, 50, 0, 0, 0, 0, 49039, 0, 'on Aggro Text');
