INSERT INTO `creature_text` VALUES (35304, 0, 3, 'It monitors Trolls very well!', 12, 0, 50, 5, 0, 0, 0, 0, 0, 'say Text');
INSERT INTO `creature_text` VALUES (35320, 0, 0, 'Thank you. It\'s the least I can do to support this; we need to stand together.', 12, 0, 100, 2, 0, 16120, 0, 35523, 0, 'Lady Jaina Proudmoore');
INSERT INTO `creature_text` VALUES (35320, 1, 0, 'I\'m sure Thrall will have his people under control...', 12, 0, 100, 274, 0, 16121, 0, 35529, 0, 'Lady Jaina Proudmoore');
INSERT INTO `creature_text` VALUES (35321, 0, 0, 'Tirion.', 12, 0, 100, 1, 0, 16070, 0, 35521, 0, 'King Varian Wrynn');
INSERT INTO `creature_text` VALUES (35321, 1, 0, 'You ask much of me, Tirion, to sit and watch, trusting the savages to keep themselves in check.', 12, 0, 100, 1, 0, 16071, 0, 35524, 0, 'King Varian Wrynn');
INSERT INTO `creature_text` VALUES (35321, 2, 0, 'Perhaps. I doubt the effectiveness of this gambit. We would be better served preparing our armies for the final assault.', 12, 0, 100, 1, 0, 16072, 0, 35527, 0, 'King Varian Wrynn');
INSERT INTO `creature_text` VALUES (35321, 3, 0, 'But if my presence is required in these games to gain your support in the battle, then I will remain.  For now.', 12, 0, 100, 1, 0, 0, 0, 35528, 0, 'King Varian Wrynn');
INSERT INTO `creature_text` VALUES (35361, 0, 0, 'Welcome, Warchief Thrall. Overlord Hellscream.', 12, 0, 100, 2, 0, 16056, 0, 35785, 0, 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES (35361, 1, 0, 'I hope you\'ll see the merit of these events in time, Garrosh. We cannot win against the Scourge if we continue to war against one another.', 12, 0, 100, 0, 0, 16057, 0, 35786, 0, 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES (35361, 2, 0, 'Under my roof, gentlemen, I trust you will behave.', 12, 0, 100, 0, 0, 16058, 0, 35787, 0, 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES (35361, 3, 0, 'If you will follow me, then.', 12, 0, 100, 1, 0, 16059, 0, 35788, 0, 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES (35361, 4, 0, 'King Varian, Lady Jaina. Welcome to the tournament. Your place in the coliseum is waiting.', 12, 0, 100, 1, 0, 16054, 0, 35522, 0, 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES (35361, 5, 0, 'My people are here to ensure they honor the rules of engagement. It will do your subjects well to see you in the stands.', 12, 0, 100, 1, 0, 16055, 0, 35525, 0, 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES (35361, 6, 0, '%s nods and gestures for his guests to follow.', 16, 0, 100, 273, 0, 0, 0, 35526, 0, 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES (35361, 7, 0, 'Combatants of the Alliance and Horde! You are welcome under the banner of the Argent Crusade.', 12, 0, 100, 5, 0, 0, 0, 35799, 0, 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES (35361, 8, 0, 'To the south lies our goal. We will march to the Citadel and cut out the heart of the Scourge where it dwells.', 12, 0, 100, 25, 0, 0, 0, 35800, 0, 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES (35361, 9, 0, 'But this is no task to be taken lightly.', 12, 0, 100, 0, 0, 0, 0, 35801, 0, 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES (35361, 10, 0, 'A massive attack with every able-bodied man would end in needless slaughter. Every soldier lost would rise as the enemy. Azeroth would be left defenseless against the undead threat.', 12, 0, 100, 0, 0, 0, 0, 35802, 0, 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES (35361, 11, 0, 'Instead, we require a small, concentrated strike force for the attack to succeed. For that reason, we have created the Argent Tournament.', 12, 0, 100, 0, 0, 0, 0, 35803, 0, 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES (35361, 12, 0, 'Within these walls, you will be tested. Your skill in combat will be matched against the fiercest dangers Northrend has to offer under the watchful eyes of your leaders.', 12, 0, 100, 0, 0, 0, 0, 35805, 0, 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES (35361, 13, 0, 'Your prowess, your might, and your cunning will be under close watch. These games will determine the best Azeroth has to offer.', 12, 0, 100, 0, 0, 0, 0, 35806, 0, 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES (35361, 14, 0, 'The victors will take their rightful place in the assault upon the Citadel. We will stand together in the face of evil, and Arthas WILL fall!', 12, 0, 100, 0, 0, 0, 0, 35807, 0, 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES (35368, 0, 0, 'Garrosh, I expect you to control yourself here. I do not want a repeat of the Violet Citadel.', 12, 0, 100, 1, 0, 16181, 0, 35552, 0, 'Thrall');
INSERT INTO `creature_text` VALUES (35368, 1, 0, 'We are guests here, Garrosh, and you will conduct yourself honorably.', 12, 0, 100, 0, 0, 16182, 0, 35558, 0, 'Thrall');
INSERT INTO `creature_text` VALUES (35368, 2, 0, 'Thank you for the invitation, Lord Fordring. We look forward to observing these games.', 12, 0, 100, 66, 0, 16183, 0, 35559, 0, 'Thrall');
INSERT INTO `creature_text` VALUES (35368, 3, 0, 'Wise words.', 12, 0, 100, 1, 0, 16184, 0, 35560, 0, 'Thrall');
INSERT INTO `creature_text` VALUES (35368, 4, 0, 'Garrosh, enough!', 12, 0, 100, 5, 0, 16185, 0, 35561, 0, 'Thrall');
INSERT INTO `creature_text` VALUES (35368, 5, 0, 'Of course, Tirion. I apologize for his outburst.  It will not be repeated.', 12, 0, 100, 0, 0, 16186, 0, 35562, 0, 'Thrall');
INSERT INTO `creature_text` VALUES (35372, 0, 0, 'Pah! That was a show of strength, worthy of a leader. I only regret I did not kill that human before the mage interfered.', 12, 0, 100, 5, 0, 16027, 0, 35553, 0, 'Garrosh Hellscream');
INSERT INTO `creature_text` VALUES (35372, 1, 0, 'What honor is there in thrashing about with blunted sticks? This is a waste of time.', 12, 0, 100, 1, 0, 16028, 0, 35554, 0, 'Garrosh Hellscream');
INSERT INTO `creature_text` VALUES (35372, 2, 0, 'Speak for yourself, Thrall.', 12, 0, 100, 1, 0, 16029, 0, 35555, 0, 'Garrosh Hellscream');
INSERT INTO `creature_text` VALUES (35372, 3, 0, 'Words of a fool, you mean. The Horde will destroy the undead without your aid, human, or that of the pompous king.', 12, 0, 100, 274, 0, 16030, 0, 35556, 0, 'Garrosh Hellscream');
INSERT INTO `creature_text` VALUES (35372, 4, 0, 'Bah.', 12, 0, 100, 1, 0, 16031, 0, 35557, 0, 'Garrosh Hellscream');
INSERT INTO `creature_text` VALUES (35378, 0, 0, 'This mastiff will help you find the hidden worgen.', 12, 0, 100, 0, 0, 19696, 0, 38744, 0, 'Lorna Crowley to Player');
INSERT INTO `creature_text` VALUES (35451, 0, 0, 'You spoiled my grand entrance, rat.', 12, 0, 100, 0, 0, 16256, 0, 35546, 0, 'The Black Knight - SAY_INTRO_1');
INSERT INTO `creature_text` VALUES (35451, 1, 0, 'Did you honestly think an agent of the Lich King would be bested on the field of your pathetic little tournament?', 12, 0, 100, 396, 0, 16257, 0, 35548, 0, 'The Black Knight - SAY_INTRO_2');
INSERT INTO `creature_text` VALUES (35451, 2, 0, 'I\'ve come to finish my task.', 12, 0, 100, 396, 0, 16258, 0, 35549, 0, 'The Black Knight - SAY_INTRO_3');
INSERT INTO `creature_text` VALUES (35451, 3, 0, 'This farce ends here!', 14, 0, 100, 0, 0, 16259, 0, 35767, 0, 'The Black Knight - SAY_AGGRO');
INSERT INTO `creature_text` VALUES (35451, 4, 0, 'My rotting flesh was just getting in the way!', 14, 0, 100, 0, 0, 16262, 0, 35771, 0, 'The Black Knight - SAY_PHASE_2');
INSERT INTO `creature_text` VALUES (35451, 5, 0, 'I have no need for bones to best you!', 14, 0, 100, 0, 0, 16263, 0, 35772, 0, 'The Black Knight - SAY_PHASE_3');
INSERT INTO `creature_text` VALUES (35451, 6, 0, 'Pathetic.', 14, 0, 100, 0, 0, 16260, 0, 35768, 0, 'The Black Knight - SAY_KILL_PLAYER');
INSERT INTO `creature_text` VALUES (35451, 6, 1, 'A waste of flesh.', 14, 0, 100, 0, 0, 16261, 0, 35769, 0, 'The Black Knight - SAY_KILL_PLAYER');
INSERT INTO `creature_text` VALUES (35451, 7, 0, 'No! I must not fail... again...', 14, 0, 100, 0, 0, 16264, 0, 35770, 0, 'The Black Knight - SAY_DEATH');
INSERT INTO `creature_text` VALUES (35456, 0, 0, '%s becomes enraged!', 16, 0, 100, 0, 0, 0, 0, 10677, 0, 'combat Enrage');
INSERT INTO `creature_text` VALUES (35458, 0, 0, 'Thank you, Highlord. Now, challengers, I will begin the ritual of summoning. When I am done a fearsome doomguard will appear!', 14, 0, 100, 2, 0, 16268, 0, 35726, 0, 'Wilfred Fizzlebang - Intro');
INSERT INTO `creature_text` VALUES (35458, 1, 0, 'Prepare for oblivion!', 14, 0, 100, 0, 0, 16269, 0, 35727, 0, 'Wilfred Fizzlebang - Intro');
INSERT INTO `creature_text` VALUES (35458, 2, 0, 'A-HA! I\'ve done it! Behold the absolute power of Wilfred Fizzlebang, master summoner! You are bound to me, demon!', 14, 0, 100, 5, 0, 16270, 0, 35728, 0, 'Wilfred Fizzlebang to Wilfred Fizzlebang - Intro');
INSERT INTO `creature_text` VALUES (35458, 3, 0, 'But I\'m in charge here...', 14, 0, 100, 5, 0, 16271, 0, 35729, 0, 'Wilfred Fizzlebang to Wilfred Fizzlebang - Death');
INSERT INTO `creature_text` VALUES (35463, 0, 0, '%s becomes enraged!', 16, 0, 100, 0, 0, 0, 0, 10677, 0, 'combat Enrage');
INSERT INTO `creature_text` VALUES (35476, 0, 0, 'Of course I was the one chosen to summon a demon for the competitors. I am Wilfred Fizzlebang, master summoner! Demons quail before the mere thought of being under my will!', 12, 0, 100, 1, 0, 0, 0, 35818, 0, 'Wilfred Fizzlebang');
INSERT INTO `creature_text` VALUES (35476, 0, 1, 'Soon all the world will know the name of Wilfred!', 12, 0, 100, 1, 0, 0, 0, 35816, 0, 'Wilfred Fizzlebang');
INSERT INTO `creature_text` VALUES (35476, 0, 2, 'It won\'t be long before Fizzlebang is a household word, used to scare children into bed! \"Beware, or Fizzlebang will summon a mighty demon to eat you!\"', 12, 0, 100, 1, 0, 0, 0, 35817, 0, 'Wilfred Fizzlebang');
INSERT INTO `creature_text` VALUES (35476, 0, 3, 'I, Wilfred Fizzlebang, have been invited to this quaint tournament to make use of my extensive knowledge of the summoning arts. What better choice?', 12, 0, 100, 1, 0, 0, 0, 35815, 0, 'Wilfred Fizzlebang');
