INSERT INTO `creature_text` VALUES (36597, 0, 0, 'So the Light\'s vaunted justice has finally arrived? Shall I lay down Frostmourne and throw myself at your mercy, Fordring?', 14, 0, 0, 0, 0, 17349, 0, 38070, 0, 'The Lich King - SAY_LK_INTRO_1');
