INSERT INTO `creature_text` VALUES (36597, 2, 0, 'I\'ll keep you alive to witness the end, Fordring. I would not want the Light\'s greatest champion to miss seeing this wretched world remade in my image.', 14, 0, 0, 0, 0, 17351, 0, 38072, 0, 'The Lich King - SAY_LK_INTRO_3');
INSERT INTO `creature_text` VALUES (36597, 3, 0, 'Come then, champions. Feed me your rage!', 14, 0, 0, 0, 0, 0, 0, 38073, 0, 'The Lich King - SAY_LK_AGGRO');
INSERT INTO `creature_text` VALUES (36597, 4, 0, 'I will freeze you from within until all that remains is an icy husk.', 14, 0, 0, 0, 0, 17369, 0, 38046, 0, 'The Lich King - SAY_LK_REMORSELESS_WINTER');
