INSERT INTO `creature_text` VALUES (36627, 6, 1, 'I brokes-ded it...', 14, 0, 0, 0, 0, 16987, 0, 37832, 0, 'Rotface - SAY_KILL_2');
INSERT INTO `creature_text` VALUES (36627, 7, 0, 'SLEEPY TIME!', 14, 0, 0, 0, 0, 16990, 0, 37835, 0, 'Rotface - SAY_BERSERK');
INSERT INTO `creature_text` VALUES (36627, 8, 0, 'Bad news, daddy...', 14, 0, 0, 0, 0, 16989, 0, 37834, 0, 'Rotface - SAY_DEATH');
INSERT INTO `creature_text` VALUES (36627, 9, 0, '|TInterface\\Icons\\ability_creature_disease_02.blp:16|tYou have |cFF00FF00Mutated Infection!|r', 42, 0, 0, 0, 0, 0, 0, 37872, 0, 'Rotface - EMOTE_MUTATED_INFECTION');
INSERT INTO `creature_text` VALUES (36638, 0, 0, 'You\'ve come seeking your death? Happy to oblige.', 14, 0, 100, 0, 0, 0, 0, 36908, 0, 'on Aggro Text');
INSERT INTO `creature_text` VALUES (36642, 1, 0, 'The style of this blade and its markings hinted that it might be a dragonforged sword. At last, we will have our answer.', 12, 0, 100, 1, 0, 16745, 0, 37469, 0, 'Myralion Sunblaze - Say 1');
INSERT INTO `creature_text` VALUES (36642, 2, 0, 'After all these years, can it really be --', 12, 0, 100, 6, 0, 16746, 0, 37470, 0, 'Myralion Sunblaze - Say 2');
INSERT INTO `creature_text` VALUES (36642, 3, 0, 'It falls to us to see Quel\'Delar restored for the Sunreavers.', 12, 0, 100, 0, 0, 0, 0, 37514, 0, 'Myralion Sunblaze - Say 3');
INSERT INTO `creature_text` VALUES (36656, 1, 0, 'You are not welcome here, minion of the Lich King!', 12, 0, 100, 0, 0, 0, 0, 37887, 0, 'Silver Covenant Sentinel - Say 1');
INSERT INTO `creature_text` VALUES (36658, 0, 0, 'Another shall take his place. You waste your time.', 14, 0, 100, 0, 0, 16752, 0, 36765, 0, 'Tyrannus SAY_TYRANNUS_DEATH');
INSERT INTO `creature_text` VALUES (36658, 3, 0, 'Your pursuit shall be in vain, intruders, for the Lich King has placed an army of undead at my command! Behold!', 14, 0, 100, 0, 0, 16755, 0, 36708, 0, 'Tyrannus SAY_AMBUSH_1');
INSERT INTO `creature_text` VALUES (36658, 4, 0, 'Persistent whelps! You will not reach the entrance of my lord\'s lair! Soldiers, destroy them!', 14, 0, 100, 0, 0, 16756, 0, 36709, 0, 'Tyrannus SAY_AMBUSH_2');
INSERT INTO `creature_text` VALUES (36658, 5, 0, 'Rimefang, trap them within the tunnel! BURY THEM ALIVE!', 14, 0, 100, 0, 0, 16757, 0, 36714, 0, 'Tyrannus SAY_GAUNTLET_START');
INSERT INTO `creature_text` VALUES (36658, 6, 0, 'Alas, brave, brave adventurers, your meddling has reached its end. Do you hear the clatter of bone and steel coming up the tunnel behind you? That is the sound of your impending demise.', 14, 0, 100, 0, 0, 16758, 0, 37233, 0, 'Tyrannus SAY_TYRANNUS_INTRO_1');
INSERT INTO `creature_text` VALUES (36658, 7, 0, 'Ha, such an amusing gesture from the rabble. When I have finished with you, my master\'s blade will feast upon your souls. Die.', 14, 0, 100, 0, 0, 16759, 0, 37234, 0, 'Tyrannus SAY_TYRANNUS_INTRO_3');
INSERT INTO `creature_text` VALUES (36658, 8, 0, 'I shall not fail the Lich King. Come and meet your end!', 14, 0, 100, 0, 0, 16760, 0, 38718, 0, 'Tyrannus SAY_AGGRO');
INSERT INTO `creature_text` VALUES (36658, 9, 0, 'Such a shameful display. You are better off dead.', 14, 0, 100, 0, 0, 16761, 0, 38715, 0, 'Tyrannus SAY_SLAY_1');
INSERT INTO `creature_text` VALUES (36658, 9, 1, 'Perhaps you should have stayed... in the mountains!', 14, 0, 100, 0, 0, 16762, 0, 38716, 0, 'Tyrannus SAY_SLAY_2');
INSERT INTO `creature_text` VALUES (36658, 10, 0, 'Impossible.... Rimefang.... warn....', 14, 0, 100, 0, 0, 16763, 0, 38714, 0, 'Tyrannus SAY_DEATH');
INSERT INTO `creature_text` VALUES (36658, 11, 0, 'Rimefang, destroy this fool!', 14, 0, 100, 0, 0, 16764, 0, 36648, 0, 'Tyrannus SAY_MARK_RIMEFANG_1');
INSERT INTO `creature_text` VALUES (36658, 12, 0, 'The frostwyrm Rimefang gazes at $n and readies an icy attack!', 41, 0, 100, 0, 0, 0, 0, 36649, 0, 'Tyrannus SAY_MARK_RIMEFANG_2');
INSERT INTO `creature_text` VALUES (36658, 13, 0, 'Power... overwhelming!', 14, 0, 100, 0, 0, 16765, 0, 38717, 0, 'Tyrannus SAY_DARK_MIGHT_1');
INSERT INTO `creature_text` VALUES (36658, 14, 0, '%s roars and swells with dark might!', 41, 0, 100, 0, 0, 0, 0, 36628, 0, 'Tyrannus SAY_DARK_MIGHT_2');
INSERT INTO `creature_text` VALUES (36669, 0, 0, 'It\'s good to finally see you, courier. Krasus has entrusted us with one of the few remaining copies of Ancient Dragonforged Blades.', 12, 0, 100, 1, 0, 0, 0, 37620, 0, 'Arcanist Tybalin');
INSERT INTO `creature_text` VALUES (36669, 1, 0, 'Here is the book. You must ensure it gets to Caladis Brightspear in Icecrown before the Sunreavers realize what is happening.', 12, 0, 100, 1, 0, 0, 0, 37621, 0, 'Arcanist Tybalin');
INSERT INTO `creature_text` VALUES (36669, 2, 0, 'Travel quickly! The Sunreavers will not waste any time once they realize that we have the information they seek.', 12, 0, 100, 1, 0, 0, 0, 37622, 0, 'Arcanist Tybalin');
INSERT INTO `creature_text` VALUES (36670, 0, 0, 'You\'re late, courier. No, I don\'t want any excuses; this errand is far too important.', 12, 0, 100, 1, 0, 0, 0, 37580, 0, 'Magister Hathorel');
INSERT INTO `creature_text` VALUES (36670, 1, 0, 'Here\'s the tome our representative brought from Wyrmrest. Get this to Myralion Sunblaze immediately.', 12, 0, 100, 1, 0, 0, 0, 37581, 0, 'Magister Hathorel');
INSERT INTO `creature_text` VALUES (36670, 2, 0, 'Watch your back, courier. I needn\'t remind you of the value of the book, nor the fact that the Silver Covenant will stop at nothing to take it from us.', 12, 0, 100, 1, 0, 0, 0, 37582, 0, 'Magister Hathorel');
INSERT INTO `creature_text` VALUES (36678, 0, 0, 'Just an ordinary gas cloud, but watch out because that\'s no ordinary gas cloud!', 14, 0, 0, 432, 0, 17119, 0, 37843, 0, 'Professor Putricide - SAY_GASEOUS_BLIGHT');
INSERT INTO `creature_text` VALUES (36678, 1, 0, 'Oh Festergut, you were always my favorite -- next to Rotface.  The good news is that you left behind so much gas. I can practically taste it!', 14, 0, 0, 0, 0, 17124, 0, 37848, 0, 'Professor Putricide - SAY_FESTERGUT_DEATH');
INSERT INTO `creature_text` VALUES (36678, 2, 0, 'Great news, everyone! The slime is flowing again!', 14, 0, 0, 1, 0, 17126, 0, 38120, 0, 'Professor Putricide - SAY_ROTFACE_OOZE_FLOOD1');
INSERT INTO `creature_text` VALUES (36678, 2, 1, 'Good news, everyone! I\'ve fixed the poison slime pipes!', 14, 0, 0, 1, 0, 17123, 0, 37049, 0, 'Professor Putricide - SAY_ROTFACE_OOZE_FLOOD2');
INSERT INTO `creature_text` VALUES (36678, 3, 0, 'Terrible news, everyone. Rotface is dead, but great news everyone! He left behind plenty of ooze for me to use! What? I\'m a poet and I didn\'t know it... Astounding!', 14, 0, 0, 0, 0, 17146, 0, 37847, 0, 'Professor Putricide - SAY_ROTFACE_DEATH');
INSERT INTO `creature_text` VALUES (36678, 4, 0, 'Good news, everyone! I think I\'ve perfected a plague that will destroy all life on Azeroth!', 14, 0, 0, 0, 0, 17114, 0, 37838, 0, 'Professor Putricide - SAY_AGGRO');
INSERT INTO `creature_text` VALUES (36678, 5, 0, '%s begins to cast Unstable Experiment!', 41, 0, 0, 0, 0, 0, 0, 38500, 0, 'Professor Putricide - EMOTE_UNSTABLE_EXPERIMENT');
INSERT INTO `creature_text` VALUES (36678, 6, 0, 'Two oozes, one room. So many delightful possibilities!', 14, 0, 0, 0, 0, 17122, 0, 37846, 0, 'Professor Putricide - SAY_PHASE_TRANSITION_HEROIC');
INSERT INTO `creature_text` VALUES (36678, 7, 0, 'Hrm, I don\'t feel a thing. Wha?! Where\'d those come from?', 14, 0, 0, 15, 0, 17120, 0, 37844, 0, 'Professor Putricide - SAY_TRANSFORM_1');
INSERT INTO `creature_text` VALUES (36678, 8, 0, 'Tastes like... Cherry! OH! Excuse me!', 14, 0, 0, 15, 0, 17121, 0, 37845, 0, 'Professor Putricide - SAY_TRANSFORM_2');
INSERT INTO `creature_text` VALUES (36678, 9, 0, '|TInterface\\Icons\\inv_misc_herb_evergreenmoss.blp:16|t%s cast |cFF00FF00Malleable Goo!|r', 41, 0, 0, 0, 0, 0, 0, 38499, 0, 'Professor Putricide - EMOTE_MALLEABLE_GOO');
INSERT INTO `creature_text` VALUES (36678, 10, 0, '%s cast |cFFFF7F00Choking Gas Bomb!|r', 41, 0, 0, 0, 0, 0, 0, 38498, 0, 'Professor Putricide - EMOTE_CHOKING_GAS_BOMB');
INSERT INTO `creature_text` VALUES (36678, 11, 0, 'Hrm, interesting...', 14, 0, 0, 0, 0, 17115, 0, 37839, 0, 'Professor Putricide - SAY_KILL_1');
INSERT INTO `creature_text` VALUES (36678, 11, 1, 'That was unexpected!', 14, 0, 0, 0, 0, 17116, 0, 38545, 0, 'Professor Putricide - SAY_KILL_2');
INSERT INTO `creature_text` VALUES (36678, 12, 0, 'Great news, everyone!', 14, 0, 0, 0, 0, 17118, 0, 38546, 0, 'Professor Putricide - SAY_BERSERK');
INSERT INTO `creature_text` VALUES (36678, 13, 0, 'Bad news, everyone... I don\'t think I\'m going to make it...', 14, 0, 0, 0, 0, 17117, 0, 37841, 0, 'Professor Putricide - SAY_DEATH');
INSERT INTO `creature_text` VALUES (36712, 0, 0, 'Squealer take this land! Tauren will burn!', 12, 0, 100, 0, 0, 0, 0, 38323, 0, 'Chief Squealer Thornmantle');
INSERT INTO `creature_text` VALUES (36723, 0, 0, 'You are not worthy to face the Lich King!', 14, 0, 100, 0, 0, 16921, 0, 38664, 0, 'general SAY_AGGRO');
INSERT INTO `creature_text` VALUES (36723, 1, 0, 'Master, I have failed...', 14, 0, 100, 0, 0, 16922, 0, 36921, 0, 'general SAY_DEATH');
INSERT INTO `creature_text` VALUES (36764, 0, 0, 'You\'re a beautiful sight... you have no idea.', 12, 0, 100, 0, 0, 0, 0, 37269, 0, 'Alliance Slave');
INSERT INTO `creature_text` VALUES (36764, 0, 1, '%s lets his mining pick drag on the ground as she approaches, clearly exhausted.', 16, 0, 100, 0, 0, 0, 0, 37284, 0, 'Alliance Slave');
INSERT INTO `creature_text` VALUES (36764, 0, 2, 'I owe you a long night of drinking, my friend.', 12, 0, 100, 0, 0, 0, 0, 37263, 0, 'Alliance Slave');
INSERT INTO `creature_text` VALUES (36764, 0, 3, 'If by life or death I can repay you, I will.', 12, 0, 100, 0, 0, 0, 0, 37287, 0, 'Alliance Slave');
INSERT INTO `creature_text` VALUES (36764, 0, 4, 'I\'m going to return to the front of the quarry. Kill a few extra for me.', 12, 0, 100, 0, 0, 0, 0, 37294, 0, 'Alliance Slave');
INSERT INTO `creature_text` VALUES (36764, 0, 5, 'I\'d almost given up hope.', 12, 0, 100, 0, 0, 0, 0, 37079, 0, 'Alliance Slave - On Liberation');
INSERT INTO `creature_text` VALUES (36764, 0, 6, 'I thought I might die in this pit. Thank you!', 12, 0, 100, 0, 0, 0, 0, 37262, 0, 'Alliance Slave - On Liberation');
INSERT INTO `creature_text` VALUES (36764, 0, 7, 'Don\'t let a single one of them live.', 12, 0, 100, 0, 0, 0, 0, 37264, 0, 'Alliance Slave - On Liberation');
INSERT INTO `creature_text` VALUES (36764, 0, 8, 'Please, carry out our vengeance on the Scourgelord.', 12, 0, 100, 0, 0, 0, 0, 37265, 0, 'Alliance Slave - On Liberation');
INSERT INTO `creature_text` VALUES (36764, 0, 9, 'When you kill the Pit Master, spit on his corpse for me, will you?', 12, 0, 100, 0, 0, 0, 0, 37266, 0, 'Alliance Slave - On Liberation');
INSERT INTO `creature_text` VALUES (36764, 0, 10, 'I\'d lost all track of time in this forsaken place. You\'re a sight for sore eyes, friend.', 12, 0, 100, 0, 0, 0, 0, 37268, 0, 'Alliance Slave - On Liberation');
INSERT INTO `creature_text` VALUES (36764, 0, 11, 'Too many of us died in this pit. Far too many.', 12, 0, 100, 0, 0, 0, 0, 37270, 0, 'Alliance Slave - On Liberation');
INSERT INTO `creature_text` VALUES (36764, 0, 12, 'I\'m so glad you\'re here. I wouldn\'t have made it much longer.', 12, 0, 100, 0, 0, 0, 0, 37271, 0, 'Alliance Slave - On Liberation');
INSERT INTO `creature_text` VALUES (36764, 0, 13, 'Now is the time for revenge.', 12, 0, 100, 0, 0, 0, 0, 37273, 0, 'Alliance Slave - On Liberation');
INSERT INTO `creature_text` VALUES (36764, 0, 14, 'You deserve a mighty man-hug.', 12, 0, 100, 0, 0, 0, 0, 37274, 0, 'Alliance Slave - On Liberation');
INSERT INTO `creature_text` VALUES (36764, 0, 15, 'I will find a way to repay you someday, hero.', 12, 0, 100, 0, 0, 0, 0, 37275, 0, 'Alliance Slave - On Liberation');
INSERT INTO `creature_text` VALUES (36764, 0, 16, 'I can hardly believe my eyes. Thank you. Really, thank you.', 12, 0, 100, 0, 0, 0, 0, 37276, 0, 'Alliance Slave - On Liberation');
INSERT INTO `creature_text` VALUES (36764, 0, 17, '%s nods his thanks.', 16, 0, 100, 0, 0, 0, 0, 37277, 0, 'Alliance Slave - On Liberation');
INSERT INTO `creature_text` VALUES (36764, 0, 18, '%s offers an appreciative smile.', 16, 0, 100, 0, 0, 0, 0, 37278, 0, 'Alliance Slave - On Liberation');
INSERT INTO `creature_text` VALUES (36764, 0, 19, '%s almost collapses as he stumbles towards you.', 16, 0, 100, 0, 0, 0, 0, 37279, 0, 'Alliance Slave - On Liberation');
INSERT INTO `creature_text` VALUES (36764, 0, 20, '%s stares at you for a moment, clearly finding it hard to believe that you\'re real.', 16, 0, 100, 0, 0, 0, 0, 37280, 0, 'Alliance Slave - On Liberation');
INSERT INTO `creature_text` VALUES (36764, 0, 21, '%s offers a quick, cynical laugh at his fortune.', 16, 0, 100, 0, 0, 0, 0, 37281, 0, 'Alliance Slave - On Liberation');
INSERT INTO `creature_text` VALUES (36764, 0, 22, '%s raises an empty hand in toast to you.', 16, 0, 100, 0, 0, 0, 0, 37282, 0, 'Alliance Slave - On Liberation');
INSERT INTO `creature_text` VALUES (36764, 0, 23, '%s closes his eyes, savoring freedom for a moment.', 16, 0, 100, 0, 0, 0, 0, 37283, 0, 'Alliance Slave - On Liberation');
INSERT INTO `creature_text` VALUES (36764, 0, 24, 'I\'m going to return to help free the rest of the slaves.  Thank you again, hero.', 16, 0, 100, 0, 0, 0, 0, 37293, 0, 'Alliance Slave - On Liberation');
