INSERT INTO `creature_text` VALUES (36789, 2, 0, 'My strength is returning. Press on, heroes!', 14, 0, 0, 0, 0, 17070, 0, 37878, 0, 'Valithria Dreamwalker - SAY_VALITHRIA_75_PERCENT');
INSERT INTO `creature_text` VALUES (36789, 3, 0, 'I will not last much longer!', 14, 0, 0, 0, 0, 17069, 0, 21784, 0, 'Valithria Dreamwalker - SAY_VALITHRIA_25_PERCENT');
INSERT INTO `creature_text` VALUES (36789, 4, 0, 'Forgive me for what I do! I... cannot... stop... ONLY NIGHTMARES REMAIN!', 14, 0, 0, 0, 0, 17072, 0, 0, 0, 'Valithria Dreamwalker - SAY_VALITHRIA_DEATH');
INSERT INTO `creature_text` VALUES (36789, 5, 0, 'A tragic loss...', 14, 0, 0, 0, 0, 17066, 0, 0, 0, 'Valithria Dreamwalker - SAY_VALITHRIA_PLAYER_DEATH');
INSERT INTO `creature_text` VALUES (36789, 6, 0, 'FAILURES!', 14, 0, 0, 0, 0, 17067, 0, 0, 0, 'Valithria Dreamwalker - SAY_VALITHRIA_BERSERK');
INSERT INTO `creature_text` VALUES (36789, 7, 0, 'I AM RENEWED! Ysera grant me the favor to lay these foul creatures to rest!', 14, 0, 0, 0, 0, 17071, 0, 37852, 0, 'Valithria Dreamwalker - SAY_VALITHRIA_SUCCESS');
INSERT INTO `creature_text` VALUES (36794, 1, 0, 'Worthless gnat! Death is all that awaits you!', 14, 0, 100, 0, 0, 16753, 0, 36852, 0, 'Tyrannus SAY_TYRANNUS_OUTRO_7');
INSERT INTO `creature_text` VALUES (36794, 2, 0, 'Do not think that I shall permit you entry into my master\'s sanctum so easily. Pursue me if you dare.', 14, 0, 100, 0, 0, 16754, 0, 36853, 0, 'Tyrannus SAY_TYRANNUS_OUTRO_9');
INSERT INTO `creature_text` VALUES (36794, 3, 0, 'Rimefang, trap them within the tunnel! BURY THEM ALIVE!', 14, 0, 100, 0, 0, 16757, 0, 36714, 1, 'SAY_TYRANNUS_CAVERN_ENTRANCE');
INSERT INTO `creature_text` VALUES (36814, 0, 0, 'Let the Scythe unbind that which was not meant to be bound!  Let the soul master the beast, lest the beast master the soul!', 12, 0, 100, 1, 0, 0, 0, 37730, 0, 'Talran of the Wild to Player');
INSERT INTO `creature_text` VALUES (36815, 0, 0, '%s becomes enraged!', 16, 0, 100, 0, 0, 0, 0, 10677, 0, 'combat Enrage');
INSERT INTO `creature_text` VALUES (36823, 0, 0, 'You have come to bring Arthas to justice? To see the Lich King destroyed?', 14, 0, 0, 0, 0, 17394, 0, 38114, 0, 'Terenas Menethil - SAY_TERENAS_INTRO_1');
INSERT INTO `creature_text` VALUES (36823, 1, 0, 'First, you must escape Frostmourne\'s hold, or be damned as I am; trapped within this cursed blade for all eternity. ', 14, 0, 0, 0, 0, 17395, 0, 38115, 0, 'Terenas Menethil - SAY_TERENAS_INTRO_2');
INSERT INTO `creature_text` VALUES (36823, 2, 0, 'Aid me in destroying these tortured souls! Together we will loosen Frostmourne\'s hold and weaken the Lich King from within!', 14, 0, 0, 0, 0, 17396, 0, 38116, 0, 'Terenas Menethil - SAY_TERENAS_INTRO_3');
INSERT INTO `creature_text` VALUES (36838, 0, 0, '|TInterface\\Icons\\Spell_Fire_Flameshock.blp:20|t|cFFBC0000 OVERHEAT  |r|TInterface\\Icons\\Spell_Fire_Flameshock.blp:20|t', 42, 0, 100, 0, 0, 0, 0, 36720, 0, 'Alliance Gunship Cannon - SAY_OVERHEAT');
INSERT INTO `creature_text` VALUES (36839, 0, 0, '|TInterface\\Icons\\Spell_Fire_Flameshock.blp:20|t|cFFBC0000 OVERHEAT  |r|TInterface\\Icons\\Spell_Fire_Flameshock.blp:20|t', 42, 0, 100, 0, 0, 0, 0, 36720, 0, 'Horde Gunship Cannon - SAY_OVERHEAT');
INSERT INTO `creature_text` VALUES (36853, 0, 0, 'You are fools to have come to this place. The icy winds of Northrend will consume your souls!', 14, 0, 0, 0, 0, 17007, 0, 37511, 0, 'Sindragosa - SAY_AGGRO');
INSERT INTO `creature_text` VALUES (36853, 1, 0, 'Suffer, mortals, as your pathetic magic betrays you!', 14, 0, 0, 0, 0, 17014, 0, 37138, 0, 'Sindragosa - SAY_UNCHAINED_MAGIC');
INSERT INTO `creature_text` VALUES (36853, 2, 0, '%s prepares to unleash a wave of blistering cold!', 41, 0, 0, 0, 0, 0, 0, 37108, 0, 'Sindragosa - EMOTE_WARN_BLISTERING_COLD');
INSERT INTO `creature_text` VALUES (36853, 3, 0, 'Can you feel the cold hand of death upon your heart?', 14, 0, 0, 0, 0, 17013, 0, 37137, 0, 'Sindragosa - SAY_BLISTERING_COLD');
INSERT INTO `creature_text` VALUES (36853, 4, 0, 'Aaagh! It burns! What sorcery is this!?', 14, 0, 0, 0, 0, 17015, 0, 38550, 0, 'Sindragosa - SAY_RESPITE_FOR_A_TORMENTED_SOUL');
INSERT INTO `creature_text` VALUES (36853, 5, 0, 'Your incursion ends here! None shall survive!', 14, 0, 0, 0, 0, 17012, 0, 37136, 0, 'Sindragosa - SAY_AIR_PHASE');
INSERT INTO `creature_text` VALUES (36853, 6, 0, 'Now, feel my master\'s limitless power and despair!', 14, 0, 0, 0, 0, 17016, 0, 37109, 0, 'Sindragosa - SAY_PHASE_2');
INSERT INTO `creature_text` VALUES (36853, 7, 0, '%s fires a frozen orb towards $n!', 41, 0, 0, 0, 0, 0, 0, 38672, 0, 'Sindragosa - EMOTE_WARN_FROZEN_ORB');
INSERT INTO `creature_text` VALUES (36853, 8, 0, 'Perish!', 14, 0, 0, 0, 0, 17008, 0, 37133, 0, 'Sindragosa - SAY_KILL 1');
INSERT INTO `creature_text` VALUES (36853, 8, 1, 'The flaw of mortality....', 14, 0, 0, 0, 0, 17009, 0, 37134, 0, 'Sindragosa - SAY_KILL 2');
INSERT INTO `creature_text` VALUES (36853, 9, 0, 'Enough! I tire of these games!', 14, 0, 0, 0, 0, 17011, 0, 37140, 0, 'Sindragosa - SAY_BERSERK');
INSERT INTO `creature_text` VALUES (36853, 10, 0, 'Free... at last....', 14, 0, 0, 0, 0, 17010, 0, 37139, 0, 'Sindragosa - SAY_DEATH');
INSERT INTO `creature_text` VALUES (36853, 11, 0, '%s goes into a berserker rage!', 41, 0, 100, 0, 0, 0, 0, 34057, 0, 'sindragosa EMOTE_BERSERK_RAID');
INSERT INTO `creature_text` VALUES (36855, 0, 0, 'You have found your way here, because you are among the few gifted with true vision in a world cursed with blindness.', 14, 0, 0, 0, 0, 17272, 0, 38139, 0, 'Lady Deathwhisper - SAY_INTRO_1');
INSERT INTO `creature_text` VALUES (36855, 1, 0, 'You can see through the fog that hangs over this world like a shroud, and grasp where true power lies.', 14, 0, 0, 0, 0, 17273, 0, 38140, 0, 'Lady Deathwhisper - SAY_INTRO_2');
INSERT INTO `creature_text` VALUES (36855, 2, 0, 'Fix your eyes upon your crude hands: the sinew, the soft meat, the dark blood coursing within.', 14, 0, 0, 0, 0, 16878, 0, 38134, 0, 'Lady Deathwhisper - SAY_INTRO_3');
INSERT INTO `creature_text` VALUES (36855, 3, 0, 'It is a weakness; a crippling flaw....  A joke played by the Creators upon their own creations.', 14, 0, 0, 0, 0, 17268, 0, 38135, 0, 'Lady Deathwhisper - SAY_INTRO_4');
INSERT INTO `creature_text` VALUES (36855, 4, 0, 'The sooner you come to accept your condition as a defect, the sooner you will find yourselves in a position to transcend it.', 14, 0, 0, 0, 0, 17269, 0, 38136, 0, 'Lady Deathwhisper - SAY_INTRO_5');
INSERT INTO `creature_text` VALUES (36855, 5, 0, 'Through our Master, all things are possible. His power is without limit, and his will unbending.', 14, 0, 0, 0, 0, 17270, 0, 38137, 0, 'Lady Deathwhisper - SAY_INTRO_6');
INSERT INTO `creature_text` VALUES (36855, 6, 0, 'Those who oppose him will be destroyed utterly, and those who serve -- who serve wholly, unquestioningly, with utter devotion of mind and soul -- elevated to heights beyond your ken.', 14, 0, 0, 0, 0, 17271, 0, 38138, 0, 'Lady Deathwhisper - SAY_INTRO_7');
INSERT INTO `creature_text` VALUES (36855, 7, 0, 'What is this disturbance? You dare trespass upon this hallowed ground? This shall be your final resting place!', 14, 0, 0, 0, 0, 16868, 0, 38122, 0, 'Lady Deathwhisper - SAY_AGGRO');
INSERT INTO `creature_text` VALUES (36855, 8, 0, 'Enough! I see I must take matters into my own hands!', 14, 0, 0, 0, 0, 16877, 0, 38126, 0, 'Lady Deathwhisper - SAY_PHASE_2');
INSERT INTO `creature_text` VALUES (36855, 9, 0, '%s\'s Mana Barrier shimmers and fades away!', 41, 0, 0, 0, 0, 0, 0, 38121, 0, 'Lady Deathwhisper - SAY_PHASE_2_EMOTE');
INSERT INTO `creature_text` VALUES (36855, 10, 0, 'You are weak, powerless to resist my will!', 14, 0, 0, 0, 0, 16876, 0, 38127, 0, 'Lady Deathwhisper - SAY_DOMINATE_MIND');
INSERT INTO `creature_text` VALUES (36855, 11, 0, 'Take this blessing and show these intruders a taste of the Master\'s power!', 14, 0, 0, 0, 0, 16873, 0, 38130, 0, 'Lady Deathwhisper - SAY_DARK_EMPOWERMENT');
INSERT INTO `creature_text` VALUES (36855, 12, 0, 'I release you from the curse of flesh!', 14, 0, 0, 0, 0, 16874, 0, 38132, 0, 'Lady Deathwhisper - SAY_DARK_TRANSFORMATION');
INSERT INTO `creature_text` VALUES (36855, 13, 0, 'Arise, and exult in your pure form!', 14, 0, 0, 0, 0, 16875, 0, 38133, 0, 'Lady Deathwhisper - SAY_ANIMATE_DEAD');
INSERT INTO `creature_text` VALUES (36855, 14, 0, 'Do you yet grasp the futility of your actions?', 14, 0, 0, 0, 0, 16869, 0, 38123, 0, 'Lady Deathwhisper - SAY_KILL_1');
INSERT INTO `creature_text` VALUES (36855, 14, 1, 'Embrace the darkness... darkness eternal.', 14, 0, 0, 0, 0, 16870, 0, 38124, 0, 'Lady Deathwhisper - SAY_KILL_2');
INSERT INTO `creature_text` VALUES (36855, 15, 0, 'This charade has gone on long enough!', 14, 0, 0, 0, 0, 16872, 0, 38128, 0, 'Lady Deathwhisper - SAY_BERSERK');
INSERT INTO `creature_text` VALUES (36855, 16, 0, 'All part of the Master\'s plan.... Your end is inevitable....', 14, 0, 0, 0, 0, 16871, 0, 38125, 0, 'Lady Deathwhisper - SAY_DEATH');
INSERT INTO `creature_text` VALUES (36856, 0, 0, 'You\'re in luck. I\'ve got just what you need in the load I\'m about to wash.', 12, 0, 100, 0, 0, 0, 0, 36761, 0, 'Shandy Glossgleam');
INSERT INTO `creature_text` VALUES (36856, 1, 0, 'See the piles of laundry and the bucket of water? I\'ll run out what I need next, and you put it in the tub. Ready?', 12, 0, 100, 0, 0, 0, 0, 36762, 0, 'Shandy Glossgleam');
INSERT INTO `creature_text` VALUES (36856, 2, 0, 'Quick, add some shirts to the laundry!', 12, 0, 100, 0, 0, 0, 0, 36788, 0, 'Shandy Glossgleam');
INSERT INTO `creature_text` VALUES (36856, 3, 0, 'Add the unmentionables... uh, I mean, the \'delicates\'!', 12, 0, 100, 25, 0, 0, 0, 36789, 0, 'Shandy Glossgleam');
INSERT INTO `creature_text` VALUES (36856, 4, 0, 'The tub needs more water!', 12, 0, 100, 25, 0, 0, 0, 36790, 0, 'Shandy Glossgleam');
INSERT INTO `creature_text` VALUES (36856, 5, 0, 'Toss some pants in to the tub!', 12, 0, 100, 25, 0, 0, 0, 36787, 0, 'Shandy Glossgleam');
INSERT INTO `creature_text` VALUES (36856, 6, 0, 'I should keep you around.', 12, 0, 17, 0, 0, 0, 0, 37989, 0, 'Shandy Glossgleam');
INSERT INTO `creature_text` VALUES (36856, 6, 1, 'Well done!', 12, 0, 17, 0, 0, 0, 0, 36764, 0, 'Shandy Glossgleam');
INSERT INTO `creature_text` VALUES (36856, 6, 2, 'That\'s how it\'s done!', 12, 0, 17, 0, 0, 0, 0, 37986, 0, 'Shandy Glossgleam');
INSERT INTO `creature_text` VALUES (36856, 6, 3, 'Clean and tidy!', 12, 0, 17, 0, 0, 0, 0, 37985, 0, 'Shandy Glossgleam');
INSERT INTO `creature_text` VALUES (36856, 6, 4, 'Nice. I don\'t want to know what that stain was.', 12, 0, 17, 0, 0, 0, 0, 37987, 0, 'Shandy Glossgleam');
INSERT INTO `creature_text` VALUES (36856, 6, 5, 'Aquanos can hardly keep up!', 12, 0, 15, 0, 0, 0, 0, 37988, 0, 'Shandy Glossgleam');
INSERT INTO `creature_text` VALUES (36856, 7, 0, 'Oh, no! That wasn\'t right. Now I\'ll have to go get more detergent so we can start over!', 12, 0, 100, 0, 0, 0, 0, 36763, 0, 'Shandy Glossgleam');
INSERT INTO `creature_text` VALUES (36856, 8, 0, 'Aquanos, stop sending the clothes so high! You didn\'t have to see the look on Aethas Sunreaver\'s face when he found his pants in the fountain!', 12, 0, 100, 0, 0, 0, 0, 36817, 0, 'Shandy Glossgleam');
INSERT INTO `creature_text` VALUES (36856, 9, 0, 'See how easy that was with everyone working together? Just take what you need from the clean laundry here, but don\'t forget to return it when you\'re finished.', 12, 0, 100, 0, 0, 0, 0, 36791, 0, 'Shandy Glossgleam');
INSERT INTO `creature_text` VALUES (36884, 0, 0, 'Haaadoouuuu....', 12, 0, 100, 0, 0, 0, 0, 43407, 0, 'on Aggro Text');
INSERT INTO `creature_text` VALUES (36884, 1, 0, '... KEN!!', 14, 0, 100, 0, 0, 0, 0, 43408, 0, 'on Aggro Text');
INSERT INTO `creature_text` VALUES (36913, 0, 0, 'Cry out in torment... Let your pain overcome your might...', 15, 0, 14.2857, 0, 0, 0, 0, 38355, 0, 'Eye of the Lichking');
INSERT INTO `creature_text` VALUES (36913, 0, 1, 'Release yourself from the chains of life...', 15, 0, 14.2857, 0, 0, 0, 0, 38352, 0, 'Eye of the Lichking');
INSERT INTO `creature_text` VALUES (36913, 0, 2, 'The Lich King is unstoppable...', 15, 0, 14.2857, 0, 0, 0, 0, 38350, 0, 'Eye of the Lichking');
INSERT INTO `creature_text` VALUES (36913, 0, 3, 'The Master\'s will remains inviolate...', 15, 0, 14.2857, 0, 0, 0, 0, 38347, 0, 'Eye of the Lichking');
INSERT INTO `creature_text` VALUES (36913, 0, 4, 'You cannot escape the Master\'s Wrath...', 15, 0, 14.2857, 0, 0, 0, 0, 38348, 0, 'Eye of the Lichking');
INSERT INTO `creature_text` VALUES (36913, 0, 5, 'Death consumes you...', 15, 0, 14.2857, 0, 0, 0, 0, 38353, 0, 'Eye of the Lichking');
INSERT INTO `creature_text` VALUES (36913, 0, 6, 'Give in... to the cold embrace of death...', 15, 0, 14.2857, 0, 0, 0, 0, 38351, 0, 'Eye of the Lichking');
INSERT INTO `creature_text` VALUES (36913, 0, 7, 'He watches your every move...', 15, 0, 100, 0, 0, 0, 0, 38354, 0, 'Eye of the Lich King - Whisper');
INSERT INTO `creature_text` VALUES (36936, 0, 0, '%s becomes enraged!', 16, 0, 100, 0, 0, 0, 0, 10677, 0, 'combat Enrage');
INSERT INTO `creature_text` VALUES (36939, 0, 0, 'Rise up, sons and daughters of the Horde! Today we battle a hated enemy of the Horde! LOK\'TAR OGAR!', 14, 0, 100, 0, 0, 17087, 0, 37689, 0, 'High Overlord Saurfang - SAY_SAURFANG_INTRO_1');
INSERT INTO `creature_text` VALUES (36939, 1, 0, 'Kor\'kron, take us out!', 14, 0, 100, 0, 0, 17088, 0, 37691, 0, 'High Overlord Saurfang - SAY_SAURFANG_INTRO_2');
INSERT INTO `creature_text` VALUES (36939, 2, 0, 'What is that? Something approaching in the distance.', 14, 0, 100, 0, 0, 17089, 0, 37692, 0, 'High Overlord Saurfang - SAY_SAURFANG_INTRO_3');
INSERT INTO `creature_text` VALUES (36939, 3, 0, 'ALLIANCE GUNSHIP! ALL HANDS ON DECK!', 14, 0, 100, 0, 0, 17090, 0, 37694, 0, 'High Overlord Saurfang - SAY_SAURFANG_INTRO_4');
INSERT INTO `creature_text` VALUES (36939, 4, 0, 'You answer to Saurfang now!', 14, 0, 100, 0, 0, 17091, 0, 37697, 0, 'High Overlord Saurfang - SAY_SAURFANG_INTRO_5');
INSERT INTO `creature_text` VALUES (36939, 5, 0, 'You will know our business soon. Kor\'kron, ANNIHILATE THEM!', 14, 0, 100, 0, 0, 17092, 0, 37698, 0, 'High Overlord Saurfang - SAY_SAURFANG_INTRO_6');
INSERT INTO `creature_text` VALUES (36939, 6, 0, 'This is not your battle, dwarf. Back down or we will be forced to destroy your ship.', 14, 0, 100, 0, 0, 17083, 0, 37685, 0, 'High Overlord Saurfang - SAY_SAURFANG_INTRO_A');
INSERT INTO `creature_text` VALUES (36939, 7, 0, 'You DARE board my ship? Your death will come swiftly.', 14, 0, 100, 0, 0, 17083, 0, 37712, 0, 'High Overlord Saurfang - SAY_SAURFANG_BOARD');
INSERT INTO `creature_text` VALUES (36939, 8, 0, 'Reavers, Sergeants, attack!', 14, 0, 100, 0, 0, 17081, 0, 37710, 0, 'High Overlord Saurfang - SAY_SAURFANG_ENTER_SKYBREAKER');
INSERT INTO `creature_text` VALUES (36939, 9, 0, 'Axethrowers, hurl faster!', 14, 0, 100, 0, 0, 17079, 0, 37708, 0, 'High Overlord Saurfang - SAY_SAURFANG_AXETHROWERS');
INSERT INTO `creature_text` VALUES (36939, 10, 0, 'Rocketeers, reload!', 14, 0, 100, 0, 0, 17080, 0, 37709, 0, 'High Overlord Saurfang - SAY_SAURFANG_ROCKETEERS');
INSERT INTO `creature_text` VALUES (36939, 11, 0, 'We\'re taking hull damage, get a battle-mage out here to shut down those cannons!', 14, 0, 100, 0, 0, 17082, 0, 37711, 0, 'High Overlord Saurfang - SAY_SAURFANG_MAGES');
INSERT INTO `creature_text` VALUES (36939, 12, 0, 'The Alliance falter. Onward to the Lich King!', 14, 0, 100, 0, 0, 17084, 0, 37713, 0, 'High Overlord Saurfang - SAY_SAURFANG_VICTORY');
INSERT INTO `creature_text` VALUES (36939, 13, 0, 'Damage control! Put those fires out. You haven\'t seen the last of the Horde!', 14, 0, 100, 0, 0, 17085, 0, 37714, 0, 'High Overlord Saurfang - SAY_SAURFANG_WIPE');
INSERT INTO `creature_text` VALUES (36948, 0, 0, 'Fire up the engines! We got a meetin\' with destiny, lads!', 14, 0, 100, 0, 0, 16962, 0, 37677, 0, 'Muradin Bronzebeard - SAY_MURADIN_INTRO_1');
INSERT INTO `creature_text` VALUES (36948, 1, 0, 'Hold on to yer hats!', 14, 0, 100, 0, 0, 16963, 0, 37678, 0, 'Muradin Bronzebeard - SAY_MURADIN_INTRO_2');
INSERT INTO `creature_text` VALUES (36948, 2, 0, 'What in the world is that? Grab me spyglass, crewman!', 14, 0, 100, 0, 0, 16964, 0, 37679, 0, 'Muradin Bronzebeard - SAY_MURADIN_INTRO_3');
INSERT INTO `creature_text` VALUES (36948, 3, 0, 'By me own beard! HORDE SAILIN\' IN FAST \'N HOT!', 14, 0, 100, 0, 0, 16965, 0, 37680, 0, 'Muradin Bronzebeard - SAY_MURADIN_INTRO_4');
INSERT INTO `creature_text` VALUES (36948, 4, 0, 'EVASIVE ACTION! MAN THE GUNS!', 14, 0, 100, 0, 0, 16966, 0, 37681, 0, 'Muradin Bronzebeard - SAY_MURADIN_INTRO_5');
INSERT INTO `creature_text` VALUES (36948, 5, 0, 'Cowardly dogs! Ye blindsided us!', 14, 0, 100, 0, 0, 16967, 0, 37682, 0, 'Muradin Bronzebeard - SAY_MURADIN_INTRO_6');
INSERT INTO `creature_text` VALUES (36948, 6, 0, 'Not me battle? I dunnae who ye think ye are, mister, but I got a score to settle with Arthas and yer not gettin\' in me way! FIRE ALL GUNS! FIRE! FIRE!', 14, 0, 100, 0, 0, 16968, 0, 37683, 0, 'Muradin Bronzebeard - SAY_MURADIN_INTRO_7');
INSERT INTO `creature_text` VALUES (36948, 7, 0, 'Move yer jalopy or we\'ll blow it out of the sky, orc! The Horde\'s got no business here.', 14, 0, 100, 0, 0, 16969, 0, 37699, 0, 'Muradin Bronzebeard - SAY_MURADIN_INTRO_H');
INSERT INTO `creature_text` VALUES (36948, 8, 0, 'What\'s this then?! Ye won\'t be takin\' this son o\' Ironforge\'s vessel without a fight!', 14, 0, 100, 0, 0, 16958, 0, 37704, 0, 'Muradin Bronzebeard - SAY_MURADIN_BOARD');
INSERT INTO `creature_text` VALUES (36948, 9, 0, 'Marines, Sergeants, attack!', 14, 0, 100, 0, 0, 16956, 0, 37702, 0, 'Muradin Bronzebeard - SAY_MURADIN_ENTER_ORGRIMMS_HAMMER');
INSERT INTO `creature_text` VALUES (36948, 10, 0, 'Riflemen, shoot faster!', 14, 0, 100, 0, 0, 16954, 0, 37700, 0, 'Muradin Bronzebeard - SAY_MURADIN_RIFLEMAN');
INSERT INTO `creature_text` VALUES (36948, 11, 0, 'Mortar team, reload!', 14, 0, 100, 0, 0, 16955, 0, 37701, 0, 'Muradin Bronzebeard - SAY_MURADIN_MORTAR');
INSERT INTO `creature_text` VALUES (36948, 12, 0, 'We\'re taking hull damage, get a sorcerer out here to shut down those cannons!', 14, 0, 100, 0, 0, 16957, 0, 37703, 0, 'Muradin Bronzebeard - SAY_MURADIN_SORCERERS');
INSERT INTO `creature_text` VALUES (36948, 13, 0, 'Don\'t say I didn\'t warn ya, scoundrels! Onward, brothers and sisters!', 14, 0, 100, 0, 0, 16959, 0, 37705, 0, 'Muradin Bronzebeard - SAY_MURADIN_VICTORY');
INSERT INTO `creature_text` VALUES (36948, 14, 0, 'Captain Bartlett, get us out of here! We\'re taken too much damage to stay afloat!', 14, 0, 100, 0, 0, 16960, 0, 37706, 0, 'Muradin Bronzebeard - SAY_MURADIN_WIPE');
INSERT INTO `creature_text` VALUES (36954, 0, 0, 'Your allies have arrived, Jaina, just as you promised. You will all become powerful agents of the Scourge..', 14, 0, 100, 0, 0, 17212, 0, 37172, 0, 'Lich King SAY_LICH_KING_AGGRO_A');
INSERT INTO `creature_text` VALUES (36954, 1, 0, 'I will not make the same mistake again, Sylvanas. This time there will be no escape. You will all serve me in death!', 14, 0, 100, 0, 0, 17213, 0, 37173, 0, 'Lich King SAY_LICH_KING_AGGRO_H');
INSERT INTO `creature_text` VALUES (36954, 2, 0, 'There is no escape!', 14, 0, 100, 0, 0, 17217, 0, 37177, 0, 'Lich King SAY_LICH_KING_WALL_01');
INSERT INTO `creature_text` VALUES (36954, 3, 0, 'Succumb to the chill of the grave.', 14, 0, 100, 0, 0, 17218, 0, 37175, 0, 'Lich King SAY_LICH_KING_WALL_02');
INSERT INTO `creature_text` VALUES (36954, 4, 0, 'Another dead end.', 14, 0, 100, 0, 0, 17219, 0, 37176, 0, 'Lich King SAY_LICH_KING_WALL_03');
INSERT INTO `creature_text` VALUES (36954, 5, 0, 'How long can you fight it?', 14, 0, 100, 0, 0, 17220, 0, 38668, 0, 'Lich King SAY_LICH_KING_WALL_04');
INSERT INTO `creature_text` VALUES (36954, 6, 0, 'Arise minions.  Do not let them pass.', 14, 0, 100, 0, 0, 17216, 0, 38669, 0, 'Lich King SAY_LICH_KING_GHOUL');
INSERT INTO `creature_text` VALUES (36954, 7, 0, 'Minions, sieze them.  Bring their corpses back to me.', 14, 0, 100, 0, 0, 17222, 0, 38670, 0, 'Lich King SAY_LICH_KING_ABON');
INSERT INTO `creature_text` VALUES (36954, 8, 0, 'Death\'s cold embrace awaits.', 14, 0, 100, 0, 0, 17221, 0, 37174, 0, 'Lich King SAY_LICH_KING_WINTER');
INSERT INTO `creature_text` VALUES (36954, 9, 0, 'Nowhere to run! You\'re mine now...', 14, 0, 100, 0, 0, 17223, 0, 36994, 0, 'Lich King SAY_LICH_KING_END_DUN');
INSERT INTO `creature_text` VALUES (36955, 0, 0, 'He is too powerful. We must leave this place at once! My magic can hold him in place for only a short time. Come quickly, heroes!', 14, 0, 100, 1, 0, 16644, 0, 36821, 0, 'Jaina SAY_JAINA_AGGRO');
INSERT INTO `creature_text` VALUES (36955, 1, 0, 'I will destroy this barrier. You must hold the undead back!', 14, 0, 100, 0, 0, 16607, 0, 38536, 0, 'Jaina SAY_JAINA_WALL_01');
INSERT INTO `creature_text` VALUES (36955, 2, 0, 'Another ice wall! Keep the undead from interrupting my incantations so that I may bring this wall down.', 14, 0, 100, 0, 0, 16608, 0, 38537, 0, 'Jaina SAY_JAINA_WALL_02');
INSERT INTO `creature_text` VALUES (36955, 3, 0, 'He is toying with us! I will show him what happens to ice when it meets fire!', 14, 0, 100, 0, 0, 16609, 0, 0, 0, 'Jaina SAY_JAINA_WALL_03');
INSERT INTO `creature_text` VALUES (36955, 4, 0, 'Your barriers can\'t hold us back much longer, monster! I will shatter them all!', 14, 0, 100, 0, 0, 16610, 0, 38539, 0, 'Jaina SAY_JAINA_WALL_04');
INSERT INTO `creature_text` VALUES (36955, 5, 0, 'There\'s an opening up ahead. GO NOW!', 14, 0, 100, 1, 0, 16645, 0, 38551, 0, 'Jaina SAY_JAINA_ESCAPE_01');
INSERT INTO `creature_text` VALUES (36955, 6, 0, 'We\'re almost there... Don\'t give up!', 14, 0, 100, 0, 0, 16646, 0, 38538, 0, 'Jaina SAY_JAINA_ESCAPE_02');
INSERT INTO `creature_text` VALUES (36955, 7, 0, 'It... It\'s a dead end. We have no choice but to fight. Steel yourselves, heroes, for this is our last stand!', 14, 0, 100, 1, 0, 16647, 0, 36992, 0, 'Jaina SAY_JAINA_TRAP');
INSERT INTO `creature_text` VALUES (36955, 8, 0, 'Forgive me, heroes. I should have listened to Uther. I... I just had to see for myself. To look into his eyes one last time. I am sorry.', 14, 0, 100, 1, 0, 16648, 0, 36990, 0, 'Jaina SAY_JAINA_FINAL_2');
INSERT INTO `creature_text` VALUES (36955, 9, 0, 'We now know what must be done. I will deliver this news to King Varian and Highlord Fordring.', 14, 0, 100, 1, 0, 16649, 0, 36991, 0, 'Jaina SAY_JAINA_FINAL_3');
INSERT INTO `creature_text` VALUES (36990, 0, 0, 'Why should the Banshee Queen spare your miserable life?', 14, 0, 100, 0, 0, 17033, 0, 36845, 0, 'Sylvanas SAY_SYLVANAS_OUTRO_2');
INSERT INTO `creature_text` VALUES (36990, 1, 0, 'Frostmourne? The Lich King is never without his blade. If you are lying to me...', 14, 0, 100, 0, 0, 17034, 0, 36846, 0, 'Sylvanas SAY_SYLVANAS_OUTRO_4');
INSERT INTO `creature_text` VALUES (36990, 2, 0, 'A fitting end for a traitor. Come, we must free the slaves and see what is within the Lich King\'s chamber for ourselves.', 14, 0, 100, 0, 0, 17035, 0, 36847, 0, 'Sylvanas SAY_SYLVANAS_OUTRO_10');
INSERT INTO `creature_text` VALUES (36990, 3, 0, 'Take cover behind me! Quickly!', 14, 0, 100, 0, 0, 17037, 0, 37540, 0, 'Sylvanas SAY_SYLVANAS_OUTRO_3');
INSERT INTO `creature_text` VALUES (36990, 4, 0, 'I thought he\'d never shut up. At last, Sindragosa silenced that long-winded fool. To the Halls of Reflection, champions! Our objective is near... I can sense it.', 12, 0, 100, 0, 0, 17036, 0, 37541, 0, 'Sylvanas SAY_SYLVANAS_OUTRO_4');
INSERT INTO `creature_text` VALUES (36993, 0, 0, 'I\'m not so naive as to believe your appeal for clemency, but I will listen.', 14, 0, 100, 0, 0, 16611, 0, 36854, 0, 'Jaina SAY_JAINA_OUTRO_2');
INSERT INTO `creature_text` VALUES (36993, 1, 0, 'Frostmourne lies unguarded? Impossible!', 14, 0, 100, 0, 0, 16612, 0, 36856, 0, 'Jaina SAY_JAINA_OUTRO_4');
INSERT INTO `creature_text` VALUES (36993, 2, 0, 'What a cruel end. Come, heroes. We must see if the gnome\'s story is true. If we can separate Arthas from Frostmourne, we might have a chance at stopping him.', 14, 0, 100, 0, 0, 16613, 0, 36855, 0, 'Jaina SAY_JAINA_OUTRO_10');
INSERT INTO `creature_text` VALUES (36993, 3, 0, 'Heroes, to me!', 14, 0, 100, 0, 0, 16614, 0, 37479, 0, 'Jaina SAY_JAYNA_OUTRO_3');
INSERT INTO `creature_text` VALUES (36993, 4, 0, 'The Frost Queen is gone. We must keep moving - our objective is near.', 12, 0, 100, 0, 0, 16615, 0, 37478, 0, 'Jaina SAY_JAYNA_OUTRO_4');
INSERT INTO `creature_text` VALUES (36993, 5, 0, 'I... I could not save them... Damn you, Arthas! DAMN YOU!', 12, 0, 100, 0, 0, 16616, 0, 37480, 0, 'Jaina SAY_JAYNA_OUTRO_5');
INSERT INTO `creature_text` VALUES (37007, 0, 0, 'The master\'s sanctum has been disturbed!', 14, 0, 0, 0, 0, 16865, 0, 37443, 0, 'Deathbound Ward - SAY_TRAP_ACTIVATE');
INSERT INTO `creature_text` VALUES (37007, 0, 1, 'I... awaken!', 14, 0, 0, 0, 0, 16866, 0, 38517, 0, 'Deathbound Ward - SAY_TRAP_ACTIVATE');
INSERT INTO `creature_text` VALUES (37007, 0, 2, 'Who... goes there...?', 14, 0, 0, 0, 0, 16867, 0, 38518, 0, 'Deathbound Ward - SAY_TRAP_ACTIVATE');
INSERT INTO `creature_text` VALUES (37065, 0, 0, 'Just in time, $n.  Here come the Forsaken.', 12, 0, 100, 0, 0, 19605, 0, 37410, 0, 'Prince Liam Greymane to Player');
INSERT INTO `creature_text` VALUES (37065, 1, 0, 'You will come no further, Forsaken scum!  By the war banner of the ancient Greymane clan, I expel you from our ancestral lands!', 14, 0, 100, 0, 0, 19606, 0, 37411, 0, 'Prince Liam Greymane to Player');
INSERT INTO `creature_text` VALUES (37073, 0, 0, 'You break our cave, I break your skull!', 12, 0, 100, 0, 0, 0, 0, 36904, 0, 'Rockjaw Goon');
INSERT INTO `creature_text` VALUES (37073, 0, 1, 'Why you break our home?!', 12, 0, 100, 0, 0, 0, 0, 36903, 0, 'Rockjaw Goon');
INSERT INTO `creature_text` VALUES (37073, 0, 2, 'Trogg cave all gone!  You pay for this!!', 12, 0, 100, 0, 0, 0, 0, 36905, 0, 'Rockjaw Goon');
INSERT INTO `creature_text` VALUES (37080, 0, 0, 'Thanks!  I can make it from here.', 12, 0, 100, 1, 0, 0, 0, 36982, 0, 'Wounded Coldridge Mountaineer');
INSERT INTO `creature_text` VALUES (37080, 0, 1, 'Yer a lifesaver!  For Ironforge!', 12, 0, 100, 1, 0, 0, 0, 36983, 0, 'Wounded Coldridge Mountaineer');
INSERT INTO `creature_text` VALUES (37080, 0, 2, 'Ahh, much better.  I owe ya one.', 12, 0, 100, 1, 0, 0, 0, 36984, 0, 'Wounded Coldridge Mountaineer');
INSERT INTO `creature_text` VALUES (37081, 0, 0, 'Get back, ye filthy beast!', 12, 0, 100, 0, 0, 0, 0, 37007, 0, 'Joren Ironstock');
INSERT INTO `creature_text` VALUES (37081, 0, 1, 'Eat dwarven lead!', 12, 0, 100, 0, 0, 0, 0, 37008, 0, 'Joren Ironstock');
INSERT INTO `creature_text` VALUES (37081, 0, 2, 'For Ironforge!', 12, 0, 100, 0, 0, 0, 0, 37009, 0, 'Joren Ironstock');
INSERT INTO `creature_text` VALUES (37087, 0, 0, 'Get ta work, boys!', 12, 7, 100, 22, 0, 0, 0, 36970, 0, 'Jona Ironstock');
INSERT INTO `creature_text` VALUES (37087, 0, 1, 'Don\'t fret, we\'ll be outta here soon.', 12, 7, 100, 22, 0, 0, 0, 36971, 0, 'Jona Ironstock');
INSERT INTO `creature_text` VALUES (37087, 0, 2, 'Let\'s keep this fire going, dwarves!', 12, 7, 100, 22, 0, 0, 0, 36972, 0, 'Jona Ironstock');
INSERT INTO `creature_text` VALUES (37087, 0, 3, 'What\'s Joren doin\' out there, anyhow?', 12, 7, 100, 6, 0, 0, 0, 36973, 0, 'Jona Ironstock');
INSERT INTO `creature_text` VALUES (37106, 0, 0, 'You gotta hurry,kid. We gotta get you into the game. But first, we gotta get the parts for that shredder!', 12, 0, 100, 1, 0, 0, 0, 49025, 0, 'say Text');
INSERT INTO `creature_text` VALUES (37106, 1, 0, 'Get into that shredder and win the game. The Bilgewater Cartel\'s counting on you!', 12, 0, 100, 1, 0, 0, 0, 49026, 0, 'say Text');
INSERT INTO `creature_text` VALUES (37106, 2, 0, 'You got the juice,kid. That\'s for sure! Too bad that dragon had to come and ruin everything. No matter. You made us proud. Now get back to headquarters.', 12, 0, 100, 1, 0, 0, 0, 49027, 0, 'say Text');
INSERT INTO `creature_text` VALUES (37108, 0, 0, 'Da spirits be angry with us.', 12, 0, 100, 396, 0, 0, 0, 36962, 0, 'Soothsayer Shi\'kala');
INSERT INTO `creature_text` VALUES (37108, 1, 0, 'I don\' know why the spirits be rejectin\' us so.', 12, 0, 100, 396, 0, 0, 0, 36958, 0, 'Soothsayer Shi\'kala');
INSERT INTO `creature_text` VALUES (37108, 2, 0, 'Don\' worry, child.  Grik\'nir gonna help us get through this.', 12, 0, 100, 396, 0, 0, 0, 36959, 0, 'Soothsayer Shi\'kala');
INSERT INTO `creature_text` VALUES (37112, 0, 0, 'I am from the elemental plane!', 14, 12, 100, 0, 0, 0, 0, 36949, 0, 'Wayward Fire Elemental');
INSERT INTO `creature_text` VALUES (37112, 1, 0, 'I AM FROM THE ELEMENTAL PLANE!', 12, 12, 100, 0, 0, 0, 0, 37019, 0, 'Wayward Fire Elemental');
INSERT INTO `creature_text` VALUES (37119, 0, 0, 'This is our final stand. What happens here will echo through the ages. Regardless of outcome, they will know that we fought with honor. That we fought for the freedom and safety of our people.$B$BRemember, hero, fear is your greatest enemy in these befouled halls. Steel your heart and your soul will shine brighter than a thousand suns. The enemy will falter at the sight of you. They will fall as the light of righteousness envelops them!', 14, 0, 0, 22, 0, 16653, 0, 36923, 0, 'Highlord Tirion Fordring - SAY_TIRION_INTRO_1');
INSERT INTO `creature_text` VALUES (37119, 1, 0, 'Remember, heroes, fear is your greatest enemy in these befouled halls. Steel your heart and your soul will shine brighter than a thousand suns. The enemy will falter at the sight of you. They will fall as the light of righteousness envelops them!', 14, 0, 0, 22, 0, 16654, 0, 37028, 0, 'Highlord Tirion Fordring - SAY_TIRION_INTRO_2');
INSERT INTO `creature_text` VALUES (37119, 2, 0, 'Our march upon Icecrown Citadel begins now!', 14, 0, 0, 22, 0, 16655, 0, 37029, 0, 'Highlord Tirion Fordring - SAY_TIRION_INTRO_3');
INSERT INTO `creature_text` VALUES (37119, 3, 0, 'ARTHAS! I swore that I would see you dead and the Scourge dismantled! I\'m going to finish what I started at Light\'s Hope!', 14, 0, 0, 22, 0, 16656, 0, 37031, 0, 'Highlord Tirion Fordring - SAY_TIRION_INTRO_4');
INSERT INTO `creature_text` VALUES (37119, 4, 0, 'The power of the Light knows no bounds, Saurfang. His soul is under great strain, but he lives - for now.', 12, 0, 0, 1, 0, 16658, 0, 37056, 0, 'Highlord Tirion Fordring - SAY_TIRION_INTRO_5');
INSERT INTO `creature_text` VALUES (37119, 5, 0, 'By the Light, it must be so!', 12, 0, 0, 5, 0, 16657, 0, 37038, 0, 'Highlord Tirion Fordring - SAY_TIRION_INTRO_A_5');
INSERT INTO `creature_text` VALUES (37120, 0, 0, 'So, it has begun....', 12, 0, 100, 396, 0, 16834, 0, 37248, 0, 'Highlord Darion Mograine to Player');
INSERT INTO `creature_text` VALUES (37120, 1, 0, 'Behold! The binding of sacred and corrupt; the forging of Shadow\'s Edge, a weapon of untold potential!', 12, 0, 100, 0, 0, 16835, 0, 37249, 0, 'Highlord Darion Mograine to Player');
INSERT INTO `creature_text` VALUES (37120, 2, 0, 'Bend it to your will, and you shall wield unspeakable power!', 12, 0, 100, 396, 0, 16836, 0, 37250, 0, 'Highlord Darion Mograine to Player');
INSERT INTO `creature_text` VALUES (37120, 3, 0, 'Fail, and your soul shall forever be its slave.', 12, 0, 100, 396, 0, 16837, 0, 37251, 0, 'Highlord Darion Mograine to Player');
INSERT INTO `creature_text` VALUES (37120, 4, 0, 'Father...', 12, 0, 100, 396, 0, 17411, 0, 38143, 0, 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES (37120, 5, 0, 'Father. I feared for your... your sanity.', 12, 0, 100, 396, 0, 17412, 0, 38150, 0, 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES (37120, 6, 0, 'Father... For you, I would give my life a thousand times.', 12, 0, 100, 396, 0, 17413, 0, 38151, 0, 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES (37122, 0, 0, 'Never... could reach... the top shelf...', 14, 0, 0, 0, 0, 16586, 0, 37074, 0, 'Captain Arnath - SAY_ARNATH_DEATH');
INSERT INTO `creature_text` VALUES (37122, 1, 0, 'You miserable fools never did manage to select a decent bat wing.', 14, 0, 0, 0, 0, 16587, 0, 37076, 0, 'Captain Arnath - SAY_ARNATH_RESURRECTED');
INSERT INTO `creature_text` VALUES (37122, 2, 0, 'THAT was for bringing me spoiled spider ichor!\n', 14, 0, 0, 0, 0, 16588, 0, 37075, 0, 'Captain Arnath - SAY_ARNATH_KILL');
INSERT INTO `creature_text` VALUES (37122, 3, 0, 'Don\'t... let Finklestein use me... for his potions...', 14, 0, 0, 0, 0, 16589, 0, 37077, 0, 'Captain Arnath - SAY_ARNATH_SECOND_DEATH');
INSERT INTO `creature_text` VALUES (37122, 4, 0, 'The loss of our comrades was unpreventable. They lived and died in the service of the Argent Crusade.', 14, 0, 0, 0, 0, 16590, 0, 37534, 0, 'Captain Arnath - SAY_ARNATH_SURVIVE_TALK');
INSERT INTO `creature_text` VALUES (37122, 5, 0, 'Even dying here beats spending another day collecting reagents for that madman, Finklestein. ', 14, 0, 0, 0, 0, 16585, 0, 36948, 0, 'Captain Arnath - SAY_ARNATH_INTRO_2');
INSERT INTO `creature_text` VALUES (37123, 0, 0, 'No amount of healing can save me now. Fight on, brothers...', 14, 0, 0, 0, 0, 16810, 0, 37665, 0, 'Captain Brandon - SAY_BRANDON_DEATH');
INSERT INTO `creature_text` VALUES (37123, 1, 0, 'What? This strength...? All of the pain is gone! You... must join me in the eternal embrace of death!\n', 14, 0, 0, 0, 0, 16811, 0, 37664, 0, 'Captain Brandon - SAY_BRANDON_RESURRECTED');
INSERT INTO `creature_text` VALUES (37123, 2, 0, 'It doesn\'t hurt anymore, does it?\n', 14, 0, 0, 0, 0, 16812, 0, 37658, 0, 'Captain Brandon - SAY_BRANDON_KILL');
INSERT INTO `creature_text` VALUES (37123, 3, 0, 'I\'m sorry...', 14, 0, 0, 0, 0, 16813, 0, 37659, 0, 'Captain Brandon - SAY_BRANDON_SECOND_DEATH');
INSERT INTO `creature_text` VALUES (37123, 4, 0, 'You have done much in this war against the Scourge. May the light embrace you. ', 14, 0, 0, 0, 0, 16815, 0, 37533, 0, 'Captain Brandon - SAY_BRANDON_SURVIVE_TALK');
INSERT INTO `creature_text` VALUES (37124, 0, 0, 'Please... burn my remains. Let me live warm in the afterlife...', 14, 0, 0, 0, 0, 16844, 0, 37666, 0, 'Captain Grondel - SAY_GRONDEL_DEATH');
INSERT INTO `creature_text` VALUES (37124, 1, 0, 'No! Why was I denied a death by flame? You must all BURN!  ', 14, 0, 0, 0, 0, 16845, 0, 37667, 0, 'Captain Grondel - SAY_GRONDEL_RESURRECTED');
INSERT INTO `creature_text` VALUES (37124, 2, 0, 'Can you feel the burn?', 14, 0, 0, 0, 0, 16846, 0, 37660, 0, 'Captain Grondel - SAY_GRONDEL_KILL');
INSERT INTO `creature_text` VALUES (37124, 3, 0, 'What... have I done? No!', 14, 0, 0, 0, 0, 16847, 0, 37661, 0, 'Captain Grondel - SAY_GRONDEL_SECOND_DEATH');
INSERT INTO `creature_text` VALUES (37124, 4, 0, 'What can possibly redeem this unholy place? Thank you.', 14, 0, 0, 0, 0, 16849, 0, 37535, 0, 'Captain Grondel - SAY_GRONDEL_SURVIVE_TALK');
INSERT INTO `creature_text` VALUES (37125, 0, 0, 'It was... a worthy afterlife.', 14, 0, 0, 0, 0, 16998, 0, 37668, 0, 'Captain Rupert - SAY_RUPERT_DEATH');
INSERT INTO `creature_text` VALUES (37125, 1, 0, 'There is no escaping the Lich King\'s will. Prepare for an explosive encounter!', 14, 0, 0, 0, 0, 16999, 0, 37669, 0, 'Captain Rupert - SAY_RUPERT_RESURRECTED');
INSERT INTO `creature_text` VALUES (37125, 2, 0, 'So that\'s what happens what you stand too close to a bomb!', 14, 0, 0, 0, 0, 17000, 0, 37662, 0, 'Captain Rupert - SAY_RUPERT_KILL');
INSERT INTO `creature_text` VALUES (37125, 3, 0, 'What an... explosive ending!', 14, 0, 0, 0, 0, 17001, 0, 37663, 0, 'Captain Rupert - SAY_RUPERT_SECOND_DEATH');
INSERT INTO `creature_text` VALUES (37125, 4, 0, 'Beware the dangers that lie ahead... and do try to remain in one piece. ', 14, 0, 0, 0, 0, 17003, 0, 37536, 0, 'Captain Rupert - SAY_RUPERT_SURVIVE_TALK');
INSERT INTO `creature_text` VALUES (37126, 0, 0, 'You may have once fought beside me, Crok, but now you are nothing more than a traitor. Come, your second death approaches!', 14, 0, 0, 0, 0, 17017, 0, 37024, 0, 'Sister Svalna - SAY_EVENT_START');
INSERT INTO `creature_text` VALUES (37126, 1, 0, 'Miserable creatures! Die! ', 14, 0, 0, 0, 0, 17018, 0, 37065, 0, 'Sister Svalna - SAY_KILL_CAPTAIN');
INSERT INTO `creature_text` VALUES (37126, 2, 0, 'Foolish Crok. You brought my reinforcements with you. Arise, Argent Champions, and serve the Lich King in death!', 14, 0, 0, 0, 0, 17019, 0, 37020, 0, 'Sister Svanlna - SAY_RESURRECT_CAPTAINS');
INSERT INTO `creature_text` VALUES (37126, 3, 0, 'Come, Scourgebane. I\'ll show the master which of us is truly worthy of the title of \"Champion\"!', 14, 0, 0, 0, 0, 17020, 0, 37653, 0, 'Sister Svalna - SAY_AGGRO');
INSERT INTO `creature_text` VALUES (37126, 4, 0, 'What a pitiful choice of an ally, Crok!', 14, 0, 0, 0, 0, 17021, 0, 37654, 0, 'Sister Svalna - SAY_KILL');
INSERT INTO `creature_text` VALUES (37126, 5, 0, 'They died so easily. No matter.', 14, 0, 0, 0, 0, 17022, 0, 37163, 0, 'Sister Svalna - SAY_CAPTAIN_DEATH');
INSERT INTO `creature_text` VALUES (37126, 6, 0, 'Perhaps... you were right, Crok. ', 14, 0, 0, 0, 0, 17023, 0, 37135, 0, 'Sister Svalna - SAY_DEATH');
INSERT INTO `creature_text` VALUES (37126, 7, 0, '%s has impaled $n!', 41, 0, 0, 0, 0, 0, 0, 37977, 0, 'Sister Svalna - EMOTE_SVALNA_IMPALE');
INSERT INTO `creature_text` VALUES (37126, 8, 0, '%s\'s Aether Shield has been shattered by $n!', 41, 0, 0, 0, 0, 0, 0, 37978, 0, 'Sister Svalna - EMOTE_SVALNA_BROKEN_SHIELD');
INSERT INTO `creature_text` VALUES (37129, 0, 0, 'Ready your arms, my Argent Brothers. The Vrykul will protect the Frost Queen with their lives.', 14, 0, 0, 0, 0, 16819, 0, 36945, 0, 'Crok Scourgebane - SAY_CROK_INTRO_1');
INSERT INTO `creature_text` VALUES (37129, 1, 0, 'Enough idle banter! Our champions have arrived - support them as we push our way through the hall!', 14, 0, 0, 0, 0, 16820, 0, 36946, 0, 'Crok Scourgebane - SAY_CROK_INTRO_3');
INSERT INTO `creature_text` VALUES (37129, 2, 0, 'Draw them back to us, and we\'ll assist you. ', 14, 0, 0, 0, 0, 16821, 0, 37013, 0, 'Crok Scourgebane - SAY_CROK_COMBAT_WP_0');
INSERT INTO `creature_text` VALUES (37129, 3, 0, 'Quickly, push on!', 14, 0, 0, 0, 0, 16823, 0, 37015, 0, 'Crok Scourgebane - SAY_CROK_COMBAT_WP_1');
INSERT INTO `creature_text` VALUES (37129, 4, 0, 'Her reinforcements will arrive shortly, we must bring her down quickly!', 14, 0, 0, 0, 0, 16824, 0, 37016, 0, 'Crok Scourgebane - SAY_CROK_FINAL_WP');
INSERT INTO `creature_text` VALUES (37129, 5, 0, 'I\'ll draw her attacks. Return our brothers to their graves, then help me bring her down!', 14, 0, 0, 15, 0, 16825, 0, 37017, 0, 'Crok Scourgebane - SAY_CROK_COMBAT_SVALNA');
INSERT INTO `creature_text` VALUES (37129, 6, 0, 'Augh! I must rest for a moment. \n', 14, 0, 0, 0, 0, 16826, 0, 37164, 0, 'Crok Scourgebane - SAY_CROK_WEAKENING_GAUNTLET');
INSERT INTO `creature_text` VALUES (37129, 7, 0, 'Champions! I cannot hold her back any longer.', 14, 0, 0, 0, 0, 16827, 0, 37165, 0, 'Crok Scourgebane - SAY_CROK_WEAKENING_SVALNA');
INSERT INTO `creature_text` VALUES (37129, 8, 0, 'Vengeance alone... was not enough!', 14, 0, 0, 0, 0, 16828, 0, 0, 0, 'Crok Scourgebane - SAY_CROK_DEATH');
INSERT INTO `creature_text` VALUES (37157, 0, 0, 'Guards!I expect this $r to be clapped into chains!', 12, 0, 100, 0, 0, 0, 0, 36947, 0, 'on Aggro Text');
INSERT INTO `creature_text` VALUES (37158, 0, 0, '%s prepares to attack!', 41, 0, 100, 0, 0, 0, 0, 37211, 0, 'Quel\'Delar');
INSERT INTO `creature_text` VALUES (37171, 0, 0, '%s becomes enraged!', 16, 0, 100, 0, 0, 0, 0, 10677, 0, 'combat Enrage');
INSERT INTO `creature_text` VALUES (37172, 0, 0, 'You know, that does smell pretty... Hey! Stop spraying that thing at me!', 12, 0, 100, 1, 0, 0, 0, 38296, 0, 'Detective Snap Snagglebolt');
INSERT INTO `creature_text` VALUES (37172, 1, 0, 'Don\'t tempt me! Do you even know what\'s in those things?', 12, 0, 100, 1, 0, 0, 0, 38382, 0, 'Detective Snap Snagglebolt');
INSERT INTO `creature_text` VALUES (37173, 0, 0, 'What we gon\' do now, you ask?  We wait.', 12, 0, 100, 396, 0, 0, 0, 36957, 0, 'Soothsayer Rikkari');
INSERT INTO `creature_text` VALUES (37173, 1, 0, 'Grik\'nir says he gon\' talk to the elemental, get it to fight on our side.', 12, 0, 100, 396, 0, 0, 0, 36960, 0, 'Soothsayer Rikkari');
INSERT INTO `creature_text` VALUES (37173, 2, 0, 'Soon enough we take over dis valley.  Soon enough.', 12, 0, 100, 396, 0, 0, 0, 36961, 0, 'Soothsayer Rikkari');
INSERT INTO `creature_text` VALUES (37174, 0, 0, 'Our land be a land of ice an\' snow.', 12, 0, 100, 396, 0, 0, 0, 36963, 0, 'Soothsayer Mirim\'koa');
INSERT INTO `creature_text` VALUES (37174, 1, 0, 'But beneath the earth, child, there always be fire.', 12, 0, 100, 396, 0, 0, 0, 36964, 0, 'Soothsayer Mirim\'koa');
INSERT INTO `creature_text` VALUES (37174, 2, 0, 'De spirit come from deep down to talk with Grik\'nir.', 12, 0, 100, 396, 0, 0, 0, 36965, 0, 'Soothsayer Mirim\'koa');
INSERT INTO `creature_text` VALUES (37181, 0, 0, 'You now stand upon the hallowed ground of the Scourge. The Light won\'t protect you here, paladin. Nothing will protect you...', 14, 0, 0, 0, 0, 17230, 0, 38054, 0, 'The Lich King - SAY_LK_INTRO_1');
INSERT INTO `creature_text` VALUES (37181, 1, 0, 'You could have been my greatest champion, Fordring: A force of darkness that would wash over this world and deliver it into a new age of strife.', 14, 0, 0, 0, 0, 17231, 0, 38055, 0, 'The Lich King - SAY_LK_INTRO_2');
INSERT INTO `creature_text` VALUES (37181, 2, 0, 'But that honor is no longer yours. Soon, I will have a new champion. ', 14, 0, 0, 0, 0, 17232, 0, 38056, 0, 'The Lich King - SAY_LK_INTRO_3');
INSERT INTO `creature_text` VALUES (37181, 3, 0, 'The breaking of this one has been taxing. The atrocities I have committed upon his soul. He has resisted for so long, but he will bow down before his king soon.', 14, 0, 0, 0, 0, 17233, 0, 37034, 0, 'The Lich King - SAY_LK_INTRO_4');
INSERT INTO `creature_text` VALUES (37181, 4, 0, 'In the end, you will all serve me.', 14, 0, 0, 0, 0, 17234, 0, 38059, 0, 'The Lich King - SAY_LK_INTRO_5');
INSERT INTO `creature_text` VALUES (37183, 0, 0, 'NEVER! I... I will never... serve... you...', 14, 0, 0, 0, 0, 17078, 0, 37035, 0, 'Highlord Bolvar Fordragon - SAY_BOLVAR_INTRO_1');
INSERT INTO `creature_text` VALUES (37184, 0, 0, 'Rocket Pack Active!', 15, 0, 100, 0, 0, 0, 0, 37537, 0, 'Zafod Boombox - SAY_ZAFOD_ROCKET_PACK_ACTIVE');
INSERT INTO `creature_text` VALUES (37184, 1, 0, 'Rocket Pack Disabled. Move closer to Zafod Boombox to re-activate.', 15, 0, 100, 0, 0, 0, 0, 37538, 0, 'Zafod Boombox - SAY_ZAFOD_ROCKET_PACK_DISABLED');
INSERT INTO `creature_text` VALUES (37187, 0, 0, 'Kor\'kron, move out! Champions, watch your backs. The Scourge have been...', 14, 0, 0, 22, 0, 17103, 0, 37634, 0, 'High Overlord Saurfang - SAY_INTRO_HORDE_1');
INSERT INTO `creature_text` VALUES (37187, 1, 0, 'My boy died at the Wrath Gate. I am here only to collect his body.', 12, 0, 0, 397, 0, 17097, 0, 37638, 0, 'High Overlord Saurfang - SAY_INTRO_HORDE_3');
INSERT INTO `creature_text` VALUES (37187, 2, 0, 'We named him Dranosh - It means \"Heart of Draenor\" in orcish. I would not let the warlocks take him. My boy would be safe, hidden away by the elders of Garadar.', 12, 0, 0, 1, 0, 17098, 0, 37640, 0, 'High Overlord Saurfang - SAY_INTRO_HORDE_5');
INSERT INTO `creature_text` VALUES (37187, 3, 0, 'I made a promise to his mother before she died; that I would cross the Dark Portal alone - whether I lived or died, my son would be safe. Untainted...', 12, 0, 0, 1, 0, 17099, 0, 37641, 0, 'High Overlord Saurfang - SAY_INTRO_HORDE_6');
INSERT INTO `creature_text` VALUES (37187, 4, 0, 'Today, I fulfill that promise.', 12, 0, 0, 397, 0, 17100, 0, 37642, 0, 'High Overlord Saurfang - SAY_INTRO_HORDE_7');
INSERT INTO `creature_text` VALUES (37187, 5, 0, '%s charges!', 16, 0, 0, 53, 0, 17104, 0, 37643, 0, 'High Overlord Saurfang - SAY_INTRO_HORDE_8');
INSERT INTO `creature_text` VALUES (37187, 6, 0, 'Behind you lies the body of my only son. Nothing will keep me from him. ', 12, 0, 0, 0, 0, 17094, 0, 37525, 0, 'High Overlord Saurfang - SAY_OUTRO_ALLIANCE_8');
INSERT INTO `creature_text` VALUES (37187, 7, 0, 'High Overlord Saurfang walks over to his son and kneels before his son\'s body.', 16, 0, 0, 0, 0, 0, 0, 0, 0, 'High Overlord Saurfang - SAY_OUTRO_ALLIANCE_12');
INSERT INTO `creature_text` VALUES (37187, 8, 0, '[Orcish] No\'ku kil zil\'nok ha tar.', 12, 0, 0, 0, 0, 17096, 0, 0, 0, 'High Overlord Saurfang - SAY_OUTRO_ALLIANCE_13');
INSERT INTO `creature_text` VALUES (37187, 9, 0, 'Higher Overlord Saurfang picks up the body of his son and walks over towards Varian', 16, 0, 0, 0, 0, 0, 0, 0, 0, 'High Overlord Saurfang - SAY_OUTRO_ALLIANCE_14');
INSERT INTO `creature_text` VALUES (37187, 10, 0, 'I will not forget this... kindness. I thank you, Highness', 12, 0, 0, 0, 0, 17095, 0, 37529, 0, 'High Overlord Saurfang - SAY_OUTRO_ALLIANCE_15');
INSERT INTO `creature_text` VALUES (37187, 11, 0, '%s coughs.', 16, 0, 0, 0, 0, 17105, 0, 37646, 0, 'High Overlord Saurfang - SAY_OUTRO_HORDE_1');
INSERT INTO `creature_text` VALUES (37187, 12, 0, '%s weeps over the corpse of his son.', 16, 0, 0, 15, 0, 17106, 0, 37647, 0, 'High Overlord Saurfang - SAY_OUTRO_HORDE_2');
INSERT INTO `creature_text` VALUES (37187, 13, 0, 'You will have a proper ceremony in Nagrand, next to the pyres of your mother and ancestors.\n', 12, 0, 0, 0, 0, 17101, 0, 37648, 0, 'High Overlord Saurfang - SAY_OUTRO_HORDE_3');
INSERT INTO `creature_text` VALUES (37187, 14, 0, 'Honor, young heroes... no matter how dire the battle... never forsake it.', 12, 0, 0, 0, 0, 17102, 0, 37649, 0, 'High Overlord Saurfang - SAY_OUTRO_HORDE_4');
INSERT INTO `creature_text` VALUES (37187, 15, 0, 'The paladin still lives? Is it possible, Highlord? Could he have survived?', 12, 0, 0, 6, 0, 17107, 0, 37051, 0, 'High Overlord Saurfang - SAY_SAURFANG_INTRO_1');
INSERT INTO `creature_text` VALUES (37187, 16, 0, 'Then we must save him! If we rescue Bolvar Fordragon, we may quell the unrest between the Alliance and the Horde.', 12, 0, 0, 5, 0, 17108, 0, 37053, 0, 'High Overlord Saurfang - SAY_SAURFANG_INTRO_2');
INSERT INTO `creature_text` VALUES (37187, 17, 0, 'Our mission is now clear: The Lich King will answer for his crimes and we will save Highlord Bolvar Fordragon!', 12, 0, 0, 15, 0, 17109, 0, 37054, 0, 'High Overlord Saurfang - SAY_SAURFANG_INTRO_3');
INSERT INTO `creature_text` VALUES (37187, 18, 0, 'Kor\'kron, prepare Orgrim\'s Hammer for its final voyage! Champions, our gunship will find a point to dock on the upper reaches of the citadel. Meet us there!', 14, 0, 0, 22, 0, 17110, 0, 37055, 0, 'High Overlord Saurfang - SAY_SAURFANG_INTRO_4');
INSERT INTO `creature_text` VALUES (37188, 0, 0, '%s cries.', 16, 0, 0, 18, 0, 16651, 0, 29772, 0, 'Lady Jaina Proudmoore - SAY_OUTRO_ALLIANCE_17');
INSERT INTO `creature_text` VALUES (37188, 1, 0, 'It was nothing, your majesty. Just... I\'m proud of my king.', 12, 0, 0, 0, 0, 16652, 0, 37546, 0, 'Lady Jaina Proudmoore - SAY_OUTRO_ALLIANCE_19');
INSERT INTO `creature_text` VALUES (37195, 0, 0, 'Get our men in position immediately, Tobias.  We cannot let the Scythe fall into the hands of the Forsaken!!', 12, 0, 100, 53, 0, 0, 0, 37819, 0, 'Lord Darius Crowley to Player');
INSERT INTO `creature_text` VALUES (37195, 1, 0, 'Lorna!', 14, 0, 100, 0, 0, 19511, 0, 38641, 0, 'Lord Darius Crowley to Player');
INSERT INTO `creature_text` VALUES (37195, 2, 0, 'Does this toad speak for you, Genn?  Do you come to our dwelling as a friend?  Or do you come as a tyrant?', 14, 0, 100, 0, 0, 19510, 0, 38640, 0, 'Lord Darius Crowley to Player');
INSERT INTO `creature_text` VALUES (37195, 3, 0, 'Aye, Genn.  It is not law that binds us.  It is something far stronger.  My men are ready to give their lives under your command.', 12, 0, 100, 273, 0, 19512, 0, 38642, 0, 'Lord Darius Crowley to Player');
INSERT INTO `creature_text` VALUES (37200, 0, 0, 'Let\'s get a move on then! Move ou...', 14, 0, 0, 0, 0, 16974, 0, 37457, 0, 'Muradin Bronzebeard - SAY_INTRO_ALLIANCE_1');
INSERT INTO `creature_text` VALUES (37200, 1, 0, 'A lone orc against the might of the Alliance???', 14, 0, 0, 0, 0, 16970, 0, 37460, 0, 'Muradin Bronzebeard - SAY_INTRO_ALLIANCE_4');
INSERT INTO `creature_text` VALUES (37200, 2, 0, 'Charge!!!\n', 14, 0, 0, 0, 0, 16971, 0, 37461, 0, 'Muradin Bronzebeard - SAY_INTRO_ALLIANCE_5');
INSERT INTO `creature_text` VALUES (37200, 3, 0, '%s gasps for air.', 16, 0, 0, 0, 0, 16975, 0, 37502, 0, 'Muradin Bronzebeard - SAY_OUTRO_ALLIANCE_1');
INSERT INTO `creature_text` VALUES (37200, 4, 0, 'That was Saurfang\'s boy - the Horde commander at the Wrath Gate. Such a tragic end...', 12, 0, 0, 0, 0, 16976, 0, 37503, 0, 'Muradin Bronzebeard - SAY_OUTRO_ALLIANCE_2');
INSERT INTO `creature_text` VALUES (37200, 5, 0, 'What in the... There, in the distance!\n', 12, 0, 0, 0, 0, 16977, 0, 37504, 0, 'Muradin Bronzebeard - SAY_OUTRO_ALLIANCE_3');
INSERT INTO `creature_text` VALUES (37200, 6, 0, 'A Horde Zeppelin flies up to the rise.', 16, 0, 0, 0, 0, 0, 0, 0, 0, 'Muradin Bronzebeard - SAY_OUTRO_ALLIANCE_4');
INSERT INTO `creature_text` VALUES (37200, 7, 0, 'Soldiers, fall in! Looks like the Horde are comin\' to take another shot!\n', 14, 0, 0, 0, 0, 16978, 0, 37505, 0, 'Muradin Bronzebeard - SAY_OUTRO_ALLIANCE_5');
INSERT INTO `creature_text` VALUES (37200, 8, 0, 'The Zeppelin docks, and High Overlord Saurfang hops out, confronting the Alliance soldiers and Muradin', 16, 0, 0, 0, 0, 0, 0, 0, 0, 'Muradin Bronzebeard - SAY_OUTRO_ALLIANCE_6');
INSERT INTO `creature_text` VALUES (37200, 9, 0, 'Don\'t force me hand, orc. We can\'t let ye pass.', 12, 0, 0, 0, 0, 16972, 0, 37524, 0, 'Muradin Bronzebeard - SAY_OUTRO_ALLIANCE_7');
INSERT INTO `creature_text` VALUES (37200, 10, 0, 'I... I can\'t do it. Get back on yer ship and we\'ll spare yer life. ', 12, 0, 0, 0, 0, 16973, 0, 37526, 0, 'Muradin Bronzebeard - SAY_OUTRO_ALLIANCE_9');
INSERT INTO `creature_text` VALUES (37200, 11, 0, 'A mage portal from Stormwind appears between the two and Varian Wrynn and Jaina Proudmoore emerge.', 16, 0, 0, 0, 0, 0, 0, 0, 0, 'Muradin Bronzebeard - SAY_OUTRO_ALLIANCE_10');
INSERT INTO `creature_text` VALUES (37200, 12, 0, 'Right away, yer majesty!', 12, 0, 0, 0, 0, 16979, 0, 37547, 0, 'Muradin Bronzebeard - SAY_OUTRO_ALLIANCE_21');
INSERT INTO `creature_text` VALUES (37200, 13, 0, 'Could it be, Lord Fordring? If Bolvar lives, mayhap there is hope fer peace between the Alliance and the Horde. We must reach the top o\' this cursed place and free the paladin!', 12, 0, 0, 6, 0, 16980, 0, 37037, 0, 'Muradin Bronzebeard - SAY_SAURFANG_INTRO_2');
INSERT INTO `creature_text` VALUES (37200, 14, 0, 'Prepare the Skybreaker fer an aerial assault on the citadel!', 14, 0, 0, 5, 0, 16981, 0, 37043, 0, 'Muradin Bronzebeard - SAY_SAURFANG_INTRO_3');
INSERT INTO `creature_text` VALUES (37200, 15, 0, 'Heroes, ye must fight yer way to a clear extraction point within Icecrown. We\'ll try an\' rendezvous on the ramparts!', 14, 0, 0, 22, 0, 16982, 0, 37046, 0, 'Muradin Bronzebeard - SAY_SAURFANG_INTRO_4');
INSERT INTO `creature_text` VALUES (37217, 0, 0, '%s cries out with a loud, baying howl!', 41, 0, 0, 0, 0, 0, 0, 37849, 0, 'Precious - EMOTE_PRECIOUS_ZOMBIES');
INSERT INTO `creature_text` VALUES (37221, 0, 0, 'The chill of this place... I can feel my blood freezing.', 14, 0, 100, 0, 0, 16631, 0, 37591, 0, 'Jaina SAY_JAINA_INTRO_1');
INSERT INTO `creature_text` VALUES (37221, 1, 0, 'What is that! Up ahead! Could it be? Heroes, at my side!', 14, 0, 100, 0, 0, 16632, 0, 37592, 0, 'Jaina SAY_JAINA_INTRO_2');
INSERT INTO `creature_text` VALUES (37221, 2, 0, 'Frostmourne: the blade that destroyed our kingdom...', 14, 0, 100, 0, 0, 16633, 0, 37593, 0, 'Jaina SAY_JAINA_INTRO_3');
INSERT INTO `creature_text` VALUES (37221, 3, 0, 'Stand back! Touch that blade and your soul will be scarred for all eternity! I must attempt to commune with the spirits locked away within Frostmourne. Give me space. Back up, please. ', 14, 0, 100, 0, 0, 16634, 0, 37594, 0, 'Jaina SAY_JAINA_INTRO_4');
INSERT INTO `creature_text` VALUES (37221, 4, 0, 'Uther! Dear Uther! I... I\'m so sorry.', 12, 0, 100, 0, 0, 16635, 0, 37595, 0, 'Jaina SAY_JAINA_INTRO_5');
INSERT INTO `creature_text` VALUES (37221, 5, 0, 'Arthas is here? Maybe I...', 12, 0, 100, 0, 0, 16636, 0, 37596, 0, 'Jaina SAY_JAINA_INTRO_6');
INSERT INTO `creature_text` VALUES (37221, 6, 0, 'But Uther, if there\'s any hope of reaching Arthas. I... I must try.', 12, 0, 100, 0, 0, 16637, 0, 37597, 0, 'Jaina SAY_JAINA_INTRO_7');
INSERT INTO `creature_text` VALUES (37221, 7, 0, 'Tell me how, Uther? How do I destroy my prince? My...', 12, 0, 100, 0, 0, 16638, 0, 37598, 0, 'Jaina SAY_JAINA_INTRO_8');
INSERT INTO `creature_text` VALUES (37221, 8, 0, 'You\'re right, Uther. Forgive me. I... I don\'t know what got a hold of me. We will deliver this information to the King and the knights that battle the Scourge within Icecrown Citadel.', 12, 0, 100, 0, 0, 16639, 0, 37599, 0, 'Jaina SAY_JAINA_INTRO_9');
INSERT INTO `creature_text` VALUES (37221, 9, 0, 'Who could bear such a burden?', 12, 0, 100, 0, 0, 16640, 0, 37600, 0, 'Jaina SAY_JAINA_INTRO_10');
INSERT INTO `creature_text` VALUES (37221, 10, 0, 'Then maybe there is still hope...', 12, 0, 100, 0, 0, 16641, 0, 37601, 0, 'Jaina SAY_JAINA_INTRO_11');
INSERT INTO `creature_text` VALUES (37221, 11, 0, 'You won\'t deny me this, Arthas! I must know... I must find out... ', 14, 0, 100, 0, 0, 16642, 0, 37602, 0, 'Jaina SAY_JAINA_INTRO_END');
INSERT INTO `creature_text` VALUES (37223, 0, 0, 'I... I don\'t believe it! Frostmourne stands before us - unguarded - just as the gnome claimed. Come, heroes!', 14, 0, 100, 0, 0, 17049, 0, 37568, 0, 'Sylvanas SAY_SYLVANAS_INTRO_1');
INSERT INTO `creature_text` VALUES (37223, 1, 0, 'Standing this close to the blade that ended my life... The pain... It is renewed.', 14, 0, 100, 0, 0, 17050, 0, 37569, 0, 'Sylvanas SAY_SYLVANAS_INTRO_2');
INSERT INTO `creature_text` VALUES (37223, 2, 0, 'I dare not touch it. Stand back! Stand back as I attempt to commune with the blade! Perhaps our salvation lies within...', 14, 0, 100, 0, 0, 17051, 0, 37570, 0, 'Sylvanas SAY_SYLVANAS_INTRO_3');
INSERT INTO `creature_text` VALUES (37223, 3, 0, 'Uther... Uther the Lightbringer. How...', 12, 0, 100, 0, 0, 17052, 0, 37571, 0, 'Sylvanas SAY_SYLVANAS_INTRO_4');
INSERT INTO `creature_text` VALUES (37223, 4, 0, 'The Lich King is here? Then my destiny shall be fulfilled on this day! ', 12, 0, 100, 0, 0, 17053, 0, 37572, 0, 'Sylvanas SAY_SYLVANAS_INTRO_5');
INSERT INTO `creature_text` VALUES (37223, 5, 0, 'There must be a way...', 12, 0, 100, 0, 0, 17054, 0, 37573, 0, 'Sylvanas SAY_SYLVANAS_INTRO_6');
INSERT INTO `creature_text` VALUES (37223, 6, 0, 'Who could bear such a burden?', 12, 0, 100, 0, 0, 17055, 0, 37600, 0, 'Sylvanas SAY_SYLVANAS_INTRO_7');
INSERT INTO `creature_text` VALUES (37223, 7, 0, 'The Frozen Throne...', 12, 0, 100, 0, 0, 17056, 0, 37575, 0, 'Sylvanas SAY_SYLVANAS_INTRO_8');
INSERT INTO `creature_text` VALUES (37223, 8, 0, 'You will not escape me that easily, Arthas! I will have my vengeance! ', 14, 0, 100, 0, 0, 17057, 0, 37576, 0, 'Sylvanas SAY_SYLVANAS_INTRO_END');
INSERT INTO `creature_text` VALUES (37225, 0, 0, 'Jaina! Could it truly be you?', 14, 0, 100, 0, 0, 16666, 0, 37603, 0, 'Uther SAY_UTHER_INTRO_A2_1');
INSERT INTO `creature_text` VALUES (37225, 1, 0, 'Jaina, you haven\'t much time. The Lich King sees what the sword sees. He will be here shortly. ', 12, 0, 100, 0, 0, 16667, 0, 37604, 0, 'Uther SAY_UTHER_INTRO_A2_2');
INSERT INTO `creature_text` VALUES (37225, 2, 0, 'No, girl. Arthas is not here. Arthas is merely a presence within the Lich King\'s mind. A dwindling presence...', 12, 0, 100, 0, 0, 16668, 0, 37605, 0, 'Uther SAY_UTHER_INTRO_A2_3');
INSERT INTO `creature_text` VALUES (37225, 3, 0, 'Jaina, listen to me. You must destroy the Lich King. You cannot reason with him. He will kill you and your allies and raise you all as powerful soldiers of the Scourge.', 12, 0, 100, 0, 0, 16669, 0, 37606, 0, 'Uther SAY_UTHER_INTRO_A2_4');
INSERT INTO `creature_text` VALUES (37225, 4, 0, 'Snap out of it, girl. You must destroy the Lich King at the place where he merged with Ner\'zhul - atop the spire, at the Frozen Throne. It is the only way.', 12, 0, 100, 0, 0, 16670, 0, 37607, 0, 'Uther SAY_UTHER_INTRO_A2_5');
INSERT INTO `creature_text` VALUES (37225, 5, 0, 'There is... something else that you should know about the Lich King. Control over the Scourge must never be lost. Even if you were to strike down the Lich King, another would have to take his place. For without the control of its master, the Scourge would run rampant across the world - destroying all living things.', 12, 0, 100, 0, 0, 16671, 0, 37608, 0, 'Uther SAY_UTHER_INTRO_A2_6');
INSERT INTO `creature_text` VALUES (37225, 6, 0, 'A grand sacrifice by a noble soul...', 12, 0, 100, 0, 0, 16672, 0, 37609, 0, 'Uther SAY_UTHER_INTRO_A2_7');
INSERT INTO `creature_text` VALUES (37225, 7, 0, 'I do not know, Jaina. I suspect that the piece of Arthas that might be left inside the Lich King is all that holds the Scourge from annihilating Azeroth.', 12, 0, 100, 0, 0, 16673, 0, 37610, 0, 'Uther SAY_UTHER_INTRO_A2_8');
INSERT INTO `creature_text` VALUES (37225, 8, 0, 'No, Jaina! ARRRRRRGHHHH... He... He is coming. You... You must...', 12, 0, 100, 0, 0, 16674, 0, 37611, 0, 'Uther SAY_UTHER_INTRO_A2_9');
INSERT INTO `creature_text` VALUES (37225, 9, 0, 'Careful, girl. I\'ve heard talk of that cursed blade saving us before. Look around you and see what has been born of Frostmourne.', 12, 0, 100, 0, 0, 16659, 0, 37583, 0, 'Uther SAY_UTHER_INTRO_H2_1');
INSERT INTO `creature_text` VALUES (37225, 10, 0, 'You haven\'t much time. The Lich King sees what the sword sees. He will be here shortly. ', 12, 0, 100, 0, 0, 16660, 0, 37584, 0, 'Uther SAY_UTHER_INTRO_H2_2');
INSERT INTO `creature_text` VALUES (37225, 11, 0, 'You cannot defeat the Lich King. Not here. You would be a fool to try. He will kill those that follow you and raise them as powerful soldiers of the Scourge. But for you, Sylvanas, his reward for you would be worse than the last.', 12, 0, 100, 0, 0, 16661, 0, 37585, 0, 'Uther SAY_UTHER_INTRO_H2_3');
INSERT INTO `creature_text` VALUES (37225, 12, 0, 'Perhaps, but know this: there must always be a Lich King. Even if you were to strike down Arthas, another would have to take his place, for without the control of the Lich King, the Scourge would wash over this world like locusts, destroying all that they touched.', 12, 0, 100, 0, 0, 16662, 0, 37586, 0, 'Uther SAY_UTHER_INTRO_H2_4');
INSERT INTO `creature_text` VALUES (37225, 13, 0, 'I do not know, Banshee Queen. I suspect that the piece of Arthas that might be left inside the Lich King is all that holds the Scourge from annihilating Azeroth.', 12, 0, 100, 0, 0, 16663, 0, 37587, 0, 'Uther SAY_UTHER_INTRO_H2_5');
INSERT INTO `creature_text` VALUES (37225, 14, 0, 'Alas, the only way to defeat the Lich King is to destroy him at the place where he was created.', 12, 0, 100, 0, 0, 16664, 0, 37588, 0, 'Uther SAY_UTHER_INTRO_H2_6');
INSERT INTO `creature_text` VALUES (37225, 15, 0, 'Aye. ARRRRRRGHHHH... He... He is coming. You... You must...', 12, 0, 100, 0, 0, 16665, 0, 37589, 0, 'Uther SAY_UTHER_INTRO_H2_7');
INSERT INTO `creature_text` VALUES (37225, 16, 0, 'Halt! Do not carry that blade any further!', 14, 0, 100, 25, 0, 16675, 0, 37201, 0, 'Uther the Lightbringer');
INSERT INTO `creature_text` VALUES (37225, 17, 0, 'Do you realize what you\'ve done?', 14, 0, 100, 5, 0, 16676, 0, 37202, 0, 'Uther the Lightbringer');
INSERT INTO `creature_text` VALUES (37225, 18, 0, 'You have forged this blade from saronite, the very blood of an old god. The power of the Lich King calls to this weapon.', 12, 0, 100, 1, 0, 16677, 0, 37204, 0, 'Uther the Lightbringer');
INSERT INTO `creature_text` VALUES (37225, 19, 0, 'Each moment you tarry here, Quel\'Delar drinks in the evil of this place.', 12, 0, 100, 1, 0, 16678, 0, 38442, 0, 'Uther the Lightbringer');
INSERT INTO `creature_text` VALUES (37225, 20, 0, 'There is only one way to cleanse this sword. Make haste for the Sunwell and immerse the blade in its waters.', 12, 0, 100, 25, 0, 16679, 0, 37205, 0, 'Uther the Lightbringer');
INSERT INTO `creature_text` VALUES (37225, 21, 0, 'I can resist Frostmourne\'s call no more...', 12, 0, 100, 1, 0, 16680, 0, 37206, 0, 'Uther the Lightbringer');
INSERT INTO `creature_text` VALUES (37226, 0, 0, 'SILENCE, PALADIN!', 14, 0, 100, 0, 0, 17225, 0, 37613, 0, 'Lich King SAY_LK_INTRO_1');
INSERT INTO `creature_text` VALUES (37226, 1, 0, 'So you wish to commune with the dead? You shall have your wish.', 14, 0, 100, 0, 0, 17226, 0, 37614, 0, 'Lich King SAY_LK_INTRO_2');
INSERT INTO `creature_text` VALUES (37226, 2, 0, 'Falric. Marwyn. Bring their corpses to my chamber when you are through.', 14, 0, 100, 0, 0, 17227, 0, 37615, 0, 'Lich King SAY_LK_INTRO_3');
INSERT INTO `creature_text` VALUES (37226, 3, 0, 'Foolish girl. You seek that which I killed long ago. He is merely a ghost now. A faint echo in my mind... ', 14, 0, 100, 0, 0, 17229, 0, 37617, 0, 'Lich King SAY_LK_JAINA_INTRO_END');
INSERT INTO `creature_text` VALUES (37226, 4, 0, 'I will not make the same mistake again, Sylvanas. This time there will be no escape. You will all serve me in death!', 14, 0, 100, 0, 0, 17228, 0, 37616, 0, 'Lich King SAY_LK_SYLVANAS_INTRO_END');
INSERT INTO `creature_text` VALUES (37230, 0, 0, 'A screeching cry pierces the air above!', 41, 0, 100, 0, 0, 0, 0, 37161, 1, 'Spire Frostwyrm to Player');
INSERT INTO `creature_text` VALUES (37491, 1, 0, 'You miserable fools never did manage to select a decent bat wing.', 14, 0, 0, 0, 0, 16587, 0, 37076, 0, 'Captain Arnath - SAY_ARNATH_RESURRECTED');
INSERT INTO `creature_text` VALUES (37491, 2, 0, 'THAT was for bringing me spoiled spider ichor!\n', 14, 0, 0, 0, 0, 16588, 0, 37075, 0, 'Captain Arnath - SAY_ARNATH_KILL');
INSERT INTO `creature_text` VALUES (37491, 3, 0, 'Don\'t... let Finklestein use me... for his potions...', 14, 0, 0, 0, 0, 16589, 0, 37077, 0, 'Captain Arnath - SAY_ARNATH_SECOND_DEATH');
INSERT INTO `creature_text` VALUES (37493, 1, 0, 'What? This strength...? All of the pain is gone! You... must join me in the eternal embrace of death!\n', 14, 0, 0, 0, 0, 16811, 0, 37664, 0, 'Captain Brandon - SAY_BRANDON_RESURRECTED');
INSERT INTO `creature_text` VALUES (37493, 2, 0, 'It doesn\'t hurt anymore, does it?\n', 14, 0, 0, 0, 0, 16812, 0, 37658, 0, 'Captain Brandon - SAY_BRANDON_KILL');
INSERT INTO `creature_text` VALUES (37493, 3, 0, 'I\'m sorry...', 14, 0, 0, 0, 0, 16813, 0, 37659, 0, 'Captain Brandon - SAY_BRANDON_SECOND_DEATH');
INSERT INTO `creature_text` VALUES (37494, 1, 0, 'No! Why was I denied a death by flame? You must all BURN!  ', 14, 0, 0, 0, 0, 16845, 0, 37667, 0, 'Captain Grondel - SAY_GRONDEL_RESURRECTED');
INSERT INTO `creature_text` VALUES (37494, 2, 0, 'Can you feel the burn?', 14, 0, 0, 0, 0, 16846, 0, 37660, 0, 'Captain Grondel - SAY_GRONDEL_KILL');
INSERT INTO `creature_text` VALUES (37494, 3, 0, 'What... have I done? No!', 14, 0, 0, 0, 0, 16847, 0, 37661, 0, 'Captain Grondel - SAY_GRONDEL_SECOND_DEATH');
INSERT INTO `creature_text` VALUES (37495, 1, 0, 'There is no escaping the Lich King\'s will. Prepare for an explosive encounter!', 14, 0, 0, 0, 0, 16999, 0, 37669, 0, 'Captain Rupert - SAY_RUPERT_RESURRECTED');
INSERT INTO `creature_text` VALUES (37495, 2, 0, 'So that\'s what happens what you stand too close to a bomb!', 14, 0, 0, 0, 0, 17000, 0, 37662, 0, 'Captain Rupert - SAY_RUPERT_KILL');
INSERT INTO `creature_text` VALUES (37495, 3, 0, 'What an... explosive ending!', 14, 0, 0, 0, 0, 17001, 0, 37663, 0, 'Captain Rupert - SAY_RUPERT_SECOND_DEATH');
INSERT INTO `creature_text` VALUES (37500, 0, 0, 'Volcano rocks - new low price! You\'re gonna love my rocks.', 14, 0, 50, 1, 0, 0, 0, 37100, 0, 'say Text');
INSERT INTO `creature_text` VALUES (37500, 0, 1, 'Volcano rocks for sale! Hot off the mountain! You want \'em, I got \'em!', 14, 0, 50, 1, 0, 0, 0, 37099, 0, 'say Text');
INSERT INTO `creature_text` VALUES (37500, 0, 2, 'Take home a piece of the Cataclysm! Volcano rocks for sale - hot, Hot, HOT!', 14, 0, 50, 1, 0, 0, 0, 37102, 0, 'say Text');
INSERT INTO `creature_text` VALUES (37500, 0, 3, 'Get your volcano rocks here! ...Great for cats!', 14, 0, 50, 1, 0, 0, 0, 37101, 0, 'say Text');
INSERT INTO `creature_text` VALUES (37500, 0, 4, 'Volcano rocks. Million and one uses. Lava, java, sauna, banana....', 14, 0, 50, 1, 0, 0, 0, 37104, 0, 'say Text');
INSERT INTO `creature_text` VALUES (37500, 0, 5, 'Papers to weigh? Doors to stop? Windows to break? bam, Bam, BAM! Volcano rocks.', 14, 0, 50, 1, 0, 0, 0, 37103, 0, 'say Text');
INSERT INTO `creature_text` VALUES (37500, 0, 6, 'Volcano rocks! Limited time offer! Papers of authenticity and everything!', 14, 0, 50, 1, 0, 0, 0, 37106, 0, 'say Text');
INSERT INTO `creature_text` VALUES (37507, 0, 0, 'You be dead soon!', 12, 0, 100, 0, 0, 0, 0, 10434, 0, 'Frostmane Blade');
INSERT INTO `creature_text` VALUES (37507, 0, 1, 'Killing you be easy.', 12, 0, 100, 0, 0, 0, 0, 1909, 0, 'Frostmane Blade');
INSERT INTO `creature_text` VALUES (37507, 0, 2, 'I gonna make you into mojo!', 12, 0, 100, 0, 0, 0, 0, 10435, 0, 'Frostmane Blade');
INSERT INTO `creature_text` VALUES (37507, 0, 3, 'My weapon be thirsty!', 12, 0, 100, 0, 0, 0, 0, 1908, 0, 'Frostmane Blade');
INSERT INTO `creature_text` VALUES (37513, 0, 0, 'You, <Snort> you are strong <Gurgle>. But... Strong enough for THE BOARPOCALYPSE?', 14, 0, 100, 0, 0, 0, 0, 37129, 0, 'on Aggro Text');
INSERT INTO `creature_text` VALUES (37513, 1, 0, 'You ... survive the boars? <Snort> So many boars! So sad! <Gurgle>', 12, 0, 100, 0, 0, 0, 0, 37130, 0, 'on Death Text');
INSERT INTO `creature_text` VALUES (37513, 2, 0, '%s becomes enraged!', 16, 0, 100, 0, 0, 0, 0, 10677, 0, 'combat Enrage');
INSERT INTO `creature_text` VALUES (37518, 0, 0, '<Static>$bIs this thing on?  Hello?', 12, 7, 100, 0, 0, 0, 0, 37115, 0, 'Milo Geartwinge');
INSERT INTO `creature_text` VALUES (37518, 1, 0, 'Ahh, there you are.  Hello!  I figured that, while you\'re flying to Kharanos, I\'d give you some data about what\'s going on outside of Coldridge Valley.', 12, 7, 100, 0, 0, 0, 0, 37117, 0, 'Milo Geartwinge');
INSERT INTO `creature_text` VALUES (37518, 2, 0, 'It seems you Coldridge folks are dreadfully under-informed about the events of the last few days.', 12, 7, 100, 0, 0, 0, 0, 37118, 0, 'Milo Geartwinge');
INSERT INTO `creature_text` VALUES (37518, 3, 0, 'If you thought you had problems with trolls in Coldridge, wait \'til you get to Kharanos!  That big cataclysm got the trolls all stirred up out here.', 12, 7, 100, 0, 0, 0, 0, 37119, 0, 'Milo Geartwinge');
INSERT INTO `creature_text` VALUES (37518, 4, 0, 'And if you\'ll look to your left, you\'ll see that the gnomes have retaken Gnomeregan.  Hooray for us!', 12, 7, 100, 0, 0, 0, 0, 43112, 0, 'Milo Geartwinge');
INSERT INTO `creature_text` VALUES (37518, 5, 0, 'There\'s plenty else going on out here, too.  We\'ve opened up the path to the airport, there\'s rumors of Dark Irons in the east... oh, and I think something weird is going on with that dwarf king, Magni.  Might want to check it out.', 12, 7, 100, 0, 0, 0, 0, 43113, 0, 'Milo Geartwinge');
INSERT INTO `creature_text` VALUES (37518, 6, 0, 'And here we are!  Welcome to Kharanos!  Watch your step on the way out.', 12, 7, 100, 0, 0, 0, 0, 43115, 0, 'Milo Geartwinge');
INSERT INTO `creature_text` VALUES (37554, 0, 0, 'He\'s... too powerful. Heroes, quickly... come to me! We must leave this place at once! I will do what I can to hold him in place while we flee.', 14, 0, 100, 0, 0, 17058, 0, 37168, 0, 'Sylvanas SAY_SYLVANA_AGGRO');
INSERT INTO `creature_text` VALUES (37554, 1, 0, 'No wall can hold the Banshee Queen. Keep the undead at bay, heroes. I will tear this barrier down!', 14, 0, 100, 0, 0, 17029, 0, 38540, 0, 'Sylvanas SAY_SYLVANA_WALL_01');
INSERT INTO `creature_text` VALUES (37554, 2, 0, 'Another barrier? Stand strong, champions. I will bring the wall down.', 14, 0, 100, 0, 0, 17030, 0, 38541, 0, 'Sylvanas SAY_SYLVANA_WALL_02');
INSERT INTO `creature_text` VALUES (37554, 3, 0, 'I grow tired of these games, Arthas! Your walls can\'t stop me!', 14, 0, 100, 0, 0, 17031, 0, 38542, 0, 'Sylvanas SAY_SYLVANA_WALL_03');
INSERT INTO `creature_text` VALUES (37554, 4, 0, 'You won\'t impede our escape, fiend! Keep the undead off me while I bring this barrier down.', 14, 0, 100, 0, 0, 17032, 0, 38543, 0, 'Sylvanas SAY_SYLVANA_WALL_04');
INSERT INTO `creature_text` VALUES (37554, 5, 0, 'There\'s an opening up ahead. GO NOW!', 14, 0, 100, 1, 0, 17059, 0, 38551, 0, 'Sylvanas SAY_SYLVANA_ESCAPE_01');
INSERT INTO `creature_text` VALUES (37554, 6, 0, 'We\'re almost there... Don\'t give up!', 14, 0, 100, 0, 0, 17060, 0, 38538, 0, 'Sylvanas SAY_SYLVANA_ESCAPE_02');
INSERT INTO `creature_text` VALUES (37554, 7, 0, 'BLASTED DEAD END! So this is how it ends. Prepare yourselves, heroes, for today we make our final stand!', 14, 0, 100, 5, 0, 17061, 0, 37170, 0, 'Sylvanas SAY_SYLVANA_TRAP');
INSERT INTO `creature_text` VALUES (37554, 8, 0, 'We are safe... for now. His strength has increased tenfold since our last battle. It will take a mighty army to destroy the Lich King. An army greater than even the Horde can rouse.', 14, 0, 100, 1, 0, 17062, 0, 37171, 0, 'Sylvanas SAY_SYLVANA_FINAL_2');
INSERT INTO `creature_text` VALUES (37592, 0, 0, 'Heroes! We will hold off the undead as long as we can, even to our dying breath. Deal with the Scourgelord!', 14, 0, 100, 0, 0, 17150, 0, 37232, 0, 'Gorkun SAY_GORKUN_INTRO_2');
INSERT INTO `creature_text` VALUES (37592, 1, 0, 'Brave champions, we owe you our lives, our freedom.... Though it be a tiny gesture in the face of this enormous debt, I pledge that from this day forth, all will know of your deeds, and the blazing path of light you cut through the shadow of this dark citadel. ', 14, 0, 100, 0, 0, 0, 0, 37320, 0, 'Gorkun SAY_GORKUN_OUTRO_1');
INSERT INTO `creature_text` VALUES (37592, 2, 0, 'This day will stand as a testament not only to your valor, but to the fact that no foe, not even the Lich King himself, can stand when Alliance and Horde set aside their differences and---', 14, 0, 100, 0, 0, 0, 0, 37321, 0, 'Gorkun SAY_GORKUN_OUTRO_2');
INSERT INTO `creature_text` VALUES (37596, 0, 0, 'The Argent Crusade and the Knights of the Ebon Blade have assaulted the gates of the Icecrown Citadel and are preparing for a massive attack upon the Scourge. Our mission is a bit more subtle, but equally as important.', 12, 0, 100, 0, 0, 17038, 0, 37380, 0, 'sylvanas SAY_INTRO_1');
INSERT INTO `creature_text` VALUES (37596, 1, 0, 'With the attention of the Lich King turned towards the front gate, we\'ll be working our way through the side in search of information that will enable us to defeat him - once and for all.', 12, 0, 100, 0, 0, 17039, 0, 37381, 0, 'sylvanas SAY_INTRO_2');
INSERT INTO `creature_text` VALUES (37596, 2, 0, 'Our scouts have reported that the Lich King has a private chamber, outside of the Frozen Throne, deep within a place called the Halls of Reflection. That is our target, champions. ', 12, 0, 100, 0, 0, 17040, 0, 37382, 0, 'sylvanas SAY_INTRO_3');
INSERT INTO `creature_text` VALUES (37596, 3, 0, 'We will cut a swath of destruction through this cursed place and find a way to enter the Halls of Reflection. If there is anything of value to be found here, it will be found in the halls.', 12, 0, 100, 0, 0, 17041, 0, 37383, 0, 'sylvanas SAY_INTRO_4');
INSERT INTO `creature_text` VALUES (37596, 4, 0, 'Your first mission is to destroy the machines of death within this malevolent engine of souls and clear a path for our soldiers. ', 12, 0, 100, 0, 0, 17042, 0, 37376, 0, 'sylvanas SAY_INTRO_5');
INSERT INTO `creature_text` VALUES (37596, 5, 0, 'The Dark Lady watches over you. Make haste!', 12, 0, 100, 0, 0, 17043, 0, 37384, 0, 'sylvanas SAY_INTRO_6');
INSERT INTO `creature_text` VALUES (37597, 0, 0, 'Thank the Light for seeing you here safely! We have much work to do if we are to defeat the Lich King and put an end to the Scourge.', 12, 0, 100, 0, 0, 16617, 0, 37370, 0, 'jaina SAY_INTRO_1');
INSERT INTO `creature_text` VALUES (37597, 1, 0, 'Our allies within the Argent Crusade and the Knights of the Ebon Blade have broken through the front gate of Icecrown and are attempting to establish a foothold within the citadel. ', 12, 0, 100, 0, 0, 16618, 0, 37371, 0, 'jaina SAY_INTRO_2');
INSERT INTO `creature_text` VALUES (37597, 2, 0, 'Their success hinges upon what we discover within these cursed halls. Although our mission is fraught with peril, we must persevere!', 12, 0, 100, 0, 0, 16619, 0, 37372, 0, 'jaina SAY_INTRO_3');
INSERT INTO `creature_text` VALUES (37597, 3, 0, 'With the attention of the Lich King drawn towards the front gate, we will be working our way through the side in search of information that will enable us to defeat the Scourge once and for all.', 12, 0, 100, 0, 0, 16620, 0, 37373, 0, 'jaina SAY_INTRO_4');
INSERT INTO `creature_text` VALUES (37597, 4, 0, 'King Varian\'s SI:7 agents have gathered information about a private sanctum of the Lich King\'s deep within a place called the Halls of Reflection. ', 12, 0, 100, 0, 0, 16621, 0, 37374, 0, 'jaina SAY_INTRO_5');
INSERT INTO `creature_text` VALUES (37597, 5, 0, 'We will carve a path through this wretched place and find a way to enter the Halls of Reflection. I sense powerful magic hidden away within those halls. Magic that could be the key to destroying the Scourge!', 12, 0, 100, 0, 0, 16622, 0, 37375, 0, 'jaina SAY_INTRO_6');
INSERT INTO `creature_text` VALUES (37597, 6, 0, 'Your first mission is to destroy the machines of death within this malevolent engine of souls and clear a path for our soldiers. ', 12, 0, 100, 0, 0, 16623, 0, 37376, 0, 'jaina SAY_INTRO_7');
INSERT INTO `creature_text` VALUES (37597, 7, 0, 'Make haste, champions! I will prepare the troops to fall in behind you.', 12, 0, 100, 0, 0, 16624, 0, 37377, 0, 'jaina SAY_INTRO_8');
INSERT INTO `creature_text` VALUES (37602, 0, 0, 'Still an unfortunate accident of fire-fireworks-lit flammable-gas - Lectriquea?', 14, 0, 100, 0, 0, 0, 0, 0, 0, 'say Text');
INSERT INTO `creature_text` VALUES (37671, 0, 0, 'That crate won\'t deliver itself, friend. Get a move on!', 12, 0, 100, 1, 0, 0, 0, 38024, 0, 'Crown Supply Guard');
INSERT INTO `creature_text` VALUES (37671, 0, 1, 'Time is money, friend. Go go go!', 12, 0, 100, 1, 0, 0, 0, 38022, 0, 'Crown Supply Guard');
INSERT INTO `creature_text` VALUES (37671, 0, 2, 'The Lovely Merchant is waiting for that. Hurry it up!', 12, 0, 100, 1, 0, 0, 0, 38023, 0, 'Crown Supply Guard');
INSERT INTO `creature_text` VALUES (37694, 0, 0, 'Thank you!', 12, 0, 100, 0, 0, 0, 0, 26056, 0, 'Enslaved Villager');
INSERT INTO `creature_text` VALUES (37694, 1, 0, 'The Forsaken will pay for what they\'ve done!', 12, 0, 100, 0, 0, 0, 0, 37239, 0, 'Enslaved Villager');
INSERT INTO `creature_text` VALUES (37694, 2, 0, 'It\'s true then?  Even those afflicted by the Curse are fighting the Forsaken!', 12, 0, 100, 0, 0, 0, 0, 37238, 0, 'Enslaved Villager');
INSERT INTO `creature_text` VALUES (37701, 0, 0, 'Consider yourselves lucky to be alive, Gilnean dogs!  Now get to work before we change our minds!', 14, 0, 100, 25, 0, 0, 0, 37254, 0, 'Forsaken Slavedriver');
INSERT INTO `creature_text` VALUES (37704, 0, 0, 'Quel\'Delar leaps to life in the presence of Frostmourne!', 41, 0, 100, 0, 0, 0, 0, 37645, 0, 'Frostmourne Altar Bunny (Quel\'Delar)');
INSERT INTO `creature_text` VALUES (37735, 0, 0, '%s becomes enraged!', 16, 0, 100, 0, 0, 0, 0, 10677, 0, 'combat Enrage');
INSERT INTO `creature_text` VALUES (37743, 0, 0, 'Grab anything you think we can fence.', 12, 0, 50, 1, 0, 0, 0, 37310, 0, 'say Text');
INSERT INTO `creature_text` VALUES (37743, 0, 1, 'There\'s got to be more. Double check the bodies!', 12, 0, 50, 1, 0, 0, 0, 37315, 0, 'say Text');
INSERT INTO `creature_text` VALUES (37743, 0, 2, 'Hey did anyone loot the mailbox yet?', 12, 0, 50, 1, 0, 0, 0, 37314, 0, 'say Text');
INSERT INTO `creature_text` VALUES (37743, 0, 3, 'Most of this stuff is beads and hides. Where\'s all the treasure?', 12, 0, 50, 1, 0, 0, 0, 37312, 0, 'say Text');
INSERT INTO `creature_text` VALUES (37743, 0, 4, 'Time is wasting. I want this place picked cleaned before they counter-attack', 12, 0, 50, 1, 0, 0, 0, 37313, 0, 'say Text');
INSERT INTO `creature_text` VALUES (37743, 0, 5, 'Has anyone seen the quilboar they had caged up over here?', 12, 0, 50, 1, 0, 0, 0, 37317, 0, 'say Text');
INSERT INTO `creature_text` VALUES (37743, 0, 6, 'Have we cleaned out the inn yet? Burn the rest of it when you\'re done!', 12, 0, 50, 1, 0, 0, 0, 37311, 0, 'say Text');
INSERT INTO `creature_text` VALUES (37743, 0, 7, 'Hurry it up. The soldiers have already pulled out of here', 12, 0, 50, 1, 0, 0, 0, 37309, 0, 'say Text');
INSERT INTO `creature_text` VALUES (37803, 0, 0, 'We will not die as slaves... we\'re Gilneans!', 12, 0, 100, 1, 0, 0, 0, 37400, 0, 'Marcus');
INSERT INTO `creature_text` VALUES (37804, 0, 0, 'Kaja\'Cola! Poissard the Weasel advise you to drink, or else ... !', 14, 0, 50, 0, 0, 0, 0, 0, 0, 'say Text');
INSERT INTO `creature_text` VALUES (37804, 0, 1, '\"Lift it challenge the Goa\" to drink Kaja\'Cola!', 14, 0, 50, 0, 0, 0, 0, 0, 0, 'say Text');
INSERT INTO `creature_text` VALUES (37804, 0, 2, 'Drink Kaja\'Cola!', 14, 0, 50, 0, 0, 0, 0, 37246, 0, 'say Text');
INSERT INTO `creature_text` VALUES (37804, 0, 3, 'Kaja\'Cola is proud to sponsor the Bilgewater Buccaneers!', 14, 0, 50, 0, 0, 0, 0, 0, 0, 'say Text');
INSERT INTO `creature_text` VALUES (37804, 0, 4, 'Kaja\'Cola! When your hacks take the upper hand!', 14, 0, 50, 0, 0, 0, 0, 0, 0, 'say Text');
INSERT INTO `creature_text` VALUES (37804, 0, 5, 'New k \' Cola! Still one hundred percent of ideas are more!', 14, 0, 50, 0, 0, 0, 0, 0, 0, 'say Text');
INSERT INTO `creature_text` VALUES (37806, 0, 0, 'Worthless mongrel.  I will order our outhouses cleaned with this rag you call a banner.', 14, 0, 100, 0, 0, 0, 0, 37412, 0, 'Captain Asther to Player');
INSERT INTO `creature_text` VALUES (37808, 0, 0, 'Corpse-men take Koroth\'s banner!  Corpse-men get smashed to bitses!!!', 14, 0, 100, 53, 0, 0, 0, 37409, 0, 'Koroth the Hillbreaker to Player');
INSERT INTO `creature_text` VALUES (37813, 0, 0, 'For every Horde soldier that you killed -- for every Alliance dog that fell, the Lich King\'s armies grew. Even now the val\'kyr work to raise your fallen as Scourge.\n', 14, 0, 0, 0, 0, 16701, 0, 37458, 0, 'Deathbringer Saurfang - SAY_INTRO_ALLIANCE_2');
INSERT INTO `creature_text` VALUES (37813, 1, 0, 'Things are about to get much worse. Come, taste the power that the Lich King has bestowed upon me!', 14, 0, 0, 0, 0, 16702, 0, 37459, 0, 'Deathbringer Saurfang - SAY_INTRO_ALLIANCE_3');
INSERT INTO `creature_text` VALUES (37813, 2, 0, 'Dwarves...', 14, 0, 0, 0, 0, 16703, 0, 37471, 0, 'Deathbringer Saurfang - SAY_INTRO_ALLIANCE_6');
INSERT INTO `creature_text` VALUES (37813, 3, 0, '%s immobilizes Muradin and his guards.', 16, 0, 0, 0, 0, 0, 0, 0, 0, 'Deathbringer Saurfang - SAY_INTRO_ALLIANCE_7');
INSERT INTO `creature_text` VALUES (37813, 4, 0, 'Join me, father. Join me and we will crush this world in the name of the Scourge -- for the glory of the Lich King!', 14, 0, 0, 0, 0, 16704, 0, 37635, 0, 'Deathbringer Saurfang - SAY_INTRO_HORDE_2');
INSERT INTO `creature_text` VALUES (37813, 5, 0, 'Stubborn and old. What chance do you have? I am stronger, and more powerful than you ever were.', 14, 0, 0, 5, 0, 16705, 0, 37639, 0, 'Deathbringer Saurfang - SAY_INTRO_HORDE_4');
INSERT INTO `creature_text` VALUES (37813, 6, 0, 'Pathetic old orc! Come then, heroes. Come and face the might of the Scourge!', 14, 0, 0, 15, 0, 16706, 0, 37644, 0, 'Deathbringer Saurfang - SAY_INTRO_HORDE_9');
INSERT INTO `creature_text` VALUES (37813, 7, 0, 'BY THE MIGHT OF THE LICH KING!', 14, 0, 0, 0, 0, 16694, 0, 38464, 0, 'Deathbringer Saurfang - SAY_AGGRO');
INSERT INTO `creature_text` VALUES (37813, 8, 0, 'The ground runs red with your blood!', 14, 0, 0, 0, 0, 16699, 0, 37995, 0, 'Deathbringer Saurfang - SAY_MARK_OF_THE_FALLEN_CHAMPION');
INSERT INTO `creature_text` VALUES (37813, 9, 0, 'Feast, my minions!', 14, 0, 0, 0, 0, 16700, 0, 38466, 0, 'Deathbringer Saurfang - SAY_BLOOD_BEASTS');
INSERT INTO `creature_text` VALUES (37813, 10, 0, 'You are nothing!', 14, 0, 0, 0, 0, 16695, 0, 37991, 0, 'Deathbringer Saurfang - SAY_KILL_1');
INSERT INTO `creature_text` VALUES (37813, 10, 1, 'Your soul will find no redemption here!', 14, 0, 0, 0, 0, 16696, 0, 37992, 0, 'Deathbringer Saurfang - SAY_KILL_2');
INSERT INTO `creature_text` VALUES (37813, 11, 0, '%s goes into a frenzy!', 41, 0, 0, 0, 0, 0, 0, 38630, 0, 'Deathbringer Saurfang - SAY_FRENZY');
INSERT INTO `creature_text` VALUES (37813, 12, 0, 'I have become... DEATH!', 14, 0, 0, 0, 0, 16698, 0, 37994, 0, 'Deathbringer Saurfang - SAY_BERSERK');
INSERT INTO `creature_text` VALUES (37813, 13, 0, 'I... Am... Released...', 14, 0, 0, 0, 0, 16697, 0, 37477, 0, 'Deathbringer Saurfang - SAY_DEATH');
INSERT INTO `creature_text` VALUES (37813, 14, 0, '%s\'s Blood Beasts gain the scent of blood!', 41, 0, 0, 0, 0, 0, 0, 38631, 0, 'Deathbringer Saurfang - EMOTE_SCENT_OF_BLOOD');
INSERT INTO `creature_text` VALUES (37846, 1, 0, 'Quel\'Delar.', 12, 0, 100, 1, 0, 16808, 0, 37484, 0, 'Blood Queen Lana\'thel - Say 1');
INSERT INTO `creature_text` VALUES (37846, 2, 0, 'Quiet, fools. If I had wished to kill you, you\'d already be dead.', 12, 0, 100, 0, 0, 0, 0, 37894, 0, 'Blood Queen Lana\'thel - Say 2');
INSERT INTO `creature_text` VALUES (37846, 3, 0, 'As Quel\'Serrar was forged by the dragons and given to the kaldorei, its twin, Quel\'Delar, was given to my people.', 12, 0, 100, 1, 0, 16809, 0, 37485, 0, 'Blood Queen Lana\'thel - Say 3');
INSERT INTO `creature_text` VALUES (37846, 4, 0, 'The king bestowed the blade upon my friend, Thalorien Dawnseeker.', 12, 0, 100, 1, 0, 16800, 0, 37486, 0, 'Blood Queen Lana\'thel - Say 4');
INSERT INTO `creature_text` VALUES (37846, 5, 0, 'But even Thalorien\'s skill and Quel\'Delar\'s magic could not save Silvermoon from the might of the Scourge.', 12, 0, 100, 1, 0, 16801, 0, 37487, 0, 'Blood Queen Lana\'thel - Say 5');
INSERT INTO `creature_text` VALUES (37846, 6, 0, 'Thalorien fell before the gates of the Sunwell, buying time for others to escape.', 12, 0, 100, 1, 0, 16802, 0, 37488, 0, 'Blood Queen Lana\'thel - Say 6');
INSERT INTO `creature_text` VALUES (37846, 7, 0, 'After the battle, I recovered the sword from the field. I bore it to Northrend in the service of my prince, seeking to avenge our people\'s defeat.', 12, 0, 100, 16, 0, 16803, 0, 37490, 0, 'Blood Queen Lana\'thel - Say 7');
INSERT INTO `creature_text` VALUES (37846, 8, 0, 'Arthas shattered our forces and took the most powerful of us into his service as the San\'layn.', 12, 0, 100, 1, 0, 16804, 0, 37491, 0, 'Blood Queen Lana\'thel - Say 8');
INSERT INTO `creature_text` VALUES (37846, 9, 0, 'My memories of Thalorien went cold and so did his blade.', 12, 0, 100, 1, 0, 16805, 0, 37492, 0, 'Blood Queen Lana\'thel - Say 9');
INSERT INTO `creature_text` VALUES (37846, 10, 0, 'It was I who brought Quel\'Delar here, to return it to its makers. In breaking the weapon, so did I break its power.', 12, 0, 100, 25, 0, 16806, 0, 37493, 0, 'Blood Queen Lana\'thel - Say 10');
INSERT INTO `creature_text` VALUES (37846, 11, 0, 'Quel\'Delar will never serve another!', 12, 0, 100, 5, 0, 16807, 0, 37494, 0, 'Blood Queen Lana\'thel - Say 11');
INSERT INTO `creature_text` VALUES (37857, 0, 0, 'So predictable...', 12, 0, 100, 396, 0, 17252, 0, 37506, 0, 'The Lich King');
INSERT INTO `creature_text` VALUES (37857, 1, 0, 'Did you truly expect to fulfill Mograine\'s task unopposed?', 12, 0, 100, 6, 0, 17253, 0, 37507, 0, 'The Lich King');
INSERT INTO `creature_text` VALUES (37857, 2, 0, 'You both shall suffer as his father does...', 12, 0, 100, 396, 0, 17254, 0, 37508, 0, 'The Lich King');
INSERT INTO `creature_text` VALUES (37857, 3, 0, '...in eternal unrest!', 12, 0, 100, 0, 0, 17255, 0, 37509, 0, 'The Lich King');
INSERT INTO `creature_text` VALUES (37857, 4, 0, 'Die well, fool.', 12, 0, 100, 397, 0, 17256, 0, 37510, 0, 'The Lich King');
INSERT INTO `creature_text` VALUES (37870, 0, 0, 'Just as Goldrinn\'s spirit once blessed our druids, let $n be blessed with the wisdom of his race and the ferocity of the wolf god.', 12, 0, 100, 1, 0, 0, 0, 37727, 0, 'Lyros Swiftwind to Player');
INSERT INTO `creature_text` VALUES (37873, 0, 0, 'Just as Daral\'nir soothes the cursed druids who gave into the beast and abandoned balance, let Tal\'doren soothe $n.', 12, 0, 100, 1, 0, 0, 0, 37729, 0, 'Vassandra Stormclaw to Player');
INSERT INTO `creature_text` VALUES (37875, 0, 0, 'No... I\'d sooner die than have one of your kind for a king!', 12, 0, 100, 0, 0, 19631, 0, 38633, 0, 'Lord Godfrey to Player');
INSERT INTO `creature_text` VALUES (37876, 0, 0, 'It\'s over Godfrey.  You have no support left among the eastern lords.', 12, 0, 100, 1, 0, 0, 0, 38632, 0, 'King Genn Greymane to Player');
INSERT INTO `creature_text` VALUES (37879, 0, 0, 'Stand down, Muradin. Let a grieving father pass.', 12, 0, 0, 0, 0, 16690, 0, 37527, 0, 'King Varian Wrynn - SAY_OUTRO_ALLIANCE_11');
INSERT INTO `creature_text` VALUES (37879, 1, 0, 'I... I was not at the Wrath Gate, but the soldiers who survived told me much of what happened. Your son fought with honor. He died a hero\'s death. He deserves a hero\'s burial.', 12, 0, 0, 0, 0, 16691, 0, 37530, 0, 'King Varian Wrynn - SAY_OUTRO_ALLIANCE_16');
INSERT INTO `creature_text` VALUES (37879, 2, 0, 'Jaina? Why are you crying?', 12, 0, 0, 0, 0, 16692, 0, 37543, 0, 'King Varian Wrynn - SAY_OUTRO_ALLIANCE_18');
INSERT INTO `creature_text` VALUES (37879, 3, 0, 'Bah! Muradin, secure the deck and prepare our soldiers for an assault on the upper citadel. I\'ll send out another regiment from Stormwind.\n', 12, 0, 0, 0, 0, 16693, 0, 37545, 0, 'King Varian Wrynn - SAY_OUTRO_ALLIANCE_20');
INSERT INTO `creature_text` VALUES (37893, 0, 0, 'I hear and obey, master...', 12, 0, 100, 0, 0, 17073, 0, 37650, 0, 'Vegard the Unforgiven');
INSERT INTO `creature_text` VALUES (37893, 1, 0, 'The enemy shall suffer greatly, master!', 12, 0, 100, 0, 0, 17074, 0, 37651, 0, 'Vegard the Unforgiven');
INSERT INTO `creature_text` VALUES (37915, 0, 0, 'Guards!', 14, 6, 100, 0, 0, 0, 0, 4583, 0, 'Timothy Cunningham');
INSERT INTO `creature_text` VALUES (37951, 0, 0, '$n. Zuni. Ya\'ll find ya trainer in the trainin\' grounds to the east. Bring pride to the Darkspear.', 12, 0, 100, 1, 0, 0, 0, 37821, 0, 'Jin\'thala');
INSERT INTO `creature_text` VALUES (37953, 0, 0, 'How did you--?!  It doesn\'t matter -- I don\'t need a trap to defeat you.', 12, 0, 100, 0, 0, 0, 0, 37673, 0, 'Dark Scout to Player');
INSERT INTO `creature_text` VALUES (37955, 0, 0, 'You have made an... unwise... decision.', 14, 0, 0, 0, 0, 16782, 0, 38048, 0, 'Blood-Queen Lana\'thel - SAY_AGGRO');
INSERT INTO `creature_text` VALUES (37955, 1, 0, 'Just a taste...', 14, 0, 0, 0, 0, 16783, 0, 38053, 0, 'Blood-Queen Lana\'thel - SAY_VAMPIRIC_BITE');
INSERT INTO `creature_text` VALUES (37955, 1, 1, 'Know my hunger!', 14, 0, 0, 0, 0, 16784, 0, 38058, 0, 'Blood-Queen Lana\'thel - SAY_VAMPIRIC_BITE');
INSERT INTO `creature_text` VALUES (37955, 2, 0, 'Yes! Feed, my precious one! You are mine now!', 14, 0, 0, 0, 0, 16790, 0, 38065, 0, 'Blood-Queen Lana\'thel - SAY_MIND_CONTROL');
INSERT INTO `creature_text` VALUES (37955, 3, 0, 'You thirst for blood!', 42, 0, 0, 0, 0, 0, 0, 38515, 0, 'Blood-Queen Lana\'thel - EMOTE_BLOODTHIRST');
INSERT INTO `creature_text` VALUES (37955, 4, 0, 'SUFFER!', 14, 0, 75, 0, 0, 16786, 0, 38061, 0, 'Blood-Queen Lana\'thel - SAY_SWARMING_SHADOWS');
INSERT INTO `creature_text` VALUES (37955, 5, 0, 'Shadows amass and swarm around $n!', 41, 0, 0, 0, 0, 0, 0, 38619, 0, 'Blood-Queen Lana\'thel - EMOTE_SWARMING_SHADOWS');
INSERT INTO `creature_text` VALUES (37955, 6, 0, 'Can you handle this?', 14, 0, 75, 0, 0, 16787, 0, 38062, 0, 'Blood-Queen Lana\'thel - SAY_PACT_OF_THE_DARKFALLEN');
INSERT INTO `creature_text` VALUES (37955, 7, 0, 'Here it comes...', 14, 0, 0, 0, 0, 16788, 0, 38063, 0, 'Blood-Queen Lana\'thel - SAY_AIR_PHASE');
INSERT INTO `creature_text` VALUES (37955, 8, 0, 'Really...? Is that all you\'ve got?', 14, 0, 0, 0, 0, 16791, 0, 38066, 0, 'Blood-Queen Lana\'thel - SAY_KILL');
INSERT INTO `creature_text` VALUES (37955, 8, 1, 'Such a pity.', 14, 0, 0, 0, 0, 16792, 0, 38067, 0, 'Blood-Queen Lana\'thel - SAY_KILL');
INSERT INTO `creature_text` VALUES (37955, 9, 0, 'How... unfortunate.', 14, 0, 0, 0, 0, 16789, 0, 38064, 0, 'Blood-Queen Lana\'thel - SAY_WIPE');
INSERT INTO `creature_text` VALUES (37955, 10, 0, 'This ends NOW!', 14, 0, 0, 0, 0, 16793, 0, 38068, 0, 'Blood-Queen Lana\'thel - SAY_BERSERK');
INSERT INTO `creature_text` VALUES (37955, 11, 0, 'But... we were getting along... so... well...', 14, 0, 0, 0, 0, 16794, 0, 38069, 0, 'Blood-Queen Lana\'thel - SAY_DEATH');
INSERT INTO `creature_text` VALUES (37955, 12, 0, '%s goes into a berserker rage!', 41, 0, 100, 0, 0, 0, 0, 34057, 0, 'blood queen lana thel EMOTE_BERSERK_RAID');
INSERT INTO `creature_text` VALUES (37970, 0, 0, 'Naxxanar was merely a setback! With the power of the orb, Valanar will have his vengeance!', 14, 0, 0, 0, 0, 16685, 0, 38001, 0, 'Prince Valanar - SAY_VALANAR_INVOCATION');
INSERT INTO `creature_text` VALUES (37970, 1, 0, 'Invocation of Blood jumps to Prince Valanar!', 41, 0, 0, 0, 0, 0, 0, 38589, 0, 'Prince Valanar - EMOTE_VALANAR_INVOCATION');
INSERT INTO `creature_text` VALUES (37970, 2, 0, 'My cup runneth over.', 14, 0, 0, 0, 0, 16686, 0, 38002, 0, 'Prince Valanar - SAY_VALANAR_SPECIAL');
INSERT INTO `creature_text` VALUES (37970, 3, 0, '%s begins casting Empowered Shock Vortex!', 41, 0, 0, 0, 0, 0, 0, 38972, 0, 'Prince Valanar - EMOTE_VALANAR_SHOCK_VORTEX');
INSERT INTO `creature_text` VALUES (37970, 4, 0, 'Dinner... is served.', 14, 0, 0, 0, 0, 16681, 0, 37998, 0, 'Prince Valanar - SAY_VALANAR_KILL_1');
INSERT INTO `creature_text` VALUES (37970, 4, 1, 'Do you see NOW the power of the Darkfallen?', 14, 0, 0, 0, 0, 16682, 0, 37999, 0, 'Prince Valanar - SAY_VALANAR_KILL_2');
INSERT INTO `creature_text` VALUES (37970, 5, 0, 'BOW DOWN BEFORE THE SAN\'LAYN!', 14, 0, 0, 0, 0, 16684, 0, 38000, 0, 'Prince Valanar - SAY_VALANAR_BERSERK');
INSERT INTO `creature_text` VALUES (37970, 6, 0, '...why...?', 14, 0, 0, 0, 0, 16683, 0, 0, 0, 'Prince Valanar - SAY_VALANAR_DEATH');
INSERT INTO `creature_text` VALUES (37972, 0, 0, 'Such wondrous power! The Darkfallen Orb has made me INVINCIBLE!', 14, 0, 0, 0, 0, 16727, 0, 38006, 0, 'Prince Keleseth - SAY_KELESETH_INVOCATION');
INSERT INTO `creature_text` VALUES (37972, 1, 0, 'Invocation of Blood jumps to Prince Keleseth!', 41, 0, 0, 0, 0, 0, 0, 38588, 0, 'Prince Keleseth - EMOTE_KELESETH_INVOCATION');
INSERT INTO `creature_text` VALUES (37972, 2, 0, 'Blood will flow.', 14, 0, 0, 0, 0, 16728, 0, 38007, 0, 'Prince Keleseth - SAY_KELESETH_SPECIAL');
INSERT INTO `creature_text` VALUES (37972, 3, 0, 'Were you ever a threat?', 14, 0, 0, 0, 0, 16723, 0, 38003, 0, 'Prince Keleseth - SAY_KELESETH_KILL_1');
INSERT INTO `creature_text` VALUES (37972, 3, 1, 'Truth is found in death.', 14, 0, 0, 0, 0, 16724, 0, 38004, 0, 'Prince Keleseth - SAY_KELESETH_KILL_2');
INSERT INTO `creature_text` VALUES (37972, 4, 0, '%s cackles maniacally.\n', 16, 0, 0, 0, 0, 16726, 0, 13022, 0, 'Prince Keleseth - EMOTE_KELESETH_BERSERK');
INSERT INTO `creature_text` VALUES (37972, 5, 0, 'My queen, they... come.', 14, 0, 0, 0, 0, 16725, 0, 38005, 0, 'Prince Keleseth - SAY_KELESETH_DEATH');
INSERT INTO `creature_text` VALUES (37973, 0, 0, 'Tremble before Taldaram, mortals, for the power of the orb flows through me!', 14, 0, 0, 0, 0, 16857, 0, 38010, 0, 'Prince Taldaram - SAY_TALDARAM_INVOCATION');
INSERT INTO `creature_text` VALUES (37973, 1, 0, 'Invocation of Blood jumps to Prince Taldaram!', 41, 0, 0, 0, 0, 0, 0, 38590, 0, 'Prince Taldaram - EMOTE_TALDARAM_INVOCATION');
INSERT INTO `creature_text` VALUES (37973, 2, 0, 'Delight in the pain!', 14, 0, 0, 0, 0, 16858, 0, 38011, 0, 'Prince Taldaram - SAY_TALDARAM_SPECIAL');
INSERT INTO `creature_text` VALUES (37973, 3, 0, 'Empowered Flames speed toward $n!', 41, 0, 0, 0, 0, 0, 0, 38624, 0, 'Prince Taldaram - EMOTE_TALDARAM_FLAME');
INSERT INTO `creature_text` VALUES (37973, 4, 0, 'Worm food.', 14, 0, 0, 0, 0, 16853, 0, 38008, 0, 'Prince Taldaram - SAY_TALDARAM_KILL_1');
INSERT INTO `creature_text` VALUES (37973, 4, 1, 'Beg for mercy!', 14, 0, 0, 0, 0, 16854, 0, 38009, 0, 'Prince Taldaram - SAY_TALDARAM_KILL_2');
INSERT INTO `creature_text` VALUES (37973, 5, 0, '%s laughs.', 16, 0, 0, 0, 0, 16856, 0, 37127, 0, 'Prince Taldaram - EMOTE_TALDARAM_BERSERK');
INSERT INTO `creature_text` VALUES (37973, 6, 0, '%s gurgles and dies.', 16, 0, 0, 0, 0, 16855, 0, 0, 0, 'Prince Taldaram - EMOTE_TALDARAM_DEATH');
INSERT INTO `creature_text` VALUES (37976, 0, 0, 'Fear not, master. I shall rend the flesh of your enemy!', 12, 0, 100, 0, 0, 17077, 0, 37657, 0, 'Vegard the Unforgiven');
INSERT INTO `creature_text` VALUES (37976, 1, 0, 'Master... I have... failed.', 12, 0, 100, 0, 0, 17076, 0, 37656, 0, 'Vegard the Unforgiven');
INSERT INTO `creature_text` VALUES (37976, 2, 0, 'It is done, master.', 12, 0, 100, 0, 0, 17075, 0, 37655, 0, 'Vegard the Unforgiven');
INSERT INTO `creature_text` VALUES (37988, 0, 0, 'Ya, mon. Let\'s crack some tiki target skulls!', 12, 0, 100, 5, 0, 21366, 0, 37818, 0, 'Jin\'thala Lvl 1');
INSERT INTO `creature_text` VALUES (37988, 1, 0, 'Ya trainer should be somewhere in the grounds \'ere. I\'ll catch you lata, mon.', 12, 0, 100, 1, 0, 21367, 0, 39087, 0, 'Jin\'thala Lvl 1');
INSERT INTO `creature_text` VALUES (38004, 0, 0, 'Foolish mortals. You thought us defeated so easily? The San\'layn are the Lich King\'s immortal soldiers! Now you shall face their might combined!', 14, 0, 0, 1, 0, 16795, 0, 37997, 0, 'Blood-Queen Lana\'thel - SAY_INTRO_1');
INSERT INTO `creature_text` VALUES (38004, 1, 0, 'Rise up, brothers, and destroy our enemies!', 14, 0, 0, 0, 0, 16796, 0, 38079, 0, 'Blood-Queen Lana\'thel - SAY_INTRO_2');
INSERT INTO `creature_text` VALUES (38017, 0, 0, 'Are you certain you should be helping these mortals in their quest for the sword?', 12, 0, 100, 1, 0, 0, 0, 37774, 0, 'Kalecgos');
INSERT INTO `creature_text` VALUES (38017, 1, 0, 'These blades, Krasus... They were made long ago, when things were... different.', 12, 0, 100, 1, 0, 0, 0, 37775, 0, 'Kalecgos');
INSERT INTO `creature_text` VALUES (38017, 2, 0, 'Our enemies once turned our strongest weapon against us. What makes you think the prismatic blades will be any different?', 12, 0, 100, 1, 0, 0, 0, 37782, 0, 'Kalecgos');
INSERT INTO `creature_text` VALUES (38017, 3, 0, 'As you wish, my queen. I will not stand in their way, but I will keep a close watch.', 12, 0, 100, 16, 0, 0, 0, 37783, 0, 'Kalecgos');
INSERT INTO `creature_text` VALUES (38029, 0, 0, 'Keep them occupied, my brothers!  Allow $n to retrieve the scythe!', 14, 0, 100, 0, 0, 0, 0, 37795, 0, 'Tobias Mistmantle to Player');
INSERT INTO `creature_text` VALUES (38029, 1, 0, 'Fall back!  Retreat into the woods!', 14, 0, 100, 0, 0, 0, 0, 37796, 0, 'Tobias Mistmantle to Player');
INSERT INTO `creature_text` VALUES (38051, 0, 0, 'Darius!  The Dark Rangers have the Scythe!  They got to it before we were able to reach it.', 12, 0, 100, 0, 0, 0, 0, 37810, 0, 'Tobias Mistmantle to Player');
INSERT INTO `creature_text` VALUES (38051, 1, 0, 'It will be done!', 12, 0, 100, 0, 0, 0, 0, 37812, 0, 'Tobias Mistmantle to Player');
INSERT INTO `creature_text` VALUES (38065, 0, 0, 'That crate won\'t deliver itself, friend. Get a move on!', 12, 0, 100, 1, 0, 0, 0, 38024, 0, 'Crown Supply Guard');
INSERT INTO `creature_text` VALUES (38065, 0, 1, 'Time is money, friend. Go go go!', 12, 0, 100, 1, 0, 0, 0, 38022, 0, 'Crown Supply Guard');
INSERT INTO `creature_text` VALUES (38065, 0, 2, 'The Lovely Merchant is waiting for that. Hurry it up!', 12, 0, 100, 1, 0, 0, 0, 38023, 0, 'Crown Supply Guard');
INSERT INTO `creature_text` VALUES (38066, 0, 0, 'You know, that does smell pretty... Hey! Stop spraying that thing at me!', 12, 0, 100, 1, 0, 0, 0, 38296, 0, 'Inspector Snip Snagglebolt');
INSERT INTO `creature_text` VALUES (38066, 1, 0, 'Don\'t tempt me! Do you even know what\'s in those things?', 12, 0, 100, 1, 0, 0, 0, 38382, 0, 'Inspector Snip Snagglebolt');
INSERT INTO `creature_text` VALUES (38112, 0, 0, 'Men, women, and children... None were spared the master\'s wrath. Your death will be no different.', 14, 0, 100, 0, 0, 16710, 0, 37931, 0, 'falric SAY_AGGRO');
INSERT INTO `creature_text` VALUES (38112, 1, 0, 'Sniveling maggot!', 14, 0, 100, 0, 0, 16711, 0, 37941, 0, 'falric SAY_SLAY_1');
INSERT INTO `creature_text` VALUES (38112, 1, 1, 'The children of Stratholme fought with more ferocity!', 14, 0, 100, 0, 0, 16712, 0, 37942, 0, 'falric SAY_SLAY_2');
INSERT INTO `creature_text` VALUES (38112, 2, 0, 'Marwyn, finish them...', 14, 0, 100, 0, 0, 16713, 0, 37943, 0, 'falric SAY_DEATH');
INSERT INTO `creature_text` VALUES (38112, 3, 0, 'Despair... so delicious...', 14, 0, 100, 0, 0, 16715, 0, 37945, 0, 'falric SAY_IMPENDING_DESPAIR');
INSERT INTO `creature_text` VALUES (38112, 4, 0, 'Fear... so exhilarating...', 14, 0, 100, 0, 0, 16716, 0, 37946, 0, 'falric SAY_DEFILING_HORROR');
INSERT INTO `creature_text` VALUES (38112, 5, 0, 'As you wish, my lord.', 14, 0, 100, 0, 0, 16717, 0, 37953, 0, 'Falric SAY_FALRIC_INTRO_1');
INSERT INTO `creature_text` VALUES (38112, 6, 0, 'Soldiers of Lordaeron, rise to meet your master\'s call!', 14, 0, 100, 0, 0, 16714, 0, 37944, 0, 'Falric SAY_FALRIC_INTRO_2');
INSERT INTO `creature_text` VALUES (38113, 0, 0, 'Death is all that you will find here!', 14, 0, 100, 0, 0, 16734, 0, 37948, 0, 'marwyn SAY_AGGRO');
INSERT INTO `creature_text` VALUES (38113, 1, 0, 'I saw the same look in his eyes when he died. Terenas could hardly believe it.', 14, 0, 100, 0, 0, 16735, 0, 37949, 0, 'marwyn SAY_SLAY_1');
INSERT INTO `creature_text` VALUES (38113, 1, 1, 'Choke on your suffering!', 14, 0, 100, 0, 0, 16736, 0, 37950, 0, 'marwyn SAY_SLAY_2');
INSERT INTO `creature_text` VALUES (38113, 2, 0, 'Yes... Run... Run to meet your destiny... Its bitter, cold embrace, awaits you.', 14, 0, 100, 0, 0, 16737, 0, 37951, 0, 'marwyn SAY_DEATH');
INSERT INTO `creature_text` VALUES (38113, 3, 0, 'Your flesh shall decay before your very eyes!', 14, 0, 100, 0, 0, 16739, 0, 37954, 0, 'marwyn SAY_CORRUPTED_FLESH_1');
INSERT INTO `creature_text` VALUES (38113, 3, 1, 'Waste away into nothingness!', 14, 0, 100, 0, 0, 16740, 0, 37955, 0, 'marwyn SAY_CORRUPTED_FLESH_2');
INSERT INTO `creature_text` VALUES (38113, 4, 0, 'As you wish, my lord.', 14, 0, 100, 0, 0, 16741, 0, 37953, 0, 'marwyn SAY_MARWYN_INTRO_1');
INSERT INTO `creature_text` VALUES (38120, 0, 0, 'It\'s really quite simple, $n. Just attach these fireworks to the wild cluckers and I\'ll use this controller to fly them into the cage.', 12, 0, 100, 5, 0, 0, 0, 49894, 0, 'Hobart Grapplehammer to Player');
INSERT INTO `creature_text` VALUES (38120, 1, 0, '$n, over here if you please.', 12, 0, 100, 3, 0, 0, 0, 41262, 0, 'Hobart Grapplehammer to Player');
INSERT INTO `creature_text` VALUES (38120, 2, 0, 'Don\'t underestimate the Mechachicken, $n. After all, it is a Hobart Grapplehammer invention!', 12, 0, 100, 274, 0, 0, 0, 49900, 0, 'Hobart Grapplehammer to Player');
INSERT INTO `creature_text` VALUES (38120, 3, 0, 'Greely, load The Biggest Egg Ever into the Poultryizer. Mechnuggets for everyone!', 14, 0, 100, 5, 0, 0, 0, 38106, 0, 'Hobart Grapplehammer to Player');
INSERT INTO `creature_text` VALUES (38120, 4, 0, 'Shut it down, shut it down! The eggs are bad!!!', 14, 0, 100, 0, 0, 0, 0, 38170, 0, 'Hobart Grapplehammer to Player');
INSERT INTO `creature_text` VALUES (38120, 5, 0, 'The marketing research Megs has conducted suggests that a naga invasion is imminent!', 12, 0, 100, 5, 0, 0, 0, 49934, 0, 'Hobart Grapplehammer to Player');
INSERT INTO `creature_text` VALUES (38120, 6, 0, 'Take your time, my $g boy : girl;. Now what can I modify this shredder boot to be? Hmm.', 12, 0, 100, 6, 0, 0, 0, 50086, 0, 'Hobart Grapplehammer to Player');
INSERT INTO `creature_text` VALUES (38120, 7, 0, 'I\'ve set the Bootzooka to fire several rockets per shot. I\'m fairly certain that I perfectly calibrated the targeting.', 12, 0, 100, 273, 0, 0, 0, 50087, 0, 'Hobart Grapplehammer to Player');
INSERT INTO `creature_text` VALUES (38120, 8, 0, 'Booster rockets for cages. What will I think of next? I am a genius!', 12, 0, 100, 11, 0, 0, 0, 50112, 0, 'Hobart Grapplehammer to Player');
INSERT INTO `creature_text` VALUES (38122, 0, 0, 'Hey, $n, come over here, would ya? I\'ve got something for you to do.', 12, 0, 100, 70, 0, 6820, 0, 41261, 0, 'Bamm Megabomb to Player');
INSERT INTO `creature_text` VALUES (38122, 1, 0, 'Now get us some real eggs.', 12, 0, 100, 273, 0, 0, 0, 49899, 0, 'Bamm Megabomb to Player');
INSERT INTO `creature_text` VALUES (38124, 0, 0, 'Genius idea, sir. Your brain power never ceases to amaze me!', 14, 0, 100, 1, 0, 0, 0, 38119, 0, 'Assistant Greely to Player');
INSERT INTO `creature_text` VALUES (38124, 1, 0, 'This thing is huge! Reverse the polarity on the Poultryizer. We\'ll have to shove it in through the outtake.', 14, 0, 100, 5, 0, 0, 0, 38141, 0, 'Assistant Greely to Player');
INSERT INTO `creature_text` VALUES (38124, 2, 0, 'There it goes.  It should only be a moment now....', 14, 0, 100, 1, 0, 0, 0, 38142, 0, 'Assistant Greely to Player');
INSERT INTO `creature_text` VALUES (38124, 3, 0, '$n, get to the Mechashark X-Steam Controller. Rid us of the Hammer menace!', 12, 0, 100, 1, 0, 0, 0, 49911, 0, 'Assistant Greely to Player');
INSERT INTO `creature_text` VALUES (38124, 4, 0, 'Look for the mounds of glowing stuff on the ground.', 12, 0, 100, 1, 0, 0, 0, 50082, 0, 'Assistant Greely to Player');
INSERT INTO `creature_text` VALUES (38124, 5, 0, 'Greely drools a little as she continues her mindless labor.', 16, 0, 100, 0, 0, 0, 0, 50101, 0, 'Assistant Greely to Player');
INSERT INTO `creature_text` VALUES (38124, 6, 0, 'Get rid of those Steamwheedle Sharks before they trounce us!', 12, 0, 100, 1, 0, 0, 0, 50105, 0, 'Assistant Greely to Player');
INSERT INTO `creature_text` VALUES (38124, 7, 0, 'It\'s time for a little sabotage!', 12, 0, 100, 11, 0, 0, 0, 50109, 0, 'Assistant Greely to Player');
INSERT INTO `creature_text` VALUES (38124, 8, 0, 'Deliver the footbomb uniform to the Slave Pits. Hurry, $n!', 12, 0, 100, 25, 0, 0, 0, 50111, 0, 'Assistant Greely to Player');
INSERT INTO `creature_text` VALUES (38160, 0, 0, 'Excellent work, champions! We shall set up our base camp in these chambers. My magi will get the Scourge transport device working shortly. Step inside it when you are ready for your next mission. I will meet you on the other side.', 14, 0, 100, 0, 0, 16625, 0, 37369, 0, 'jaina SAY_JAINA_OUTRO');
INSERT INTO `creature_text` VALUES (38161, 0, 0, 'Excellent work, champions! We shall set up our base camp in these chambers. My magi will get the Scourge transport device working shortly. Step inside when you are ready for your next mission. I will meet you on the other side.', 14, 0, 100, 0, 0, 17044, 0, 37387, 0, 'sylvanas SAY_SYLVANAS_OUTRO');
INSERT INTO `creature_text` VALUES (38218, 0, 0, 'The Forsaken think we\'re weak.  A broken people.  They think we\'ll roll over like a scared dog.', 14, 0, 100, 0, 0, 19623, 0, 37888, 0, 'Prince Liam Greymane to Krennan Aranas');
INSERT INTO `creature_text` VALUES (38218, 1, 0, 'How wrong they are.  We will fight them in the fields until the last trench collapses and the last cannon is silenced.', 14, 0, 100, 0, 0, 19624, 0, 37893, 0, 'Prince Liam Greymane to Krennan Aranas');
INSERT INTO `creature_text` VALUES (38218, 2, 0, 'We will fight them on the streets until the last shot is fired.  And when there\'s no more ammunition, we\'ll crush their skulls with the stones that pave our city.', 14, 0, 100, 0, 0, 19625, 0, 37891, 0, 'Prince Liam Greymane to Krennan Aranas');
INSERT INTO `creature_text` VALUES (38218, 3, 0, 'We will fight them in the alleys, until our knuckles are skinned and bloody and our rapiers lay on the ground shattered.', 14, 0, 100, 0, 0, 19626, 0, 37892, 0, 'Prince Liam Greymane to Krennan Aranas');
INSERT INTO `creature_text` VALUES (38218, 4, 0, 'And if we find ourselves surrounded and disarmed... wounded and without hope... we will lift our heads in defiance and spit in their faces.', 14, 0, 100, 25, 0, 19627, 0, 37895, 0, 'Prince Liam Greymane to Krennan Aranas');
